INSERT INTO `products` (`id`, `nameOfProduct`, `description`, `category`, `price`, `weightDefault`, `dateOfImporting`, `expriredDay`, `imgAssetId`, `imgPublicId`, `adminCreate`, `provider`, `status`) VALUES
(1, 'Sầu riêng cơm Ri6', '<h1><strong>Sầu ri&ecirc;ng cơm Ri6</strong></h1>\r\n\r\n<p><strong>- Xuất xứ:&nbsp;</strong>Việt Nam</p>\r\n\r\n<p>-&nbsp;<strong>Ti&ecirc;u chuẩn:&nbsp;</strong>Tr&aacute;i c&acirc;y sạch</p>\r\n\r\n<p><strong>- Đặc Điểm:</strong></p>\r\n\r\n<ul>\r\n	<li>Sầu ri&ecirc;ng c&oacute; m&ugrave;i đậm v&agrave; vị ngọt b&eacute;o đặc trưng.</li>\r\n	<li>Cơm d&agrave;y, v&agrave;ng v&agrave; dẻo hơn.</li>\r\n	<li>Thường được ăn trực tiếp hoặc l&agrave;m kem, b&aacute;nh.</li>\r\n</ul>\r\n\r\n<p><strong>Sầu Ri&ecirc;ng&nbsp;</strong>c&oacute; một m&ugrave;i hương độc đ&aacute;o, lưu lại trong thời gian d&agrave;i v&agrave; lan nhanh trong diện rộng. C&oacute; nhiều người sẽ kh&ocirc;ng th&iacute;ch m&ugrave;i thơm của sầu ri&ecirc;ng, nhưng đến khi quen rồi, bạn sẽ cảm thấy &quot;nghiện&quot;. Sầu ri&ecirc;ng Ri6 được trồng v&agrave; thu hoạch theo ti&ecirc;u chuẩn an to&agrave;n thực phẩm, c&oacute; vị ngọt đậm, b&eacute;o, cơm dẻo, d&agrave;y v&agrave; v&agrave;ng. Người ta thường d&ugrave;ng sầu ri&ecirc;ng để ăn trực tiếp hoặc chế biến c&aacute;c m&oacute;n ăn vặt.</p>\r\n\r\n<p><strong>- Hướng dẫn sử dụng</strong></p>\r\n\r\n<ul>\r\n	<li>Để t&aacute;ch vỏ sầu ri&ecirc;ng, bạn cần x&aacute;c định phần m&uacute;i v&agrave; cắt từ đỉnh tới giữa m&uacute;i.&nbsp;</li>\r\n	<li>Bạn cắt dọc như vậy từ 2 đến 4 m&uacute;i của quả.&nbsp;</li>\r\n	<li>Tiếp theo, d&ugrave;ng tay t&aacute;ch quả sầu ri&ecirc;ng theo đường cắt.&nbsp;</li>\r\n	<li>Cuối c&ugrave;ng, lấy phần thịt bỏ v&agrave;o hộp hoặc đĩa rồi t&aacute;ch cho hết m&uacute;i.&nbsp;</li>\r\n</ul>\r\n\r\n<p><strong>Hướng dẫn bảo quản</strong></p>\r\n\r\n<ul>\r\n	<li>Cần bảo vệ sầu ri&ecirc;ng ở nhiệt độ thấp, th&iacute;ch hợp để tr&aacute;nh t&igrave;nh trạng bị nứt vỏ, thối vỏ.</li>\r\n	<li>Thường nh&agrave; sản xuất để nhiệt độ th&iacute;ch hợp trong kho lạnh l&agrave; dưới 15 độ C</li>\r\n	<li>Đối với sầu ri&ecirc;ng nguy&ecirc;n quả th&igrave; khi chưa d&ugrave;ng ngay bạn n&ecirc;n để ở nơi tho&aacute;ng m&aacute;t, kh&ocirc; r&aacute;o</li>\r\n	<li>C&ograve;n nếu bạn mua sầu ri&ecirc;ng đ&atilde; được&nbsp;t&aacute;ch sẵn vỏ nhưng bạn chưa muốn ăn ngay th&igrave; c&aacute;ch bảo quản tốt nhất l&agrave; cho v&agrave;o hộp k&iacute;n, bọc cẩn thận rồi để v&agrave;o ngăn m&aacute;t của tủ lạnh khoảng 2 đến 3 ng&agrave;y. Hoặc c&oacute; thể để trong t&uacute;i h&uacute;t ch&acirc;n kh&ocirc;ng.</li>\r\n	<li>Tuy nhi&ecirc;n c&oacute; 1 điều bạn cũng cần ch&uacute; &yacute; đ&oacute; l&agrave; nếu như sầu ri&ecirc;ng đ&atilde; ch&iacute;n kỹ th&igrave; bạn n&ecirc;n để nhiệt độ b&ecirc;n ngo&agrave;i v&agrave; n&ecirc;n sử dụng</li>\r\n</ul>\r\n\r\n<p><strong><em>Lưu &yacute;:</em></strong></p>\r\n\r\n<ul>\r\n	<li>C&aacute;c bạn n&ecirc;n d&ugrave;ng c&agrave;ng sớm c&agrave;ng tốt bởi quả sẽ giữ được độ tươi ngon trong thời điểm đầu c&ograve;n khi để sang ng&agrave;y thứ 3 hoặc qu&aacute; l&acirc;u sầu ri&ecirc;ng sẽ bị ch&iacute;n qu&aacute; dẫn đến bị cay hoặc đắng khi ăn.</li>\r\n	<li>Nhiều gia đ&igrave;nh thường bảo quản sầu ri&ecirc;ng bằng c&aacute;ch bỏ nguy&ecirc;n m&uacute;i v&agrave;o ngăn đ&aacute; tủ lạnh, khi sử dụng th&igrave; chất lượng giảm đi rất nhiều, m&uacute;i nhũn nước v&agrave; h&oacute;a đen.</li>\r\n</ul>\r\n\r\n<p><strong>- Th&ocirc;ng tin dinh dưỡng:</strong></p>\r\n\r\n<ul>\r\n	<li>Sầu ri&ecirc;ng c&oacute; chứa một lượng vitamin B kh&aacute; cao, c&aacute;c vitamin B sẽ c&oacute; một loạt c&aacute;c lợi &iacute;ch cho sức khỏe như ngăn ngừa l&atilde;o h&oacute;a v&agrave; bệnh tim, gi&uacute;p tăng HDL (cholesterol tốt) v&agrave; thậm ch&iacute; c&oacute; thể gi&uacute;p cải thiện t&acirc;m trạng, giảm bớt trầm cảm.</li>\r\n	<li>Trong sầu ri&ecirc;ng chứa một lượng&nbsp;kali phong ph&uacute;,&nbsp;l&agrave; chất điện ph&acirc;n quan trọng gi&uacute;p kiểm so&aacute;t huyết &aacute;p, giữ cho nồng độ natri ở mức độ cho ph&eacute;p v&agrave; gi&uacute;p điều h&ograve;a nhịp tim.</li>\r\n	<li>Một trong những lợi &iacute;ch kh&aacute;c của sầu ri&ecirc;ng l&agrave; c&oacute; chứa h&agrave;m lượng chất xơ cao. Mỗi khẩu phần ăn n&ecirc;n cần chứa 9g chất xơ tương đương 37% nhu cầu h&agrave;ng ng&agrave;y của bạn, điều n&agrave;y thật kh&oacute; thực hiện khi hầu như loại vi chất n&agrave;y c&oacute; nhiều trong tr&aacute;i c&acirc;y hơn l&agrave; c&aacute;c bữa ăn. Ch&iacute;nh v&igrave; thế, nếu bạn cần hấp thụ một lượng chất xơ ổn định, th&igrave; sầu ri&ecirc;ng l&agrave; loại tr&aacute;i c&acirc;y tốt để bạn sử dụng.</li>\r\n	<li>S&acirc;u ri&ecirc;ng c&ograve;n l&agrave; loại tr&aacute;i c&acirc;y chứa nhiều vitamin C, gi&uacute;p tăng cường sức đề kh&aacute;ng, giảm cholesterol, hỗ trợ chữa l&agrave;nh vết thương.</li>\r\n</ul>\r\n\r\n\r\n', 'Trái cây Việt', 169000, 1, '2024-07-05', 7, 'ec11cb547e25f2827fb11daf66020961', 'nmdt3lejps7xowvc4nvq', 3, 2, 1),
(2, 'Dưa hấu đỏ không hạt', '<h1><strong>Dưa hấu đỏ kh&ocirc;ng hạt&nbsp;</strong></h1>\n\n<h1><strong>- Xuất xứ:&nbsp;</strong>Việt Nam</h1>\n\n<p>-&nbsp;<strong>Ti&ecirc;u chuẩn chất lượng:&nbsp;</strong>Tr&aacute;i c&acirc;y sạch</p>\n\n<p><strong>- Trọng lượng:&nbsp;</strong>3,5-4kg/Tr&aacute;i</p>\n\n<p><strong>- Dưa Hấu Đỏ Kh&ocirc;ng Hạt</strong>&nbsp;thuộc giống dưa kh&ocirc;ng hạt, mọng nước hơn v&agrave; ngọt đậm. Dưa được trồng v&agrave; thu hoạch đạt ti&ecirc;u chuẩn an to&agrave;n thực phẩm, đảm bảo cho người ti&ecirc;u d&ugrave;ng.&nbsp;</p>\n\n<p><strong>- Gi&aacute; trị dinh dưỡng:</strong></p>\n\n<p>Dưa hấu đỏ l&agrave; nguồn cung cấp dồi d&agrave;o vitamin C, lycopene, kali v&agrave; c&aacute;c kho&aacute;ng chất thiết yếu kh&aacute;c cho cơ thể.</p>\n\n<ul>\n	<li><strong>Vitamin C:</strong>&nbsp;Gi&uacute;p tăng cường hệ miễn dịch, chống oxy h&oacute;a v&agrave; bảo vệ cơ thể khỏi c&aacute;c bệnh tật.</li>\n	<li><strong>Lycopene:</strong>&nbsp;Một chất chống oxy h&oacute;a mạnh mẽ gi&uacute;p bảo vệ cơ thể khỏi c&aacute;c bệnh tim mạch, ung thư v&agrave; c&aacute;c bệnh m&atilde;n t&iacute;nh kh&aacute;c.</li>\n	<li><strong>Kali:</strong>&nbsp;Gi&uacute;p điều h&ograve;a huyết &aacute;p v&agrave; duy tr&igrave; chức năng tim mạch.</li>\n</ul>\n\n<p><strong>- Lợi &iacute;ch sức khỏe:</strong></p>\n\n<ul>\n	<li><strong>Tăng cường hệ miễn dịch:</strong>&nbsp;Vitamin C trong dưa hấu đỏ gi&uacute;p tăng cường hệ miễn dịch, chống lại c&aacute;c bệnh cảm c&uacute;m, ho.</li>\n	<li><strong>Bảo vệ tim mạch:</strong>&nbsp;Lycopene trong dưa hấu đỏ gi&uacute;p bảo vệ tim mạch, giảm nguy cơ mắc c&aacute;c bệnh tim mạch như nhồi m&aacute;u cơ tim v&agrave; đột quỵ.</li>\n	<li><strong>Giảm nguy cơ ung thư:</strong>&nbsp;Lycopene trong dưa hấu đỏ cũng gi&uacute;p giảm nguy cơ mắc một số loại ung thư như ung thư tuyến tiền liệt, ung thư v&uacute; v&agrave; ung thư phổi.</li>\n	<li><strong>Hỗ trợ ti&ecirc;u h&oacute;a:</strong>&nbsp;Chất xơ trong dưa hấu đỏ gi&uacute;p hỗ trợ ti&ecirc;u h&oacute;a, ngăn ngừa t&aacute;o b&oacute;n.</li>\n	<li><strong>L&agrave;m đẹp da:</strong>&nbsp;Vitamin C v&agrave; chất chống oxy h&oacute;a trong dưa hấu đỏ gi&uacute;p bảo vệ da khỏi t&aacute;c hại của &aacute;nh nắng mặt trời, l&agrave;m giảm nếp nhăn v&agrave; tăng độ đ&agrave;n hồi cho da</li>\n</ul>\n\n<p><strong>- Hướng dẫn sử dụng:</strong></p>\n\n<ul>\n	<li>Dưa hấu c&oacute; thể ăn trực tiếp, l&agrave;m nước &eacute;p, xay sinh tố.</li>\n	<li>Dưa hấu ngon hơn khi ướp lạnh trước khi ăn.</li>\n</ul>\n\n<p><strong>- C&aacute;ch bảo quản dưa hấu:</strong></p>\n\n<p><strong>+ Bảo quản ở nhiệt độ ph&ograve;ng:</strong></p>\n\n<ul>\n	<li><strong>Chọn dưa hấu ch&iacute;n tới:</strong>&nbsp;N&ecirc;n chọn những quả dưa hấu c&oacute; m&agrave;u đỏ tươi, gai nhọn đều đặn, vỏ hơi mềm khi ấn nhẹ. Tr&aacute;nh chọn những quả dưa hấu chưa ch&iacute;n hoặc qu&aacute; ch&iacute;n v&igrave; dễ bị dập n&aacute;t v&agrave; nhanh hỏng.</li>\n	<li><strong>Rửa sạch v&agrave; để r&aacute;o:</strong>&nbsp;Sau khi mua về, bạn n&ecirc;n rửa sạch dưa hấu dưới v&ograve;i nước chảy để loại bỏ bụi bẩn v&agrave; vi khuẩn. Sau đ&oacute;, để r&aacute;o nước ho&agrave;n to&agrave;n.</li>\n	<li><strong>Bảo quản ở nơi tho&aacute;ng m&aacute;t:</strong>&nbsp;N&ecirc;n bảo quản dưa hấu ở nơi tho&aacute;ng m&aacute;t, tr&aacute;nh &aacute;nh nắng trực tiếp. Bạn c&oacute; thể để dưa hấu trong rổ hoặc hộp c&oacute; lỗ th&ocirc;ng hơi để đảm bảo lưu th&ocirc;ng kh&iacute; tốt.</li>\n	<li><strong>Hạn chế chồng chất:</strong>&nbsp;Tr&aacute;nh chồng chất dưa hấu l&ecirc;n nhau v&igrave; c&oacute; thể l&agrave;m dập n&aacute;t quả. N&ecirc;n xếp dưa hấu th&agrave;nh từng lớp ri&ecirc;ng biệt.</li>\n	<li><strong>Thời gian bảo quản:</strong>&nbsp;Với c&aacute;ch bảo quản n&agrave;y, dưa hấu c&oacute; thể giữ được độ tươi ngon trong khoảng 1-2 tuần.</li>\n</ul>\n\n<p><strong>+ Bảo quản trong tủ lạnh:</strong></p>\n\n<ul>\n	<li><strong>C&aacute;ch bảo quản:</strong>&nbsp;Dưa hấu c&oacute; thể được bảo quản trong tủ lạnh để giữ được độ tươi ngon l&acirc;u hơn.</li>\n	<li><strong>Chuẩn bị:</strong>&nbsp;Rửa sạch dưa hấu, để r&aacute;o nước. Sau đ&oacute;, cho dưa hấu v&agrave;o hộp k&iacute;n hoặc t&uacute;i nilon c&oacute; đục lỗ để đảm bảo lưu th&ocirc;ng kh&iacute;.</li>\n	<li><strong>Nhiệt độ:</strong>&nbsp;N&ecirc;n bảo quản dưa hấu ở ngăn m&aacute;t tủ lạnh với nhiệt độ từ 8-10 độ C.</li>\n	<li><strong>Thời gian bảo quản:</strong>&nbsp;Dưa hấu bảo quản trong tủ lạnh c&oacute; thể giữ được độ tươi ngon trong khoảng 2-3 tuần.</li>\n</ul>\n\n<p><strong>- Mẹo chọn dưa hấu mọng nước:</strong></p>\n\n<ul>\n	<li><strong>Vỏ:</strong>&nbsp;N&ecirc;n chọn quả dưa hấu c&oacute; vỏ m&agrave;u xanh sẫm, đều m&agrave;u, kh&ocirc;ng bị sần s&ugrave;i, nứt nẻ hay c&oacute; đốm đen. Vỏ dưa hấu ch&iacute;n gi&agrave; thường c&oacute; những đường v&acirc;n lưới r&otilde; r&agrave;ng.</li>\n	<li><strong>Cuống:</strong>&nbsp;Cuống dưa hấu tươi n&ecirc;n c&ograve;n cuống, m&agrave;u n&acirc;u sẫm v&agrave; hơi kh&ocirc;. Tr&aacute;nh chọn những quả dưa hấu c&oacute; cuống bị h&eacute;o &uacute;a hoặc bị cắt cụt.</li>\n	<li><strong>Rốn:</strong>&nbsp;N&ecirc;n chọn quả dưa hấu c&oacute; rốn nhỏ, l&otilde;m s&acirc;u v&agrave;o trong. Rốn dưa hấu c&agrave;ng nhỏ th&igrave; vỏ dưa c&agrave;ng mỏng v&agrave; thịt dưa c&agrave;ng ngọt.</li>\n	<li><strong>H&igrave;nh dạng:</strong>&nbsp;N&ecirc;n chọn quả dưa hấu c&oacute; h&igrave;nh dạng c&acirc;n đối, hai đầu đều nhau. Tr&aacute;nh chọn những quả dưa hấu bị m&eacute;o m&oacute; hoặc c&oacute; h&igrave;nh dạng kỳ dị.</li>\n	<li><strong>Trọng lượng:</strong>&nbsp;N&ecirc;n chọn quả dưa hấu c&oacute; trọng lượng tương đối nặng so với k&iacute;ch thước. Quả dưa hấu nặng tay thường chứa nhiều nước v&agrave; thịt dưa d&agrave;y.</li>\n	<li><strong>Vỗ tay:</strong>&nbsp;D&ugrave;ng tay vỗ nhẹ v&agrave;o vỏ dưa hấu. Nếu nghe tiếng &quot;bộp bộp&quot; vang xa th&igrave; đ&oacute; l&agrave; quả dưa hấu ch&iacute;n gi&agrave;, ngọt v&agrave; mọng nước. Ngược lại, nếu nghe tiếng &quot;cạch cạch&quot; th&igrave; quả dưa hấu c&oacute; thể c&ograve;n non hoặc bị hỏng.</li>\n	<li>Với Farmers Market ti&ecirc;u ch&iacute; h&agrave;ng đầu l&agrave; sản phảm đạt chất lượng cao, nguồn gốc r&otilde; r&agrave;ng, mang đến trải nghiệm tốt nhất cho kh&aacute;ch h&agrave;ng.&nbsp;</li>\n</ul>\n', 'Trái cây Việt', 196000, 1, '2024-07-05', 7, 'f0783b4ee14961efe581fab9fba79bc5', 'ogivj4cjn4vi6lzcfgro', 3, 2, 1),
(3, 'Ổi nữ hoàng', '<h1><strong>Ổi nữ ho&agrave;ng</strong></h1>\n\n<p><strong>- Xuất xứ:&nbsp;</strong>Việt Nam&nbsp;</p>\n\n<p>-&nbsp;<strong>Ti&ecirc;u chuẩn chất lượng:&nbsp;</strong>Tr&aacute;i c&acirc;y sạch</p>\n\n<p><strong>- Đặc điểm :</strong></p>\n\n<ul>\n	<li>Ổi nữ ho&agrave;ng c&oacute; thịt quả kh&aacute; d&agrave;y v&agrave; ruột rất nhỏ c&oacute; một &iacute;t hạt.</li>\n	<li>Ổi c&oacute; m&ugrave;i thơm m&aacute;t, gi&ograve;n v&agrave; ngọt đặc trưng.</li>\n	<li>Được tuyển chọn khắt khe, đảm bảo vệ sinh thực phẩm.</li>\n	<li><strong>H&igrave;nh dạng:</strong>&nbsp;Ổi Nữ Ho&agrave;ng c&oacute; h&igrave;nh bầu dục, hơi thu&ocirc;n d&agrave;i, vỏ mỏng m&agrave;u xanh v&agrave;ng khi ch&iacute;n.</li>\n	<li><strong>Thịt quả:</strong>&nbsp;Thịt quả d&agrave;y, m&agrave;u trắng hồng, &iacute;t xơ v&agrave; c&oacute; vị ngọt thanh, gi&ograve;n tan.</li>\n	<li><strong>Hạt:</strong>&nbsp;Hạt ổi nhỏ, c&oacute; thể ăn được.</li>\n</ul>\n\n<p><strong>Ổi Nữ Ho&agrave;ng</strong>&nbsp;c&oacute; dạng h&igrave;nh cầu tr&ograve;n, tr&aacute;i to, phần thịt quả kh&aacute; d&agrave;y v&agrave; ruột rất nhỏ c&oacute; một &iacute;t hạt. Ổi tỏa ra một m&ugrave;i thơm m&aacute;t đặc trưng, khi ăn bạn sẽ cảm nhận được độ gi&ograve;n v&agrave; ngọt nơi đầu lưỡi cho đến khi ăn hết quả. Đ&acirc;y l&agrave; những tr&aacute;i ổi được tuyển chọn khắt khe, đảm bảo vệ sinh thực phẩm cho người ti&ecirc;u d&ugrave;ng.&nbsp;</p>\n\n<p><strong>- Lợi &iacute;ch của Ổi Nữ Ho&agrave;ng:</strong></p>\n\n<ul>\n	<li><strong>Gi&agrave;u vitamin C:</strong>&nbsp;Ổi Nữ Ho&agrave;ng l&agrave; nguồn cung cấp dồi d&agrave;o vitamin C, gi&uacute;p tăng cường hệ miễn dịch, chống lại c&aacute;c bệnh cảm c&uacute;m, ho.</li>\n	<li><strong>Gi&agrave;u chất xơ:</strong>&nbsp;Ổi Nữ Ho&agrave;ng chứa nhiều chất xơ, gi&uacute;p hỗ trợ hệ ti&ecirc;u h&oacute;a, ngăn ngừa t&aacute;o b&oacute;n.</li>\n	<li><strong>Gi&agrave;u kali:</strong>&nbsp;Ổi Nữ Ho&agrave;ng chứa nhiều kali, gi&uacute;p điều h&ograve;a huyết &aacute;p, giảm nguy cơ mắc c&aacute;c bệnh tim mạch.</li>\n	<li><strong>Gi&agrave;u vitamin A:</strong>&nbsp;Ổi Nữ Ho&agrave;ng chứa nhiều vitamin A, gi&uacute;p bảo vệ mắt, ngăn ngừa c&aacute;c bệnh về mắt như đục thủy tinh thể, tho&aacute;i h&oacute;a điểm v&agrave;ng.</li>\n	<li><strong>Gi&uacute;p giảm c&acirc;n:</strong>&nbsp;Chất xơ trong Ổi Nữ Ho&agrave;ng gi&uacute;p tạo cảm gi&aacute;c no l&acirc;u, hỗ trợ giảm c&acirc;n hiệu quả.</li>\n	<li><strong>L&agrave;m đẹp da:</strong>&nbsp;Vitamin C v&agrave; chất chống oxy h&oacute;a trong Ổi Nữ Ho&agrave;ng gi&uacute;p bảo vệ da khỏi t&aacute;c hại của &aacute;nh nắng mặt trời, l&agrave;m giảm nếp nhăn v&agrave; tăng độ đ&agrave;n hồi cho da.</li>\n</ul>\n\n<p><strong>- C&aacute;ch sử dụng Ổi Nữ Ho&agrave;ng:</strong></p>\n\n<p>Ổi Nữ Ho&agrave;ng c&oacute; thể ăn tươi, l&agrave;m sinh tố, salad, hoặc chế biến th&agrave;nh c&aacute;c m&oacute;n ăn kh&aacute;c nhau như b&aacute;nh, mứt, ...</p>\n\n<p><strong>- C&aacute;ch lựa chọn ổi ngon</strong></p>\n\n<ul>\n	<li>N&ecirc;n chọn mua Ổi Nữ Ho&agrave;ng c&oacute; vỏ m&agrave;u xanh v&agrave;ng, b&oacute;ng mượt, kh&ocirc;ng bị dập n&aacute;t hoặc hư hỏng.</li>\n	<li>Với Farmers Market ti&ecirc;u ch&iacute; h&agrave;ng đầu l&agrave; sản phảm đạt chất lượng cao, nguồn gốc r&otilde; r&agrave;ng, mang đến trải nghiệm tốt nhất cho kh&aacute;ch h&agrave;ng.&nbsp;</li>\n</ul>\n', 'Trái cây Việt', 49000, 1, '2024-07-05', 7, '13216f499a8e2acd14fd381fded6f178', 'beuunbrg01cvmed5kkab', 3, 3, 1),
(4, 'Thanh long ruột đỏ', '<h1><strong>Thanh long ruột đỏ</strong></h1>\n\n<p><strong>- Xuất xứ:</strong>&nbsp;Việt Nam</p>\n\n<p><strong>- Ti&ecirc;u chuẩn chất lượng:</strong>&nbsp;Tr&aacute;i c&acirc;y sạch</p>\n\n<p><strong>- Trọng lượng sản phẩm:&nbsp;</strong>1kg</p>\n\n<p><strong>- Đặc Điểm:</strong></p>\n\n<ul>\n	<li>Tr&aacute;i c&acirc;y Việt canh t&aacute;c an to&agrave;n, tuyển chọn loại nhất</li>\n	<li>C&oacute; ruột đỏ, ngọt, gi&agrave;u dinh dưỡng hơn thanh long trắng</li>\n</ul>\n\n<ul>\n</ul>\n\n<p>-<strong>&nbsp;Thanh long ruột đỏ</strong>&nbsp;l&agrave; một nguồn cung cấp dồi d&agrave;o vitamin C, vitamin B1, vitamin B2, vitamin B3, kali, magie v&agrave; c&aacute;c kho&aacute;ng chất thiết yếu kh&aacute;c cho cơ thể.</p>\n\n<p><strong>- Lợi &iacute;ch sức khỏe của thanh long ruột đỏ:</strong></p>\n\n<ul>\n	<li>Tăng cường hệ miễn dịch: Vitamin C trong thanh long ruột đỏ gi&uacute;p tăng cường hệ miễn dịch, chống lại c&aacute;c bệnh cảm c&uacute;m, ho.</li>\n	<li>Hỗ trợ sức khỏe tim mạch: Kali trong thanh long ruột đỏ gi&uacute;p điều h&ograve;a huyết &aacute;p v&agrave; duy tr&igrave; chức năng tim mạch.</li>\n	<li>Hỗ trợ hệ ti&ecirc;u h&oacute;a: Chất xơ trong thanh long ruột đỏ gi&uacute;p hỗ trợ hệ ti&ecirc;u h&oacute;a, ngăn ngừa t&aacute;o b&oacute;n.</li>\n	<li>L&agrave;m đẹp da: Vitamin C v&agrave; chất chống oxy h&oacute;a trong thanh long ruột đỏ gi&uacute;p bảo vệ da khỏi t&aacute;c hại của &aacute;nh nắng mặt trời, l&agrave;m giảm nếp nhăn v&agrave; tăng độ đ&agrave;n hồi cho da.</li>\n	<li>Hỗ trợ giảm c&acirc;n: Chất xơ trong thanh long ruột đỏ gi&uacute;p tạo cảm gi&aacute;c no l&acirc;u, hỗ trợ giảm c&acirc;n hiệu quả.</li>\n</ul>\n\n<p><strong>- C&aacute;ch sử dụng thanh long ruột đỏ:</strong></p>\n\n<ul>\n	<li>Thanh long ruột đỏ c&oacute; thể ăn tươi, l&agrave;m sinh tố, salad, hoặc chế biến th&agrave;nh c&aacute;c m&oacute;n ăn kh&aacute;c nhau như b&aacute;nh, mứt, ...</li>\n</ul>\n\n<p><strong>-&nbsp;C&aacute;ch lựa chọn thanh long ngon</strong></p>\n\n<ul>\n	<li>N&ecirc;n chọn mua thanh long ruột đỏ c&oacute; vỏ ngo&agrave;i m&agrave;u hồng hoặc đỏ tươi, vảy nhỏ đều nhau.</li>\n	<li>Với Farmers Market ti&ecirc;u ch&iacute; h&agrave;ng đầu l&agrave; sản phảm đạt chất lượng cao, nguồn gốc r&otilde; r&agrave;ng, mang đến trải nghiệm tốt nhất cho kh&aacute;ch h&agrave;ng.&nbsp;</li>\n</ul>\n\n<p><strong>- C&aacute;ch bảo quản thanh long:</strong></p>\n\n<p><strong>+ Bảo quản ở nhiệt độ ph&ograve;ng:</strong></p>\n\n<ul>\n	<li><strong>Chọn thanh long ch&iacute;n tới:</strong>&nbsp;N&ecirc;n chọn những quả thanh long c&oacute; m&agrave;u đỏ tươi, gai nhọn đều đặn, vỏ hơi mềm khi ấn nhẹ. Tr&aacute;nh chọn những quả thanh long chưa ch&iacute;n hoặc qu&aacute; ch&iacute;n v&igrave; dễ bị dập n&aacute;t v&agrave; nhanh hỏng.</li>\n	<li><strong>Rửa sạch v&agrave; để r&aacute;o:</strong>&nbsp;Sau khi mua về, bạn n&ecirc;n rửa sạch thanh long dưới v&ograve;i nước chảy để loại bỏ bụi bẩn v&agrave; vi khuẩn. Sau đ&oacute;, để r&aacute;o nước ho&agrave;n to&agrave;n.</li>\n	<li><strong>Bảo quản ở nơi tho&aacute;ng m&aacute;t:</strong>&nbsp;N&ecirc;n bảo quản thanh long ở nơi tho&aacute;ng m&aacute;t, tr&aacute;nh &aacute;nh nắng trực tiếp. Bạn c&oacute; thể để thanh long trong rổ hoặc hộp c&oacute; lỗ th&ocirc;ng hơi để đảm bảo lưu th&ocirc;ng kh&iacute; tốt.</li>\n	<li><strong>Hạn chế chồng chất:</strong>&nbsp;Tr&aacute;nh chồng chất thanh long l&ecirc;n nhau v&igrave; c&oacute; thể l&agrave;m dập n&aacute;t quả. N&ecirc;n xếp thanh long th&agrave;nh từng lớp ri&ecirc;ng biệt.</li>\n	<li><strong>Thời gian bảo quản:</strong>&nbsp;Với c&aacute;ch bảo quản n&agrave;y, thanh long c&oacute; thể giữ được độ tươi ngon trong khoảng 5 - 8 ng&agrave;y.</li>\n</ul>\n\n<p><strong>+ Bảo quản trong tủ lạnh:</strong></p>\n\n<ul>\n	<li><strong>C&aacute;ch bảo quản:</strong>&nbsp;Thanh long c&oacute; thể được bảo quản trong tủ lạnh để giữ được độ tươi ngon l&acirc;u hơn.</li>\n	<li><strong>Chuẩn bị:</strong>&nbsp;Rửa sạch thanh long, để r&aacute;o nước. Sau đ&oacute;, cho thanh long v&agrave;o hộp k&iacute;n hoặc t&uacute;i nilon c&oacute; đục lỗ để đảm bảo lưu th&ocirc;ng kh&iacute;.</li>\n	<li><strong>Nhiệt độ:</strong>&nbsp;N&ecirc;n bảo quản thanh long ở ngăn m&aacute;t tủ lạnh với nhiệt độ từ 8-10 độ C.</li>\n	<li><strong>Thời gian bảo quản:</strong>&nbsp;Thanh long bảo quản trong tủ lạnh c&oacute; thể giữ được độ tươi ngon trong khoảng 15 - 20 ng&agrave;y.</li>\n</ul>\n', 'Trái cây Việt', 69000, 1, '2024-07-05', 7, 'bdc6cca7616769913e5ef030699eb66c', 'j3w2obe8e6bc96v7vmra', 3, 1, 1),
(5, 'Cam Sành Vắt Nước', '<h1><strong>Cam S&agrave;nh Vắt Nước</strong></h1>\n\n<p><strong>Xuất xứ:&nbsp;</strong>Việt Nam</p>\n\n<p><strong>Ti&ecirc;u chuẩn chất lượng:&nbsp;</strong>Tr&aacute;i c&acirc;y sạch</p>\n\n<p>Khối lượng: 5&nbsp;- 6Tr&aacute;i/Kg</p>\n\n<p><strong>Đặc điểm nổi bật của cam s&agrave;nh</strong></p>\n\n<ul>\n	<li>Quy tr&igrave;nh chăm s&oacute;c ti&ecirc;n tiến</li>\n	<li>Dễ nhận dạng nhờ lớp vỏ d&agrave;y, sần s&ugrave;i, thường c&oacute; m&agrave;u xanh lục nhạt, khi ch&iacute;n c&oacute; sắc cam.</li>\n	<li>M&uacute;i thịt m&agrave;u cam, c&oacute; vị thơm đặc trưng, ngọt dịu.</li>\n</ul>\n\n<p><strong>Cam S&agrave;nh</strong>&nbsp;c&oacute; k&iacute;ch thước vừa phải, mọng nước, vỏ xanh xen ch&uacute;t v&agrave;ng với m&uacute;i m&agrave;u cam đẹp mắt. D&ugrave;ng l&agrave;m nước &eacute;p cũng rất ngon</p>\n\n<p><strong>Lợi &iacute;ch khi sử dụng cam s&agrave;nh</strong></p>\n\n<ul>\n	<li>Nước cam chứa h&agrave;m lượng vitamin C cao, l&agrave; chất chống oxy h&oacute;a mạnh mẽ gi&uacute;p bảo vệ cơ thể khỏi c&aacute;c gốc tự do, giảm nguy cơ mắc c&aacute;c bệnh m&atilde;n t&iacute;nh như ung thư, tim mạch v&agrave; l&atilde;o h&oacute;a.</li>\n	<li>Ngo&agrave;i vitamin C, nước cam c&ograve;n chứa c&aacute;c hợp chất chống oxy h&oacute;a kh&aacute;c như flavonoid, carotenoid, anthocyanin,... gi&uacute;p tăng cường hệ miễn dịch v&agrave; bảo vệ cơ thể khỏi t&aacute;c hại của m&ocirc;i trường.</li>\n	<li>Nước cam l&agrave; nguồn cung cấp kali dồi d&agrave;o, gi&uacute;p b&agrave;i tiết citrat qua nước tiểu, từ đ&oacute; l&agrave;m giảm nguy cơ h&igrave;nh th&agrave;nh sỏi thận.</li>\n	<li>Vitamin C trong nước cam gi&uacute;p tăng cường sức khỏe của mạch m&aacute;u, giảm nguy cơ đột quỵ.</li>\n	<li>Chất xơ trong nước cam cũng gi&uacute;p tăng cường hệ vi khuẩn đường ruột, hỗ trợ ti&ecirc;u h&oacute;a tốt hơn.</li>\n	<li>Nước cam c&ograve;n c&oacute; t&aacute;c dụng thanh lọc cơ thể, gi&uacute;p loại bỏ độc tố ra khỏi cơ thể.</li>\n	<li>Nước cam c&ograve;n chứa c&aacute;c vitamin v&agrave; kho&aacute;ng chất kh&aacute;c như vitamin A, vitamin E, kẽm,... g&oacute;p phần tăng cường sức đề kh&aacute;ng cho cơ thể.</li>\n	<li>Vitamin C trong nước cam gi&uacute;p sản sinh collagen, tăng độ đ&agrave;n hồi cho da, giảm nếp nhăn v&agrave; chống l&atilde;o h&oacute;a.</li>\n</ul>\n\n<p><strong>C&aacute;ch lựa chọn những tr&aacute;i cam s&agrave;nh ngon, ngọt v&agrave; kh&ocirc;ng bị chua</strong></p>\n\n<ul>\n	<li>Chọn những quả cam s&agrave;nh c&oacute; h&igrave;nh d&aacute;ng tr&ograve;n đều, kh&ocirc;ng bị m&eacute;o m&oacute; hay b&ecirc;n to b&ecirc;n nhỏ. Tr&aacute;nh chọn những quả cam bị dập n&aacute;t hoặc c&oacute; dấu hiệu bị s&acirc;u bệnh.</li>\n	<li>Chọn những quả c&oacute; cam s&agrave;nh ngon vỏ thường c&oacute; m&agrave;u xanh b&oacute;ng, kh&ocirc;ng qu&aacute; to hay qu&aacute; sần s&ugrave;i b&ecirc;n ngo&agrave;i.</li>\n	<li>Cam s&agrave;nh ngon thường c&oacute; k&iacute;ch thước vừa phải, cầm lại kh&aacute; nặng tay. Điều n&agrave;y cho thấy quả cam c&oacute; nhiều nước v&agrave; mọng thịt.</li>\n	<li>Cuống cam s&agrave;nh ngon phải c&ograve;n tươi v&agrave; d&iacute;nh chặt v&agrave;o quả. Tr&aacute;nh chọn những quả cam c&oacute; cuống bị h&eacute;o &uacute;a hoặc rụng rời.</li>\n	<li>Với Farmers Market ti&ecirc;u ch&iacute; h&agrave;ng đầu l&agrave; sản phảm đạt chất lượng cao, nguồn gốc r&otilde; r&agrave;ng, mang đến trải nghiệm tốt nhất cho kh&aacute;ch h&agrave;ng.&nbsp;</li>\n</ul>\n\n<p><strong>C&aacute;ch bảo quản cam s&agrave;nh tươi v&agrave; ngon</strong></p>\n\n<ul>\n	<li>Cam n&ecirc;n được bảo quản ở nhiệt độ từ 10&deg;C đến 15&deg;C. Đ&acirc;y l&agrave; nhiệt độ l&yacute; tưởng để cam giữ được độ tươi ngon trong thời gian d&agrave;i nhất.</li>\n	<li>Cam cần được bảo quản ở độ ẩm khoảng 85%. Độ ẩm cao sẽ khiến cam bị thối rữa, trong khi độ ẩm thấp sẽ khiến cam bị kh&ocirc; h&eacute;o.</li>\n	<li>Cam cần được bảo quản ở nơi c&oacute; lưu th&ocirc;ng kh&ocirc;ng kh&iacute; tốt để tr&aacute;nh bị thối rữa</li>\n	<li>Kh&ocirc;ng n&ecirc;n bọc k&iacute;n t&uacute;i đựng cam v&igrave; điều n&agrave;y sẽ khiến cam dễ bị hỏng</li>\n	<li>N&ecirc;n để cam ở ngăn m&aacute;t tủ lạnh, tr&aacute;nh xa c&aacute;c loại tr&aacute;i c&acirc;y v&agrave; thực phẩm kh&aacute;c như thịt, c&aacute; v&igrave; những thực phẩm n&agrave;y c&oacute; thể khiến cam bị &aacute;m m&ugrave;i.</li>\n	<li>Bạn c&oacute; thể bọc từng quả cam bằng giấy b&aacute;o trước khi cho v&agrave;o t&uacute;i đựng để gi&uacute;p cam giữ được độ tươi ngon l&acirc;u hơn.</li>\n	<li>N&ecirc;n sử dụng t&uacute;i zip, t&uacute;i nhựa hoặc t&uacute;i lưới để bảo quản cam v&igrave; những loại t&uacute;i n&agrave;y c&oacute; thể gi&uacute;p cam được th&ocirc;ng tho&aacute;ng kh&iacute;.</li>\n</ul>\n\n<p><strong>Những lưu &yacute; cần biết khi sử dụng cam s&agrave;nh</strong></p>\n\n<ul>\n	<li>Những người c&oacute; bệnh về dạ d&agrave;y, t&aacute; tr&agrave;ng hoặc vi&ecirc;m tuyến tụy n&ecirc;n hạn chế hoặc tr&aacute;nh sử dụng cam, đặc biệt l&agrave; cam chua</li>\n	<li>Buổi s&aacute;ng l&agrave; thời điểm tốt nhất để sử dụng nước cam, nhất l&agrave; sau bữa s&aacute;ng khoảng 1 đến 2 tiếng</li>\n</ul>\n', 'Trái cây Việt', 22000, 1, '2024-07-05', 7, 'f5b4c1fe5d80872b48b88e21b7d0222e', 'lyvmoiw8jlidhemkzpd3', 3, 2, 1),
(6, 'Đu đủ ', '<h1><strong>Đu đủ&nbsp;</strong></h1>\n\n<p><strong>- Ti&ecirc;u chuẩn chất lượng:&nbsp;</strong>Tr&aacute;i c&acirc;y sạch</p>\n\n<p><strong>- Xuất xứ</strong>: Việt Nam</p>\n\n<p><strong>-Đặc điểm nổi bật của đu đủ:</strong></p>\n\n<ul>\n	<li>Đu đủ c&oacute; vị ngọt thanh, thơm ngon.</li>\n	<li>Khi ch&iacute;n, đu đủ c&oacute; m&ugrave;i thơm nồng n&agrave;n đặc trưng.</li>\n	<li>Vỏ đu đủ khi ch&iacute;n c&oacute; m&agrave;u v&agrave;ng, cam hoặc đỏ t&ugrave;y thuộc v&agrave;o giống.</li>\n	<li>Thịt đu đủ c&oacute; m&agrave;u v&agrave;ng cam hoặc đỏ.</li>\n</ul>\n\n<p><strong>- Lợi &iacute;ch tuyệt vời của đu đủ</strong></p>\n\n<ul>\n	<li>Đu đủ l&agrave; nguồn cung cấp dồi d&agrave;o vitamin, kho&aacute;ng chất v&agrave; chất chống oxy h&oacute;a c&oacute; lợi cho sức khỏe.</li>\n	<li>Ăn đu đủ ch&iacute;n rất tốt cho hệ ti&ecirc;u h&oacute;a v&igrave; n&oacute; chứa h&agrave;m lượng chất xơ&nbsp;dồi d&agrave;o t&aacute;c dụng chữa t&aacute;o b&oacute;n, nhuận tr&agrave;ng, giảm đầy hơi, chướng bụng. Đu đủ ch&iacute;n c&ograve;n chứa nhiều chất chống oxy h&oacute;a c&oacute; thể giảm vi&ecirc;m, cải thiện sức khỏe tim mạch v&agrave; gi&uacute;p trẻ h&oacute;a, săn chắc l&agrave;n da.</li>\n	<li>Đu đủ chứa c&aacute;c chất chống vi&ecirc;m tự nhi&ecirc;n như vitamin C v&agrave; chất chống oxy h&oacute;a. Những chất n&agrave;y c&oacute; khả năng giảm vi&ecirc;m nhiễm&nbsp;trong hệ h&ocirc; hấp v&agrave; gi&uacute;p l&agrave;m dịu c&aacute;c triệu chứng của c&aacute;c bệnh vi&ecirc;m đường h&ocirc; hấp&nbsp;</li>\n	<li>Vitamin C trong đu đủ gi&uacute;p sản xuất collagen, gi&uacute;p da đ&agrave;n hồi v&agrave; khỏe mạnh.</li>\n	<li>Đu đủ chứa lycopene, một chất chống oxy h&oacute;a mạnh gi&uacute;p bảo vệ cơ thể khỏi c&aacute;c gốc tự do, giảm nguy cơ mắc c&aacute;c bệnh ung thư.</li>\n	<li>Vitamin C trong đu đủ gi&uacute;p tăng cường hệ miễn dịch ở trẻ em.</li>\n</ul>\n\n<p><strong>- C&aacute;ch lựa chọn đu đủ ngon</strong></p>\n\n<ul>\n	<li>N&ecirc;n chọn mua đu đủ c&oacute; vỏ v&agrave;ng đều, kh&ocirc;ng bị dập n&aacute;t hay hư hỏng.</li>\n	<li>N&ecirc;n chọn quả d&agrave;i, cầm nặng tay, cuống c&ograve;n nhựa d&iacute;nh. Những quả n&agrave;y khi ăn th&igrave; vừa ngọt lại thơm ngon, &iacute;t hạt, thịt dầy</li>\n	<li>N&ecirc;n tr&aacute;nh mua đu đủ nếu trời mưa trước đ&oacute; một v&agrave;i ng&agrave;y v&igrave; đu đủ ăn sẽ nhạt.</li>\n	<li>Đu đủ ngon nhất l&agrave; loại đu đủ hai da: vỏ ngo&agrave;i v&agrave;ng c&oacute; lốm đốm xanh nhỏ li ti, quả thu&ocirc;n d&agrave;i l&agrave; đặc ruột.</li>\n	<li>Với Farmers Market ti&ecirc;u ch&iacute; h&agrave;ng đầu l&agrave; sản phảm đạt chất lượng cao, nguồn gốc r&otilde; r&agrave;ng, mang đến trải nghiệm tốt nhất cho kh&aacute;ch h&agrave;ng.&nbsp;</li>\n</ul>\n\n<p><strong>- C&aacute;ch bảo quản đu đủ tốt nhất</strong></p>\n\n<ul>\n	<li>Cần tr&aacute;nh để đu đủ dưới &aacute;nh nắng trực tiếp v&igrave; c&oacute; thể khiến quả bị nẫu hoặc hư hỏng.</li>\n	<li>Bạn c&oacute; thể bọc đu đủ xanh bằng giấy b&aacute;o hoặc t&uacute;i giấy để tạo m&ocirc;i trường k&iacute;n. Ethylene do quả tiết ra sẽ bị giữ lại b&ecirc;n trong, th&uacute;c đẩy qu&aacute; tr&igrave;nh ch&iacute;n nhanh hơn.</li>\n	<li>Đảm bảo nơi bảo quản đu đủ c&oacute; th&ocirc;ng gi&oacute; tốt để quả kh&ocirc;ng bị b&iacute; v&agrave; thối rữa.</li>\n</ul>\n\n<p><strong>- Những lưu &yacute; khi thưởng thức đu đủ</strong></p>\n\n<ul>\n	<li>D&ugrave;ng vừa đủ, kh&ocirc;ng n&ecirc;n ăn qu&aacute; nhiều đu đủ,&nbsp;v&igrave; c&oacute; thể g&acirc;y đầy bụng, kh&oacute; ti&ecirc;u v&agrave; một số t&aacute;c dụng phụ kh&aacute;c.</li>\n	<li>Hạt đu đủ c&oacute; chứa chất độc t&ecirc;n carpine. Nếu ti&ecirc;u thụ nhiều ch&uacute;ng c&oacute; thể l&agrave;m loạn mạch, suy nhược thần kinh.&nbsp;</li>\n	<li>Nếu đang bị ti&ecirc;u chảy bạn kh&ocirc;ng n&ecirc;n ăn đu đủ để tr&aacute;nh l&agrave;m t&igrave;nh trạng th&ecirc;m nặng nề.</li>\n	<li>Một số người n&ecirc;n hạn chế ăn đu đủ như người bị bệnh đường h&ocirc; hấp, bị lo&atilde;ng m&aacute;u, bệnh thận, bệnh về dạ d&agrave;y ruột&hellip;</li>\n</ul>\n\n<p><strong>- Những đối tượng n&agrave;o th&igrave; kh&ocirc;ng n&ecirc;n sử dụng đu đủ ?</strong></p>\n\n<ul>\n	<li>Đu đủ ch&iacute;n l&agrave; thực phẩm an to&agrave;n v&agrave; bổ dưỡng đối với phụ nữ mang thai. Tuy nhi&ecirc;n, đu đủ xanh được xem l&agrave; thực phẩm cấm kị.</li>\n	<li>Một số người kh&ocirc;ng n&ecirc;n ăn đu đủ như người đường huyết cao hoặc người bị v&agrave;ng da, người bị dạ d&agrave;y, người c&oacute; cơ địa dị ứng, người ti&ecirc;u h&oacute;a k&eacute;m, người bị lo&atilde;ng m&aacute;u&hellip; v&igrave; c&oacute; thể l&agrave;m cho t&igrave;nh trạng bệnh t&igrave;nh th&ecirc;m trầm trọng hơn.</li>\n	<li>Kh&ocirc;ng an to&agrave;n cho trẻ sơ sinh dưới 1 tuổi</li>\n</ul>\n', 'Trái cây Việt', 29000, 1, '2024-07-05', 7, 'f53324d3e8e525cbf1c698da1eeb8291', 'qianei1gcpzsekyrftxq', 3, 3, 1),
(7, 'Dưa hấu đỏ', '<h1><strong>Dưa hấu đỏ</strong></h1>\n\n<p><strong>- Xuất xứ:&nbsp;</strong>Việt Nam</p>\n\n<p><strong>- Quy c&aacute;ch:&nbsp;</strong>2kg - 2.5kg/tr&aacute;i</p>\n\n<p><strong>-Đặc điểm:</strong></p>\n\n<ul>\n	<li><strong>&nbsp;Dưa Hấu Đỏ</strong>&nbsp;mọng nước v&agrave; ngọt đậm. Dưa được trồng v&agrave; thu hoạch đạt ti&ecirc;u chuẩn an to&agrave;n thực phẩm, đảm bảo cho người ti&ecirc;u d&ugrave;ng.</li>\n	<li><strong>Vỏ:</strong>&nbsp;Vỏ dưa hấu đỏ d&agrave;y, m&agrave;u xanh lục hoặc sọc xanh lục, c&oacute; thể c&oacute; hoặc kh&ocirc;ng c&oacute; đốm.</li>\n	<li><strong>Thịt:</strong>&nbsp;Thịt dưa hấu đỏ c&oacute; m&agrave;u đỏ tươi, hồng hoặc v&agrave;ng, mọng nước v&agrave; ngọt thanh.</li>\n	<li><strong>Hạt:</strong>&nbsp;Dưa hấu đỏ c&oacute; nhiều hạt m&agrave;u đen, cứng, c&oacute; thể ăn được hoặc loại bỏ t&ugrave;y theo sở th&iacute;ch.</li>\n</ul>\n\n<p><strong>- Gi&aacute; trị dinh dưỡng:</strong></p>\n\n<p>Dưa hấu đỏ l&agrave; nguồn cung cấp dồi d&agrave;o vitamin C, lycopene, kali v&agrave; c&aacute;c kho&aacute;ng chất thiết yếu kh&aacute;c cho cơ thể.</p>\n\n<ul>\n	<li><strong>Vitamin C:</strong>&nbsp;Gi&uacute;p tăng cường hệ miễn dịch, chống oxy h&oacute;a v&agrave; bảo vệ cơ thể khỏi c&aacute;c bệnh tật.</li>\n	<li><strong>Lycopene:</strong>&nbsp;Một chất chống oxy h&oacute;a mạnh mẽ gi&uacute;p bảo vệ cơ thể khỏi c&aacute;c bệnh tim mạch, ung thư v&agrave; c&aacute;c bệnh m&atilde;n t&iacute;nh kh&aacute;c.</li>\n	<li><strong>Kali:</strong>&nbsp;Gi&uacute;p điều h&ograve;a huyết &aacute;p v&agrave; duy tr&igrave; chức năng tim mạch.</li>\n</ul>\n\n<p><strong>- Lợi &iacute;ch sức khỏe:</strong></p>\n\n<ul>\n	<li><strong>Tăng cường hệ miễn dịch:</strong>&nbsp;Vitamin C trong dưa hấu đỏ gi&uacute;p tăng cường hệ miễn dịch, chống lại c&aacute;c bệnh cảm c&uacute;m, ho.</li>\n	<li><strong>Bảo vệ tim mạch:</strong>&nbsp;Lycopene trong dưa hấu đỏ gi&uacute;p bảo vệ tim mạch, giảm nguy cơ mắc c&aacute;c bệnh tim mạch như nhồi m&aacute;u cơ tim v&agrave; đột quỵ.</li>\n	<li><strong>Giảm nguy cơ ung thư:</strong>&nbsp;Lycopene trong dưa hấu đỏ cũng gi&uacute;p giảm nguy cơ mắc một số loại ung thư như ung thư tuyến tiền liệt, ung thư v&uacute; v&agrave; ung thư phổi.</li>\n	<li><strong>Hỗ trợ ti&ecirc;u h&oacute;a:</strong>&nbsp;Chất xơ trong dưa hấu đỏ gi&uacute;p hỗ trợ ti&ecirc;u h&oacute;a, ngăn ngừa t&aacute;o b&oacute;n.</li>\n	<li><strong>L&agrave;m đẹp da:</strong>&nbsp;Vitamin C v&agrave; chất chống oxy h&oacute;a trong dưa hấu đỏ gi&uacute;p bảo vệ da khỏi t&aacute;c hại của &aacute;nh nắng mặt trời, l&agrave;m giảm nếp nhăn v&agrave; tăng độ đ&agrave;n hồi cho da</li>\n</ul>\n\n<p><strong>- Hướng dẫn sử dụng:</strong></p>\n\n<ul>\n	<li>Dưa hấu c&oacute; thể ăn trực tiếp, l&agrave;m nước &eacute;p, xay sinh tố.</li>\n	<li>Dưa hấu ngon hơn khi ướp lạnh trước khi ăn.</li>\n</ul>\n\n<p><strong>- C&aacute;ch bảo quản dưa hấu:</strong></p>\n\n<p><strong>+ Bảo quản ở nhiệt độ ph&ograve;ng:</strong></p>\n\n<ul>\n	<li><strong>Chọn dưa hấu ch&iacute;n tới:</strong>&nbsp;N&ecirc;n chọn những quả dưa hấu c&oacute; m&agrave;u đỏ tươi, gai nhọn đều đặn, vỏ hơi mềm khi ấn nhẹ. Tr&aacute;nh chọn những quả dưa hấu chưa ch&iacute;n hoặc qu&aacute; ch&iacute;n v&igrave; dễ bị dập n&aacute;t v&agrave; nhanh hỏng.</li>\n	<li><strong>Rửa sạch v&agrave; để r&aacute;o:</strong>&nbsp;Sau khi mua về, bạn n&ecirc;n rửa sạch dưa hấu dưới v&ograve;i nước chảy để loại bỏ bụi bẩn v&agrave; vi khuẩn. Sau đ&oacute;, để r&aacute;o nước ho&agrave;n to&agrave;n.</li>\n	<li><strong>Bảo quản ở nơi tho&aacute;ng m&aacute;t:</strong>&nbsp;N&ecirc;n bảo quản dưa hấu ở nơi tho&aacute;ng m&aacute;t, tr&aacute;nh &aacute;nh nắng trực tiếp. Bạn c&oacute; thể để dưa hấu trong rổ hoặc hộp c&oacute; lỗ th&ocirc;ng hơi để đảm bảo lưu th&ocirc;ng kh&iacute; tốt.</li>\n	<li><strong>Hạn chế chồng chất:</strong>&nbsp;Tr&aacute;nh chồng chất dưa hấu l&ecirc;n nhau v&igrave; c&oacute; thể l&agrave;m dập n&aacute;t quả. N&ecirc;n xếp dưa hấu th&agrave;nh từng lớp ri&ecirc;ng biệt.</li>\n	<li><strong>Thời gian bảo quản:</strong>&nbsp;Với c&aacute;ch bảo quản n&agrave;y, dưa hấu c&oacute; thể giữ được độ tươi ngon trong khoảng 1-2 tuần.</li>\n</ul>\n\n<p><strong>+ Bảo quản trong tủ lạnh:</strong></p>\n\n<ul>\n	<li><strong>C&aacute;ch bảo quản:</strong>&nbsp;Dưa hấu c&oacute; thể được bảo quản trong tủ lạnh để giữ được độ tươi ngon l&acirc;u hơn.</li>\n	<li><strong>Chuẩn bị:</strong>&nbsp;Rửa sạch dưa hấu, để r&aacute;o nước. Sau đ&oacute;, cho dưa hấu v&agrave;o hộp k&iacute;n hoặc t&uacute;i nilon c&oacute; đục lỗ để đảm bảo lưu th&ocirc;ng kh&iacute;.</li>\n	<li><strong>Nhiệt độ:</strong>&nbsp;N&ecirc;n bảo quản dưa hấu ở ngăn m&aacute;t tủ lạnh với nhiệt độ từ 8-10 độ C.</li>\n	<li><strong>Thời gian bảo quản:</strong>&nbsp;Dưa hấu bảo quản trong tủ lạnh c&oacute; thể giữ được độ tươi ngon trong khoảng 2-3 tuần.</li>\n</ul>\n\n<p><strong>- Mẹo chọn dưa hấu mọng nước:</strong></p>\n\n<ul>\n	<li><strong>Vỏ:</strong>&nbsp;N&ecirc;n chọn quả dưa hấu c&oacute; vỏ m&agrave;u xanh sẫm, đều m&agrave;u, kh&ocirc;ng bị sần s&ugrave;i, nứt nẻ hay c&oacute; đốm đen. Vỏ dưa hấu ch&iacute;n gi&agrave; thường c&oacute; những đường v&acirc;n lưới r&otilde; r&agrave;ng.</li>\n	<li><strong>Cuống:</strong>&nbsp;Cuống dưa hấu tươi n&ecirc;n c&ograve;n cuống, m&agrave;u n&acirc;u sẫm v&agrave; hơi kh&ocirc;. Tr&aacute;nh chọn những quả dưa hấu c&oacute; cuống bị h&eacute;o &uacute;a hoặc bị cắt cụt.</li>\n	<li><strong>Rốn:</strong>&nbsp;N&ecirc;n chọn quả dưa hấu c&oacute; rốn nhỏ, l&otilde;m s&acirc;u v&agrave;o trong. Rốn dưa hấu c&agrave;ng nhỏ th&igrave; vỏ dưa c&agrave;ng mỏng v&agrave; thịt dưa c&agrave;ng ngọt.</li>\n	<li><strong>H&igrave;nh dạng:</strong>&nbsp;N&ecirc;n chọn quả dưa hấu c&oacute; h&igrave;nh dạng c&acirc;n đối, hai đầu đều nhau. Tr&aacute;nh chọn những quả dưa hấu bị m&eacute;o m&oacute; hoặc c&oacute; h&igrave;nh dạng kỳ dị.</li>\n	<li><strong>Trọng lượng:</strong>&nbsp;N&ecirc;n chọn quả dưa hấu c&oacute; trọng lượng tương đối nặng so với k&iacute;ch thước. Quả dưa hấu nặng tay thường chứa nhiều nước v&agrave; thịt dưa d&agrave;y.</li>\n	<li><strong>Vỗ tay:</strong>&nbsp;D&ugrave;ng tay vỗ nhẹ v&agrave;o vỏ dưa hấu. Nếu nghe tiếng &quot;bộp bộp&quot; vang xa th&igrave; đ&oacute; l&agrave; quả dưa hấu ch&iacute;n gi&agrave;, ngọt v&agrave; mọng nước. Ngược lại, nếu nghe tiếng &quot;cạch cạch&quot; th&igrave; quả dưa hấu c&oacute; thể c&ograve;n non hoặc bị hỏng.</li>\n	<li>Với Farmers Market ti&ecirc;u ch&iacute; h&agrave;ng đầu l&agrave; sản phảm đạt chất lượng cao, nguồn gốc r&otilde; r&agrave;ng, mang đến trải nghiệm tốt nhất cho kh&aacute;ch h&agrave;ng.&nbsp;</li>\n</ul>\n', 'Trái cây Việt', 1625000, 2, '2024-07-05', 7, '00a24b2293b0905d26233c5701423639', 'wxsoisl4k4rbwuuqtrjs', 3, 1, 1),
(8, 'Xoài Tứ Quý loại 1', '<h1><strong>Xo&agrave;i Tứ Qu&yacute; loại 1&nbsp;</strong></h1>\n\n<p><strong>- Xuất xứ</strong>: Việt Nam</p>\n\n<p>-&nbsp;<strong>Ti&ecirc;u chuẩn chất lượng:&nbsp;</strong>Tr&aacute;i c&acirc;y sạch</p>\n\n<p><strong>- Trọng lượng sản phẩm:&nbsp;</strong>1kg</p>\n\n<p><strong>- Đặc điểm nổi bật:</strong></p>\n\n<ul>\n	<li>Xo&agrave;i tươi ngon, tr&aacute;i to, xo&agrave;i v&agrave;ng tươi, đẹp mắt,&nbsp;phần thịt ngọt, gi&ograve;n v&agrave; c&oacute; vỏ mỏng.&nbsp;</li>\n	<li>Hạt của xo&agrave;i tứ qu&yacute; cũng rất nhỏ. Tươi ngon, kh&ocirc;ng bị dập, hư</li>\n</ul>\n\n<p><strong>- Lợi &iacute;ch của Xo&agrave;i Tứ Qu&yacute; loại 1:</strong></p>\n\n<ul>\n	<li>Xo&agrave;i Tứ Qu&yacute; loại 1 l&agrave; nguồn cung cấp dồi d&agrave;o vitamin C, vitamin A, kali v&agrave; chất xơ.</li>\n	<li>Gi&uacute;p tăng cường hệ miễn dịch, cải thiện sức khỏe ti&ecirc;u h&oacute;a, tốt cho tim mạch v&agrave; gi&uacute;p đẹp da.</li>\n	<li>C&oacute; t&iacute;nh giải nhiệt cao, rất th&iacute;ch hợp để sử dụng trong những ng&agrave;y n&oacute;ng bức.</li>\n</ul>\n\n<p><strong>- C&aacute;ch sử dụng Xo&agrave;i Tứ Qu&yacute; loại 1:</strong></p>\n\n<ul>\n	<li>Xo&agrave;i Tứ Qu&yacute; loại 1 c&oacute; thể ăn tươi trực tiếp như một m&oacute;n ăn nhẹ hoặc tr&aacute;ng miệng.</li>\n	<li>Xo&agrave;i Tứ Qu&yacute; loại 1 cũng c&oacute; thể được sử dụng để l&agrave;m sinh tố, salad, kem xo&agrave;i, mứt xo&agrave;i,...</li>\n	<li>Ngo&agrave;i ra, xo&agrave;i Tứ Qu&yacute; loại 1 c&ograve;n c&oacute; thể được sấy kh&ocirc; để bảo quản l&acirc;u hơn v&agrave; sử dụng như một m&oacute;n ăn vặt.</li>\n</ul>\n\n<p><strong>- C&aacute;ch bảo quản Xo&agrave;i Tứ Qu&yacute; loại 1:</strong></p>\n\n<ul>\n	<li>Xo&agrave;i Tứ Qu&yacute; loại 1 n&ecirc;n được bảo quản ở nhiệt độ ph&ograve;ng khi c&ograve;n xanh v&agrave; chuyển v&agrave;o tủ lạnh khi đ&atilde; ch&iacute;n.</li>\n	<li>Xo&agrave;i Tứ Qu&yacute; loại 1 c&oacute; thể bảo quản trong tủ lạnh từ 3 đến 5 ng&agrave;y.</li>\n	<li>N&ecirc;n để xo&agrave;i Tứ Qu&yacute; loại 1 trong hộp k&iacute;n hoặc rổ c&oacute; l&oacute;t khăn giấy để tr&aacute;nh bị b&iacute; v&agrave; hư hỏng.</li>\n</ul>\n\n<p><strong>- C&aacute;ch lựa chọn xo&agrave;i ngon:</strong></p>\n\n<ul>\n	<li>N&ecirc;n chọn mua Xo&agrave;i C&aacute;t H&ograve;a Lộc c&oacute; vỏ m&agrave;u v&agrave;ng nhạt, vỏ mỏng, mịn v&agrave; dễ b&oacute;c.</li>\n	<li>Tr&aacute;nh mua những quả xo&agrave;i bị dập n&aacute;t, vỏ bị th&acirc;m hoặc c&oacute; m&ugrave;i h&ocirc;i.</li>\n	<li>Với Farmers Market ti&ecirc;u ch&iacute; h&agrave;ng đầu l&agrave; sản phảm đạt chất lượng cao, nguồn gốc r&otilde; r&agrave;ng, mang đến trải nghiệm tốt nhất cho kh&aacute;ch h&agrave;ng.&nbsp;</li>\n</ul>\n\n<p><strong>- C&aacute;ch bảo quản xo&agrave;i được l&acirc;u:</strong></p>\n\n<p><strong>+ Bảo quản ở nơi tho&aacute;ng m&aacute;t:</strong></p>\n\n<ul>\n	<li>N&ecirc;n bảo quản xo&agrave;i ở nơi tho&aacute;ng m&aacute;t, tr&aacute;nh &aacute;nh nắng trực tiếp.</li>\n	<li>Bạn c&oacute; thể để xo&agrave;i trong rổ hoặc hộp c&oacute; lỗ th&ocirc;ng hơi để đảm bảo lưu th&ocirc;ng kh&iacute; tốt.</li>\n	<li>Hạn chế chồng chất xo&agrave;i l&ecirc;n nhau v&igrave; c&oacute; thể l&agrave;m dập n&aacute;t quả.</li>\n	<li>N&ecirc;n xếp xo&agrave;i th&agrave;nh từng lớp ri&ecirc;ng biệt.</li>\n</ul>\n\n<p><strong>+ Bảo quản trong tủ lạnh:</strong></p>\n\n<ul>\n	<li>Xo&agrave;i c&oacute; thể được bảo quản trong tủ lạnh để giữ được độ tươi ngon l&acirc;u hơn.</li>\n	<li>Chuẩn bị: Rửa sạch xo&agrave;i, để r&aacute;o nước. Sau đ&oacute;, cho xo&agrave;i v&agrave;o hộp k&iacute;n hoặc t&uacute;i nilon c&oacute; đục lỗ để đảm bảo lưu th&ocirc;ng kh&iacute;.</li>\n	<li>Nhiệt độ: N&ecirc;n bảo quản xo&agrave;i ở ngăn m&aacute;t tủ lạnh với nhiệt độ từ 8-10 độ C.</li>\n	<li>Thời gian bảo quản: Xo&agrave;i bảo quản trong tủ lạnh c&oacute; thể giữ được độ tươi ngon trong khoảng 2-3 tuần.</li>\n</ul>\n', 'Trái cây Việt', 69000, 1, '2024-07-05', 7, 'da5e2de8795c39c69a8cfdef254d2c86', 'q5xt0g8euapcwef5miqc', 3, 2, 1),
(9, 'Dưa Hạ Uyển', '<h1><strong>Dưa Hạ Uyển</strong></h1>\n\n<p>-&nbsp;<strong>Xuất xứ:</strong>&nbsp;Việt Nam</p>\n\n<p><strong>- Ti&ecirc;u chuẩn chất lượng:&nbsp;</strong>Tr&aacute;i c&acirc;y sạch</p>\n\n<p>-&nbsp;<strong>Khối lượng</strong>; 1,7 - 2,2kg/tr&aacute;i</p>\n\n<p><strong>- Đặc điểm nổi bật của dưa Hạ Uyển</strong></p>\n\n<ul>\n	<li>Dưa Hạ Uyển l&agrave; giống dưa lưới được trồng phổ biến ại Việt Nam</li>\n	<li>Dưa Hạ Uyển c&oacute; m&ugrave;i thơm dịu nhẹ, rất dễ chịu v&agrave; c&oacute; thịt m&agrave;u v&agrave;ng cam ăn rất ngọt v&agrave; thơm</li>\n</ul>\n\n<p><strong>- Lợi &iacute;ch tuyệt vời của dưa hạ uyển</strong></p>\n\n<ul>\n	<li>Dưa lưới Hạ Uyển c&oacute;&nbsp;hương vị ngọt m&aacute;t tuyệt vời v&agrave; c&oacute; nhiều dưỡng chất&nbsp;&nbsp;bổ &iacute;ch. Chứa nhiều Acid Folic, Niancin, magi&ecirc;, sắt, canxi, vitamin C v&agrave; A c&ugrave;ng c&aacute;c kho&aacute;ng chất thiết yếu</li>\n	<li>Hỗ trợ c&acirc;n bằng huyết &aacute;p nhờ lượng kali cao.</li>\n	<li>Tăng cường sức mạnh cho thị lực.</li>\n	<li>Th&uacute;c đẩy ti&ecirc;u h&oacute;a hiệu quả.</li>\n	<li>Cung cấp độ ẩm cho da v&agrave; cơ thể.</li>\n	<li>Bổ sung dưỡng chất quan trọng.</li>\n</ul>\n\n<p><strong>- C&aacute;ch lựa chọn được quả dưa Hạ Uyển ngon v&agrave; ngọt</strong></p>\n\n<ul>\n	<li>N&ecirc;n chọn những quả căng mọng, vỏ nguy&ecirc;n vẹn, với v&acirc;n nổi bật tr&ecirc;n phần vỏ</li>\n	<li>Đảm bảo dưa c&oacute; m&agrave;u xanh tươi tự nhi&ecirc;n v&agrave; mọng nước tươi ngon.</li>\n	<li>H&atilde;y tr&aacute;nh chọn những quả nhẹ, kh&ocirc;ng đ&agrave;n hồi tốt, vỏ mất t&iacute;nh đ&agrave;n hồi, hoặc c&oacute; dấu hiệu hỏng h&oacute;c như mềm, th&acirc;m, nứt, hay m&ugrave;i kh&oacute; chịu.</li>\n	<li>N&ecirc;n ấn nhẹ v&agrave;o quả dưa để kiểm tra độ ch&iacute;n. Nếu quả dưa mềm v&agrave; c&oacute; độ đ&agrave;n hồi th&igrave; đ&atilde; ch&iacute;n</li>\n	<li>Với Farmers Market ti&ecirc;u ch&iacute; h&agrave;ng đầu l&agrave; sản phảm đạt chất lượng cao, nguồn gốc r&otilde; r&agrave;ng, mang đến trải nghiệm tốt nhất cho kh&aacute;ch h&agrave;ng.&nbsp;</li>\n</ul>\n\n<p><strong>- C&aacute;ch bảo quản dưa Hạ Uyển tốt nhất</strong></p>\n\n<ul>\n	<li>Bảo quản dưa Hạ Uyển trong ngăn m&aacute;t tủ lạnh để giữ độ tươi ngon l&acirc;u hơn, nhưng n&ecirc;n bọc bằng giấy hoặc để trong hộp.&nbsp;</li>\n	<li>Tr&aacute;nh để dưa gần c&aacute;c loại tr&aacute;i c&acirc;y hoặc rau củ kh&aacute;c v&igrave; ethylene từ những thực phẩm n&agrave;y c&oacute; thể khiến dưa ch&iacute;n nhanh hơn.</li>\n	<li>Tr&aacute;nh để dưa lưới Hạ Uyển trong m&ocirc;i trường qu&aacute; ẩm ướt v&igrave; c&oacute; thể khiến quả bị thối rữa.</li>\n</ul>\n\n<p><strong>- Những lưu &yacute; khi sử dụng dưa Hạ Uyển</strong></p>\n\n<ul>\n	<li><strong>Kh&ocirc;ng n&ecirc;n ăn dưa lưới Hạ Uyển khi quả chưa ch&iacute;n:</strong>&nbsp;Dưa lưới Hạ Uyển chưa ch&iacute;n c&oacute; thể chứa nhiều axit c&oacute; thể g&acirc;y kh&oacute; ti&ecirc;u, đầy bụng.</li>\n	<li><strong>Hạn chế ăn dưa lưới Hạ Uyển khi bụng đ&oacute;i:</strong>&nbsp;Dưa lưới Hạ Uyển c&oacute; t&iacute;nh h&agrave;n, ăn nhiều khi bụng đ&oacute;i c&oacute; thể g&acirc;y lạnh bụng, ti&ecirc;u chảy.</li>\n</ul>\n\n<p><strong>- Phụ nữ mang thai v&agrave; trẻ em ăn dưa Hạ Uyển&nbsp;c&oacute; được kh&ocirc;ng?</strong></p>\n\n<ul>\n	<li>Dưa lưới Hạ Uyển l&agrave; nguồn cung cấp dồi d&agrave;o vitamin A, C, E v&agrave; c&aacute;c kho&aacute;ng chất như kali, magie, canxi,... rất tốt cho sức khỏe của phụ nữ mang thai, nhưng kh&ocirc;ng được ăn khi đang đ&oacute;i</li>\n	<li>Hệ ti&ecirc;u h&oacute;a của<strong>&nbsp;trẻ sơ sinh</strong>&nbsp;c&ograve;n non yếu,<strong>&nbsp;chưa đủ khả năng để ti&ecirc;u h&oacute;a dưa lưới&nbsp;</strong>Hạ Uyển. Ăn dưa lưới Hạ Uyển c&oacute; thể khiến trẻ bị đầy bụng, kh&oacute; ti&ecirc;u, ti&ecirc;u chảy,...</li>\n</ul>\n', 'Trái cây Việt', 165000, 1, '2024-07-05', 7, '14a951fd4f8e78da9a4a2d90b5a9e930', 'vfq8u9d1rb8nqmwaa6zc', 3, 3, 1),
(10, 'Quýt đường lão Hiếu Liêm', '<p><strong>Qu&yacute;t đường l&atilde;o Hiếu Li&ecirc;m</strong></p>\n\n<p><strong>- Xuất xứ:&nbsp;</strong>Việt Nam</p>\n\n<p><strong>- Ti&ecirc;u chuẩn chất lượng:&nbsp;</strong>Tr&aacute;i c&acirc;y sạch</p>\n\n<p><strong>- Quy c&aacute;ch:</strong>&nbsp;1kg/ T&uacute;i</p>\n\n<p><strong>Qu&yacute;t Đường Hướng Hữu Cơ (Kg)</strong>&nbsp;được trồng theo hướng hữu cơ, đảm bảo về chất lượng v&agrave; ti&ecirc;u ch&iacute; thực phẩm sạch. Với&nbsp;vị ngọt thanh, qu&yacute;t đường l&agrave; một tr&aacute;i c&acirc;y thơm ngon.&nbsp;</p>\n\n<p><strong>- Lợi &iacute;ch:</strong></p>\n\n<ul>\n	<li><strong>Tăng cường hệ miễn dịch:</strong>&nbsp;Vitamin C trong qu&yacute;t&nbsp;gi&uacute;p tăng cường hệ miễn dịch, chống lại c&aacute;c bệnh nhiễm tr&ugrave;ng.</li>\n	<li><strong>Hỗ trợ ti&ecirc;u h&oacute;a:</strong>&nbsp;Chất xơ trong qu&yacute;t&nbsp;gi&uacute;p hỗ trợ ti&ecirc;u h&oacute;a, ngăn ngừa t&aacute;o b&oacute;n.</li>\n	<li><strong>Tốt cho tim mạch:</strong>&nbsp;Kali trong qu&yacute;t gi&uacute;p điều h&ograve;a huyết &aacute;p, giảm nguy cơ mắc bệnh tim mạch.</li>\n	<li><strong>Gi&uacute;p đẹp da:</strong>&nbsp;Vitamin A v&agrave; C trong qu&yacute;t gi&uacute;p chống l&atilde;o h&oacute;a, l&agrave;m đẹp da.</li>\n	<li><strong>Ngăn ngừa ung thư:</strong>&nbsp;Chất chống oxy h&oacute;a trong qu&yacute;t gi&uacute;p ngăn ngừa ung thư.</li>\n</ul>\n\n<p><strong>C&ocirc;ng dụng:</strong></p>\n\n<ul>\n	<li>Ăn trực tiếp l&agrave; c&aacute;ch phổ biến nhất để thưởng thức qu&yacute;t.</li>\n	<li>C&oacute; thể &eacute;p lấy nước để uống, tốt cho sức khỏe v&agrave; l&agrave;m đẹp da.</li>\n	<li>Sử dụng trong c&aacute;c m&oacute;n salad, m&oacute;n tr&aacute;ng miệng hoặc l&agrave;m mứt.</li>\n</ul>\n\n<p><strong>- C&aacute;ch lựa chọn:</strong></p>\n\n<ul>\n	<li>Vỏ qu&yacute;t ngon thường c&oacute; m&agrave;u v&agrave;ng cam s&aacute;ng b&oacute;ng, đều m&agrave;u, kh&ocirc;ng bị sần s&ugrave;i hay c&oacute; đốm. Tr&aacute;nh chọn những quả c&oacute; vỏ m&agrave;u xanh hoặc đốm n&acirc;u v&igrave; c&oacute; thể chưa ch&iacute;n hoặc bị hư hỏng.</li>\n	<li>Với Farmers Market ti&ecirc;u ch&iacute; h&agrave;ng đầu l&agrave; sản phảm đạt chất lượng cao, nguồn gốc r&otilde; r&agrave;ng, mang đến trải nghiệm tốt nhất cho kh&aacute;ch h&agrave;ng.&nbsp;</li>\n</ul>\n\n<p><strong>- Hướng dẫn bảo quản:</strong></p>\n\n<ul>\n	<li>Bảo quản ở nơi kh&ocirc; r&aacute;o, tho&aacute;ng m&aacute;t.</li>\n	<li>N&ecirc;n để trong ngăn m&aacute;t tủ lạnh để giữ độ tươi ngon l&acirc;u hơn.</li>\n	<li>N&ecirc;n ăn qu&yacute;t trong v&ograve;ng 1-2 tuần sau khi mua.</li>\n</ul>\n\n<p><strong>- Phụ nữ mang thai v&agrave; trẻ em ăn qu&yacute;t c&oacute; được kh&ocirc;ng?</strong></p>\n\n<p><strong>+ Phụ nữ mang thai c&oacute; thể ăn qu&yacute;t.</strong>&nbsp;Qu&yacute;t l&agrave; loại tr&aacute;i c&acirc;y chứa nhiều vitamin, kho&aacute;ng chất v&agrave; chất chống oxy h&oacute;a tốt cho sức khỏe, đặc biệt l&agrave; vitamin C, folate v&agrave; kali.</p>\n\n<ul>\n	<li><strong>Vitamin C:</strong>&nbsp;Gi&uacute;p tăng cường hệ miễn dịch, hỗ trợ hấp thu sắt v&agrave; collagen, giảm nguy cơ tiền sản giật.</li>\n	<li><strong>Folate:</strong>&nbsp;Gi&uacute;p ngăn ngừa dị tật ống thần kinh ở thai nhi.</li>\n	<li><strong>Kali:</strong>&nbsp;Gi&uacute;p điều h&ograve;a huyết &aacute;p, giảm nguy cơ chuột r&uacute;t.</li>\n</ul>\n\n<p>+&nbsp;<strong>C&oacute; thể cho trẻ ăn qu&yacute;t khi b&eacute; đủ 6 th&aacute;ng tuổi.</strong>&nbsp;Qu&yacute;t l&agrave; nguồn cung cấp vitamin C, chất xơ v&agrave; kali dồi d&agrave;o, tốt cho sức khỏe của trẻ.</p>\n\n<ul>\n	<li><strong>Vitamin C:</strong>&nbsp;Gi&uacute;p tăng cường hệ miễn dịch, chống lại c&aacute;c bệnh nhiễm tr&ugrave;ng.</li>\n	<li><strong>Chất xơ:</strong>&nbsp;Hỗ trợ ti&ecirc;u h&oacute;a, ngăn ngừa t&aacute;o b&oacute;n.</li>\n	<li><strong>Kali:</strong>&nbsp;Gi&uacute;p điều h&ograve;a huyết &aacute;p, ph&aacute;t triển cơ bắp v&agrave; hệ thần kinh.</li>\n</ul>\n\n<p><strong>- Lưu &yacute;:</strong></p>\n\n<ul>\n	<li>Kh&ocirc;ng n&ecirc;n ăn qu&yacute;t khi đang đ&oacute;i v&igrave; c&oacute; thể g&acirc;y kh&oacute; ti&ecirc;u.</li>\n	<li>Hạn chế ăn qu&yacute;t nếu bạn bị n&oacute;ng trong người.</li>\n	<li>Bắt đầu cho trẻ ăn qu&yacute;t với lượng nhỏ, khoảng 1/4 quả mỗi ng&agrave;y.</li>\n	<li>Cho trẻ ăn qu&yacute;t khi b&eacute; đang ngồi v&agrave; c&oacute; người lớn gi&aacute;m s&aacute;t.</li>\n	<li>Ăn qu&yacute;t với lượng vừa phải, khoảng 1-2 quả mỗi ng&agrave;y.</li>\n</ul>\n', 'Trái cây Việt', 89000, 1, '2024-07-05', 7, 'aec0a73298d8f7ab5a1e4a0e2042642d', 'hhwplr9e4owk9p8rikxs', 3, 1, 1);
INSERT INTO `products` (`id`, `nameOfProduct`, `description`, `category`, `price`, `weightDefault`, `dateOfImporting`, `expriredDay`, `imgAssetId`, `imgPublicId`, `adminCreate`, `provider`, `status`) VALUES
(11, 'Thanh long ruột trắng', '<h1><strong>Thanh long ruột trắng&nbsp;</strong></h1>\n\n<p><strong>- Xuất xứ</strong>: Việt Nam</p>\n\n<p><strong>- Ti&ecirc;u chuẩn chất lượng:</strong>&nbsp;Tr&aacute;i c&acirc;y sạch</p>\n\n<p><strong>- Khối lượng:&nbsp;</strong>1kg</p>\n\n<p><strong>Thanh long ruột trắng</strong>&nbsp;( c&ograve;n gọi l&agrave; thanh long trắng) l&agrave; một loại tr&aacute;i c&acirc;y nhiệt đới thuộc họ xương rồng, c&oacute; t&ecirc;n khoa học l&agrave; Hylocereus undatus. Loại tr&aacute;i c&acirc;y n&agrave;y c&oacute; vỏ ngo&agrave;i m&agrave;u hồng đậm, gai nhọn, ruột b&ecirc;n trong m&agrave;u trắng ng&agrave;, vị ngọt thanh m&aacute;t v&agrave; chứa nhiều dinh dưỡng.</p>\n\n<p><strong>- Đặc điểm:</strong></p>\n\n<ul>\n	<li><strong>Vỏ:</strong>&nbsp;M&agrave;u hồng đậm, gai nhọn, xếp đều đặn.</li>\n	<li><strong>Ruột:</strong>&nbsp;M&agrave;u trắng ng&agrave;, c&oacute; nhiều hạt đen nhỏ.</li>\n	<li><strong>Vị:&nbsp;</strong>Ngọt thanh m&aacute;t.</li>\n	<li><strong>M&ugrave;i:</strong>&nbsp;Thơm dịu.</li>\n	<li><strong>K&iacute;ch thước</strong>: Quả thanh long ruột trắng c&oacute; h&igrave;nh bầu dục, k&iacute;ch thước trung b&igrave;nh từ 300g đến 1kg.</li>\n</ul>\n\n<p><strong>- Dinh dưỡng:</strong></p>\n\n<p>Thanh long ruột trắng l&agrave; nguồn cung cấp dồi d&agrave;o vitamin C, chất xơ, sắt, canxi, magie, kali v&agrave; c&aacute;c chất chống oxy h&oacute;a.</p>\n\n<ul>\n	<li><strong>Vitamin C:</strong>&nbsp;Gi&uacute;p tăng cường hệ miễn dịch, chống lại c&aacute;c gốc tự do, hỗ trợ da s&aacute;ng khỏe.</li>\n	<li><strong>Chất xơ:</strong>&nbsp;Gi&uacute;p hỗ trợ hệ ti&ecirc;u h&oacute;a, ngăn ngừa t&aacute;o b&oacute;n.</li>\n	<li><strong>Sắt:</strong>&nbsp;Gi&uacute;p bổ sung m&aacute;u, ph&ograve;ng ngừa thiếu m&aacute;u.</li>\n	<li><strong>Canxi:</strong>&nbsp;Gi&uacute;p ph&aacute;t triển v&agrave; bảo vệ hệ xương răng.</li>\n	<li><strong>Magie:</strong>&nbsp;Gi&uacute;p thư gi&atilde;n cơ bắp, giảm căng thẳng.</li>\n	<li><strong>Kali:</strong>&nbsp;Gi&uacute;p điều h&ograve;a huyết &aacute;p, tốt cho tim mạch.</li>\n	<li><strong>Chất chống oxy h&oacute;a:</strong>&nbsp;Gi&uacute;p bảo vệ cơ thể khỏi c&aacute;c bệnh tim mạch, ung thư, l&atilde;o h&oacute;a.</li>\n</ul>\n\n<p><strong>- Lợi &iacute;ch:</strong></p>\n\n<ul>\n	<li>Tăng cường hệ miễn dịch.</li>\n	<li>Hỗ trợ hệ ti&ecirc;u h&oacute;a.</li>\n	<li>Bổ sung m&aacute;u.</li>\n	<li>Tốt cho tim mạch.</li>\n	<li>L&agrave;m đẹp da.</li>\n	<li>Giảm c&acirc;n.</li>\n	<li>Giải nhiệt cơ thể.</li>\n</ul>\n\n<p><strong>- C&aacute;ch sử dụng:</strong></p>\n\n<ul>\n	<li>Thanh long ruột trắng c&oacute; thể ăn tươi, l&agrave;m sinh tố, salad, nước &eacute;p, hoặc sấy kh&ocirc;.</li>\n	<li>N&ecirc;n chọn mua thanh long ruột trắng c&oacute; vỏ m&agrave;u hồng đậm, gai nhọn đều đặn, ruột trắng ng&agrave;, kh&ocirc;ng bị dập n&aacute;t.</li>\n	<li>N&ecirc;n bảo quản thanh long ruột trắng ở nơi kh&ocirc; r&aacute;o, tho&aacute;ng m&aacute;t, tr&aacute;nh &aacute;nh nắng trực tiếp.</li>\n</ul>\n\n<p><strong>- Lưu &yacute;:</strong></p>\n\n<ul>\n	<li>Kh&ocirc;ng n&ecirc;n ăn qu&aacute; nhiều thanh long ruột trắng trong một ng&agrave;y v&igrave; c&oacute; thể g&acirc;y đầy bụng, kh&oacute; ti&ecirc;u.</li>\n	<li>Người c&oacute; bệnh tiểu đường n&ecirc;n tham khảo &yacute; kiến b&aacute;c sĩ trước khi ăn thanh long ruột trắng.</li>\n</ul>\n\n<p><strong>-&nbsp;C&aacute;ch lựa chọn thanh long ngon</strong></p>\n\n<ul>\n	<li>N&ecirc;n chọn mua thanh long c&oacute; vỏ ngo&agrave;i m&agrave;u hồng hoặc đỏ tươi, vảy nhỏ đều nhau.</li>\n	<li>Với Farmers Market ti&ecirc;u ch&iacute; h&agrave;ng đầu l&agrave; sản phảm đạt chất lượng cao, nguồn gốc r&otilde; r&agrave;ng, mang đến trải nghiệm tốt nhất cho kh&aacute;ch h&agrave;ng.&nbsp;</li>\n</ul>\n\n<p><strong>- C&aacute;ch bảo quản thanh long:</strong></p>\n\n<p><strong>+ Bảo quản ở nhiệt độ ph&ograve;ng:</strong></p>\n\n<ul>\n	<li><strong>Chọn thanh long ch&iacute;n tới:</strong>&nbsp;N&ecirc;n chọn những quả thanh long c&oacute; m&agrave;u đỏ tươi, gai nhọn đều đặn, vỏ hơi mềm khi ấn nhẹ. Tr&aacute;nh chọn những quả thanh long chưa ch&iacute;n hoặc qu&aacute; ch&iacute;n v&igrave; dễ bị dập n&aacute;t v&agrave; nhanh hỏng.</li>\n	<li><strong>Rửa sạch v&agrave; để r&aacute;o:</strong>&nbsp;Sau khi mua về, bạn n&ecirc;n rửa sạch thanh long dưới v&ograve;i nước chảy để loại bỏ bụi bẩn v&agrave; vi khuẩn. Sau đ&oacute;, để r&aacute;o nước ho&agrave;n to&agrave;n.</li>\n	<li><strong>Bảo quản ở nơi tho&aacute;ng m&aacute;t:</strong>&nbsp;N&ecirc;n bảo quản thanh long ở nơi tho&aacute;ng m&aacute;t, tr&aacute;nh &aacute;nh nắng trực tiếp. Bạn c&oacute; thể để thanh long trong rổ hoặc hộp c&oacute; lỗ th&ocirc;ng hơi để đảm bảo lưu th&ocirc;ng kh&iacute; tốt.</li>\n	<li><strong>Hạn chế chồng chất:</strong>&nbsp;Tr&aacute;nh chồng chất thanh long l&ecirc;n nhau v&igrave; c&oacute; thể l&agrave;m dập n&aacute;t quả. N&ecirc;n xếp thanh long th&agrave;nh từng lớp ri&ecirc;ng biệt.</li>\n	<li><strong>Thời gian bảo quản:</strong>&nbsp;Với c&aacute;ch bảo quản n&agrave;y, thanh long c&oacute; thể giữ được độ tươi ngon trong khoảng 5 - 8 ng&agrave;y.</li>\n</ul>\n\n<p><strong>+ Bảo quản trong tủ lạnh:</strong></p>\n\n<ul>\n	<li><strong>C&aacute;ch bảo quản:</strong>&nbsp;Thanh long c&oacute; thể được bảo quản trong tủ lạnh để giữ được độ tươi ngon l&acirc;u hơn.</li>\n	<li><strong>Chuẩn bị:</strong>&nbsp;Rửa sạch thanh long, để r&aacute;o nước. Sau đ&oacute;, cho thanh long v&agrave;o hộp k&iacute;n hoặc t&uacute;i nilon c&oacute; đục lỗ để đảm bảo lưu th&ocirc;ng kh&iacute;.</li>\n	<li><strong>Nhiệt độ:</strong>&nbsp;N&ecirc;n bảo quản thanh long ở ngăn m&aacute;t tủ lạnh với nhiệt độ từ 8-10 độ C.</li>\n	<li><strong>Thời gian bảo quản:</strong>&nbsp;Thanh long bảo quản trong tủ lạnh c&oacute; thể giữ được độ tươi ngon trong khoảng 15 - 20 ng&agrave;y.</li>\n</ul>\n', 'Trái cây Việt', 69000, 1, '2024-07-05', 7, '780caf8218589059382839c8efdb2394', 'w2ckvvh1vlv05hfphgxp', 3, 2, 1),
(12, 'Xoài giống Úc', '<h1><strong>Xo&agrave;i giống &Uacute;c</strong></h1>\n\n<p><strong>- Xuất xứ:&nbsp;</strong>Việt Nam</p>\n\n<p><strong>- Ti&ecirc;u chuẩn chất lượng:&nbsp;</strong>Tr&aacute;i c&acirc;y sạch</p>\n\n<p><strong>- Đặc điểm của xo&agrave;i &Uacute;c:</strong></p>\n\n<ul>\n	<li><strong>Quả:</strong>&nbsp;Xo&agrave;i &Uacute;c c&oacute; quả to tr&ograve;n, trọng lượng trung b&igrave;nh 800g/tr&aacute;i, c&oacute; khi l&ecirc;n đến 1,5 kg. Vỏ quả c&oacute; m&agrave;u xanh lục khi chưa ch&iacute;n v&agrave; chuyển sang m&agrave;u v&agrave;ng ửng hồng khi ch&iacute;n. Vệt m&agrave;u đỏ đặc trưng của xo&agrave;i &Uacute;c được tạo ra nhờ &aacute;nh nắng mặt trời.</li>\n	<li><strong>Thịt:</strong>&nbsp;Thịt xo&agrave;i &Uacute;c d&agrave;y, m&agrave;u v&agrave;ng cam, &iacute;t xơ v&agrave; c&oacute; vị ngọt thanh.</li>\n	<li><strong>Hạt:</strong>&nbsp;Hạt xo&agrave;i &Uacute;c nhỏ, dẹt.</li>\n	<li><strong>M&ugrave;a vụ:</strong>&nbsp;Xo&agrave;i &Uacute;c c&oacute; m&ugrave;a vụ từ th&aacute;ng 4 đến th&aacute;ng 6 h&agrave;ng năm.</li>\n</ul>\n\n<p><strong>- Lợi &iacute;ch của xo&agrave;i &Uacute;c:</strong></p>\n\n<ul>\n	<li>Xo&agrave;i &Uacute;c l&agrave; nguồn cung cấp dồi d&agrave;o vitamin A, C, E v&agrave; c&aacute;c kho&aacute;ng chất như kali, magi&ecirc;, canxi.</li>\n	<li>Vitamin A trong xo&agrave;i &Uacute;c gi&uacute;p s&aacute;ng mắt, tăng cường hệ miễn dịch v&agrave; bảo vệ da khỏi t&aacute;c hại của tia UV.</li>\n	<li>Vitamin C trong xo&agrave;i &Uacute;c gi&uacute;p tăng cường hệ miễn dịch, chống oxy h&oacute;a v&agrave; hỗ trợ qu&aacute; tr&igrave;nh hấp thụ sắt.</li>\n	<li>Kali trong xo&agrave;i &Uacute;c gi&uacute;p điều h&ograve;a huyết &aacute;p v&agrave; tốt cho tim mạch.</li>\n	<li>Magi&ecirc; trong xo&agrave;i &Uacute;c gi&uacute;p giảm nguy cơ mắc bệnh tim mạch, đột quỵ v&agrave; tiểu đường.</li>\n	<li>Canxi trong xo&agrave;i &Uacute;c gi&uacute;p chắc khỏe xương v&agrave; răng.</li>\n</ul>\n\n<p><strong>-Hướng dẫn bảo quản v&agrave; sử dụng</strong></p>\n\n<ul>\n	<li>Ăn trực tiếp v&agrave; ngon hơn khi để lạnh. &nbsp;</li>\n	<li>Th&iacute;ch hợp xay sinh tố hoặc ăn k&egrave;m c&ugrave;ng yogurt v&agrave; ngũ cốc.&nbsp;&nbsp;</li>\n</ul>\n\n<p><strong>- C&aacute;ch lựa chọn xo&agrave;i ngon:</strong></p>\n\n<ul>\n	<li>N&ecirc;n chọn mua Xo&agrave;i C&aacute;t H&ograve;a Lộc c&oacute; vỏ m&agrave;u v&agrave;ng nhạt, vỏ mỏng, mịn v&agrave; dễ b&oacute;c.</li>\n	<li>Tr&aacute;nh mua những quả xo&agrave;i bị dập n&aacute;t, vỏ bị th&acirc;m hoặc c&oacute; m&ugrave;i h&ocirc;i.</li>\n	<li>Với Farmers Market ti&ecirc;u ch&iacute; h&agrave;ng đầu l&agrave; sản phảm đạt chất lượng cao, nguồn gốc r&otilde; r&agrave;ng, mang đến trải nghiệm tốt nhất cho kh&aacute;ch h&agrave;ng.&nbsp;</li>\n</ul>\n\n<p><strong>- C&aacute;ch bảo quản xo&agrave;i được l&acirc;u:</strong></p>\n\n<p><strong>+ Bảo quản ở nơi tho&aacute;ng m&aacute;t:</strong></p>\n\n<ul>\n	<li>N&ecirc;n bảo quản xo&agrave;i ở nơi tho&aacute;ng m&aacute;t, tr&aacute;nh &aacute;nh nắng trực tiếp.</li>\n	<li>Bạn c&oacute; thể để xo&agrave;i trong rổ hoặc hộp c&oacute; lỗ th&ocirc;ng hơi để đảm bảo lưu th&ocirc;ng kh&iacute; tốt.</li>\n	<li>Hạn chế chồng chất xo&agrave;i l&ecirc;n nhau v&igrave; c&oacute; thể l&agrave;m dập n&aacute;t quả.</li>\n	<li>N&ecirc;n xếp xo&agrave;i th&agrave;nh từng lớp ri&ecirc;ng biệt.</li>\n</ul>\n\n<p><strong>+ Bảo quản trong tủ lạnh:</strong></p>\n\n<ul>\n	<li>Xo&agrave;i c&oacute; thể được bảo quản trong tủ lạnh để giữ được độ tươi ngon l&acirc;u hơn.</li>\n	<li>Chuẩn bị: Rửa sạch xo&agrave;i, để r&aacute;o nước. Sau đ&oacute;, cho xo&agrave;i v&agrave;o hộp k&iacute;n hoặc t&uacute;i nilon c&oacute; đục lỗ để đảm bảo lưu th&ocirc;ng kh&iacute;.</li>\n	<li>Nhiệt độ: N&ecirc;n bảo quản xo&agrave;i ở ngăn m&aacute;t tủ lạnh với nhiệt độ từ 8-10 độ C.</li>\n	<li>Thời gian bảo quản: Xo&agrave;i bảo quản trong tủ lạnh c&oacute; thể giữ được độ tươi ngon trong khoảng 2-3 tuần.</li>\n</ul>\n', 'Trái cây Việt', 89000, 1, '2024-07-05', 7, 'cd767cce52c0fea29647cecfb72b504f', 'lmtuchrz1trcjycrxbjc', 3, 3, 1),
(13, 'Chuối Kid', '<h1><strong>Chuối Kid</strong></h1>\n\n<p><strong>- Xuất xứ:&nbsp;</strong>Việt Nam</p>\n\n<p><strong>- Ti&ecirc;u chuẩn chất lượng:</strong>&nbsp;tr&aacute;i c&acirc;y sạch</p>\n\n<p><strong>- Đặc điểm của Chuối Kid:</strong></p>\n\n<ul>\n	<li><strong>K&iacute;ch thước:</strong>Chuối Kid c&oacute; k&iacute;ch thước nhỏ hơn so với c&aacute;c loại chuối th&ocirc;ng thường kh&aacute;c. Chiều d&agrave;i trung b&igrave;nh của quả chuối Kid khoảng 10-12 cm, đường k&iacute;nh khoảng 3-4 cm.</li>\n	<li><strong>Vỏ:</strong>&nbsp;Vỏ chuối Kid mỏng, m&agrave;u v&agrave;ng tươi khi ch&iacute;n v&agrave; chuyển sang m&agrave;u v&agrave;ng sẫm khi ch&iacute;n nẫu.</li>\n	<li><strong>Thịt quả:</strong>&nbsp;Thịt quả chuối Kid c&oacute; m&agrave;u trắng ng&agrave;, mềm mịn, &iacute;t xơ v&agrave; c&oacute; vị ngọt thanh.</li>\n	<li><strong>Hạt:</strong>&nbsp;Hạt chuối Kid nhỏ, m&agrave;u đen v&agrave; &iacute;t.</li>\n</ul>\n\n<p><strong>- Lợi &iacute;ch của Chuối Kid:</strong></p>\n\n<ul>\n	<li><strong>Gi&agrave;u dinh dưỡng:</strong>&nbsp;Chuối Kid l&agrave; nguồn cung cấp dồi d&agrave;o vitamin C, vitamin B6, kali v&agrave; chất xơ.</li>\n	<li><strong>Tốt cho sức khỏe:</strong>&nbsp;Chuối Kid gi&uacute;p tăng cường hệ miễn dịch, cải thiện sức khỏe ti&ecirc;u h&oacute;a, tốt cho tim mạch v&agrave; gi&uacute;p đẹp da.</li>\n	<li><strong>Dễ trồng v&agrave; chăm s&oacute;c:</strong>&nbsp;C&acirc;y chuối Kid dễ trồng v&agrave; chăm s&oacute;c, th&iacute;ch hợp với kh&iacute; hậu nhiệt đới.</li>\n	<li><strong>Tiện lợi:</strong>&nbsp;Chuối Kid c&oacute; k&iacute;ch thước nhỏ gọn, dễ d&agrave;ng mang theo v&agrave; thưởng thức mọi l&uacute;c mọi nơi.</li>\n</ul>\n\n<p><strong>- C&aacute;ch sử dụng Chuối Kid:</strong></p>\n\n<ul>\n	<li>Chuối Kid c&oacute; thể ăn tươi trực tiếp như một m&oacute;n ăn nhẹ hoặc tr&aacute;ng miệng.</li>\n	<li>Chuối Kid cũng c&oacute; thể được sử dụng để l&agrave;m sinh tố, salad, b&aacute;nh ngọt, mứt,...</li>\n	<li>Chuối Kid l&agrave; nguy&ecirc;n liệu tuyệt vời cho c&aacute;c m&oacute;n ăn dặm cho trẻ em.</li>\n</ul>\n\n<p><strong>- C&aacute;ch bảo quản Chuối Kid:</strong></p>\n\n<ul>\n	<li>Chuối Kid n&ecirc;n được bảo quản ở nhiệt độ ph&ograve;ng khi c&ograve;n xanh v&agrave; chuyển v&agrave;o tủ lạnh khi đ&atilde; ch&iacute;n.</li>\n	<li>Chuối Kid c&oacute; thể bảo quản trong tủ lạnh từ 3 đến 5 ng&agrave;y.</li>\n	<li>N&ecirc;n để chuối Kid trong hộp k&iacute;n hoặc rổ c&oacute; l&oacute;t khăn giấy để tr&aacute;nh bị b&iacute; v&agrave; hư hỏng.</li>\n</ul>\n\n<p><strong>Những lưu &yacute; khi thưởng thức chuối&nbsp;</strong></p>\n\n<ul>\n	<li>Kh&ocirc;ng n&ecirc;n ăn chuối khi đang đ&oacute;i</li>\n	<li>N&ecirc;n ăn chuối sau bữa ăn khoảng 1 đến 2 tiếng</li>\n	<li>Kh&ocirc;ng n&ecirc;n sử dụng chuối kết hợp với một số loại thực phẩm kh&aacute;c như khoai t&acirc;y, khoai lang, dưa hấu, ...</li>\n	<li>Kh&ocirc;ng n&ecirc;n ăn qu&aacute; nhiều chuối trong một ng&agrave;y</li>\n</ul>\n', 'Trái cây Việt', 29000, 1, '2024-07-05', 7, '7b690277a498e35a47b021b6258df6fe', 'dcirtmq7spi7c2cigawj', 3, 1, 1),
(14, 'Dưa lưới ruột xanh Angelica Chuẩn An Toàn Quốc Tế L1', '<h1><strong>Dưa lưới ruột xanh Angelica Chuẩn An To&agrave;n Quốc Tế loại 1</strong></h1>\n\n<p><strong>- Xuất xứ:&nbsp;</strong>Việt Nam</p>\n\n<p><strong>- Ti&ecirc;u chuẩn chất lượng:&nbsp;</strong>chuẩn an to&agrave;n quốc tế loại 1, tr&aacute;i c&acirc;y sạch</p>\n\n<p><strong>- Trọng lượng:&nbsp;</strong>1kg<strong>&nbsp;-</strong>1,2kg/Tr&aacute;i</p>\n\n<p><strong>Đặc điểm:&nbsp;</strong>Quả to c&oacute; v&acirc;n lưới nhẹ, nh&igrave;n đẹp mắt. L&uacute;c ch&iacute;n dưa c&oacute; m&ugrave;i rất thơm k&egrave;m vị ngọt đậm.</p>\n\n<p>Dưa lưới&nbsp;l&agrave; nguồn cung cấp dồi d&agrave;o vitamin C, vitamin A, kali, magie v&agrave; c&aacute;c kho&aacute;ng chất thiết yếu kh&aacute;c cho cơ thể.</p>\n\n<ul>\n	<li><strong>Vitamin C:</strong>&nbsp;Gi&uacute;p tăng cường hệ miễn dịch, chống oxy h&oacute;a v&agrave; bảo vệ cơ thể khỏi c&aacute;c bệnh tật.</li>\n	<li><strong>Vitamin A:</strong>&nbsp;Gi&uacute;p hỗ trợ sức khỏe mắt, da v&agrave; hệ miễn dịch.</li>\n	<li><strong>Kali:</strong>&nbsp;Gi&uacute;p điều h&ograve;a huyết &aacute;p v&agrave; duy tr&igrave; chức năng tim mạch.</li>\n	<li><strong>Magie:</strong>&nbsp;Gi&uacute;p hỗ trợ hệ cơ bắp v&agrave; xương khớp.</li>\n</ul>\n\n<p><strong>- Lợi &iacute;ch sức khỏe:</strong></p>\n\n<ul>\n	<li><strong>Tăng cường hệ miễn dịch:</strong>&nbsp;Vitamin C v&agrave; vitamin A trong Dưa lưới biển Hằng Nga Danny Green Organic gi&uacute;p tăng cường hệ miễn dịch, chống lại c&aacute;c bệnh cảm c&uacute;m, ho.</li>\n	<li><strong>Hỗ trợ sức khỏe mắt:</strong>&nbsp;Vitamin A trong Dưa lưới biển Hằng Nga Danny Green Organic gi&uacute;p hỗ trợ sức khỏe mắt, giảm nguy cơ mắc c&aacute;c bệnh về mắt như đục thủy tinh thể, tho&aacute;i h&oacute;a điểm v&agrave;ng.</li>\n	<li><strong>Điều h&ograve;a huyết &aacute;p:</strong>&nbsp;Kali trong Dưa lưới biển Hằng Nga Danny Green Organic gi&uacute;p điều h&ograve;a huyết &aacute;p, giảm nguy cơ mắc c&aacute;c bệnh tim mạch.</li>\n	<li><strong>Hỗ trợ hệ cơ bắp v&agrave; xương khớp:</strong>&nbsp;Magie trong Dưa lưới biển Hằng Nga Danny Green Organic gi&uacute;p hỗ trợ hệ cơ bắp v&agrave; xương khớp, giảm nguy cơ lo&atilde;ng xương v&agrave; chuột r&uacute;t.</li>\n	<li><strong>L&agrave;m đẹp da:</strong>&nbsp;Vitamin C v&agrave; chất chống oxy h&oacute;a trong Dưa lưới biển Hằng Nga Danny Green Organic gi&uacute;p bảo vệ da khỏi t&aacute;c hại của &aacute;nh nắng mặt trời, l&agrave;m giảm nếp nhăn v&agrave; tăng độ đ&agrave;n hồi cho da.</li>\n</ul>\n\n<p><strong>- Bảo quản dưa lưới :</strong></p>\n\n<ul>\n	<li>N&ecirc;n bảo quản dưa lưới ở nơi kh&ocirc; r&aacute;o, tho&aacute;ng m&aacute;t, tr&aacute;nh &aacute;nh nắng trực tiếp.</li>\n	<li>C&oacute; thể bảo quản dưa lưới trong tủ lạnh ở nhiệt độ từ 0&deg;C đến 4&deg;C để giữ được độ tươi ngon l&acirc;u hơn.</li>\n</ul>\n\n<p>-&nbsp;<strong>C&aacute;ch lựa chọn dưa lưới&nbsp;ngon:</strong></p>\n\n<ul>\n	<li>N&ecirc;n chọn những quả dưa lưới c&oacute; h&igrave;nh dạng tr&ograve;n đều, hai đầu c&acirc;n đối, kh&ocirc;ng bị m&eacute;o m&oacute;.</li>\n	<li>Với Farmers Market ti&ecirc;u ch&iacute; h&agrave;ng đầu l&agrave; sản phảm đạt chất lượng cao, nguồn gốc r&otilde; r&agrave;ng, mang đến trải nghiệm tốt nhất cho kh&aacute;ch h&agrave;ng.&nbsp;</li>\n</ul>\n\n<p><strong>-&nbsp; Phụ nữ mang thai v&agrave; trẻ em ăn dưa lưới&nbsp;c&oacute; được kh&ocirc;ng?</strong></p>\n\n<p><strong>+ Phụ nữ mang thai:</strong></p>\n\n<p><strong>C&oacute; thể ăn:</strong>&nbsp;Dưa lưới l&agrave; nguồn cung cấp vitamin C, A, kali v&agrave; chất xơ dồi d&agrave;o, tốt cho sức khỏe của phụ nữ mang thai. Vitamin C gi&uacute;p tăng cường hệ miễn dịch, vitamin A hỗ trợ ph&aacute;t triển thị gi&aacute;c cho thai nhi, kali gi&uacute;p điều h&ograve;a huyết &aacute;p, v&agrave; chất xơ gi&uacute;p ngăn ngừa t&aacute;o b&oacute;n.</p>\n\n<p><strong>Lưu &yacute;:</strong></p>\n\n<ul>\n	<li>Gọt vỏ dưa lưới trước khi ăn</li>\n	<li>N&ecirc;n ăn dưa lưới với lượng vừa phải, khoảng 1-2 quả mỗi ng&agrave;y.</li>\n	<li>Tr&aacute;nh ăn dưa lưới khi đang bị ti&ecirc;u chảy hoặc đầy bụng.</li>\n	<li>Dưa lưới c&oacute; t&iacute;nh h&agrave;n, do vậy những người c&oacute; cơ địa n&oacute;ng, hay bị n&oacute;ng trong người n&ecirc;n hạn chế ăn.</li>\n	<li>Kh&ocirc;ng n&ecirc;n ăn dưa lưới c&ugrave;ng với c&aacute;c thực phẩm c&oacute; t&iacute;nh h&agrave;n như cua, ốc, hải sản,...</li>\n</ul>\n\n<p><strong>+ Trẻ em:</strong></p>\n\n<p><strong>C&oacute; thể ăn:</strong>&nbsp;Dưa lưới l&agrave; nguồn cung cấp vitamin C, A, kali v&agrave; chất xơ dồi d&agrave;o, tốt cho sự ph&aacute;t triển của trẻ em. Vitamin C gi&uacute;p tăng cường hệ miễn dịch, vitamin A hỗ trợ ph&aacute;t triển thị gi&aacute;c, kali gi&uacute;p điều h&ograve;a huyết &aacute;p, v&agrave; chất xơ gi&uacute;p hỗ trợ hệ ti&ecirc;u h&oacute;a.</p>\n\n<p><strong>Lưu &yacute;:</strong></p>\n\n<ul>\n	<li>Trẻ em dưới 1 tuổi n&ecirc;n tham khảo &yacute; kiến b&aacute;c sĩ trước khi ăn dưa lưới.</li>\n	<li>Cắt dưa lưới th&agrave;nh từng miếng nhỏ để trẻ dễ ăn v&agrave; tr&aacute;nh nguy cơ h&oacute;c.</li>\n	<li>Cho trẻ ăn dưa lưới với lượng vừa phải, khoảng 1/2 quả mỗi ng&agrave;y.</li>\n</ul>\n', 'Trái cây Việt', 106800, 1, '2024-07-05', 7, 'eedfb91d0e89f9f9f6f4144750939267', 'oc7xy3zlrochooedf2lu', 3, 2, 1),
(15, 'Dưa lưới TL3', '<h1><strong>Dưa lưới TL3</strong></h1>\n\n<p><strong>- Xuất xứ:&nbsp;</strong>giống Nhật, trồng tại Việt Nam</p>\n\n<p><strong>- Quy c&aacute;ch:</strong>&nbsp;1,2 - 1,6kg/ tr&aacute;i</p>\n\n<p><strong>- Đặc điểm:&nbsp;</strong><strong>Dưa lưới TL3&nbsp;</strong>được trồng&nbsp;đạt chất lượng tr&aacute;i tr&ograve;n đều, đầy thịt, ngọt mọng nước c&ugrave;ng hương thơm đậm đ&agrave;.</p>\n\n<p><strong>- Gi&aacute; trị dinh dưỡng:</strong></p>\n\n<p>Dưa lưới&nbsp;l&agrave; nguồn cung cấp dồi d&agrave;o vitamin C, vitamin A, kali, magie v&agrave; c&aacute;c kho&aacute;ng chất thiết yếu kh&aacute;c cho cơ thể.</p>\n\n<ul>\n	<li><strong>Vitamin C:</strong>&nbsp;Gi&uacute;p tăng cường hệ miễn dịch, chống oxy h&oacute;a v&agrave; bảo vệ cơ thể khỏi c&aacute;c bệnh tật.</li>\n	<li><strong>Vitamin A:</strong>&nbsp;Gi&uacute;p hỗ trợ sức khỏe mắt, da v&agrave; hệ miễn dịch.</li>\n	<li><strong>Kali:</strong>&nbsp;Gi&uacute;p điều h&ograve;a huyết &aacute;p v&agrave; duy tr&igrave; chức năng tim mạch.</li>\n	<li><strong>Magie:</strong>&nbsp;Gi&uacute;p hỗ trợ hệ cơ bắp v&agrave; xương khớp.</li>\n</ul>\n\n<p><strong>- Lợi &iacute;ch sức khỏe:</strong></p>\n\n<ul>\n	<li><strong>Tăng cường hệ miễn dịch:</strong>&nbsp;Vitamin C v&agrave; vitamin A trong Dưa lưới biển Hằng Nga Danny Green Organic gi&uacute;p tăng cường hệ miễn dịch, chống lại c&aacute;c bệnh cảm c&uacute;m, ho.</li>\n	<li><strong>Hỗ trợ sức khỏe mắt:</strong>&nbsp;Vitamin A trong Dưa lưới biển Hằng Nga Danny Green Organic gi&uacute;p hỗ trợ sức khỏe mắt, giảm nguy cơ mắc c&aacute;c bệnh về mắt như đục thủy tinh thể, tho&aacute;i h&oacute;a điểm v&agrave;ng.</li>\n	<li><strong>Điều h&ograve;a huyết &aacute;p:</strong>&nbsp;Kali trong Dưa lưới biển Hằng Nga Danny Green Organic gi&uacute;p điều h&ograve;a huyết &aacute;p, giảm nguy cơ mắc c&aacute;c bệnh tim mạch.</li>\n	<li><strong>Hỗ trợ hệ cơ bắp v&agrave; xương khớp:</strong>&nbsp;Magie trong Dưa lưới biển Hằng Nga Danny Green Organic gi&uacute;p hỗ trợ hệ cơ bắp v&agrave; xương khớp, giảm nguy cơ lo&atilde;ng xương v&agrave; chuột r&uacute;t.</li>\n	<li><strong>L&agrave;m đẹp da:</strong>&nbsp;Vitamin C v&agrave; chất chống oxy h&oacute;a trong Dưa lưới biển Hằng Nga Danny Green Organic gi&uacute;p bảo vệ da khỏi t&aacute;c hại của &aacute;nh nắng mặt trời, l&agrave;m giảm nếp nhăn v&agrave; tăng độ đ&agrave;n hồi cho da.</li>\n</ul>\n\n<p><strong>- Bảo quản dưa lưới :</strong></p>\n\n<ul>\n	<li>N&ecirc;n bảo quản dưa lưới ở nơi kh&ocirc; r&aacute;o, tho&aacute;ng m&aacute;t, tr&aacute;nh &aacute;nh nắng trực tiếp.</li>\n	<li>C&oacute; thể bảo quản dưa lưới trong tủ lạnh ở nhiệt độ từ 0&deg;C đến 4&deg;C để giữ được độ tươi ngon l&acirc;u hơn.</li>\n</ul>\n\n<p>-&nbsp;<strong>C&aacute;ch lựa chọn dưa lưới&nbsp;ngon:</strong></p>\n\n<ul>\n	<li>N&ecirc;n chọn những quả dưa lưới c&oacute; h&igrave;nh dạng tr&ograve;n đều, hai đầu c&acirc;n đối, kh&ocirc;ng bị m&eacute;o m&oacute;.</li>\n	<li>Với Farmers Market ti&ecirc;u ch&iacute; h&agrave;ng đầu l&agrave; sản phảm đạt chất lượng cao, nguồn gốc r&otilde; r&agrave;ng, mang đến trải nghiệm tốt nhất cho kh&aacute;ch h&agrave;ng.&nbsp;</li>\n</ul>\n\n<p><strong>-&nbsp; Phụ nữ mang thai v&agrave; trẻ em ăn dưa lưới&nbsp;c&oacute; được kh&ocirc;ng?</strong></p>\n\n<p><strong>+ Phụ nữ mang thai:</strong></p>\n\n<p><strong>C&oacute; thể ăn:</strong>&nbsp;Dưa lưới l&agrave; nguồn cung cấp vitamin C, A, kali v&agrave; chất xơ dồi d&agrave;o, tốt cho sức khỏe của phụ nữ mang thai. Vitamin C gi&uacute;p tăng cường hệ miễn dịch, vitamin A hỗ trợ ph&aacute;t triển thị gi&aacute;c cho thai nhi, kali gi&uacute;p điều h&ograve;a huyết &aacute;p, v&agrave; chất xơ gi&uacute;p ngăn ngừa t&aacute;o b&oacute;n.</p>\n\n<p><strong>Lưu &yacute;:</strong></p>\n\n<ul>\n	<li>Gọt vỏ dưa lưới trước khi ăn</li>\n	<li>N&ecirc;n ăn dưa lưới với lượng vừa phải, khoảng 1-2 quả mỗi ng&agrave;y.</li>\n	<li>Tr&aacute;nh ăn dưa lưới khi đang bị ti&ecirc;u chảy hoặc đầy bụng.</li>\n	<li>Dưa lưới c&oacute; t&iacute;nh h&agrave;n, do vậy những người c&oacute; cơ địa n&oacute;ng, hay bị n&oacute;ng trong người n&ecirc;n hạn chế ăn.</li>\n	<li>Kh&ocirc;ng n&ecirc;n ăn dưa lưới c&ugrave;ng với c&aacute;c thực phẩm c&oacute; t&iacute;nh h&agrave;n như cua, ốc, hải sản,...</li>\n</ul>\n\n<p><strong>+ Trẻ em:</strong></p>\n\n<p><strong>C&oacute; thể ăn:</strong>&nbsp;Dưa lưới l&agrave; nguồn cung cấp vitamin C, A, kali v&agrave; chất xơ dồi d&agrave;o, tốt cho sự ph&aacute;t triển của trẻ em. Vitamin C gi&uacute;p tăng cường hệ miễn dịch, vitamin A hỗ trợ ph&aacute;t triển thị gi&aacute;c, kali gi&uacute;p điều h&ograve;a huyết &aacute;p, v&agrave; chất xơ gi&uacute;p hỗ trợ hệ ti&ecirc;u h&oacute;a.</p>\n\n<p><strong>Lưu &yacute;:</strong></p>\n\n<ul>\n	<li>Trẻ em dưới 1 tuổi n&ecirc;n tham khảo &yacute; kiến b&aacute;c sĩ trước khi ăn dưa lưới.</li>\n	<li>Cắt dưa lưới th&agrave;nh từng miếng nhỏ để trẻ dễ ăn v&agrave; tr&aacute;nh nguy cơ h&oacute;c.</li>\n	<li>Cho trẻ ăn dưa lưới với lượng vừa phải, khoảng 1/2 quả mỗi ng&agrave;y.</li>\n</ul>\n', 'Trái cây Việt', 95000, 1, '2024-07-05', 7, 'd987626e9df10e28ee1ff31f52573e8b', 'e5pefv4cvkvbwnz06paj', 3, 2, 1),
(16, 'Dừa xiêm xanh Coco MeKo', '<h1><strong>Dừa xi&ecirc;m xanh Coco Meko</strong></h1>\n\n<p><strong>- Ti&ecirc;u chuẩn chất lượng:&nbsp;</strong>Tr&aacute;i c&acirc;y sạch</p>\n\n<p><strong>- Xuất xứ:&nbsp;</strong>Việt Nam</p>\n\n<p><strong>- Quy c&aacute;ch:&nbsp;</strong>Tr&aacute;i</p>\n\n<p><strong>- Đặc điểm nổi bật của dừa xi&ecirc;m xanh Coco Meko</strong></p>\n\n<ul>\n	<li><strong>Dừa xi&ecirc;m</strong>&nbsp;l&agrave; một trong loại dừa tươi uống nước được thị trường trong cả nước rất ưa chuộng.&nbsp;</li>\n	<li>Dừa xi&ecirc;m được tuyển chọn từ những tr&aacute;i dừa tươi nguy&ecirc;n chất chất lượng, ngọt v&agrave; nhiều nước.</li>\n</ul>\n\n<p><strong>- Hướng dẫn sử dụng dừa xi&ecirc;m</strong></p>\n\n<ul>\n	<li>Nước dừa xi&ecirc;m uống trực tiếp.</li>\n	<li>L&agrave;m nguy&ecirc;n liệu nấu ăn cho c&aacute;c m&oacute;n hấp, kho, canh.</li>\n	<li>Cơm dừa d&ugrave;ng để ăn, l&agrave;m mứt, xay sinh tố, l&agrave;m nước cốt dừa.</li>\n</ul>\n\n<p><strong>- Lợi &iacute;ch tuyệt vời của dừa xi&ecirc;m&nbsp;Coco Meko</strong></p>\n\n<ul>\n	<li>Gi&agrave;u chất dinh dưỡng như kali, đồng, sắt v&agrave; vitamin B, hỗ trợ sức khỏe to&agrave;n diện của cơ thể.</li>\n	<li>L&agrave;m đẹp da&nbsp;</li>\n	<li>Tốt cho tim mạch</li>\n	<li>C&oacute; lợi cho ti&ecirc;u ho&aacute;</li>\n	<li>Giảm vấn đề về tiết niệu</li>\n</ul>\n\n<p><strong>- C&aacute;ch chọn tr&aacute;i dừa xi&ecirc;m Coco Meko ngọt</strong></p>\n\n<ul>\n	<li>Mua dừa xi&ecirc;m đừng n&ecirc;n chọn những tr&aacute;i c&oacute; k&iacute;ch thước lớn v&igrave; dễ mua nhầm những loại dừa xi&ecirc;m lai, uống vừa kh&ocirc;ng ngon, c&ugrave;i d&agrave;y lại c&oacute; ga.</li>\n	<li>N&ecirc;n lựa tr&aacute;i c&oacute; m&agrave;u trắng ng&agrave; v&agrave; kh&ocirc;ng bị th&acirc;m</li>\n	<li>Với Farmers Market ti&ecirc;u ch&iacute; h&agrave;ng đầu l&agrave; sản phảm đạt chất lượng cao, nguồn gốc r&otilde; r&agrave;ng, mang đến trải nghiệm tốt nhất cho kh&aacute;ch h&agrave;ng.&nbsp;</li>\n</ul>\n\n<p><strong>- C&aacute;ch bảo quản dừa xi&ecirc;m Coco Meko tốt nhất</strong></p>\n\n<ul>\n	<li>Để cảm nhận trọn vẹn hương vị cũng như nước dừa ngon kh&ocirc;ng bị qu&aacute; gi&agrave; c&oacute; ga th&igrave; bạn n&ecirc;n sử dụng dừa trong 5 &ndash; 7 ng&agrave;y sau khi mua</li>\n	<li>Nếu bạn muốn bảo quản dừa xi&ecirc;m được l&acirc;u hơn th&igrave; n&ecirc;n cho ch&uacute;ng v&agrave;o ngăn m&aacute;t hoặc ngăn đ&aacute; của tủ lạnh. C&aacute;ch l&agrave;m n&agrave;y gi&uacute;p dừa để l&acirc;u m&agrave; vẫn kh&ocirc;ng bị hỏng hay mất chất.</li>\n</ul>\n\n<p><strong>- Lưu &yacute; khi sử dụng dừa xi&ecirc;m Coco Meko ngon nhất</strong></p>\n\n<ul>\n	<li>Đừng để dừa qu&aacute; l&acirc;u trong tủ lạnh, phần cơm sẽ kh&ocirc;ng c&ograve;n dẻo v&agrave; nước mất dần độ ngọt. Bạn vẫn n&ecirc;n sử dụng trong khoảng 1 tuần đầu nh&eacute;!</li>\n</ul>\n\n<p><strong>-C&oacute; n&ecirc;n cho trẻ em sử dụng nước dừa ?</strong></p>\n\n<ul>\n	<li>Nước dừa được xem l&agrave; một loại thực phẩm được khuyến kh&iacute;ch sử dụng cho trẻ sơ sinh v&agrave; c&aacute;c b&agrave; mẹ đang cho con b&uacute;.&nbsp;</li>\n	<li>Nước dừa l&agrave; một loại nước uống bổ sung tuyệt vời cung cấp nguồn năng lượng dồi d&agrave;o cho trẻ v&agrave; thậm ch&iacute; cung cấp cho trẻ lượng axit lauric quan trọng. Đ&acirc;y l&agrave; thức ăn tốt nhất tiếp theo sau sữa mẹ.</li>\n	<li>&nbsp;Nước dừa c&oacute; nhiều chất dinh dưỡng hơn c&aacute;c loại nước suối m&agrave; bạn thường d&ugrave;ng. Đ&acirc;y l&agrave; một trong nhiều l&yacute; do m&agrave; n&oacute; rất tốt cho trẻ sơ sinh, đặc biệt l&agrave; khi ch&uacute;ng bị ốm. Nước dừa cung cấp nhiều chất dinh dưỡng cần thiết cho trẻ, bao gồm: chất xơ, canxi, kali, natri, vitamin C, chất đạm.</li>\n</ul>\n', 'Trái cây Việt', 22000, 1, '2024-07-05', 7, '90a3ae798e15bcbdc4c810cd1aa0e021', 'en9kww5ekdjcahzr6dks', 3, 1, 1),
(17, 'Sầu riêng đông lạnh Musang King Malaysia', '<h1><strong>[Đ&ocirc;ng lạnh] Sầu ri&ecirc;ng Musang King Malaysia (Hộp 1KG)</strong></h1>\n\n<p><strong>- Thương Hiệu:&nbsp;</strong>Musang King</p>\n\n<p><strong>- Trọng lượng sản phẩm:</strong>&nbsp;1KG</p>\n\n<p><strong>- M&ocirc; tả sản phẩm:</strong></p>\n\n<ul>\n	<li>&nbsp;Đ&oacute;ng g&oacute;i<strong>: 1KG</strong>&nbsp;/Hộp</li>\n	<li>&nbsp;Sầu ri&ecirc;ng Musang King loại sầu ri&ecirc;ng thuộc h&agrave;ng ngon nhất, m&uacute;i to c&ograve;n hạt th&igrave; cực l&eacute;p. Kh&ocirc;ng qu&aacute; ngọt nhưng độ b&eacute;o vượt trội.</li>\n	<li>&nbsp;M&uacute;i m&agrave;u v&agrave;ng nghệ v&agrave; mịn m&agrave;ng</li>\n</ul>\n\n<p><strong>- Lưu trữ, bảo quản:</strong>&nbsp;Bảo quản ở ngăn m&aacute;t tủ lạnh</p>\n\n<p><strong>- Th&ocirc;ng tin dinh dưỡng:</strong></p>\n\n<ul>\n	<li>Sầu ri&ecirc;ng c&oacute; chứa một lượng vitamin B kh&aacute; cao, c&aacute;c vitamin B sẽ c&oacute; một loạt c&aacute;c lợi &iacute;ch cho sức khỏe như ngăn ngừa l&atilde;o h&oacute;a v&agrave; bệnh tim, gi&uacute;p tăng HDL (cholesterol tốt) v&agrave; thậm ch&iacute; c&oacute; thể gi&uacute;p cải thiện t&acirc;m trạng, giảm bớt trầm cảm.</li>\n	<li>Trong sầu ri&ecirc;ng chứa một lượng&nbsp;kali phong ph&uacute;,&nbsp;l&agrave; chất điện ph&acirc;n quan trọng gi&uacute;p kiểm so&aacute;t huyết &aacute;p, giữ cho nồng độ natri ở mức độ cho ph&eacute;p v&agrave; gi&uacute;p điều h&ograve;a nhịp tim.</li>\n	<li>Một trong những lợi &iacute;ch kh&aacute;c của sầu ri&ecirc;ng l&agrave; c&oacute; chứa h&agrave;m lượng chất xơ cao. Mỗi khẩu phần ăn n&ecirc;n cần chứa 9g chất xơ tương đương 37% nhu cầu h&agrave;ng ng&agrave;y của bạn, điều n&agrave;y thật kh&oacute; thực hiện khi hầu như loại vi chất n&agrave;y c&oacute; nhiều trong tr&aacute;i c&acirc;y hơn l&agrave; c&aacute;c bữa ăn. Ch&iacute;nh v&igrave; thế, nếu bạn cần hấp thụ một lượng chất xơ ổn định, th&igrave; sầu ri&ecirc;ng l&agrave; loại tr&aacute;i c&acirc;y tốt để bạn sử dụng.</li>\n	<li>S&acirc;u ri&ecirc;ng c&ograve;n l&agrave; loại tr&aacute;i c&acirc;y chứa nhiều vitamin C, gi&uacute;p tăng cường sức đề kh&aacute;ng, giảm cholesterol, hỗ trợ chữa l&agrave;nh vết thương.</li>\n</ul>\n\n<p>&nbsp;</p>\n', 'Trái cây Việt', 599000, 1, '2024-07-05', 7, 'ffeaabbd8f646196f75f1bf319374cfe', 'lb92uu3im1nyqvucjeip', 3, 2, 1),
(18, 'Xoài cát chu loại 1', '<h1><strong>Xo&agrave;i c&aacute;t chu loại 1&nbsp;</strong></h1>\n\n<p><strong>- Xuất xứ</strong>: Việt Nam</p>\n\n<p><strong>- Ti&ecirc;u chuẩn chất lượng:&nbsp;</strong>Tr&aacute;i c&acirc;y sạch</p>\n\n<p><strong>- Đặc điểm sản phẩm:</strong>&nbsp;&Iacute;t xơ, vị ngọt ng&agrave;o nhưng kh&ocirc;ng gắt v&agrave; hương thơm nồng n&agrave;n quyến rũ.</p>\n\n<p><strong>- Th&ocirc;ng tin sản phẩm</strong></p>\n\n<ul>\n	<li>Ph&ograve;ng tr&aacute;nh tho&aacute;i h&oacute;a điểm v&agrave;ng của mắt</li>\n	<li>Gi&uacute;p chắc xương</li>\n	<li>Tốt cho người bị tiểu đường</li>\n	<li>Tăng cường hệ miễn dịch</li>\n	<li>Cải thiện&nbsp;hệ ti&ecirc;u h&oacute;a</li>\n	<li>Hỗ trợ sức khỏe&nbsp;tim mạch</li>\n	<li>Chống hen suyễn</li>\n	<li>T&oacute;c v&agrave; da khỏe đẹp</li>\n</ul>\n\n<p><strong>-Hướng dẫn bảo quản v&agrave; sử dụng</strong></p>\n\n<ul>\n	<li>Ăn trực tiếp v&agrave; ngon hơn khi để lạnh. &nbsp;</li>\n	<li>Th&iacute;ch hợp xay sinh tố hoặc ăn k&egrave;m c&ugrave;ng yogurt v&agrave; ngũ cốc.&nbsp;&nbsp;</li>\n</ul>\n\n<p><strong>- C&aacute;ch lựa chọn xo&agrave;i ngon:</strong></p>\n\n<ul>\n	<li>N&ecirc;n chọn mua Xo&agrave;i C&aacute;t Chu c&oacute; vỏ m&agrave;u v&agrave;ng nhạt, vỏ mỏng, mịn v&agrave; dễ b&oacute;c.</li>\n	<li>Tr&aacute;nh mua những quả xo&agrave;i bị dập n&aacute;t, vỏ bị th&acirc;m hoặc c&oacute; m&ugrave;i h&ocirc;i.</li>\n	<li>Với Farmers Market ti&ecirc;u ch&iacute; h&agrave;ng đầu l&agrave; sản phảm đạt chất lượng cao, nguồn gốc r&otilde; r&agrave;ng, mang đến trải nghiệm tốt nhất cho kh&aacute;ch h&agrave;ng.&nbsp;</li>\n</ul>\n\n<p><strong>- C&aacute;ch bảo quản xo&agrave;i được l&acirc;u:</strong></p>\n\n<p><strong>+ Bảo quản ở nơi tho&aacute;ng m&aacute;t:</strong></p>\n\n<ul>\n	<li>N&ecirc;n bảo quản xo&agrave;i ở nơi tho&aacute;ng m&aacute;t, tr&aacute;nh &aacute;nh nắng trực tiếp.</li>\n	<li>Bạn c&oacute; thể để xo&agrave;i trong rổ hoặc hộp c&oacute; lỗ th&ocirc;ng hơi để đảm bảo lưu th&ocirc;ng kh&iacute; tốt.</li>\n	<li>Hạn chế chồng chất xo&agrave;i l&ecirc;n nhau v&igrave; c&oacute; thể l&agrave;m dập n&aacute;t quả.</li>\n	<li>N&ecirc;n xếp xo&agrave;i th&agrave;nh từng lớp ri&ecirc;ng biệt.</li>\n</ul>\n\n<p><strong>+ Bảo quản trong tủ lạnh:</strong></p>\n\n<ul>\n	<li>Xo&agrave;i c&oacute; thể được bảo quản trong tủ lạnh để giữ được độ tươi ngon l&acirc;u hơn.</li>\n	<li>Chuẩn bị: Rửa sạch xo&agrave;i, để r&aacute;o nước. Sau đ&oacute;, cho xo&agrave;i v&agrave;o hộp k&iacute;n hoặc t&uacute;i nilon c&oacute; đục lỗ để đảm bảo lưu th&ocirc;ng kh&iacute;.</li>\n	<li>Nhiệt độ: N&ecirc;n bảo quản xo&agrave;i ở ngăn m&aacute;t tủ lạnh với nhiệt độ từ 8-10 độ C.</li>\n	<li>Thời gian bảo quản: Xo&agrave;i bảo quản trong tủ lạnh c&oacute; thể giữ được độ tươi ngon trong khoảng 2-3 tuần.</li>\n</ul>\n', 'Trái cây Việt', 99000, 1, '2024-07-05', 7, '981320debbb366bc739eaef2f2b862ed', 'l6zwl2chujmk708mr9bt', 3, 3, 1),
(19, 'Dứa MD2 Hậu Giang', '<h1><strong>Dứa MD2 Hậu Giang</strong></h1>\n\n<p><strong>- Xuất xứ:&nbsp;</strong>Hậu Giang, Việt Nam</p>\n\n<p><strong>- Ti&ecirc;u chuẩn chất lượng:</strong>&nbsp;Tr&aacute;i c&acirc;y sạch</p>\n\n<p><strong>- Khối lượng</strong>:1,7kg/Tr&aacute;i</p>\n\n<p><strong>Đặc điểm:</strong></p>\n\n<ul>\n	<li>Lựa chọn từ những quả dứa tươi ngon, an to&agrave;n thực phẩm.</li>\n	<li>C&oacute; vị ngọt&nbsp;v&agrave; nhiều nước.</li>\n	<li>Được sử dụng để ăn trực tiếp, chế biến c&aacute;c m&oacute;n ăn, l&agrave;m nước &eacute;p,...</li>\n</ul>\n\n<p><strong>- C&ocirc;ng dụng:</strong></p>\n\n<ul>\n	<li>Ăn trực tiếp</li>\n	<li>L&agrave;m salad</li>\n	<li>L&agrave;m sinh tố</li>\n	<li>L&agrave;m mứt</li>\n	<li>D&ugrave;ng trong c&aacute;c m&oacute;n tr&aacute;ng miệng</li>\n</ul>\n\n<p><strong>- C&aacute;ch bảo quản thơm:</strong></p>\n\n<p><strong>+ Bảo quản ở nhiệt độ ph&ograve;ng:</strong></p>\n\n<ul>\n	<li>Khi mua về, nếu bạn chưa muốn ăn ngay, c&oacute; thể để thơm&nbsp;ở nhiệt độ ph&ograve;ng trong khoảng 2-3 ng&agrave;y.</li>\n	<li>N&ecirc;n để thơm&nbsp;ở nơi kh&ocirc; r&aacute;o, tho&aacute;ng m&aacute;t, tr&aacute;nh &aacute;nh nắng trực tiếp v&agrave; nơi ẩm ướt.</li>\n	<li>Tr&aacute;nh xếp chồng thơm&nbsp;l&ecirc;n nhau v&igrave; c&oacute; thể l&agrave;m dập n&aacute;t quả.</li>\n</ul>\n\n<p><strong>+ Bảo quản trong tủ lạnh:</strong></p>\n\n<ul>\n	<li>Nếu bạn muốn bảo quản&nbsp;l&acirc;u hơn, c&oacute; thể cho v&agrave;o tủ lạnh.</li>\n	<li>N&ecirc;n rửa sạch v&agrave; để r&aacute;o nước trước khi cho v&agrave;o tủ lạnh.</li>\n	<li>C&oacute; thể bọc Thơm bằng giấy hoặc cho v&agrave;o hộp k&iacute;n để giữ độ tươi ngon.</li>\n	<li>Thơm c&oacute; thể bảo quản trong tủ lạnh từ 5-7 ng&agrave;y.</li>\n</ul>\n\n<p><strong>+ Bảo quản thơm đ&atilde; cắt:</strong></p>\n\n<ul>\n	<li>N&ecirc;n bọc thơm&nbsp;đ&atilde; cắt bằng m&agrave;ng bọc thực phẩm hoặc cho v&agrave;o hộp k&iacute;n để bảo quản.</li>\n	<li>Thơm mini đ&atilde; cắt chỉ n&ecirc;n bảo quản trong tủ lạnh tối đa 2 ng&agrave;y.</li>\n</ul>\n\n<p>-&nbsp;<strong>C&aacute;ch lựa chọn thơm ngon:</strong></p>\n\n<ul>\n	<li>N&ecirc;n chọn những quả&nbsp;c&oacute; h&igrave;nh dạng đều đặn, c&acirc;n đối, kh&ocirc;ng bị m&eacute;o m&oacute; hay dập n&aacute;t.</li>\n	<li>Với Farmers Market ti&ecirc;u ch&iacute; h&agrave;ng đầu l&agrave; sản phảm đạt chất lượng cao, nguồn gốc r&otilde; r&agrave;ng, mang đến trải nghiệm tốt nhất cho kh&aacute;ch h&agrave;ng.&nbsp;</li>\n</ul>\n\n<p>-&nbsp;<strong>Phụ nữ mang thai v&agrave; trẻ em c&oacute; ăn thơm được kh&ocirc;ng?</strong></p>\n\n<p><strong>+&nbsp;</strong>Phụ nữ mang thai c&oacute; thể ăn thơm m&agrave; kh&ocirc;ng gặp vấn đề g&igrave; đ&aacute;ng lo ngại.</p>\n\n<ul>\n	<li><strong>Cung cấp vitamin v&agrave; kho&aacute;ng chất</strong>: Dứa chứa nhiều vitamin C, folate, sắt, magie, mangan, đồng v&agrave; vitamin B6. Những chất n&agrave;y đ&oacute;ng vai tr&ograve; quan trọng cho việc ph&aacute;t triển thai nhi</li>\n	<li>Tăng cường hệ miễn dịch v&agrave; hệ ti&ecirc;u h&oacute;a</li>\n	<li><strong>Phục hồi sau phẩu thuật</strong>: ăn thơm gi&uacute;p giảm th&acirc;m bầm t&iacute;m, sưng đau v&agrave; vi&ecirc;m sau phẩu thuật</li>\n	<li><strong>Điều h&ograve;a huyết &aacute;p:</strong>&nbsp;Thơm c&oacute; thể hỗ trợ điều h&ograve;a huyết &aacute;p, một kh&iacute;a cạnh quan trọng trong thai k&igrave;</li>\n	<li>Ăn với h&agrave;m lượng vừa phải khoảng&nbsp;<strong>1/2 dứa mỗi tuần&nbsp;</strong></li>\n</ul>\n\n<p>+ Trẻ em c&oacute; thể ăn dứa khi trẻ được 1&nbsp;tuổi &ndash; l&uacute;c n&agrave;y hệ ti&ecirc;u h&oacute;a của b&eacute; đ&atilde; dần th&iacute;ch nghi tốt với chế độ ăn dặm.&nbsp;</p>\n\n<ul>\n	<li>Hỗ trợ hoạt động&nbsp;n&atilde;o bộ</li>\n	<li>Củng cố sức đề kh&aacute;ng</li>\n	<li>Cải thiện hệ vận động</li>\n	<li>Ăn với h&agrave;m lượng vừa phải, tr&aacute;nh k&iacute;ch th&iacute;ch dạ d&agrave;y của trẻ</li>\n</ul>\n', 'Trái cây Việt', 54000, 1, '2024-07-05', 7, '1ff2d947f12376b0c2161dc0019e2d7f', 'rc8ziuww6s08qmybayxw', 3, 1, 1),
(20, 'Chôm Chôm giống Thái Lan', '<h1><strong>Ch&ocirc;m ch&ocirc;m&nbsp;giốngTh&aacute;i Lan</strong></h1>\n\n<p><strong>- Xuất xứ:&nbsp;</strong>Việt Nam</p>\n\n<p><strong>- Ti&ecirc;u chuẩn chất lượng:</strong>&nbsp;Tr&aacute;i c&acirc;y sạch</p>\n\n<p><strong>- Khối lượng</strong>: 1kg</p>\n\n<p><strong>- Đặc Điểm:</strong></p>\n\n<ul>\n	<li>Cơm d&agrave;y, dễ tr&oacute;c hạt, thịt gi&ograve;n nhẹ, kh&ocirc;ng qu&aacute; kh&ocirc;.</li>\n	<li>Khi ch&iacute;n r&acirc;u tr&aacute;i c&oacute; m&agrave;u xanh, vỏ c&oacute; m&agrave;u đỏ, xen chấm v&agrave;ng.</li>\n	<li><strong>Vỏ:</strong>&nbsp;Vỏ ch&ocirc;m ch&ocirc;m Th&aacute;i Lan d&agrave;y, m&agrave;u đỏ tươi hoặc hồng, c&oacute; nhiều gai nhọn nhỏ.</li>\n	<li><strong>Thịt:</strong>&nbsp;Thịt ch&ocirc;m ch&ocirc;m Th&aacute;i Lan trắng ng&agrave;, xốp mềm, ngọt thanh v&agrave; mọng nước.</li>\n	<li><strong>Hương vị:</strong>&nbsp;Ch&ocirc;m ch&ocirc;m Th&aacute;i Lan c&oacute; vị ngọt thanh, thơm dịu v&agrave; c&oacute; ch&uacute;t vị chua nhẹ.</li>\n</ul>\n\n<p><strong>- Gi&aacute; trị dinh dưỡng:</strong></p>\n\n<p>Ch&ocirc;m ch&ocirc;m Th&aacute;i Lan l&agrave; nguồn cung cấp dồi d&agrave;o vitamin C, vitamin B, kali, magie, photpho v&agrave; c&aacute;c kho&aacute;ng chất thiết yếu kh&aacute;c cho cơ thể.</p>\n\n<ul>\n	<li><strong>Vitamin C:</strong>&nbsp;Gi&uacute;p tăng cường hệ miễn dịch, chống oxy h&oacute;a v&agrave; bảo vệ cơ thể khỏi c&aacute;c bệnh tật.</li>\n	<li><strong>Vitamin B:</strong>&nbsp;Gi&uacute;p hỗ trợ hệ thần kinh v&agrave; chức năng n&atilde;o bộ.</li>\n	<li><strong>Kali:</strong>&nbsp;Gi&uacute;p điều h&ograve;a huyết &aacute;p v&agrave; duy tr&igrave; chức năng tim mạch.</li>\n	<li><strong>Magie:</strong>&nbsp;Gi&uacute;p hỗ trợ hệ cơ bắp v&agrave; xương khớp.</li>\n	<li><strong>Photpho:</strong>&nbsp;Gi&uacute;p hỗ trợ chức năng thận v&agrave; răng miệng.</li>\n</ul>\n\n<p><strong>- Lợi &iacute;ch sức khỏe:</strong></p>\n\n<ul>\n	<li><strong>Tăng cường hệ miễn dịch:</strong>&nbsp;Vitamin C trong ch&ocirc;m ch&ocirc;m Th&aacute;i Lan gi&uacute;p tăng cường hệ miễn dịch, chống lại c&aacute;c bệnh cảm c&uacute;m, ho.</li>\n	<li><strong>Hỗ trợ hệ thần kinh:</strong>&nbsp;Vitamin B trong ch&ocirc;m ch&ocirc;m Th&aacute;i Lan gi&uacute;p hỗ trợ hệ thần kinh v&agrave; chức năng n&atilde;o bộ.</li>\n	<li><strong>Điều h&ograve;a huyết &aacute;p:</strong>&nbsp;Kali trong ch&ocirc;m ch&ocirc;m Th&aacute;i Lan gi&uacute;p điều h&ograve;a huyết &aacute;p v&agrave; duy tr&igrave; chức năng tim mạch.</li>\n	<li><strong>Hỗ trợ hệ cơ bắp v&agrave; xương khớp:</strong>&nbsp;Magie trong ch&ocirc;m ch&ocirc;m Th&aacute;i Lan gi&uacute;p hỗ trợ hệ cơ bắp v&agrave; xương khớp.</li>\n	<li><strong>Hỗ trợ chức năng thận v&agrave; răng miệng:</strong>&nbsp;Photpho trong ch&ocirc;m ch&ocirc;m Th&aacute;i Lan gi&uacute;p hỗ trợ chức năng thận v&agrave; răng miệng.</li>\n	<li><strong>L&agrave;m đẹp da:</strong>&nbsp;Vitamin C v&agrave; chất chống oxy h&oacute;a trong ch&ocirc;m ch&ocirc;m Th&aacute;i Lan gi&uacute;p bảo vệ da khỏi t&aacute;c hại của &aacute;nh nắng mặt trời, l&agrave;m giảm nếp nhăn v&agrave; tăng độ đ&agrave;n hồi cho da.</li>\n</ul>\n\n<p><strong>- C&aacute;ch sử dụng:</strong></p>\n\n<p>Ch&ocirc;m ch&ocirc;m&nbsp;c&oacute; thể ăn trực tiếp, &eacute;p nước, l&agrave;m sinh tố, salad hoặc chế biến th&agrave;nh c&aacute;c m&oacute;n ăn kh&aacute;c nhau như b&aacute;nh, mứt, ...</p>\n\n<p><strong>- C&aacute;ch lựa chọn ch&ocirc;m ch&ocirc;m ngon;</strong></p>\n\n<ul>\n	<li>N&ecirc;n chọn mua ch&ocirc;m ch&ocirc;m Th&aacute;i Lan c&oacute; vỏ m&agrave;u đỏ tươi hoặc hồng, gai nhọn nhỏ v&agrave; đều đặn.</li>\n	<li>Với Farmers Market ti&ecirc;u ch&iacute; h&agrave;ng đầu l&agrave; sản phảm đạt chất lượng cao, nguồn gốc r&otilde; r&agrave;ng, mang đến trải nghiệm tốt nhất cho kh&aacute;ch h&agrave;ng.&nbsp;</li>\n</ul>\n\n<p><strong>- C&aacute;ch bảo quản ch&ocirc;m ch&ocirc;m&nbsp;được l&acirc;u:</strong></p>\n\n<p><strong>+ Bảo quản ở nhiệt độ ph&ograve;ng:</strong></p>\n\n<ul>\n	<li><strong>Chọn ch&ocirc;m ch&ocirc;m ch&iacute;n tới:</strong>&nbsp;N&ecirc;n chọn những quả ch&ocirc;m ch&ocirc;m c&oacute; m&agrave;u đỏ tươi, gai nhọn đều đặn, vỏ hơi mềm khi ấn nhẹ. Tr&aacute;nh chọn những quả ch&ocirc;m ch&ocirc;m chưa ch&iacute;n hoặc qu&aacute; ch&iacute;n v&igrave; dễ bị dập n&aacute;t v&agrave; nhanh hỏng.</li>\n	<li><strong>Rửa sạch v&agrave; để r&aacute;o:</strong>&nbsp;Sau khi mua về, bạn n&ecirc;n rửa sạch ch&ocirc;m ch&ocirc;m dưới v&ograve;i nước chảy để loại bỏ bụi bẩn v&agrave; vi khuẩn. Sau đ&oacute;, để r&aacute;o nước ho&agrave;n to&agrave;n.</li>\n	<li><strong>Bảo quản ở nơi tho&aacute;ng m&aacute;t:</strong>&nbsp;N&ecirc;n bảo quản ch&ocirc;m ch&ocirc;m ở nơi tho&aacute;ng m&aacute;t, tr&aacute;nh &aacute;nh nắng trực tiếp. Bạn c&oacute; thể để ch&ocirc;m ch&ocirc;m trong rổ hoặc hộp c&oacute; lỗ th&ocirc;ng hơi để đảm bảo lưu th&ocirc;ng kh&iacute; tốt.</li>\n	<li><strong>Hạn chế chồng chất:</strong>&nbsp;Tr&aacute;nh chồng chất ch&ocirc;m ch&ocirc;m l&ecirc;n nhau v&igrave; c&oacute; thể l&agrave;m dập n&aacute;t quả. N&ecirc;n xếp ch&ocirc;m ch&ocirc;m th&agrave;nh từng lớp ri&ecirc;ng biệt.</li>\n	<li><strong>Thời gian bảo quản:</strong>&nbsp;Với c&aacute;ch bảo quản n&agrave;y, ch&ocirc;m ch&ocirc;m c&oacute; thể giữ được độ tươi ngon trong khoảng 2-3 ng&agrave;y.</li>\n</ul>\n\n<p><strong>+ Bảo quản trong tủ lạnh:</strong></p>\n\n<ul>\n	<li><strong>C&aacute;ch bảo quản:</strong>&nbsp;Ch&ocirc;m ch&ocirc;m c&oacute; thể được bảo quản trong tủ lạnh để giữ được độ tươi ngon l&acirc;u hơn.</li>\n	<li><strong>Chuẩn bị:</strong>&nbsp;Rửa sạch ch&ocirc;m ch&ocirc;m, để r&aacute;o nước. Sau đ&oacute;, cho ch&ocirc;m ch&ocirc;m v&agrave;o hộp k&iacute;n hoặc t&uacute;i nilon c&oacute; đục lỗ để đảm bảo lưu th&ocirc;ng kh&iacute;.</li>\n	<li><strong>Nhiệt độ:</strong>&nbsp;N&ecirc;n bảo quản ch&ocirc;m ch&ocirc;m ở ngăn m&aacute;t tủ lạnh với nhiệt độ từ 8-10 độ C.</li>\n	<li><strong>Thời gian bảo quản:</strong>&nbsp;Ch&ocirc;m ch&ocirc;m bảo quản trong tủ lạnh c&oacute; thể giữ được độ tươi ngon trong khoảng 5-7 ng&agrave;y.</li>\n</ul>\n', 'Trái cây Việt', 79000, 1, '2024-07-05', 7, '9762798353af3fb579d5eb7c46ad7d2d', 'npec9xyvohw4k4fdwhot', 3, 2, 1),
(21, 'Trái xay nhung', '<h1><strong>Tr&aacute;i xay nhung</strong></h1>\n\n<p><strong>- Xuất xứ:</strong>&nbsp;Việt Nam<br />\n<strong>- Trọng Lượng:&nbsp;</strong>1KG/Hộp</p>\n\n<p><strong>- Ti&ecirc;u chuẩn chất lượng:</strong>&nbsp;Tr&aacute;i c&acirc;y sạch</p>\n\n<p><strong>- Đặc điểm:&nbsp;</strong></p>\n\n<ul>\n	<li>Sản phẩm&nbsp;được tuyển chọn kỹ lưỡng, chất lượng</li>\n	<li>Tr&aacute;i xay c&oacute; thịt m&agrave;u v&agrave;ng đậm, xốp v&agrave; mềm tr&aacute;i to, cơm d&agrave;y, ngọt b&ugrave;i xen lẫn vị chua nhẹ</li>\n</ul>\n\n<p><strong>- Th&ocirc;ng tinh dinh dưỡng:</strong></p>\n\n<ul>\n	<li>Cơm của quả xay c&oacute;&nbsp;t&aacute;c dụng nhuận tr&agrave;ng v&agrave; tốt cho hệ ti&ecirc;u h&oacute;a.</li>\n	<li>Tr&aacute;i xay nhung c&ograve;n c&oacute; thể chế biến th&agrave;nh&nbsp;<a href=\"https://www.bachhoaxanh.com/ruou-ngon-cac-loai\">rượu</a>&nbsp;uống c&oacute;&nbsp;t&aacute;c dụng&nbsp;l&agrave;m thuốc bổ, k&iacute;ch th&iacute;ch ti&ecirc;u h&oacute;a, k&iacute;ch th&iacute;ch ăn ngon</li>\n</ul>\n\n<p><strong>- Hướng dẫn sử dụng:</strong></p>\n\n<ul>\n	<li>Tr&aacute;i xay&nbsp;c&oacute; thể ăn trực tiếp hoặc l&agrave;m mứt, rim&nbsp;muối, đường.</li>\n</ul>\n\n<p><strong>- Th&agrave;nh phần dinh dưỡng:</strong></p>\n\n<p>Tr&aacute;i xay nhung chứa nhiều vitamin C, vitamin E, kali, magie, canxi v&agrave; c&aacute;c kho&aacute;ng chất thiết yếu kh&aacute;c cho cơ thể.</p>\n\n<p><strong>- C&ocirc;ng dụng:</strong></p>\n\n<ul>\n	<li><strong>Trị cảm c&uacute;m, ho:</strong>&nbsp;Vitamin C trong tr&aacute;i xay nhung gi&uacute;p tăng cường hệ miễn dịch, chống lại c&aacute;c bệnh cảm c&uacute;m, ho.</li>\n	<li><strong>Hạ sốt:</strong>&nbsp;Tr&aacute;i xay nhung c&oacute; t&aacute;c dụng hạ sốt hiệu quả.</li>\n	<li><strong>Giảm đau nhức:</strong>&nbsp;Tr&aacute;i xay nhung c&oacute; t&aacute;c dụng giảm đau nhức cơ bắp, khớp xương.</li>\n	<li><strong>Lợi tiểu:</strong>&nbsp;Tr&aacute;i xay nhung gi&uacute;p lợi tiểu, thanh lọc cơ thể.</li>\n	<li><strong>Hỗ trợ ti&ecirc;u h&oacute;a:</strong>&nbsp;Tr&aacute;i xay nhung gi&uacute;p hỗ trợ ti&ecirc;u h&oacute;a, ngăn ngừa t&aacute;o b&oacute;n.</li>\n	<li><strong>L&agrave;m đẹp da:</strong>&nbsp;Vitamin C v&agrave; chất chống oxy h&oacute;a trong tr&aacute;i xay nhung gi&uacute;p bảo vệ da khỏi t&aacute;c hại của &aacute;nh nắng mặt trời, l&agrave;m giảm nếp nhăn v&agrave; tăng độ đ&agrave;n hồi cho da.</li>\n</ul>\n\n<p><strong>- C&aacute;ch sử dụng:</strong></p>\n\n<p>Tr&aacute;i xay nhung c&oacute; thể sử dụng để nấu nước uống, sắc thuốc hoặc ng&acirc;m rượu.</p>\n\n<ul>\n	<li><strong>Ăn trực tiếp,&nbsp;</strong>b&oacute;c vỏ</li>\n	<li><strong>Nấu nước uống:</strong>&nbsp;Lấy 10-20 quả xay nhung rửa sạch, cho v&agrave;o nồi nước nấu s&ocirc;i khoảng 15 ph&uacute;t, sau đ&oacute; tắt bếp v&agrave; để nguội. C&oacute; thể th&ecirc;m mật ong hoặc đường ph&egrave;n để tăng th&ecirc;m hương vị. Uống nước xay nhung mỗi ng&agrave;y 1-2 lần.</li>\n	<li><strong>Sắc thuốc:</strong>&nbsp;Lấy 30-50 quả xay nhung rửa sạch, phơi kh&ocirc;, sau đ&oacute; sao v&agrave;ng hạ thổ. Cho xay nhung v&agrave;o ấm nước s&ocirc;i, h&atilde;m trong 10-15 ph&uacute;t rồi uống. Uống thuốc xay nhung mỗi ng&agrave;y 2-3 lần.</li>\n	<li><strong>Ng&acirc;m rượu:</strong>&nbsp;Lấy 1 kg xay nhung rửa sạch, phơi kh&ocirc;, sau đ&oacute; cho v&agrave;o b&igrave;nh thủy tinh ng&acirc;m với 5 l&iacute;t rượu trắng. Ng&acirc;m trong khoảng 1 th&aacute;ng l&agrave; c&oacute; thể sử dụng. Uống rượu xay nhung mỗi ng&agrave;y 1-2 ch&eacute;n nhỏ.</li>\n</ul>\n\n<p><strong>- C&acirc;n nhắc</strong></p>\n\n<ul>\n	<li>Phụ nữ mang thai v&agrave; cho con b&uacute; n&ecirc;n tham khảo &yacute; kiến b&aacute;c sĩ trước khi sử dụng tr&aacute;i xay nhung.</li>\n	<li>Người c&oacute; bệnh l&yacute; tim mạch, huyết &aacute;p cao n&ecirc;n thận trọng khi sử dụng tr&aacute;i xay nhung.</li>\n	<li>Kh&ocirc;ng n&ecirc;n sử dụng tr&aacute;i xay nhung qu&aacute; liều.</li>\n</ul>\n', 'Trái cây Việt', 55000, 1, '2024-07-05', 7, '575a831b50857dc7f388cffa577ae862', 'm38rahhq3eftbi52z7ur', 3, 1, 1);
INSERT INTO `products` (`id`, `nameOfProduct`, `description`, `category`, `price`, `weightDefault`, `dateOfImporting`, `expriredDay`, `imgAssetId`, `imgPublicId`, `adminCreate`, `provider`, `status`) VALUES
(22, 'Dưa lưới ruột cam vỏ cam Pansy', '<h1><strong>Dưa lưới ruột cam vỏ cam Pansy&nbsp;</strong></h1>\n\n<p><strong>- Xuất xứ:&nbsp;</strong>Việt Nam</p>\n\n<p>-&nbsp;<strong>Ti&ecirc;u chuẩn chất lượng:&nbsp;</strong>chuẩn an to&agrave;n quốc tế loại 1, tr&aacute;i c&acirc;y sạch&nbsp;</p>\n\n<p>-&nbsp;<strong>Trọng lượng:&nbsp;</strong>1,7kg/Tr&aacute;i</p>\n\n<p><strong>- Đặc điểm:&nbsp;</strong>Quả to h&igrave;nh oval, vỏ xanh nhạt c&oacute; v&acirc;n lưới nhẹ đẹp mắt. Ruột c&oacute; m&agrave;u cam, thịt dưa gi&ograve;n gi&ograve;n thanh m&aacute;t. L&uacute;c ch&iacute;n dưa c&oacute; m&ugrave;i rất thơm k&egrave;m vị ngọt đậm.</p>\n\n<p><strong>Hướng dẫn sử dụng:</strong></p>\n\n<ul>\n	<li>Dưa lưới d&ugrave;ng để ăn trực tiếp.</li>\n	<li>Bạn c&oacute; thể d&ugrave;ng dưa lưới để chế biến salad, sinh tố, nước &eacute;p.</li>\n</ul>\n\n<p><strong>- Gi&aacute; trị dinh dưỡng:</strong></p>\n\n<p>Dưa lưới&nbsp;l&agrave; nguồn cung cấp dồi d&agrave;o vitamin C, vitamin A, kali, magie v&agrave; c&aacute;c kho&aacute;ng chất thiết yếu kh&aacute;c cho cơ thể.</p>\n\n<ul>\n	<li><strong>Vitamin C:</strong>&nbsp;Gi&uacute;p tăng cường hệ miễn dịch, chống oxy h&oacute;a v&agrave; bảo vệ cơ thể khỏi c&aacute;c bệnh tật.</li>\n	<li><strong>Vitamin A:</strong>&nbsp;Gi&uacute;p hỗ trợ sức khỏe mắt, da v&agrave; hệ miễn dịch.</li>\n	<li><strong>Kali:</strong>&nbsp;Gi&uacute;p điều h&ograve;a huyết &aacute;p v&agrave; duy tr&igrave; chức năng tim mạch.</li>\n	<li><strong>Magie:</strong>&nbsp;Gi&uacute;p hỗ trợ hệ cơ bắp v&agrave; xương khớp.</li>\n</ul>\n\n<p><strong>- Lợi &iacute;ch sức khỏe:</strong></p>\n\n<ul>\n	<li><strong>Tăng cường hệ miễn dịch:</strong>&nbsp;Vitamin C v&agrave; vitamin A trong Dưa lưới biển Hằng Nga Danny Green Organic gi&uacute;p tăng cường hệ miễn dịch, chống lại c&aacute;c bệnh cảm c&uacute;m, ho.</li>\n	<li><strong>Hỗ trợ sức khỏe mắt:</strong>&nbsp;Vitamin A trong Dưa lưới biển Hằng Nga Danny Green Organic gi&uacute;p hỗ trợ sức khỏe mắt, giảm nguy cơ mắc c&aacute;c bệnh về mắt như đục thủy tinh thể, tho&aacute;i h&oacute;a điểm v&agrave;ng.</li>\n	<li><strong>Điều h&ograve;a huyết &aacute;p:</strong>&nbsp;Kali trong Dưa lưới biển Hằng Nga Danny Green Organic gi&uacute;p điều h&ograve;a huyết &aacute;p, giảm nguy cơ mắc c&aacute;c bệnh tim mạch.</li>\n	<li><strong>Hỗ trợ hệ cơ bắp v&agrave; xương khớp:</strong>&nbsp;Magie trong Dưa lưới biển Hằng Nga Danny Green Organic gi&uacute;p hỗ trợ hệ cơ bắp v&agrave; xương khớp, giảm nguy cơ lo&atilde;ng xương v&agrave; chuột r&uacute;t.</li>\n	<li><strong>L&agrave;m đẹp da:</strong>&nbsp;Vitamin C v&agrave; chất chống oxy h&oacute;a trong Dưa lưới biển Hằng Nga Danny Green Organic gi&uacute;p bảo vệ da khỏi t&aacute;c hại của &aacute;nh nắng mặt trời, l&agrave;m giảm nếp nhăn v&agrave; tăng độ đ&agrave;n hồi cho da.</li>\n</ul>\n\n<p><strong>- Bảo quản dưa lưới :</strong></p>\n\n<ul>\n	<li>N&ecirc;n bảo quản dưa lưới ở nơi kh&ocirc; r&aacute;o, tho&aacute;ng m&aacute;t, tr&aacute;nh &aacute;nh nắng trực tiếp.</li>\n	<li>C&oacute; thể bảo quản dưa lưới trong tủ lạnh ở nhiệt độ từ 0&deg;C đến 4&deg;C để giữ được độ tươi ngon l&acirc;u hơn.</li>\n</ul>\n\n<p>-&nbsp;<strong>C&aacute;ch lựa chọn dưa lưới&nbsp;ngon:</strong></p>\n\n<ul>\n	<li>N&ecirc;n chọn những quả dưa lưới c&oacute; h&igrave;nh dạng tr&ograve;n đều, hai đầu c&acirc;n đối, kh&ocirc;ng bị m&eacute;o m&oacute;.</li>\n	<li>Với Farmers Market ti&ecirc;u ch&iacute; h&agrave;ng đầu l&agrave; sản phảm đạt chất lượng cao, nguồn gốc r&otilde; r&agrave;ng, mang đến trải nghiệm tốt nhất cho kh&aacute;ch h&agrave;ng.&nbsp;</li>\n</ul>\n\n<p><strong>-&nbsp; Phụ nữ mang thai v&agrave; trẻ em ăn dưa lưới&nbsp;c&oacute; được kh&ocirc;ng?</strong></p>\n\n<p><strong>+ Phụ nữ mang thai:</strong></p>\n\n<p><strong>C&oacute; thể ăn:</strong>&nbsp;Dưa lưới l&agrave; nguồn cung cấp vitamin C, A, kali v&agrave; chất xơ dồi d&agrave;o, tốt cho sức khỏe của phụ nữ mang thai. Vitamin C gi&uacute;p tăng cường hệ miễn dịch, vitamin A hỗ trợ ph&aacute;t triển thị gi&aacute;c cho thai nhi, kali gi&uacute;p điều h&ograve;a huyết &aacute;p, v&agrave; chất xơ gi&uacute;p ngăn ngừa t&aacute;o b&oacute;n.</p>\n\n<p><strong>Lưu &yacute;:</strong></p>\n\n<ul>\n	<li>Gọt vỏ dưa lưới trước khi ăn</li>\n	<li>N&ecirc;n ăn dưa lưới với lượng vừa phải, khoảng 1-2 quả mỗi ng&agrave;y.</li>\n	<li>Tr&aacute;nh ăn dưa lưới khi đang bị ti&ecirc;u chảy hoặc đầy bụng.</li>\n	<li>Dưa lưới c&oacute; t&iacute;nh h&agrave;n, do vậy những người c&oacute; cơ địa n&oacute;ng, hay bị n&oacute;ng trong người n&ecirc;n hạn chế ăn.</li>\n	<li>Kh&ocirc;ng n&ecirc;n ăn dưa lưới c&ugrave;ng với c&aacute;c thực phẩm c&oacute; t&iacute;nh h&agrave;n như cua, ốc, hải sản,...</li>\n</ul>\n\n<p><strong>+ Trẻ em:</strong></p>\n\n<p><strong>C&oacute; thể ăn:</strong>&nbsp;Dưa lưới l&agrave; nguồn cung cấp vitamin C, A, kali v&agrave; chất xơ dồi d&agrave;o, tốt cho sự ph&aacute;t triển của trẻ em. Vitamin C gi&uacute;p tăng cường hệ miễn dịch, vitamin A hỗ trợ ph&aacute;t triển thị gi&aacute;c, kali gi&uacute;p điều h&ograve;a huyết &aacute;p, v&agrave; chất xơ gi&uacute;p hỗ trợ hệ ti&ecirc;u h&oacute;a.</p>\n\n<p><strong>Lưu &yacute;:</strong></p>\n\n<ul>\n	<li>Trẻ em dưới 1 tuổi n&ecirc;n tham khảo &yacute; kiến b&aacute;c sĩ trước khi ăn dưa lưới.</li>\n	<li>Cắt dưa lưới th&agrave;nh từng miếng nhỏ để trẻ dễ ăn v&agrave; tr&aacute;nh nguy cơ h&oacute;c.</li>\n	<li>Cho trẻ ăn dưa lưới với lượng vừa phải, khoảng 1/2 quả mỗi ng&agrave;y.</li>\n</ul>\n', 'Trái cây Việt', 151300, 1, '2024-07-05', 7, 'e173251ffed319aa0319bea3c81d4bc0', 'qrmctk4sak7uqf913jyj', 3, 2, 1),
(23, 'Chuối Unifarm', '<h1><strong>Chuối Unifarm 3 tr&aacute;i&nbsp;</strong></h1>\n\n<p><strong>Xuất xứ:</strong>&nbsp;Việt Nam</p>\n\n<p><strong>Quy c&aacute;ch:&nbsp;</strong>3 Tr&aacute;i/ Nh&aacute;nh</p>\n\n<p><strong>Ti&ecirc;u chuẩn chất lượng:&nbsp;</strong>Tr&aacute;i c&acirc;y sạch</p>\n\n<p><strong>Đặc điểm nổi bật của chuối Unifarm 3 tr&aacute;i&nbsp;</strong></p>\n\n<ul>\n	<li>Chuối Unifarm 3 tr&aacute;i l&agrave; sản phẩm được nhiều người ưa chuộng bởi hương vị thơm ngon, gi&aacute; th&agrave;nh hợp l&yacute; v&agrave; chất lượng đảm bảo&nbsp;</li>\n	<li>Chuối c&oacute; vị ngọt thanh, mọng nước v&agrave; gi&agrave;u chất dinh dưỡng</li>\n</ul>\n\n<p>Chuối Unifarm&nbsp;<strong>l&agrave; một giống chuối ngoại nhập, c&oacute; m&ugrave;i thơm v&agrave; c&oacute; vị ngọt đặc trưng</strong>. Đ&acirc;y l&agrave; loại quả cung cấp kali v&agrave; axit folic tuyệt vời cho sức khỏe. L&agrave; m&oacute;n tr&aacute;i c&acirc;y l&yacute; tưởng cho người gi&agrave; v&agrave; trẻ nhỏ.</p>\n\n<p>&nbsp;</p>\n\n<p><strong>Lợi &iacute;ch của chuối Unifarm 3 tr&aacute;i</strong></p>\n\n<ul>\n	<li>Chuối Unifarm 3 tr&aacute;i l&agrave; nguồn cung cấp dồi d&agrave;o vitamin, kho&aacute;ng chất v&agrave; chất xơ c&oacute; lợi cho sức khỏe.</li>\n</ul>\n\n<ul>\n	<li>Vitamin v&agrave; kho&aacute;ng chất trong chuối gi&uacute;p tăng cường hệ miễn dịch, hỗ trợ ti&ecirc;u h&oacute;a, giảm c&acirc;n v&agrave; l&agrave;m đẹp da.</li>\n	<li>Chất xơ trong chuối gi&uacute;p hỗ trợ ti&ecirc;u h&oacute;a, ngăn ngừa t&aacute;o b&oacute;n v&agrave; giảm nguy cơ mắc c&aacute;c bệnh tim mạch.</li>\n	<li>Chuối l&agrave; loại tr&aacute;i c&acirc;y &iacute;t calo v&agrave; gi&agrave;u chất xơ, gi&uacute;p bạn cảm thấy no l&acirc;u v&agrave; hạn chế ăn vặt.</li>\n	<li>Vitamin C trong chuối l&agrave; chất chống oxy h&oacute;a mạnh gi&uacute;p tăng cường hệ miễn dịch, chống lại c&aacute;c bệnh nhiễm tr&ugrave;ng v&agrave; cảm lạnh.</li>\n</ul>\n\n<p><strong>C&aacute;ch lựa chọn</strong></p>\n\n<ul>\n	<li>Kh&aacute;ch h&agrave;ng n&ecirc;n lựa chọn những nh&aacute;nh chuối c&oacute; m&agrave;u v&agrave;ng tươi v&agrave; kh&ocirc;ng bị trầy hoặc h&eacute;o</li>\n	<li>Với Farmers Market ti&ecirc;u ch&iacute; h&agrave;ng đầu l&agrave; sản phảm đạt chất lượng cao, nguồn gốc r&otilde; r&agrave;ng, mang đến trải nghiệm tốt nhất cho kh&aacute;ch h&agrave;ng.&nbsp;</li>\n</ul>\n\n<p><strong>C&aacute;ch bảo quản chuối Dole tốt nhất</strong></p>\n\n<ul>\n	<li>&nbsp;Bạn c&oacute; thể d&ugrave;ng giấy b&aacute;o hoặc m&agrave;ng bọc thực phẩm để g&oacute;i từng quả chuối lại. Sau đ&oacute; bạn chỉ việc cho chuối v&agrave;o tủ lạnh l&agrave; đ&atilde; c&oacute; thể giữ cho chuối tươi ngon l&acirc;u hơn.</li>\n	<li>Bọc cuốn chuối lại để k&eacute;o d&agrave;i thờ gian sử dụng</li>\n</ul>\n\n<p><strong>Những lưu &yacute; khi thưởng thức chuối</strong></p>\n\n<ul>\n	<li>Kh&ocirc;ng n&ecirc;n ăn chuối khi đang đ&oacute;i</li>\n	<li>N&ecirc;n ăn chuối sau bữa ăn khoảng 1 đến 2 tiếng</li>\n	<li>Kh&ocirc;ng n&ecirc;n sử dụng chuối kết hợp với một số loại thực phẩm kh&aacute;c như khoai t&acirc;y, khoai lang, dưa hấu, ...</li>\n	<li>Kh&ocirc;ng n&ecirc;n ăn qu&aacute; nhiều chuối trong một ng&agrave;y</li>\n</ul>\n', 'Trái cây Việt', 26000, 1, '2024-07-05', 7, 'f881cf01b1dcdab0e71ee2fbfd3a49aa', 'ezwyzdhii5s47llzs8wb', 3, 3, 1),
(24, 'Bưởi da xanh túi lưới', '<h1><strong>Bưởi da xanh t&uacute;i lưới</strong></h1>\n\n<p><strong>- Xuất xứ:&nbsp;</strong>Việt Nam</p>\n\n<p><strong>- Ti&ecirc;u chuẩn chất lượng:&nbsp;</strong>Tr&aacute;i c&acirc;y sạch</p>\n\n<p><strong>- Khối lượng:&nbsp;</strong>1Kg - 1,1Kg/Tr&aacute;i</p>\n\n<p><strong>- Đặc điểm nổi bật của bưởi da xanh t&uacute;i lưới</strong></p>\n\n<ul>\n	<li>Bưởi&nbsp;l&agrave; một loại thực phẩm v&ocirc; c&ugrave;ng l&agrave;nh mạnh để đưa v&agrave;o chế độ ăn uống.</li>\n	<li>Phần ruột m&agrave;u hồng trắng với c&aacute;c t&eacute;p mọng nước v&ocirc; c&ugrave;ng hấp dẫn. Tr&aacute;i c&oacute; vị ngọt, bưởi h&aacute;i xuống để c&agrave;ng l&acirc;u độ ngọt c&agrave;ng cao.</li>\n</ul>\n\n<p><strong>- Lợi &iacute;ch khi sử dụng bưởi da xanh t&uacute;i lưới</strong></p>\n\n<ul>\n	<li>Tăng cường hệ miễn dịch</li>\n	<li>Chống Oxy ho&aacute;, ph&ograve;ng ngừa ung thư</li>\n	<li>Tăng cường sức đề kh&aacute;ng, ph&ograve;ng ngừa cảm c&uacute;m</li>\n	<li>Gi&uacute;p ti&ecirc;u ho&aacute; tốt</li>\n	<li>Hạ đường huyết hiệu quả</li>\n	<li>Gi&uacute;p tr&aacute;i tim khoẻ mạnh</li>\n</ul>\n\n<p><strong>- C&aacute;ch lựa chọn bưởi da xanh t&uacute;i lưới ngon</strong></p>\n\n<ul>\n	<li>N&ecirc;n chọn những quả bưởi c&oacute; h&igrave;nh d&aacute;ng c&acirc;n đối, kh&ocirc;ng bị sần s&ugrave;i hay m&eacute;o m&oacute;.</li>\n	<li>Cuống bưởi da xanh t&uacute;i lưới loại 1 c&ograve;n tươi, m&agrave;u xanh v&agrave; d&iacute;nh chặt v&agrave;o quả.</li>\n	<li>Khi g&otilde; nhẹ v&agrave;o vỏ bưởi, bạn sẽ nghe thấy tiếng k&ecirc;u &quot;bộp bộp&quot;. &Acirc;m thanh n&agrave;y cho thấy bưởi đ&atilde; ch&iacute;n v&agrave; c&oacute; nhiều nước.</li>\n	<li>Với Farmers Market ti&ecirc;u ch&iacute; h&agrave;ng đầu l&agrave; sản phảm đạt chất lượng cao, nguồn gốc r&otilde; r&agrave;ng, mang đến trải nghiệm tốt nhất cho kh&aacute;ch h&agrave;ng.&nbsp;</li>\n</ul>\n\n<p><strong>- C&aacute;ch bảo quản bưởi da xanh t&uacute;i lưới</strong></p>\n\n<ul>\n	<li>\n	<p><strong>Bước 1 &ndash; Rửa sạch:</strong>&nbsp;Bạn c&oacute; thể loại bỏ bất kỳ dấu vết bụi bẩn n&agrave;o tr&ecirc;n bề mặt vỏ bằng vải kh&ocirc; hoặc khăn giấy. Lưu &yacute; kh&ocirc;ng đem đi rửa với nước, như vậy tr&aacute;nh l&agrave;m cho bưởi bị thấm nước v&agrave; nhanh hỏng hơn.</p>\n	</li>\n	<li>\n	<p><strong>Bước 2 &ndash; Cho quả v&agrave;o rổ hoặc ngăn rau củ:</strong>&nbsp;Bạn c&oacute; thể cho bưởi v&agrave;o ngăn c&ugrave;ng với c&aacute;c loại rau củ kh&aacute;c. Hoặc cho v&agrave;o rổ, th&ugrave;ng m&agrave; kh&ocirc;ng đậy nắp. H&atilde;y nhớ đừng xếp chồng l&ecirc;n nhau.</p>\n	</li>\n	<li>\n	<p><strong>Bước 3 &ndash; Ghi nhớ ng&agrave;y bảo quản:</strong>&nbsp;V&igrave; quả c&oacute; thể giữ trong nhiệt độ thường trong một v&agrave;i ng&agrave;y. Do đ&oacute;, h&atilde;y viết ra tờ giấy ng&agrave;y bảo quản v&agrave; d&aacute;n v&agrave;o sổ tay hoặc bất cứ đ&acirc;u thuận tiện. Nhờ đ&oacute; bạn sẽ biết ch&uacute;ng đ&atilde; được bảo quản trong bao l&acirc;u.</p>\n	</li>\n	<li>\n	<p><strong>Bước 4 &ndash; Bảo quản bưởi trong tủ lạnh:</strong>&nbsp;H&atilde;y đặt ở nơi tho&aacute;ng m&aacute;t, tr&aacute;nh xa c&aacute;c loại thực phẩm c&oacute; m&ugrave;i nồng. Bưởi sẽ giữ được đến 2 tuần trong ngăn m&aacute;t tủ lạnh.</p>\n	</li>\n</ul>\n\n<p><strong>- Những lưu &yacute; khi thưởng thức bưởi da xanh t&uacute;i lưới</strong></p>\n\n<ul>\n	<li>\n	<p>Kh&ocirc;ng sử dụng khi đang đ&oacute;i</p>\n	</li>\n</ul>\n\n<ul>\n	<li>\n	<p>Kh&ocirc;ng ăn bưởi khi bị bệnh dạ d&agrave;y, t&aacute; tr&agrave;ng</p>\n	</li>\n	<li>\n	<p>Kh&ocirc;ng ăn bưởi c&ugrave;ng gan lợn</p>\n	</li>\n	<li>\n	<p>Kh&ocirc;ng ăn khi ti&ecirc;u h&oacute;a k&eacute;m</p>\n	</li>\n	<li>\n	<p>Tr&aacute;nh ăn bưởi trước khi uống thuốc 1 tiếng v&igrave; c&oacute; thể ảnh hưởng đến t&aacute;c dụng của thuốc</p>\n	</li>\n	<li>N&ecirc;n ăn bưởi với lượng vừa phải, khoảng 1/2 m&uacute;i mỗi ng&agrave;y.</li>\n</ul>\n\n<p><strong>- Phụ nữ mang thai v&agrave; trẻ em ăn bưởi&nbsp;c&oacute; được kh&ocirc;ng?</strong></p>\n\n<p><strong>+&nbsp;Đối với phụ nữ mang thai:</strong></p>\n\n<ul>\n	<li>Bổ sung vitamin v&agrave; kho&aacute;ng chất cần thiết cho thai kỳ như vitamin C, kali, folate,...</li>\n	<li>Hỗ trợ tăng cường hệ miễn dịch, giảm nguy cơ mắc c&aacute;c bệnh cảm c&uacute;m, ho.</li>\n	<li>Gi&uacute;p kiểm so&aacute;t huyết &aacute;p, tốt cho tim mạch.</li>\n	<li>Cung cấp chất xơ, hỗ trợ ti&ecirc;u h&oacute;a v&agrave; ngăn ngừa t&aacute;o b&oacute;n.</li>\n	<li>C&oacute; thể gi&uacute;p giảm bớt c&aacute;c triệu chứng ốm ngh&eacute;n, buồn n&ocirc;n</li>\n</ul>\n\n<p><strong>+&nbsp;Đối với trẻ em (18 th&aacute;ng tuổi trở l&ecirc;n):&nbsp;</strong></p>\n\n<ul>\n	<li>Cung cấp vitamin v&agrave; kho&aacute;ng chất cần thiết cho sự ph&aacute;t triển của trẻ.</li>\n	<li>Tăng cường hệ miễn dịch, gi&uacute;p trẻ &iacute;t ốm vặt hơn.</li>\n	<li>Hỗ trợ hệ ti&ecirc;u h&oacute;a khỏe mạnh.</li>\n	<li>Gi&uacute;p thanh nhiệt, giải độc cơ thể.</li>\n</ul>\n', 'Trái cây Việt', 89000, 1, '2024-07-05', 7, '8fd0cc60e21ca580441c356a2b602ea8', 'py7qkmuohivziuldfa78', 3, 1, 1),
(25, 'Cóc cày', '<h1><strong>C&oacute;c c&agrave;y</strong></h1>\n\n<p><strong>- Xuất xứ:</strong>&nbsp;Việt Nam</p>\n\n<p><strong>- Ti&ecirc;u chuẩn chất lượng</strong>: Tr&aacute;i c&acirc;y sạch</p>\n\n<p>-&nbsp;<strong>Khối lượng:&nbsp;</strong>1kg</p>\n\n<p><strong>- Đặc điểm:</strong>&nbsp;</p>\n\n<ul>\n	<li>Thịt c&oacute;c gi&ograve;n xốp, ngọt thơm xen kẽ vị chua nh&egrave; nhẹ kh&ocirc;ng &quot;ứa nước miếng&quot; kh&ocirc;ng được.</li>\n	<li>C&oacute;c c&agrave;y l&agrave; một loại quả hạch, c&oacute; k&iacute;ch thước lớn hơn quả c&oacute;c thường.&nbsp;</li>\n	<li>Khi xanh, vỏ c&oacute;c c&agrave;y m&agrave;u xanh, thịt gi&ograve;n v&agrave; chua ngọt.&nbsp;</li>\n	<li>Khi ch&iacute;n, vỏ c&oacute;c c&agrave;y chuyển sang m&agrave;u v&agrave;ng, thịt m&agrave;u v&agrave;ng-xanh nhạt, ngọt đậm v&agrave; thơm.</li>\n</ul>\n\n<p><strong>- Hướng dẫn bảo quản:</strong></p>\n\n<ul>\n	<li>Để bảo quản quả c&oacute;c tươi thật l&acirc;u khi mua m&oacute;c về bạn đừng n&ecirc;n rửa ch&uacute;ng m&agrave; h&atilde;y để ở nơi kh&ocirc; r&aacute;o. Khi n&agrave;o ăn đến đ&acirc;u ra rửa đến, nghe hơi lạ nhưng với tr&aacute;i c&oacute;c v&agrave; một một loại tr&aacute;i c&acirc;y kh&aacute;c như bầu, b&iacute;,... khi bạn rửa ch&uacute;ng đồng nghĩa ch&uacute;ng sẽ mất đi &ldquo;Lớp bảo vệ&rdquo; tự nhi&ecirc;n. V&igrave; vậy nếu ch&uacute;ng bụi bẩn qu&aacute; bạn n&ecirc;n lấy khăn l&acirc;u nhẹ nh&agrave;ng nh&eacute;.</li>\n	<li>Hoặc bạn c&oacute; thể rửa sạch quả c&oacute;c, lau kh&ocirc; ch&uacute;ng v&agrave; để trong t&uacute;i b&oacute;ng. Đặt ở ngăn m&aacute;t của tủ lạnh. Việc bảo quản c&oacute;c trong ngăn m&aacute;t tủ lạnh sẽ gi&uacute;p c&oacute;c được tươi khoảng 15 ng&agrave;y.</li>\n</ul>\n\n<p><strong>- Gi&aacute; trị dinh dưỡng:</strong></p>\n\n<p>C&oacute;c c&agrave;y l&agrave; một loại tr&aacute;i c&acirc;y gi&agrave;u vitamin C, vitamin B, kali v&agrave; chất xơ. Vitamin C gi&uacute;p tăng cường hệ miễn dịch, vitamin B gi&uacute;p chuyển h&oacute;a thức ăn th&agrave;nh năng lượng, kali gi&uacute;p điều h&ograve;a huyết &aacute;p v&agrave; chất xơ gi&uacute;p hỗ trợ ti&ecirc;u h&oacute;a.</p>\n\n<p><strong>- C&ocirc;ng dụng:</strong></p>\n\n<p>C&oacute;c c&agrave;y c&oacute; thể ăn tươi, &eacute;p nước hoặc chế biến th&agrave;nh nhiều m&oacute;n ăn ngon như: gỏi c&oacute;c c&agrave;y, salad c&oacute;c c&agrave;y, nước mắm c&oacute;c c&agrave;y,...</p>\n\n<p><strong>- C&aacute;ch chọn mua:</strong></p>\n\n<ul>\n	<li>N&ecirc;n chọn mua c&oacute;c c&agrave;y c&oacute; vỏ m&agrave;u v&agrave;ng hoặc n&acirc;u, kh&ocirc;ng bị dập n&aacute;t.</li>\n	<li>C&oacute;c c&agrave;y ch&iacute;n c&oacute; m&ugrave;i thơm dịu nhẹ.</li>\n	<li>N&ecirc;n b&oacute;p nhẹ quả c&oacute;c c&agrave;y, nếu cảm thấy mềm th&igrave; l&agrave; quả ch&iacute;n.</li>\n	<li>Với Farmers Market ti&ecirc;u ch&iacute; h&agrave;ng đầu l&agrave; sản phảm đạt chất lượng cao, nguồn gốc r&otilde; r&agrave;ng, mang đến trải nghiệm tốt nhất cho kh&aacute;ch h&agrave;ng.&nbsp;</li>\n</ul>\n', 'Trái cây Việt', 49000, 1, '2024-07-05', 7, 'f804d1f3a4c36d93d9073ff3b1d4f772', 'kuu8mr9srfqpspusid6p', 3, 3, 1),
(26, 'Bơ sáp Cuba', '<p><strong>Bơ s&aacute;p Cuba (Size 300G up/Tr&aacute;i) - 1Kg&nbsp;</strong></p>\n\n<p><strong>- Xuất xứ:&nbsp;</strong>Việt Nam</p>\n\n<p><strong>- Ti&ecirc;u chuẩn chất lượng:</strong>&nbsp;tr&aacute;i c&acirc;y sạch</p>\n\n<p><strong>- Quy c&aacute;ch:&nbsp;</strong>Size 300G up/Tr&aacute;i</p>\n\n<p><strong>- Đặc điểm nổi bật:&nbsp;</strong></p>\n\n<ul>\n	<li>Bơ Cuba cơm d&agrave;y, m&agrave;u v&agrave;ng đậm, dẻo, thơm b&eacute;o ngậy, vỏ dễ b&oacute;c.</li>\n	<li>Tr&aacute;i bơ thường c&oacute; k&iacute;ch thước lớn hơn, h&igrave;nh quả l&ecirc; hoặc h&igrave;nh cầu, vỏ m&agrave;u xanh l&aacute; c&acirc;y đậm khi chưa ch&iacute;n v&agrave; chuyển sang m&agrave;u hơi v&agrave;ng khi ch&iacute;n.&nbsp;</li>\n	<li>Vỏ của bơ Cuba d&agrave;y hơn v&agrave; c&oacute; kết cấu sần s&ugrave;i, gi&uacute;p bảo vệ phần thịt b&ecirc;n trong tốt hơn. Thịt bơ thơm ngon đặt trưng, b&eacute;o v&agrave; ăn kh&ocirc;ng ngấy.</li>\n</ul>\n\n<p>Để&nbsp;<strong><em>bơ cuba</em></strong>&nbsp;lu&ocirc;n giữ được hương bị tươi ngon, bạn cần tu&acirc;n thủ một số lưu &yacute; cần thiết trong&nbsp;<strong><em>c&aacute;ch bảo quản bơ</em></strong>&nbsp;như sau:</p>\n\n<ul>\n	<li>Kh&ocirc;ng n&ecirc;n nắn bơ qu&aacute; nhiều.</li>\n	<li>Để bơ ở nơi m&aacute;t, nhiệt độ l&yacute; tưởng l&agrave; từ 23-25 độ C.</li>\n	<li>Tốt nhất n&ecirc;n để bơ tiếp x&uacute;c với mặt đất, kh&ocirc;ng để bơ trong t&uacute;i ni-l&ocirc;ng v&igrave; bơ rất dễ bị thối.</li>\n	<li>Nếu bơ đ&atilde; mềm n&ecirc;n cho bơ v&agrave;o ngăn m&aacute;t tủ lạnh để bảo quản. Tuy nhi&ecirc;n kh&ocirc;ng n&ecirc;n để bơ trong tủ lạnh qu&aacute; l&acirc;u.</li>\n	<li>Nếu cắt bơ nhưng chưa kịp sử dụng th&igrave; n&ecirc;n b&ocirc;i l&ecirc;n bề mặt miếng bơ một lớp dầu mỏng rồi bọc k&iacute;n bằng m&agrave;ng bọc thực phẩm, bảo quản trong tủ lạnh để d&ugrave;ng dần.</li>\n	<li>C&oacute; thể d&ugrave;ng b&aacute;o bọc quả bơ để l&agrave;m chậm qu&aacute; tr&igrave;nh bơ ph&acirc;n hủy, c&aacute;ch n&agrave;y gi&uacute;p bơ tươi l&acirc;u hơn.</li>\n	<li>Nếu bơ ch&iacute;n nhiều m&agrave; bạn thưởng thức kh&ocirc;ng kịp, h&atilde;y cắt bơ th&agrave;nh từng l&aacute;t rồi bỏ v&agrave;o hộp nhựa rồi cho v&agrave;o ngăn đ&aacute;. Khi ăn th&igrave; chờ bơ tan ra, hương vị cũng rất thơm ngon.</li>\n</ul>\n\n<p><strong>- Lợi &iacute;ch sức khỏe của Bơ s&aacute;p Cuba</strong></p>\n\n<ul>\n	<li>Tăng cường hệ miễn dịch: Vitamin A v&agrave; vitamin E trong Bơ s&aacute;p 034 đầu d&ograve;ng Đăk Lăk gi&uacute;p tăng cường hệ miễn dịch, chống lại c&aacute;c bệnh cảm c&uacute;m, ho.</li>\n	<li>Hỗ trợ sức khỏe tim mạch: Kali trong Bơ s&aacute;p Cuba gi&uacute;p điều h&ograve;a huyết &aacute;p v&agrave; duy tr&igrave; chức năng tim mạch.</li>\n	<li>Hỗ trợ sức khỏe mắt: Vitamin A trong Bơ s&aacute;p Cuba gi&uacute;p bảo vệ sức khỏe mắt, ngăn ngừa c&aacute;c bệnh về mắt như đục thủy tinh thể, tho&aacute;i h&oacute;a điểm v&agrave;ng.</li>\n	<li>L&agrave;m đẹp da: Vitamin A, vitamin E v&agrave; chất chống oxy h&oacute;a trong Bơ s&aacute;p Cuba gi&uacute;p bảo vệ da khỏi t&aacute;c hại của &aacute;nh nắng mặt trời, l&agrave;m giảm nếp nhăn v&agrave; tăng độ đ&agrave;n hồi cho da.</li>\n	<li>Hỗ trợ giảm c&acirc;n: Chất xơ trong Bơ s&aacute;p Cuba gi&uacute;p tạo cảm gi&aacute;c no l&acirc;u, hỗ trợ giảm c&acirc;n hiệu quả.</li>\n</ul>\n', 'Trái cây Việt', 89000, 1, '2024-07-05', 7, '4bf9f4babbb136abe166eecc69613160', 'jpfsivg4yrucnrjuybiq', 3, 1, 1),
(27, 'Trái tầm bóp', '<h1><strong>Tr&aacute;i tầm b&oacute;p</strong></h1>\n\n<p><strong>- Xuất xứ:</strong>&nbsp;Việt Nam</p>\n\n<p><strong>- Quy c&aacute;ch:</strong>&nbsp;1kg/Hộp</p>\n\n<p>-&nbsp;Tr&aacute;i tầm b&oacute;p, c&ograve;n được gọi l&agrave; c&agrave; chua dại, c&agrave; chua leo, c&agrave; chua hồ l&ocirc;, c&agrave; chua nọc độc, l&agrave; một loại c&acirc;y leo th&acirc;n thảo thuộc họ Solanaceae. C&acirc;y tầm b&oacute;p c&oacute; nguồn gốc từ Nam Mỹ v&agrave; được du nhập v&agrave;o Việt Nam từ l&acirc;u đời.</p>\n\n<p><strong>- Đặc điểm nổi bật:</strong></p>\n\n<ul>\n	<li>C&acirc;y&nbsp;tầm b&oacute;p&nbsp;c&oacute;&nbsp;quả&nbsp;nhỏ v&agrave; tr&ograve;n giống như&nbsp;quả&nbsp;c&agrave;, được bao bọc bằng một lớp vỏ mỏng xanh nh&igrave;n giống như lồng đ&egrave;n, khi&nbsp;b&oacute;p quả tầm b&oacute;p&nbsp;sẽ nghe được tiếng nổ nhỏ.&nbsp;</li>\n	<li>Khi ch&iacute;n m&agrave;u&nbsp;của quả&nbsp;sẽ chuyển th&agrave;nh m&agrave;u đỏ, c&oacute;&nbsp;vị&nbsp;chua chua ngọt ngọt</li>\n</ul>\n\n<p>-&nbsp;<strong>Gi&aacute; trị dinh dưỡng:</strong></p>\n\n<ul>\n	<li><strong>Chứa nhiều vitamin v&agrave; kho&aacute;ng chất:</strong>&nbsp;Tr&aacute;i tầm b&oacute;p l&agrave; nguồn cung cấp dồi d&agrave;o vitamin C, A, B6, kali, magi&ecirc;, phốt pho,...</li>\n	<li><strong>Tăng cường hệ miễn dịch:</strong>&nbsp;Vitamin C trong tr&aacute;i tầm b&oacute;p gi&uacute;p tăng cường hệ miễn dịch, chống lại c&aacute;c bệnh nhiễm tr&ugrave;ng.</li>\n	<li><strong>Tốt cho sức khỏe tim mạch:</strong>&nbsp;Kali trong tr&aacute;i tầm b&oacute;p gi&uacute;p điều h&ograve;a huyết &aacute;p, giảm nguy cơ mắc bệnh tim mạch.</li>\n	<li><strong>Chống oxy h&oacute;a:</strong>&nbsp;Tr&aacute;i tầm b&oacute;p chứa nhiều chất chống oxy h&oacute;a gi&uacute;p bảo vệ cơ thể khỏi t&aacute;c hại của gốc tự do, ngăn ngừa l&atilde;o h&oacute;a v&agrave; ung thư.</li>\n	<li><strong>Tốt cho hệ ti&ecirc;u h&oacute;a:</strong>&nbsp;Chất xơ trong tr&aacute;i tầm b&oacute;p gi&uacute;p hỗ trợ hệ ti&ecirc;u h&oacute;a, ngăn ngừa t&aacute;o b&oacute;n.</li>\n</ul>\n\n<p><strong>- Lưu &yacute; khi sử dụng tr&aacute;i tầm b&oacute;p:</strong></p>\n\n<ul>\n	<li>Kh&ocirc;ng n&ecirc;n ăn qu&aacute; nhiều tr&aacute;i tầm b&oacute;p v&igrave; c&oacute; thể g&acirc;y đầy hơi, kh&oacute; ti&ecirc;u.</li>\n	<li>Người c&oacute; bệnh thận n&ecirc;n hạn chế sử dụng tr&aacute;i tầm b&oacute;p v&igrave; c&oacute; thể l&agrave;m tăng lượng kali trong m&aacute;u.</li>\n	<li>Phụ nữ mang thai v&agrave; cho con b&uacute; n&ecirc;n tham khảo &yacute; kiến b&aacute;c sĩ trước khi sử dụng tr&aacute;i tầm b&oacute;p.</li>\n</ul>\n\n<p><strong>- Hướng dẫn sử dụng:</strong></p>\n\n<ul>\n	<li>Quả tầm b&oacute;p thường được d&ugrave;ng như một loại tr&aacute;i c&acirc;y kh&ocirc; hoặc ăn tươi t&ugrave;y v&agrave;o sở th&iacute;ch.&nbsp;</li>\n	<li>Ch&uacute;ng c&oacute; thể được thưởng thức một m&igrave;nh hoặc trộn với c&aacute;c loại tr&aacute;i c&acirc;y kh&aacute;c trong m&oacute;n salad.&nbsp;</li>\n	<li>Nếu bạn cho quả tầm b&oacute;p được phủ trong sữa hoặc s&ocirc; c&ocirc; la đen th&igrave; hương vị sẽ rất tuyệt đ&oacute; nh&eacute;!</li>\n</ul>\n\n<p>-&nbsp;Tr&aacute;i tầm b&oacute;p c&oacute; thể ăn tươi, l&agrave;m nước &eacute;p, mứt, sir&ocirc; hoặc d&ugrave;ng để nấu canh, x&agrave;o, kho. Quả tầm b&oacute;p cũng được sử dụng trong y học d&acirc;n gian để chữa c&aacute;c bệnh như cảm c&uacute;m, ho, đau họng, ti&ecirc;u chảy, lỵ,...</p>\n', 'Trái cây Việt', 79000, 1, '2024-07-05', 7, '081fe3bf4ea82d32263aeac47a37b726', 'x9kghqcgehwmu6bwglmh', 3, 2, 1),
(28, 'Bơ sáp 034 đầu dòng Đăk Lăk', '<h1><strong>Bơ s&aacute;p 034 đầu d&ograve;ng Đăk Lăk&nbsp;</strong></h1>\n\n<p><strong>- Xuất xứ:</strong>&nbsp;Việt Nam</p>\n\n<p><strong>- Ti&ecirc;u chuẩn chất lượng:</strong>&nbsp;Tr&aacute;i c&acirc;y sạch</p>\n\n<p><strong>- Đặc điểm:&nbsp;</strong></p>\n\n<ul>\n	<li>Bơ đầu d&ograve;ng 034 (tỉnh Dak Lak) - 1 trong những loại bơ s&aacute;p ngon nhất tại thị trường Việt Nam&nbsp;</li>\n	<li>Ưu điểm kh&aacute;c biệt với m&agrave;u cơm v&agrave;ng ươm, độ dẻo cao, vị b&eacute;o thơm rất hấp dẫn&nbsp;</li>\n	<li>Bơ c&ograve;n c&oacute; gi&aacute; trị dinh dưỡng cao, mang lại nhiều lợi &iacute;ch về sức khỏe v&agrave; gi&uacute;p giảm c&acirc;n hiệu quả nữa đ&oacute; nha</li>\n</ul>\n\n<p><strong>- Lợi &iacute;ch sức khỏe của Bơ s&aacute;p 034 đầu d&ograve;ng Đăk Lăk:</strong></p>\n\n<ul>\n	<li>Tăng cường hệ miễn dịch: Vitamin A v&agrave; vitamin E trong Bơ s&aacute;p 034 đầu d&ograve;ng Đăk Lăk gi&uacute;p tăng cường hệ miễn dịch, chống lại c&aacute;c bệnh cảm c&uacute;m, ho.</li>\n	<li>Hỗ trợ sức khỏe tim mạch: Kali trong Bơ s&aacute;p 034 đầu d&ograve;ng Đăk Lăk gi&uacute;p điều h&ograve;a huyết &aacute;p v&agrave; duy tr&igrave; chức năng tim mạch.</li>\n	<li>Hỗ trợ sức khỏe mắt: Vitamin A trong Bơ s&aacute;p 034 đầu d&ograve;ng Đăk Lăk gi&uacute;p bảo vệ sức khỏe mắt, ngăn ngừa c&aacute;c bệnh về mắt như đục thủy tinh thể, tho&aacute;i h&oacute;a điểm v&agrave;ng.</li>\n	<li>L&agrave;m đẹp da: Vitamin A, vitamin E v&agrave; chất chống oxy h&oacute;a trong Bơ s&aacute;p 034 đầu d&ograve;ng Đăk Lăk gi&uacute;p bảo vệ da khỏi t&aacute;c hại của &aacute;nh nắng mặt trời, l&agrave;m giảm nếp nhăn v&agrave; tăng độ đ&agrave;n hồi cho da.</li>\n	<li>Hỗ trợ giảm c&acirc;n: Chất xơ trong Bơ s&aacute;p 034 đầu d&ograve;ng Đăk Lăk gi&uacute;p tạo cảm gi&aacute;c no l&acirc;u, hỗ trợ giảm c&acirc;n hiệu quả.</li>\n</ul>\n\n<p><strong>- Hướng dẫn c&aacute;ch bảo quản bơ để giữ bơ thơm ngon l&acirc;u</strong></p>\n\n<p>Để&nbsp;<strong><em>bơ s&aacute;p</em></strong>&nbsp;lu&ocirc;n giữ được hương bị tươi ngon, bạn cần tu&acirc;n thủ một số lưu &yacute; cần thiết trong&nbsp;<strong><em>c&aacute;ch bảo quản bơ</em></strong>&nbsp;như sau:</p>\n\n<ul>\n	<li>Kh&ocirc;ng n&ecirc;n nắn bơ qu&aacute; nhiều.</li>\n	<li>Để bơ ở nơi m&aacute;t, nhiệt độ l&yacute; tưởng l&agrave; từ 23-25 độ C.</li>\n	<li>Tốt nhất n&ecirc;n để bơ tiếp x&uacute;c với mặt đất, kh&ocirc;ng để bơ trong t&uacute;i ni-l&ocirc;ng v&igrave; bơ rất dễ bị thối.</li>\n	<li>Nếu bơ đ&atilde; mềm n&ecirc;n cho bơ v&agrave;o ngăn m&aacute;t tủ lạnh để bảo quản. Tuy nhi&ecirc;n kh&ocirc;ng n&ecirc;n để bơ trong tủ lạnh qu&aacute; l&acirc;u.</li>\n	<li>Nếu cắt bơ nhưng chưa kịp sử dụng th&igrave; n&ecirc;n b&ocirc;i l&ecirc;n bề mặt miếng bơ một lớp dầu mỏng rồi bọc k&iacute;n bằng m&agrave;ng bọc thực phẩm, bảo quản trong tủ lạnh để d&ugrave;ng dần.</li>\n	<li>C&oacute; thể d&ugrave;ng b&aacute;o bọc quả bơ để l&agrave;m chậm qu&aacute; tr&igrave;nh bơ ph&acirc;n hủy, c&aacute;ch n&agrave;y gi&uacute;p bơ tươi l&acirc;u hơn.</li>\n	<li>Nếu bơ ch&iacute;n nhiều m&agrave; bạn thưởng thức kh&ocirc;ng kịp, h&atilde;y cắt bơ th&agrave;nh từng l&aacute;t rồi bỏ v&agrave;o hộp nhựa rồi cho v&agrave;o ngăn đ&aacute;. Khi ăn th&igrave; chờ bơ tan ra, hương vị cũng rất thơm ngon.</li>\n</ul>\n', 'Trái cây Việt', 357000, 1, '2024-07-05', 7, '66f5daf85b31579dc27d7c0ec1460079', 'bm3dtzegx1phrndumypl', 3, 3, 1),
(29, 'Dưa lưới tròn ruột cam mini', '<p><strong>Xuất xứ</strong>: Việt Nam</p>\n\n<p>Khối lượng: 0.8kg - 1kg/tr&aacute;i</p>\n\n<p><strong>Đặc điểm nổi bật:&nbsp;</strong></p>\n\n<ul>\n	<li>Quả c&oacute; d&aacute;ng tr&ograve;n, vỏ d&agrave;y c&oacute; h&igrave;nh g&acirc;n lưới b&ecirc;n ngo&agrave;i.&nbsp;</li>\n	<li>B&ecirc;n trong c&oacute; phần thịt v&agrave; hạt dưa m&agrave;u đỏ cam, gi&ograve;n ngọt, thanh m&aacute;t</li>\n</ul>\n\n<p><strong>Hướng dẫn sử dụng:</strong></p>\n\n<ul>\n	<li>Dưa lưới d&ugrave;ng để ăn trực tiếp.</li>\n	<li>Bạn c&oacute; thể d&ugrave;ng dưa lưới để chế biến salad, sinh tố, nước &eacute;p.</li>\n</ul>\n', 'Trái cây Việt', 49000, 1, '2024-07-05', 7, '30e6898337603ab0627bd24e8705df27', 'yyw5jztgarc5lmsll1oi', 3, 2, 1),
(30, 'Mít giống Thái tách múi', '<h1><strong>M&iacute;t giống Th&aacute;i</strong></h1>\n\n<p><strong>- Xuất xứ:</strong>&nbsp;Việt Nam</p>\n\n<p><strong>- Ti&ecirc;u chuẩn chất lượng:</strong>&nbsp;M&iacute;t giống Th&aacute;i</p>\n\n<p><strong>- Trọng lượng sản phẩm:&nbsp;</strong>Hộp 500g</p>\n\n<p><strong>- Đặc điểm</strong>: M&iacute;t Th&aacute;i m&uacute;i to, v&agrave;ng tươi, đặc biệt ngọt đậm vị v&agrave; gi&ograve;n rất hấp dẫn. M&iacute;t Th&aacute;i c&oacute; xơ ngọt hơn c&aacute;c loại m&iacute;t kh&aacute;c.</p>\n\n<ul>\n	<li><strong>H&igrave;nh dạng:</strong>&nbsp;M&iacute;t giống Th&aacute;i t&aacute;ch m&uacute;i c&oacute; h&igrave;nh bầu dục, hơi thu&ocirc;n d&agrave;i, vỏ d&agrave;y m&agrave;u xanh v&agrave;ng khi ch&iacute;n.</li>\n	<li><strong>Thịt quả:</strong>&nbsp;Thịt quả d&agrave;y, m&agrave;u v&agrave;ng cam, &iacute;t xơ v&agrave; c&oacute; vị ngọt thanh, gi&ograve;n tan.</li>\n	<li><strong>Hạt:</strong>&nbsp;Hạt m&iacute;t nhỏ</li>\n	<li><strong>M&uacute;i m&iacute;t:</strong>&nbsp;M&uacute;i m&iacute;t to, đều đặn, dễ d&agrave;ng t&aacute;ch ra khỏi c&ugrave;i m&agrave; kh&ocirc;ng cần dao.</li>\n</ul>\n\n<p><strong>- Lợi &iacute;ch của m&iacute;t giống Th&aacute;i t&aacute;ch m&uacute;i:</strong></p>\n\n<ul>\n	<li><strong>Dễ d&agrave;ng thưởng thức:</strong>&nbsp;Nhờ m&uacute;i m&iacute;t dễ d&agrave;ng t&aacute;ch ra, m&iacute;t giống Th&aacute;i t&aacute;ch m&uacute;i gi&uacute;p người thưởng thức tiết kiệm thời gian v&agrave; c&ocirc;ng sức.</li>\n	<li><strong>Hương vị thơm ngon:</strong>&nbsp;M&iacute;t giống Th&aacute;i t&aacute;ch m&uacute;i c&oacute; hương vị thơm ngon, ngọt thanh, kh&ocirc;ng bị sượng hay đắng.</li>\n	<li><strong>Gi&aacute; trị dinh dưỡng cao:</strong>&nbsp;M&iacute;t giống Th&aacute;i t&aacute;ch m&uacute;i l&agrave; nguồn cung cấp dồi d&agrave;o vitamin C, vitamin A, kali v&agrave; chất xơ.</li>\n	<li><strong>&Iacute;t hạt:</strong>&nbsp;M&iacute;t giống Th&aacute;i t&aacute;ch m&uacute;i c&oacute; số lượng hạt &iacute;t hơn so với c&aacute;c loại m&iacute;t kh&aacute;c.</li>\n</ul>\n\n<p><strong>- C&aacute;ch sử dụng m&iacute;t giống Th&aacute;i t&aacute;ch m&uacute;i:</strong></p>\n\n<ul>\n	<li>M&iacute;t giống Th&aacute;i t&aacute;ch m&uacute;i c&oacute; thể ăn tươi, bảo quản lạnh sẽ ăn ngon hơn</li>\n</ul>\n\n<p><strong>- C&aacute;ch lựa chọn m&iacute;t ngon:</strong></p>\n\n<ul>\n	<li>N&ecirc;n chọn những quả m&iacute;t c&oacute; m&ugrave;i thơm thoang thoảng, kh&ocirc;ng qu&aacute; nồng hoặc c&oacute; m&ugrave;i chua.</li>\n	<li>Với Farmers Market ti&ecirc;u ch&iacute; h&agrave;ng đầu l&agrave; sản phảm đạt chất lượng cao, nguồn gốc r&otilde; r&agrave;ng, mang đến trải nghiệm tốt nhất cho kh&aacute;ch h&agrave;ng.&nbsp;</li>\n</ul>\n', 'Trái cây Việt', 59500, 1, '2024-07-05', 7, '4beb54d594a59a9cd7116b6843fcd5fb', 'vpeoe6yduf9lh6507t4d', 3, 3, 1),
(31, 'Chôm chôm Thái Lan', '<h1><strong>Ch&ocirc;m ch&ocirc;m Th&aacute;i Lan</strong></h1>\n\n<p><strong>- Xuất xứ:&nbsp;</strong>Th&aacute;i Lan</p>\n\n<p><strong>- Ti&ecirc;u chuẩn chất lượng:</strong>&nbsp;Tr&aacute;i c&acirc;y sạch</p>\n\n<p><strong>- Khối lượng</strong>: 500g</p>\n\n<p><strong>- Đặc Điểm:</strong></p>\n\n<ul>\n	<li>Cơm d&agrave;y, dễ tr&oacute;c hạt, thịt gi&ograve;n nhẹ, kh&ocirc;ng qu&aacute; kh&ocirc;.</li>\n	<li>Khi ch&iacute;n r&acirc;u tr&aacute;i c&oacute; m&agrave;u xanh, vỏ c&oacute; m&agrave;u đỏ, xen chấm v&agrave;ng.</li>\n	<li><strong>Vỏ:</strong>&nbsp;Vỏ ch&ocirc;m ch&ocirc;m Th&aacute;i Lan d&agrave;y, m&agrave;u đỏ tươi hoặc hồng, c&oacute; nhiều gai nhọn nhỏ.</li>\n	<li><strong>Thịt:</strong>&nbsp;Thịt ch&ocirc;m ch&ocirc;m Th&aacute;i Lan trắng ng&agrave;, xốp mềm, ngọt thanh v&agrave; mọng nước.</li>\n	<li><strong>Hương vị:</strong>&nbsp;Ch&ocirc;m ch&ocirc;m Th&aacute;i Lan c&oacute; vị ngọt thanh, thơm dịu v&agrave; c&oacute; ch&uacute;t vị chua nhẹ.</li>\n</ul>\n\n<p><strong>- Gi&aacute; trị dinh dưỡng:</strong></p>\n\n<p>Ch&ocirc;m ch&ocirc;m Th&aacute;i Lan l&agrave; nguồn cung cấp dồi d&agrave;o vitamin C, vitamin B, kali, magie, photpho v&agrave; c&aacute;c kho&aacute;ng chất thiết yếu kh&aacute;c cho cơ thể.</p>\n\n<ul>\n	<li><strong>Vitamin C:</strong>&nbsp;Gi&uacute;p tăng cường hệ miễn dịch, chống oxy h&oacute;a v&agrave; bảo vệ cơ thể khỏi c&aacute;c bệnh tật.</li>\n	<li><strong>Vitamin B:</strong>&nbsp;Gi&uacute;p hỗ trợ hệ thần kinh v&agrave; chức năng n&atilde;o bộ.</li>\n	<li><strong>Kali:</strong>&nbsp;Gi&uacute;p điều h&ograve;a huyết &aacute;p v&agrave; duy tr&igrave; chức năng tim mạch.</li>\n	<li><strong>Magie:</strong>&nbsp;Gi&uacute;p hỗ trợ hệ cơ bắp v&agrave; xương khớp.</li>\n	<li><strong>Photpho:</strong>&nbsp;Gi&uacute;p hỗ trợ chức năng thận v&agrave; răng miệng.</li>\n</ul>\n\n<p><strong>- Lợi &iacute;ch sức khỏe:</strong></p>\n\n<ul>\n	<li><strong>Tăng cường hệ miễn dịch:</strong>&nbsp;Vitamin C trong ch&ocirc;m ch&ocirc;m Th&aacute;i Lan gi&uacute;p tăng cường hệ miễn dịch, chống lại c&aacute;c bệnh cảm c&uacute;m, ho.</li>\n	<li><strong>Hỗ trợ hệ thần kinh:</strong>&nbsp;Vitamin B trong ch&ocirc;m ch&ocirc;m Th&aacute;i Lan gi&uacute;p hỗ trợ hệ thần kinh v&agrave; chức năng n&atilde;o bộ.</li>\n	<li><strong>Điều h&ograve;a huyết &aacute;p:</strong>&nbsp;Kali trong ch&ocirc;m ch&ocirc;m Th&aacute;i Lan gi&uacute;p điều h&ograve;a huyết &aacute;p v&agrave; duy tr&igrave; chức năng tim mạch.</li>\n	<li><strong>Hỗ trợ hệ cơ bắp v&agrave; xương khớp:</strong>&nbsp;Magie trong ch&ocirc;m ch&ocirc;m Th&aacute;i Lan gi&uacute;p hỗ trợ hệ cơ bắp v&agrave; xương khớp.</li>\n	<li><strong>Hỗ trợ chức năng thận v&agrave; răng miệng:</strong>&nbsp;Photpho trong ch&ocirc;m ch&ocirc;m Th&aacute;i Lan gi&uacute;p hỗ trợ chức năng thận v&agrave; răng miệng.</li>\n	<li><strong>L&agrave;m đẹp da:</strong>&nbsp;Vitamin C v&agrave; chất chống oxy h&oacute;a trong ch&ocirc;m ch&ocirc;m Th&aacute;i Lan gi&uacute;p bảo vệ da khỏi t&aacute;c hại của &aacute;nh nắng mặt trời, l&agrave;m giảm nếp nhăn v&agrave; tăng độ đ&agrave;n hồi cho da.</li>\n</ul>\n\n<p><strong>- C&aacute;ch sử dụng:</strong></p>\n\n<p>Ch&ocirc;m ch&ocirc;m Th&aacute;i Lan c&oacute; thể ăn trực tiếp, &eacute;p nước, l&agrave;m sinh tố, salad hoặc chế biến th&agrave;nh c&aacute;c m&oacute;n ăn kh&aacute;c nhau như b&aacute;nh, mứt, ...</p>\n\n<p><strong>- C&aacute;ch lựa chọn ch&ocirc;m ch&ocirc;m ngon;</strong></p>\n\n<ul>\n	<li>N&ecirc;n chọn mua ch&ocirc;m ch&ocirc;m Th&aacute;i Lan c&oacute; vỏ m&agrave;u đỏ tươi hoặc hồng, gai nhọn nhỏ v&agrave; đều đặn.</li>\n	<li>Với Farmers Market ti&ecirc;u ch&iacute; h&agrave;ng đầu l&agrave; sản phảm đạt chất lượng cao, nguồn gốc r&otilde; r&agrave;ng, mang đến trải nghiệm tốt nhất cho kh&aacute;ch h&agrave;ng.&nbsp;</li>\n</ul>\n\n<p><strong>- C&aacute;ch bảo quản ch&ocirc;m ch&ocirc;m&nbsp;được l&acirc;u:</strong></p>\n\n<p><strong>+ Bảo quản ở nhiệt độ ph&ograve;ng:</strong></p>\n\n<ul>\n	<li><strong>Chọn ch&ocirc;m ch&ocirc;m ch&iacute;n tới:</strong>&nbsp;N&ecirc;n chọn những quả ch&ocirc;m ch&ocirc;m c&oacute; m&agrave;u đỏ tươi, gai nhọn đều đặn, vỏ hơi mềm khi ấn nhẹ. Tr&aacute;nh chọn những quả ch&ocirc;m ch&ocirc;m chưa ch&iacute;n hoặc qu&aacute; ch&iacute;n v&igrave; dễ bị dập n&aacute;t v&agrave; nhanh hỏng.</li>\n	<li><strong>Rửa sạch v&agrave; để r&aacute;o:</strong>&nbsp;Sau khi mua về, bạn n&ecirc;n rửa sạch ch&ocirc;m ch&ocirc;m dưới v&ograve;i nước chảy để loại bỏ bụi bẩn v&agrave; vi khuẩn. Sau đ&oacute;, để r&aacute;o nước ho&agrave;n to&agrave;n.</li>\n	<li><strong>Bảo quản ở nơi tho&aacute;ng m&aacute;t:</strong>&nbsp;N&ecirc;n bảo quản ch&ocirc;m ch&ocirc;m ở nơi tho&aacute;ng m&aacute;t, tr&aacute;nh &aacute;nh nắng trực tiếp. Bạn c&oacute; thể để ch&ocirc;m ch&ocirc;m trong rổ hoặc hộp c&oacute; lỗ th&ocirc;ng hơi để đảm bảo lưu th&ocirc;ng kh&iacute; tốt.</li>\n	<li><strong>Hạn chế chồng chất:</strong>&nbsp;Tr&aacute;nh chồng chất ch&ocirc;m ch&ocirc;m l&ecirc;n nhau v&igrave; c&oacute; thể l&agrave;m dập n&aacute;t quả. N&ecirc;n xếp ch&ocirc;m ch&ocirc;m th&agrave;nh từng lớp ri&ecirc;ng biệt.</li>\n	<li><strong>Thời gian bảo quản:</strong>&nbsp;Với c&aacute;ch bảo quản n&agrave;y, ch&ocirc;m ch&ocirc;m c&oacute; thể giữ được độ tươi ngon trong khoảng 2-3 ng&agrave;y.</li>\n</ul>\n\n<p><strong>+ Bảo quản trong tủ lạnh:</strong></p>\n\n<ul>\n	<li><strong>C&aacute;ch bảo quản:</strong>&nbsp;Ch&ocirc;m ch&ocirc;m c&oacute; thể được bảo quản trong tủ lạnh để giữ được độ tươi ngon l&acirc;u hơn.</li>\n	<li><strong>Chuẩn bị:</strong>&nbsp;Rửa sạch ch&ocirc;m ch&ocirc;m, để r&aacute;o nước. Sau đ&oacute;, cho ch&ocirc;m ch&ocirc;m v&agrave;o hộp k&iacute;n hoặc t&uacute;i nilon c&oacute; đục lỗ để đảm bảo lưu th&ocirc;ng kh&iacute;.</li>\n	<li><strong>Nhiệt độ:</strong>&nbsp;N&ecirc;n bảo quản ch&ocirc;m ch&ocirc;m ở ngăn m&aacute;t tủ lạnh với nhiệt độ từ 8-10 độ C.</li>\n	<li><strong>Thời gian bảo quản:</strong>&nbsp;Ch&ocirc;m ch&ocirc;m bảo quản trong tủ lạnh c&oacute; thể giữ được độ tươi ngon trong khoảng 5-7 ng&agrave;y.</li>\n</ul>\n', 'Trái cây Việt', 49500, 1, '2024-07-05', 7, 'b8c2cac99b4349e538bb6b3686289ef1', 'r2dsdmaferkxdlxwdqbi', 3, 2, 1),
(32, 'Dưa lưới mật Vietgap', '<h1><strong>Dưa lưới mật Vietgap</strong></h1>\n\n<p><strong>- Xuất xứ:&nbsp;</strong>Việt Nam</p>\n\n<p><strong>- Trọng lượng:&nbsp;</strong>1kg<strong>&nbsp;-</strong>1,2kg/Tr&aacute;i</p>\n\n<p><strong>Đặc điểm:&nbsp;</strong>Dưa ruột cam, gi&ograve;n ngọt nhiều nước ăn rất giải kh&aacute;t.</p>\n\n<p>Dưa lưới&nbsp;l&agrave; nguồn cung cấp dồi d&agrave;o vitamin C, vitamin A, kali, magie v&agrave; c&aacute;c kho&aacute;ng chất thiết yếu kh&aacute;c cho cơ thể.</p>\n\n<ul>\n	<li><strong>Vitamin C:</strong>&nbsp;Gi&uacute;p tăng cường hệ miễn dịch, chống oxy h&oacute;a v&agrave; bảo vệ cơ thể khỏi c&aacute;c bệnh tật.</li>\n	<li><strong>Vitamin A:</strong>&nbsp;Gi&uacute;p hỗ trợ sức khỏe mắt, da v&agrave; hệ miễn dịch.</li>\n	<li><strong>Kali:</strong>&nbsp;Gi&uacute;p điều h&ograve;a huyết &aacute;p v&agrave; duy tr&igrave; chức năng tim mạch.</li>\n	<li><strong>Magie:</strong>&nbsp;Gi&uacute;p hỗ trợ hệ cơ bắp v&agrave; xương khớp.</li>\n</ul>\n\n<p><strong>- Lợi &iacute;ch sức khỏe:</strong></p>\n\n<ul>\n	<li><strong>Tăng cường hệ miễn dịch:</strong>&nbsp;Vitamin C v&agrave; vitamin A trong Dưa lưới biển Hằng Nga Danny Green Organic gi&uacute;p tăng cường hệ miễn dịch, chống lại c&aacute;c bệnh cảm c&uacute;m, ho.</li>\n	<li><strong>Hỗ trợ sức khỏe mắt:</strong>&nbsp;Vitamin A trong Dưa lưới biển Hằng Nga Danny Green Organic gi&uacute;p hỗ trợ sức khỏe mắt, giảm nguy cơ mắc c&aacute;c bệnh về mắt như đục thủy tinh thể, tho&aacute;i h&oacute;a điểm v&agrave;ng.</li>\n	<li><strong>Điều h&ograve;a huyết &aacute;p:</strong>&nbsp;Kali trong Dưa lưới biển Hằng Nga Danny Green Organic gi&uacute;p điều h&ograve;a huyết &aacute;p, giảm nguy cơ mắc c&aacute;c bệnh tim mạch.</li>\n	<li><strong>Hỗ trợ hệ cơ bắp v&agrave; xương khớp:</strong>&nbsp;Magie trong Dưa lưới biển Hằng Nga Danny Green Organic gi&uacute;p hỗ trợ hệ cơ bắp v&agrave; xương khớp, giảm nguy cơ lo&atilde;ng xương v&agrave; chuột r&uacute;t.</li>\n	<li><strong>L&agrave;m đẹp da:</strong>&nbsp;Vitamin C v&agrave; chất chống oxy h&oacute;a trong Dưa lưới biển Hằng Nga Danny Green Organic gi&uacute;p bảo vệ da khỏi t&aacute;c hại của &aacute;nh nắng mặt trời, l&agrave;m giảm nếp nhăn v&agrave; tăng độ đ&agrave;n hồi cho da.</li>\n</ul>\n\n<p><strong>- Bảo quản dưa lưới :</strong></p>\n\n<ul>\n	<li>N&ecirc;n bảo quản dưa lưới ở nơi kh&ocirc; r&aacute;o, tho&aacute;ng m&aacute;t, tr&aacute;nh &aacute;nh nắng trực tiếp.</li>\n	<li>C&oacute; thể bảo quản dưa lưới trong tủ lạnh ở nhiệt độ từ 0&deg;C đến 4&deg;C để giữ được độ tươi ngon l&acirc;u hơn.</li>\n</ul>\n\n<p>-&nbsp;<strong>C&aacute;ch lựa chọn dưa lưới&nbsp;ngon:</strong></p>\n\n<ul>\n	<li>N&ecirc;n chọn những quả dưa lưới c&oacute; h&igrave;nh dạng tr&ograve;n đều, hai đầu c&acirc;n đối, kh&ocirc;ng bị m&eacute;o m&oacute;.</li>\n	<li>Với Farmers Market ti&ecirc;u ch&iacute; h&agrave;ng đầu l&agrave; sản phảm đạt chất lượng cao, nguồn gốc r&otilde; r&agrave;ng, mang đến trải nghiệm tốt nhất cho kh&aacute;ch h&agrave;ng.&nbsp;</li>\n</ul>\n\n<p><strong>-&nbsp; Phụ nữ mang thai v&agrave; trẻ em ăn dưa lưới&nbsp;c&oacute; được kh&ocirc;ng?</strong></p>\n\n<p><strong>+ Phụ nữ mang thai:</strong></p>\n\n<p><strong>C&oacute; thể ăn:</strong>&nbsp;Dưa lưới l&agrave; nguồn cung cấp vitamin C, A, kali v&agrave; chất xơ dồi d&agrave;o, tốt cho sức khỏe của phụ nữ mang thai. Vitamin C gi&uacute;p tăng cường hệ miễn dịch, vitamin A hỗ trợ ph&aacute;t triển thị gi&aacute;c cho thai nhi, kali gi&uacute;p điều h&ograve;a huyết &aacute;p, v&agrave; chất xơ gi&uacute;p ngăn ngừa t&aacute;o b&oacute;n.</p>\n\n<p><strong>Lưu &yacute;:</strong></p>\n\n<ul>\n	<li>Gọt vỏ dưa lưới trước khi ăn</li>\n	<li>N&ecirc;n ăn dưa lưới với lượng vừa phải, khoảng 1-2 quả mỗi ng&agrave;y.</li>\n	<li>Tr&aacute;nh ăn dưa lưới khi đang bị ti&ecirc;u chảy hoặc đầy bụng.</li>\n	<li>Dưa lưới c&oacute; t&iacute;nh h&agrave;n, do vậy những người c&oacute; cơ địa n&oacute;ng, hay bị n&oacute;ng trong người n&ecirc;n hạn chế ăn.</li>\n	<li>Kh&ocirc;ng n&ecirc;n ăn dưa lưới c&ugrave;ng với c&aacute;c thực phẩm c&oacute; t&iacute;nh h&agrave;n như cua, ốc, hải sản,...</li>\n</ul>\n\n<p><strong>+ Trẻ em:</strong></p>\n\n<p><strong>C&oacute; thể ăn:</strong>&nbsp;Dưa lưới l&agrave; nguồn cung cấp vitamin C, A, kali v&agrave; chất xơ dồi d&agrave;o, tốt cho sự ph&aacute;t triển của trẻ em. Vitamin C gi&uacute;p tăng cường hệ miễn dịch, vitamin A hỗ trợ ph&aacute;t triển thị gi&aacute;c, kali gi&uacute;p điều h&ograve;a huyết &aacute;p, v&agrave; chất xơ gi&uacute;p hỗ trợ hệ ti&ecirc;u h&oacute;a.</p>\n\n<p><strong>Lưu &yacute;:</strong></p>\n\n<ul>\n	<li>Trẻ em dưới 1 tuổi n&ecirc;n tham khảo &yacute; kiến b&aacute;c sĩ trước khi ăn dưa lưới.</li>\n	<li>Cắt dưa lưới th&agrave;nh từng miếng nhỏ để trẻ dễ ăn v&agrave; tr&aacute;nh nguy cơ h&oacute;c.</li>\n	<li>Cho trẻ ăn dưa lưới với lượng vừa phải, khoảng 1/2 quả mỗi ng&agrave;y.</li>\n</ul>\n', 'Trái cây Việt', 220800, 1, '2024-07-05', 7, '5ff4078a233b4d4fc8b5420f9f8e27e9', 'nmfubuacoosr8d4hjnj7', 3, 3, 1),
(33, 'Ổi sạch Phúc Lộc Thọ ', '<h1><strong>Ổi sạch Ph&uacute;c Lộc Thọ</strong></h1>\n\n<p><strong>- Xuất xứ:&nbsp;</strong>Việt Nam</p>\n\n<p><strong>- Ti&ecirc;u chuẩn chất lượng:</strong>&nbsp;Tr&aacute;i c&acirc;y sạch</p>\n\n<p><strong>- Quy c&aacute;ch:&nbsp;</strong>1Kg</p>\n\n<p><strong>- Đặc điểm :</strong></p>\n\n<ul>\n	<li>Đạt ti&ecirc;u chuẩn hữu cơ Vietgap.&nbsp;</li>\n	<li>L&agrave; một trong những giống ổi ngon nhất c&oacute; xuất xứ giống từ Đ&agrave;i Loan.&nbsp;</li>\n	<li>Ổi tr&aacute;i to, phần thịt quả kh&aacute; d&agrave;y, m&ugrave;i thơm m&aacute;t, rất gi&ograve;n v&agrave; ngọt.&nbsp;</li>\n	<li>Ăn ổi c&oacute; t&aacute;c dụng nhuận tr&agrave;ng, cải thiện sức khỏe tim mạch, giảm c&acirc;n, chống ung thư.&nbsp;</li>\n	<li>Được tuyển chọn khắt khe, đảm bảo vệ sinh thực phẩm.</li>\n</ul>\n\n<p><strong>Ổi Sạch Ph&uacute;c Lộc Thọ Vietgap</strong>&nbsp;l&agrave; một trong những giống ổi ngon nhất c&oacute; xuất xứ giống từ Đ&agrave;i Loan. Ổi được trồng tại Long an theo hướng ho&agrave;n to&agrave;n hữu cơ đạt chuẩn Vietgap, tr&aacute;i to, phần thịt quả kh&aacute; d&agrave;y, m&ugrave;i thơm m&aacute;t, rất gi&ograve;n v&agrave; ngọt. Đ&acirc;y l&agrave; những tr&aacute;i ổi được tuyển chọn khắt khe, đảm bảo vệ sinh thực phẩm cho người ti&ecirc;u d&ugrave;ng.&nbsp;</p>\n\n<p><strong>- Lợi &iacute;ch của Ổi:</strong></p>\n\n<ul>\n	<li><strong>Gi&agrave;u vitamin C:</strong>&nbsp;Ổi Nữ Ho&agrave;ng l&agrave; nguồn cung cấp dồi d&agrave;o vitamin C, gi&uacute;p tăng cường hệ miễn dịch, chống lại c&aacute;c bệnh cảm c&uacute;m, ho.</li>\n	<li><strong>Gi&agrave;u chất xơ:</strong>&nbsp;Ổi Nữ Ho&agrave;ng chứa nhiều chất xơ, gi&uacute;p hỗ trợ hệ ti&ecirc;u h&oacute;a, ngăn ngừa t&aacute;o b&oacute;n.</li>\n	<li><strong>Gi&agrave;u kali:</strong>&nbsp;Ổi Nữ Ho&agrave;ng chứa nhiều kali, gi&uacute;p điều h&ograve;a huyết &aacute;p, giảm nguy cơ mắc c&aacute;c bệnh tim mạch.</li>\n	<li><strong>Gi&agrave;u vitamin A:</strong>&nbsp;Ổi Nữ Ho&agrave;ng chứa nhiều vitamin A, gi&uacute;p bảo vệ mắt, ngăn ngừa c&aacute;c bệnh về mắt như đục thủy tinh thể, tho&aacute;i h&oacute;a điểm v&agrave;ng.</li>\n	<li><strong>Gi&uacute;p giảm c&acirc;n:</strong>&nbsp;Chất xơ trong Ổi Nữ Ho&agrave;ng gi&uacute;p tạo cảm gi&aacute;c no l&acirc;u, hỗ trợ giảm c&acirc;n hiệu quả.</li>\n	<li><strong>L&agrave;m đẹp da:</strong>&nbsp;Vitamin C v&agrave; chất chống oxy h&oacute;a trong Ổi Nữ Ho&agrave;ng gi&uacute;p bảo vệ da khỏi t&aacute;c hại của &aacute;nh nắng mặt trời, l&agrave;m giảm nếp nhăn v&agrave; tăng độ đ&agrave;n hồi cho da.</li>\n</ul>\n\n<p><strong>- C&aacute;ch sử dụng Ổi:</strong></p>\n\n<p>Ổi&nbsp;c&oacute; thể ăn tươi, l&agrave;m sinh tố, salad, hoặc chế biến th&agrave;nh c&aacute;c m&oacute;n ăn kh&aacute;c nhau như b&aacute;nh, mứt, ...</p>\n\n<p><strong>- C&aacute;ch lựa chọn ổi ngon</strong></p>\n\n<ul>\n	<li>N&ecirc;n chọn mua Ổi c&oacute; vỏ m&agrave;u xanh v&agrave;ng, b&oacute;ng mượt, kh&ocirc;ng bị dập n&aacute;t hoặc hư hỏng.</li>\n	<li>Với Farmers Market ti&ecirc;u ch&iacute; h&agrave;ng đầu l&agrave; sản phảm đạt chất lượng cao, nguồn gốc r&otilde; r&agrave;ng, mang đến trải nghiệm tốt nhất cho kh&aacute;ch h&agrave;ng.&nbsp;</li>\n</ul>\n', 'Trái cây Việt', 14400, 1, '2024-07-05', 7, 'd4a4ce65ddb34a4a13018ac016a66acf', 'dnv0jmj2sn4jh3acvit7', 3, 1, 1);
INSERT INTO `products` (`id`, `nameOfProduct`, `description`, `category`, `price`, `weightDefault`, `dateOfImporting`, `expriredDay`, `imgAssetId`, `imgPublicId`, `adminCreate`, `provider`, `status`) VALUES
(34, 'Xoài cát hòa lộc', '<h1><strong>Xo&agrave;i C&aacute;t Ho&agrave; Lộc</strong></h1>\n\n<p><strong>-&nbsp;Xuất xứ:&nbsp;</strong>Việt Nam</p>\n\n<p><strong>- Quy c&aacute;ch:</strong>&nbsp;1kg</p>\n\n<p><strong>- Đặc điểm</strong></p>\n\n<ul>\n	<li>Xo&agrave;i C&aacute;t H&ograve;a Lộc tr&aacute;i to, chắc tay khoảng 500G/tr&aacute;i.</li>\n	<li>Thịt d&agrave;y, v&agrave;ng ươm, ngọt thơm, đậm vị, kh&ocirc;ng xơ hoặc rất &iacute;t.</li>\n	<li>H&agrave;ng tuyển chọn, nhập tận vườn miền T&acirc;y.&nbsp;Đảm bảo đem đến nguồn tr&aacute;i c&acirc;y sạch v&agrave; chất lượng tuyệt vời khi đến tay người ti&ecirc;u d&ugrave;ng.</li>\n	<li><strong>H&igrave;nh d&aacute;ng:</strong>&nbsp;Xo&agrave;i C&aacute;t H&ograve;a Lộc c&oacute; h&igrave;nh thon d&agrave;i, hơi cong, phần cuống bầu tr&ograve;n, đu&ocirc;i tr&aacute;i nhọn.</li>\n	<li><strong>Vỏ:</strong>&nbsp;Vỏ xo&agrave;i C&aacute;t H&ograve;a Lộc khi ch&iacute;n c&oacute; m&agrave;u v&agrave;ng nhạt, vỏ mỏng, mịn v&agrave; dễ b&oacute;c.</li>\n	<li><strong>Thịt:</strong>&nbsp;Thịt xo&agrave;i C&aacute;t H&ograve;a Lộc c&oacute; m&agrave;u v&agrave;ng cam, d&agrave;y, chắc, mịn v&agrave; &iacute;t xơ.</li>\n	<li><strong>Hương vị:</strong>&nbsp;Xo&agrave;i C&aacute;t H&ograve;a Lộc c&oacute; hương vị thơm ngon, ngọt thanh, b&eacute;o ngậy v&agrave; c&oacute; ch&uacute;t vị chua nhẹ.</li>\n	<li><strong>Hạt:</strong>&nbsp;Hạt xo&agrave;i C&aacute;t H&ograve;a Lộc nhỏ, dẹt v&agrave; c&oacute; m&agrave;u n&acirc;u sẫm.</li>\n</ul>\n\n<p><strong>- Gi&aacute; trị dinh dưỡng của Xo&agrave;i C&aacute;t H&ograve;a Lộc:</strong></p>\n\n<p>Xo&agrave;i C&aacute;t H&ograve;a Lộc l&agrave; nguồn cung cấp dồi d&agrave;o vitamin C, vitamin A, kali, magie v&agrave; c&aacute;c kho&aacute;ng chất thiết yếu kh&aacute;c cho cơ thể.</p>\n\n<ul>\n	<li><strong>Vitamin C:</strong>&nbsp;Gi&uacute;p tăng cường hệ miễn dịch, chống oxy h&oacute;a v&agrave; bảo vệ cơ thể khỏi c&aacute;c bệnh tật.</li>\n	<li><strong>Vitamin A:</strong>&nbsp;Gi&uacute;p hỗ trợ sức khỏe mắt, da v&agrave; hệ miễn dịch.</li>\n	<li><strong>Kali:</strong>&nbsp;Gi&uacute;p điều h&ograve;a huyết &aacute;p v&agrave; duy tr&igrave; chức năng tim mạch.</li>\n	<li><strong>Magie:</strong>&nbsp;Gi&uacute;p hỗ trợ hệ cơ bắp v&agrave; xương khớp.</li>\n</ul>\n\n<p><strong>- Lợi &iacute;ch sức khỏe của Xo&agrave;i C&aacute;t H&ograve;a Lộc:</strong></p>\n\n<ul>\n	<li><strong>Tăng cường hệ miễn dịch:</strong>&nbsp;Vitamin C trong Xo&agrave;i C&aacute;t H&ograve;a Lộc gi&uacute;p tăng cường hệ miễn dịch, chống lại c&aacute;c bệnh cảm c&uacute;m, ho.</li>\n	<li><strong>Hỗ trợ sức khỏe mắt:</strong>&nbsp;Vitamin A trong Xo&agrave;i C&aacute;t H&ograve;a Lộc gi&uacute;p hỗ trợ sức khỏe mắt, giảm nguy cơ mắc c&aacute;c bệnh về mắt như đục thủy tinh thể, tho&aacute;i h&oacute;a điểm v&agrave;ng.</li>\n	<li><strong>Điều h&ograve;a huyết &aacute;p:</strong>&nbsp;Kali trong Xo&agrave;i C&aacute;t H&ograve;a Lộc gi&uacute;p điều h&ograve;a huyết &aacute;p v&agrave; duy tr&igrave; chức năng tim mạch.</li>\n	<li><strong>Hỗ trợ hệ cơ bắp v&agrave; xương khớp:</strong>&nbsp;Magie trong Xo&agrave;i C&aacute;t H&ograve;a Lộc gi&uacute;p hỗ trợ hệ cơ bắp v&agrave; xương khớp.</li>\n	<li><strong>L&agrave;m đẹp da:</strong>&nbsp;Vitamin C v&agrave; chất chống oxy h&oacute;a trong Xo&agrave;i C&aacute;t H&ograve;a Lộc gi&uacute;p bảo vệ da khỏi t&aacute;c hại của &aacute;nh nắng mặt trời, l&agrave;m giảm nếp nhăn v&agrave; tăng độ đ&agrave;n hồi cho da.</li>\n	<li><strong>Hỗ trợ ti&ecirc;u h&oacute;a:</strong>&nbsp;Chất xơ trong Xo&agrave;i C&aacute;t H&ograve;a Lộc gi&uacute;p hỗ trợ ti&ecirc;u h&oacute;a, ngăn ngừa t&aacute;o b&oacute;n.</li>\n</ul>\n\n<p><strong>- C&aacute;ch lựa chọn xo&agrave;i ngon:</strong></p>\n\n<ul>\n	<li>N&ecirc;n chọn mua Xo&agrave;i C&aacute;t H&ograve;a Lộc c&oacute; vỏ m&agrave;u v&agrave;ng nhạt, vỏ mỏng, mịn v&agrave; dễ b&oacute;c.</li>\n	<li>Tr&aacute;nh mua những quả xo&agrave;i bị dập n&aacute;t, vỏ bị th&acirc;m hoặc c&oacute; m&ugrave;i h&ocirc;i.</li>\n	<li>Với Farmers Market ti&ecirc;u ch&iacute; h&agrave;ng đầu l&agrave; sản phảm đạt chất lượng cao, nguồn gốc r&otilde; r&agrave;ng, mang đến trải nghiệm tốt nhất cho kh&aacute;ch h&agrave;ng.&nbsp;</li>\n</ul>\n\n<p><strong>- C&aacute;ch bảo quản xo&agrave;i được l&acirc;u:</strong></p>\n\n<p><strong>+ Bảo quản ở nơi tho&aacute;ng m&aacute;t:</strong></p>\n\n<ul>\n	<li>N&ecirc;n bảo quản xo&agrave;i ở nơi tho&aacute;ng m&aacute;t, tr&aacute;nh &aacute;nh nắng trực tiếp.</li>\n	<li>Bạn c&oacute; thể để xo&agrave;i trong rổ hoặc hộp c&oacute; lỗ th&ocirc;ng hơi để đảm bảo lưu th&ocirc;ng kh&iacute; tốt.</li>\n	<li>Hạn chế chồng chất xo&agrave;i l&ecirc;n nhau v&igrave; c&oacute; thể l&agrave;m dập n&aacute;t quả.</li>\n	<li>N&ecirc;n xếp xo&agrave;i th&agrave;nh từng lớp ri&ecirc;ng biệt.</li>\n</ul>\n\n<p><strong>+ Bảo quản trong tủ lạnh:</strong></p>\n\n<ul>\n	<li>Xo&agrave;i c&oacute; thể được bảo quản trong tủ lạnh để giữ được độ tươi ngon l&acirc;u hơn.</li>\n	<li>Chuẩn bị: Rửa sạch xo&agrave;i, để r&aacute;o nước. Sau đ&oacute;, cho xo&agrave;i v&agrave;o hộp k&iacute;n hoặc t&uacute;i nilon c&oacute; đục lỗ để đảm bảo lưu th&ocirc;ng kh&iacute;.</li>\n	<li>Nhiệt độ: N&ecirc;n bảo quản xo&agrave;i ở ngăn m&aacute;t tủ lạnh với nhiệt độ từ 8-10 độ C.</li>\n	<li>Thời gian bảo quản: Xo&agrave;i bảo quản trong tủ lạnh c&oacute; thể giữ được độ tươi ngon trong khoảng 2-3 tuần.</li>\n</ul>\n', 'Trái cây Việt', 34500, 1, '2024-07-05', 7, '3563e433e1e92900683124020e893ce0', 'ad5b7te2bsnoejz6ulxc', 3, 2, 1),
(35, 'Dưa lưới giòn Huỳnh Long', '<h1><strong>Dưa lưới gi&ograve;n Huỳnh Long</strong></h1>\n\n<p><strong>- Xuất xứ:</strong>&nbsp;Việt Nam</p>\n\n<p>-&nbsp;<strong>Ti&ecirc;u chuẩn chất lượng:&nbsp;</strong>Tr&aacute;i c&acirc;y sạch</p>\n\n<p><strong>-&nbsp;Trọng lượng:</strong>&nbsp;1.2kg-1.8kg/Tr&aacute;i</p>\n\n<p><strong>- Đặc điểm nổi bật:&nbsp;</strong></p>\n\n<ul>\n	<li>\n	<p>Tr&aacute;i c&oacute; bề ngo&agrave;i h&igrave;nh bầu dục, tr&aacute;i non m&agrave;u xanh khi ch&iacute;n chuyển sang m&agrave;u v&agrave;ng rất bắt mắt v&agrave; c&oacute; v&acirc;n lưới nhẹ.</p>\n	</li>\n	<li>\n	<p>Thịt d&agrave;y m&agrave;u v&agrave;ng cam, c&oacute; hương thơm đặc trưng, c&oacute; độ ngọt cao nhất, gi&ograve;n, nhiều nước chứ kh&ocirc;ng gi&ograve;n kh&ocirc; như kh&aacute;c loại dưa lưới th&ocirc;ng thường</p>\n	</li>\n</ul>\n\n<p><strong>- Gi&aacute; trị dinh dưỡng:</strong></p>\n\n<p>Dưa lưới gi&ograve;n Huỳnh Long l&agrave; nguồn cung cấp dồi d&agrave;o vitamin C, vitamin A, kali v&agrave; chất xơ.</p>\n\n<ul>\n	<li><strong>Vitamin C:</strong>&nbsp;Gi&uacute;p tăng cường hệ miễn dịch, chống lại c&aacute;c bệnh cảm c&uacute;m, ho.</li>\n	<li><strong>Vitamin A:</strong>&nbsp;Tốt cho mắt, ngăn ngừa c&aacute;c bệnh về mắt như đục thủy tinh thể, tho&aacute;i h&oacute;a điểm v&agrave;ng.</li>\n	<li><strong>Kali:</strong>&nbsp;Điều h&ograve;a huyết &aacute;p, giảm nguy cơ mắc c&aacute;c bệnh tim mạch.</li>\n	<li><strong>Chất xơ:</strong>&nbsp;Hỗ trợ hệ ti&ecirc;u h&oacute;a, ngăn ngừa t&aacute;o b&oacute;n.</li>\n</ul>\n\n<p><strong>- Lợi &iacute;ch sức khỏe:</strong></p>\n\n<ul>\n	<li><strong>Tăng cường hệ miễn dịch:</strong>&nbsp;Vitamin C trong dưa lưới gi&ograve;n Huỳnh Long gi&uacute;p tăng cường hệ miễn dịch, chống lại c&aacute;c bệnh cảm c&uacute;m, ho.</li>\n	<li><strong>Hỗ trợ sức khỏe tim mạch:</strong>&nbsp;Kali trong dưa lưới gi&ograve;n Huỳnh Long gi&uacute;p điều h&ograve;a huyết &aacute;p, giảm nguy cơ mắc c&aacute;c bệnh tim mạch.</li>\n	<li><strong>Tốt cho mắt:</strong>&nbsp;Vitamin A trong dưa lưới gi&ograve;n Huỳnh Long gi&uacute;p bảo vệ mắt, ngăn ngừa c&aacute;c bệnh về mắt như đục thủy tinh thể, tho&aacute;i h&oacute;a điểm v&agrave;ng.</li>\n	<li><strong>Hỗ trợ hệ ti&ecirc;u h&oacute;a:</strong>&nbsp;Chất xơ trong dưa lưới gi&ograve;n Huỳnh Long gi&uacute;p hỗ trợ hệ ti&ecirc;u h&oacute;a, ngăn ngừa t&aacute;o b&oacute;n.</li>\n	<li><strong>L&agrave;m đẹp da:</strong>&nbsp;Vitamin C v&agrave; chất chống oxy h&oacute;a trong dưa lưới gi&ograve;n Huỳnh Long gi&uacute;p bảo vệ da khỏi t&aacute;c hại của &aacute;nh nắng mặt trời, l&agrave;m giảm nếp nhăn v&agrave; tăng độ đ&agrave;n hồi cho da.</li>\n	<li><strong>Hỗ trợ giảm c&acirc;n:</strong>&nbsp;Chất xơ trong dưa lưới gi&ograve;n Huỳnh Long gi&uacute;p tạo cảm gi&aacute;c no l&acirc;u, hỗ trợ giảm c&acirc;n hiệu quả.</li>\n</ul>\n\n<p><strong>- Bảo quản dưa lưới :</strong></p>\n\n<ul>\n	<li>N&ecirc;n bảo quản dưa lưới ở nơi kh&ocirc; r&aacute;o, tho&aacute;ng m&aacute;t, tr&aacute;nh &aacute;nh nắng trực tiếp.</li>\n	<li>C&oacute; thể bảo quản dưa lưới trong tủ lạnh ở nhiệt độ từ 0&deg;C đến 4&deg;C để giữ được độ tươi ngon l&acirc;u hơn.</li>\n</ul>\n\n<p>-&nbsp;<strong>C&aacute;ch lựa chọn dưa lưới&nbsp;ngon:</strong></p>\n\n<ul>\n	<li>N&ecirc;n chọn những quả dưa lưới c&oacute; h&igrave;nh dạng tr&ograve;n đều, hai đầu c&acirc;n đối, kh&ocirc;ng bị m&eacute;o m&oacute;.</li>\n	<li>Với Farmers Market ti&ecirc;u ch&iacute; h&agrave;ng đầu l&agrave; sản phảm đạt chất lượng cao, nguồn gốc r&otilde; r&agrave;ng, mang đến trải nghiệm tốt nhất cho kh&aacute;ch h&agrave;ng.&nbsp;</li>\n</ul>\n\n<p><strong>-&nbsp; Phụ nữ mang thai v&agrave; trẻ em ăn dưa lưới&nbsp;c&oacute; được kh&ocirc;ng?</strong></p>\n\n<p><strong>+ Phụ nữ mang thai:</strong></p>\n\n<p><strong>C&oacute; thể ăn:</strong>&nbsp;Dưa lưới l&agrave; nguồn cung cấp vitamin C, A, kali v&agrave; chất xơ dồi d&agrave;o, tốt cho sức khỏe của phụ nữ mang thai. Vitamin C gi&uacute;p tăng cường hệ miễn dịch, vitamin A hỗ trợ ph&aacute;t triển thị gi&aacute;c cho thai nhi, kali gi&uacute;p điều h&ograve;a huyết &aacute;p, v&agrave; chất xơ gi&uacute;p ngăn ngừa t&aacute;o b&oacute;n.</p>\n\n<p><strong>Lưu &yacute;:</strong></p>\n\n<ul>\n	<li>Gọt vỏ dưa lưới trước khi ăn</li>\n	<li>N&ecirc;n ăn dưa lưới với lượng vừa phải, khoảng 1-2 quả mỗi ng&agrave;y.</li>\n	<li>Tr&aacute;nh ăn dưa lưới khi đang bị ti&ecirc;u chảy hoặc đầy bụng.</li>\n	<li>Dưa lưới c&oacute; t&iacute;nh h&agrave;n, do vậy những người c&oacute; cơ địa n&oacute;ng, hay bị n&oacute;ng trong người n&ecirc;n hạn chế ăn.</li>\n	<li>Kh&ocirc;ng n&ecirc;n ăn dưa lưới c&ugrave;ng với c&aacute;c thực phẩm c&oacute; t&iacute;nh h&agrave;n như cua, ốc, hải sản,...</li>\n</ul>\n\n<p><strong>+ Trẻ em:</strong></p>\n\n<p><strong>C&oacute; thể ăn:</strong>&nbsp;Dưa lưới l&agrave; nguồn cung cấp vitamin C, A, kali v&agrave; chất xơ dồi d&agrave;o, tốt cho sự ph&aacute;t triển của trẻ em. Vitamin C gi&uacute;p tăng cường hệ miễn dịch, vitamin A hỗ trợ ph&aacute;t triển thị gi&aacute;c, kali gi&uacute;p điều h&ograve;a huyết &aacute;p, v&agrave; chất xơ gi&uacute;p hỗ trợ hệ ti&ecirc;u h&oacute;a.</p>\n\n<p><strong>Lưu &yacute;:</strong></p>\n\n<ul>\n	<li>Trẻ em dưới 1 tuổi n&ecirc;n tham khảo &yacute; kiến b&aacute;c sĩ trước khi ăn dưa lưới.</li>\n	<li>Cắt dưa lưới th&agrave;nh từng miếng nhỏ để trẻ dễ ăn v&agrave; tr&aacute;nh nguy cơ h&oacute;c.</li>\n	<li>Cho trẻ ăn dưa lưới với lượng vừa phải, khoảng 1/2 quả mỗi ng&agrave;y.</li>\n</ul>\n', 'Trái cây Việt', 342000, 1, '2024-07-05', 7, '3490dc447ac22c51cd1af153e96c1cd4', 'ilhejyusyu9hw59k8rxe', 3, 3, 1),
(36, 'Sầu riêng Ri6 loại 2', '<h1><strong>Sầu ri&ecirc;ng Ri6&nbsp;</strong></h1>\n\n<p><strong>- Xu&aacute;t xứ:&nbsp;</strong>Việt Nam</p>\n\n<p>-&nbsp;<strong>Ti&ecirc;u chuẩn chất lượng:&nbsp;</strong>Tr&aacute;i c&acirc;y sạch</p>\n\n<p>-&nbsp;<strong>Trọng lượng:&nbsp;</strong>(1.5 - 2.2Kg/Tr&aacute;i)</p>\n\n<p><strong>- Đặc Điểm:</strong></p>\n\n<ul>\n	<li>Sầu ri&ecirc;ng c&oacute; m&ugrave;i đậm v&agrave; vị ngọt b&eacute;o đặc trưng.</li>\n	<li>Cơm d&agrave;y, v&agrave;ng v&agrave; dẻo hơn.</li>\n	<li>Thường được ăn trực tiếp hoặc l&agrave;m kem, b&aacute;nh.</li>\n</ul>\n\n<p><strong>Sầu Ri&ecirc;ng</strong>&nbsp;c&oacute; một m&ugrave;i hương độc đ&aacute;o, lưu lại trong thời gian d&agrave;i v&agrave; lan nhanh trong diện rộng. C&oacute; nhiều người sẽ kh&ocirc;ng th&iacute;ch m&ugrave;i thơm của sầu ri&ecirc;ng, nhưng đến khi quen rồi, bạn sẽ cảm thấy &quot;nghiện&quot;. Sầu ri&ecirc;ng Ri6 được trồng v&agrave; thu hoạch theo ti&ecirc;u chuẩn an to&agrave;n thực phẩm, c&oacute; vị ngọt đậm, b&eacute;o, cơm dẻo, d&agrave;y v&agrave; v&agrave;ng. Người ta thường d&ugrave;ng sầu ri&ecirc;ng để ăn trực tiếp hoặc chế biến c&aacute;c m&oacute;n ăn vặt.</p>\n\n<p><strong>- Hướng dẫn sử dụng</strong></p>\n\n<ul>\n	<li>Để t&aacute;ch vỏ sầu ri&ecirc;ng, bạn cần x&aacute;c định phần m&uacute;i v&agrave; cắt từ đỉnh tới giữa m&uacute;i.&nbsp;</li>\n	<li>Bạn cắt dọc như vậy từ 2 đến 4 m&uacute;i của quả.&nbsp;</li>\n	<li>Tiếp theo, d&ugrave;ng tay t&aacute;ch quả sầu ri&ecirc;ng theo đường cắt.&nbsp;</li>\n	<li>Cuối c&ugrave;ng, lấy phần thịt bỏ v&agrave;o hộp hoặc đĩa rồi t&aacute;ch cho hết m&uacute;i.&nbsp;</li>\n</ul>\n\n<p><strong>- Hướng dẫn bảo quản</strong></p>\n\n<ul>\n	<li>Cần bảo vệ sầu ri&ecirc;ng ở nhiệt độ thấp, th&iacute;ch hợp để tr&aacute;nh t&igrave;nh trạng bị nứt vỏ, thối vỏ.</li>\n	<li>Thường nh&agrave; sản xuất để nhiệt độ th&iacute;ch hợp trong kho lạnh l&agrave; dưới 15 độ C</li>\n	<li>Đối với sầu ri&ecirc;ng nguy&ecirc;n quả th&igrave; khi chưa d&ugrave;ng ngay bạn n&ecirc;n để ở nơi tho&aacute;ng m&aacute;t, kh&ocirc; r&aacute;o</li>\n	<li>C&ograve;n nếu bạn mua sầu ri&ecirc;ng đ&atilde; được&nbsp;t&aacute;ch sẵn vỏ nhưng bạn chưa muốn ăn ngay th&igrave; c&aacute;ch bảo quản tốt nhất l&agrave; cho v&agrave;o hộp k&iacute;n, bọc cẩn thận rồi để v&agrave;o ngăn m&aacute;t của tủ lạnh khoảng 2 đến 3 ng&agrave;y. Hoặc c&oacute; thể để trong t&uacute;i h&uacute;t ch&acirc;n kh&ocirc;ng.</li>\n	<li>Tuy nhi&ecirc;n c&oacute; 1 điều bạn cũng cần ch&uacute; &yacute; đ&oacute; l&agrave; nếu như sầu ri&ecirc;ng đ&atilde; ch&iacute;n kỹ th&igrave; bạn n&ecirc;n để nhiệt độ b&ecirc;n ngo&agrave;i v&agrave; n&ecirc;n sử dụng</li>\n</ul>\n\n<p><strong><em>Lưu &yacute;:</em></strong></p>\n\n<ul>\n	<li>C&aacute;c bạn n&ecirc;n d&ugrave;ng c&agrave;ng sớm c&agrave;ng tốt bởi quả sẽ giữ được độ tươi ngon trong thời điểm đầu c&ograve;n khi để sang ng&agrave;y thứ 3 hoặc qu&aacute; l&acirc;u sầu ri&ecirc;ng sẽ bị ch&iacute;n qu&aacute; dẫn đến bị cay hoặc đắng khi ăn.</li>\n	<li>Nhiều gia đ&igrave;nh thường bảo quản sầu ri&ecirc;ng bằng c&aacute;ch bỏ nguy&ecirc;n m&uacute;i v&agrave;o ngăn đ&aacute; tủ lạnh, khi sử dụng th&igrave; chất lượng giảm đi rất nhiều, m&uacute;i nhũn nước v&agrave; h&oacute;a đen.</li>\n</ul>\n\n<p><strong>- Th&ocirc;ng tin dinh dưỡng:</strong></p>\n\n<ul>\n	<li>Sầu ri&ecirc;ng c&oacute; chứa một lượng vitamin B kh&aacute; cao, c&aacute;c vitamin B sẽ c&oacute; một loạt c&aacute;c lợi &iacute;ch cho sức khỏe như ngăn ngừa l&atilde;o h&oacute;a v&agrave; bệnh tim, gi&uacute;p tăng HDL (cholesterol tốt) v&agrave; thậm ch&iacute; c&oacute; thể gi&uacute;p cải thiện t&acirc;m trạng, giảm bớt trầm cảm.</li>\n	<li>Trong sầu ri&ecirc;ng chứa một lượng&nbsp;kali phong ph&uacute;,&nbsp;l&agrave; chất điện ph&acirc;n quan trọng gi&uacute;p kiểm so&aacute;t huyết &aacute;p, giữ cho nồng độ natri ở mức độ cho ph&eacute;p v&agrave; gi&uacute;p điều h&ograve;a nhịp tim.</li>\n	<li>Một trong những lợi &iacute;ch kh&aacute;c của sầu ri&ecirc;ng l&agrave; c&oacute; chứa h&agrave;m lượng chất xơ cao. Mỗi khẩu phần ăn n&ecirc;n cần chứa 9g chất xơ tương đương 37% nhu cầu h&agrave;ng ng&agrave;y của bạn, điều n&agrave;y thật kh&oacute; thực hiện khi hầu như loại vi chất n&agrave;y c&oacute; nhiều trong tr&aacute;i c&acirc;y hơn l&agrave; c&aacute;c bữa ăn. Ch&iacute;nh v&igrave; thế, nếu bạn cần hấp thụ một lượng chất xơ ổn định, th&igrave; sầu ri&ecirc;ng l&agrave; loại tr&aacute;i c&acirc;y tốt để bạn sử dụng.</li>\n	<li>S&acirc;u ri&ecirc;ng c&ograve;n l&agrave; loại tr&aacute;i c&acirc;y chứa nhiều vitamin C, gi&uacute;p tăng cường sức đề kh&aacute;ng, giảm cholesterol, hỗ trợ chữa l&agrave;nh vết thương.</li>\n</ul>\n', 'Trái cây Việt', 484000, 1, '2024-07-05', 7, 'ba6b130aa914d5d5e075622072c7a970', 'yonpnhylyrdsq0prpxvo', 3, 1, 1),
(37, 'Xoài cát hòa lộc mango king Chú Chín', '<h1><strong>Xo&agrave;i c&aacute;t h&ograve;a lộc mango king Ch&uacute; Ch&iacute;n</strong></h1>\n\n<p><strong>-&nbsp;Xuất xứ:&nbsp;</strong>Việt Nam</p>\n\n<p>-&nbsp;<strong>Ti&ecirc;u chuẩn chất lượng:&nbsp;</strong>Tr&aacute;i c&acirc;y sạch</p>\n\n<p><strong>- Quy c&aacute;ch:</strong>&nbsp;1kg</p>\n\n<p><strong>- Đặc điểm</strong></p>\n\n<ul>\n	<li>Xo&agrave;i C&aacute;t H&ograve;a Lộc tr&aacute;i to, chắc tay khoảng 500G/tr&aacute;i.</li>\n	<li>Thịt d&agrave;y, v&agrave;ng ươm, ngọt thơm, đậm vị, kh&ocirc;ng xơ hoặc rất &iacute;t.</li>\n	<li>H&agrave;ng tuyển chọn, nhập tận vườn miền T&acirc;y.&nbsp;Đảm bảo đem đến nguồn tr&aacute;i c&acirc;y sạch v&agrave; chất lượng tuyệt vời khi đến tay người ti&ecirc;u d&ugrave;ng.</li>\n	<li><strong>H&igrave;nh d&aacute;ng:</strong>&nbsp;Xo&agrave;i C&aacute;t H&ograve;a Lộc c&oacute; h&igrave;nh thon d&agrave;i, hơi cong, phần cuống bầu tr&ograve;n, đu&ocirc;i tr&aacute;i nhọn.</li>\n	<li><strong>Vỏ:</strong>&nbsp;Vỏ xo&agrave;i C&aacute;t H&ograve;a Lộc khi ch&iacute;n c&oacute; m&agrave;u v&agrave;ng nhạt, vỏ mỏng, mịn v&agrave; dễ b&oacute;c.</li>\n	<li><strong>Thịt:</strong>&nbsp;Thịt xo&agrave;i C&aacute;t H&ograve;a Lộc c&oacute; m&agrave;u v&agrave;ng cam, d&agrave;y, chắc, mịn v&agrave; &iacute;t xơ.</li>\n	<li><strong>Hương vị:</strong>&nbsp;Xo&agrave;i C&aacute;t H&ograve;a Lộc c&oacute; hương vị thơm ngon, ngọt thanh, b&eacute;o ngậy v&agrave; c&oacute; ch&uacute;t vị chua nhẹ.</li>\n	<li><strong>Hạt:</strong>&nbsp;Hạt xo&agrave;i C&aacute;t H&ograve;a Lộc nhỏ, dẹt v&agrave; c&oacute; m&agrave;u n&acirc;u sẫm.</li>\n</ul>\n\n<p><strong>- Gi&aacute; trị dinh dưỡng của Xo&agrave;i C&aacute;t H&ograve;a Lộc:</strong></p>\n\n<p>Xo&agrave;i C&aacute;t H&ograve;a Lộc l&agrave; nguồn cung cấp dồi d&agrave;o vitamin C, vitamin A, kali, magie v&agrave; c&aacute;c kho&aacute;ng chất thiết yếu kh&aacute;c cho cơ thể.</p>\n\n<ul>\n	<li><strong>Vitamin C:</strong>&nbsp;Gi&uacute;p tăng cường hệ miễn dịch, chống oxy h&oacute;a v&agrave; bảo vệ cơ thể khỏi c&aacute;c bệnh tật.</li>\n	<li><strong>Vitamin A:</strong>&nbsp;Gi&uacute;p hỗ trợ sức khỏe mắt, da v&agrave; hệ miễn dịch.</li>\n	<li><strong>Kali:</strong>&nbsp;Gi&uacute;p điều h&ograve;a huyết &aacute;p v&agrave; duy tr&igrave; chức năng tim mạch.</li>\n	<li><strong>Magie:</strong>&nbsp;Gi&uacute;p hỗ trợ hệ cơ bắp v&agrave; xương khớp.</li>\n</ul>\n\n<p><strong>- Lợi &iacute;ch sức khỏe của Xo&agrave;i C&aacute;t H&ograve;a Lộc:</strong></p>\n\n<ul>\n	<li><strong>Tăng cường hệ miễn dịch:</strong>&nbsp;Vitamin C trong Xo&agrave;i C&aacute;t H&ograve;a Lộc gi&uacute;p tăng cường hệ miễn dịch, chống lại c&aacute;c bệnh cảm c&uacute;m, ho.</li>\n	<li><strong>Hỗ trợ sức khỏe mắt:</strong>&nbsp;Vitamin A trong Xo&agrave;i C&aacute;t H&ograve;a Lộc gi&uacute;p hỗ trợ sức khỏe mắt, giảm nguy cơ mắc c&aacute;c bệnh về mắt như đục thủy tinh thể, tho&aacute;i h&oacute;a điểm v&agrave;ng.</li>\n	<li><strong>Điều h&ograve;a huyết &aacute;p:</strong>&nbsp;Kali trong Xo&agrave;i C&aacute;t H&ograve;a Lộc gi&uacute;p điều h&ograve;a huyết &aacute;p v&agrave; duy tr&igrave; chức năng tim mạch.</li>\n	<li><strong>Hỗ trợ hệ cơ bắp v&agrave; xương khớp:</strong>&nbsp;Magie trong Xo&agrave;i C&aacute;t H&ograve;a Lộc gi&uacute;p hỗ trợ hệ cơ bắp v&agrave; xương khớp.</li>\n	<li><strong>L&agrave;m đẹp da:</strong>&nbsp;Vitamin C v&agrave; chất chống oxy h&oacute;a trong Xo&agrave;i C&aacute;t H&ograve;a Lộc gi&uacute;p bảo vệ da khỏi t&aacute;c hại của &aacute;nh nắng mặt trời, l&agrave;m giảm nếp nhăn v&agrave; tăng độ đ&agrave;n hồi cho da.</li>\n	<li><strong>Hỗ trợ ti&ecirc;u h&oacute;a:</strong>&nbsp;Chất xơ trong Xo&agrave;i C&aacute;t H&ograve;a Lộc gi&uacute;p hỗ trợ ti&ecirc;u h&oacute;a, ngăn ngừa t&aacute;o b&oacute;n.</li>\n</ul>\n\n<p><strong>- C&aacute;ch lựa chọn xo&agrave;i ngon:</strong></p>\n\n<ul>\n	<li>N&ecirc;n chọn mua Xo&agrave;i C&aacute;t H&ograve;a Lộc c&oacute; vỏ m&agrave;u v&agrave;ng nhạt, vỏ mỏng, mịn v&agrave; dễ b&oacute;c.</li>\n	<li>Tr&aacute;nh mua những quả xo&agrave;i bị dập n&aacute;t, vỏ bị th&acirc;m hoặc c&oacute; m&ugrave;i h&ocirc;i.</li>\n	<li>Với Farmers Market ti&ecirc;u ch&iacute; h&agrave;ng đầu l&agrave; sản phảm đạt chất lượng cao, nguồn gốc r&otilde; r&agrave;ng, mang đến trải nghiệm tốt nhất cho kh&aacute;ch h&agrave;ng.&nbsp;</li>\n</ul>\n\n<p><strong>- C&aacute;ch bảo quản xo&agrave;i được l&acirc;u:</strong></p>\n\n<p><strong>+ Bảo quản ở nơi tho&aacute;ng m&aacute;t:</strong></p>\n\n<ul>\n	<li>N&ecirc;n bảo quản xo&agrave;i ở nơi tho&aacute;ng m&aacute;t, tr&aacute;nh &aacute;nh nắng trực tiếp.</li>\n	<li>Bạn c&oacute; thể để xo&agrave;i trong rổ hoặc hộp c&oacute; lỗ th&ocirc;ng hơi để đảm bảo lưu th&ocirc;ng kh&iacute; tốt.</li>\n	<li>Hạn chế chồng chất xo&agrave;i l&ecirc;n nhau v&igrave; c&oacute; thể l&agrave;m dập n&aacute;t quả.</li>\n	<li>N&ecirc;n xếp xo&agrave;i th&agrave;nh từng lớp ri&ecirc;ng biệt.</li>\n</ul>\n\n<p><strong>+ Bảo quản trong tủ lạnh:</strong></p>\n\n<ul>\n	<li>Xo&agrave;i c&oacute; thể được bảo quản trong tủ lạnh để giữ được độ tươi ngon l&acirc;u hơn.</li>\n	<li>Chuẩn bị: Rửa sạch xo&agrave;i, để r&aacute;o nước. Sau đ&oacute;, cho xo&agrave;i v&agrave;o hộp k&iacute;n hoặc t&uacute;i nilon c&oacute; đục lỗ để đảm bảo lưu th&ocirc;ng kh&iacute;.</li>\n	<li>Nhiệt độ: N&ecirc;n bảo quản xo&agrave;i ở ngăn m&aacute;t tủ lạnh với nhiệt độ từ 8-10 độ C.</li>\n	<li>Thời gian bảo quản: Xo&agrave;i bảo quản trong tủ lạnh c&oacute; thể giữ được độ tươi ngon trong khoảng 2-3 tuần.</li>\n</ul>\n', 'Trái cây Việt', 189000, 1, '2024-07-05', 7, 'c320aa3b6ede2596d08e9a6c54cf8167', 'oswkpib3cwsduv5tsptj', 3, 2, 1),
(38, 'Dưa lê hồng kim Danny Green', '<h1><strong>Dưa l&ecirc; hồng kim Danny Green</strong></h1>\n\n<p><strong>- Xuất xứ:</strong>&nbsp;Việt Nam</p>\n\n<p>-&nbsp;<strong>Ti&ecirc;u chuẩn chất lượng:&nbsp;</strong>Tr&aacute;i c&acirc;y sạch-&nbsp;<strong>Trọng lượng sản phẩm:&nbsp;</strong><strong>&nbsp;</strong>1.5Kg - 2Kg/Tr&aacute;i&nbsp;</p>\n\n<p><strong>-Đặc điểm nổi bật:&nbsp;</strong></p>\n\n<ul>\n	<li>B&ecirc;n ngo&agrave;i:&nbsp;tr&aacute;i h&igrave;nh elip, vỏ v&agrave;ng tươi.</li>\n	<li>B&ecirc;n trong: ruột trắng, hơi gi&ograve;n, vị ngọt thanh.</li>\n	<li><strong>H&igrave;nh dạng:</strong>&nbsp;Dưa l&ecirc; hồng kim Danny Green c&oacute; h&igrave;nh bầu dục, hơi thu&ocirc;n d&agrave;i.</li>\n	<li><strong>Vỏ:</strong>&nbsp;Vỏ dưa mỏng, m&agrave;u v&agrave;ng tươi khi ch&iacute;n.</li>\n	<li><strong>Thịt quả:</strong>&nbsp;Thịt quả d&agrave;y, m&agrave;u trắng hồng, &iacute;t xơ v&agrave; c&oacute; vị ngọt thanh, gi&ograve;n tan.</li>\n	<li><strong>Hạt:</strong>&nbsp;Hạt dưa nhỏ, m&agrave;u trắng v&agrave; c&oacute; thể ăn được.</li>\n</ul>\n\n<p><strong>- Gi&aacute; trị dinh dưỡng:</strong></p>\n\n<p>Dưa l&ecirc; hồng kim Danny Green l&agrave; nguồn cung cấp dồi d&agrave;o vitamin C, vitamin A, kali v&agrave; chất xơ.</p>\n\n<ul>\n	<li><strong>Vitamin C:</strong>&nbsp;Gi&uacute;p tăng cường hệ miễn dịch, chống lại c&aacute;c bệnh cảm c&uacute;m, ho.</li>\n	<li><strong>Vitamin A:</strong>&nbsp;Tốt cho mắt, ngăn ngừa c&aacute;c bệnh về mắt như đục thủy tinh thể, tho&aacute;i h&oacute;a điểm v&agrave;ng.</li>\n	<li><strong>Kali:</strong>&nbsp;Điều h&ograve;a huyết &aacute;p, giảm nguy cơ mắc c&aacute;c bệnh tim mạch.</li>\n	<li><strong>Chất xơ:</strong>&nbsp;Hỗ trợ hệ ti&ecirc;u h&oacute;a, ngăn ngừa t&aacute;o b&oacute;n.</li>\n</ul>\n\n<p><strong>- Lợi &iacute;ch sức khỏe:</strong></p>\n\n<ul>\n	<li><strong>Tăng cường hệ miễn dịch:</strong>&nbsp;Vitamin C trong dưa l&ecirc; hồng kim Danny Green gi&uacute;p tăng cường hệ miễn dịch, chống lại c&aacute;c bệnh cảm c&uacute;m, ho.</li>\n	<li><strong>Hỗ trợ sức khỏe tim mạch:</strong>&nbsp;Kali trong dưa l&ecirc; hồng kim Danny Green gi&uacute;p điều h&ograve;a huyết &aacute;p, giảm nguy cơ mắc c&aacute;c bệnh tim mạch.</li>\n	<li><strong>Tốt cho mắt:</strong>&nbsp;Vitamin A trong dưa l&ecirc; hồng kim Danny Green gi&uacute;p bảo vệ mắt, ngăn ngừa c&aacute;c bệnh về mắt như đục thủy tinh thể, tho&aacute;i h&oacute;a điểm v&agrave;ng.</li>\n	<li><strong>Hỗ trợ hệ ti&ecirc;u h&oacute;a:</strong>&nbsp;Chất xơ trong dưa l&ecirc; hồng kim Danny Green gi&uacute;p hỗ trợ hệ ti&ecirc;u h&oacute;a, ngăn ngừa t&aacute;o b&oacute;n.</li>\n	<li><strong>L&agrave;m đẹp da:</strong>&nbsp;Vitamin C v&agrave; chất chống oxy h&oacute;a trong dưa l&ecirc; hồng kim Danny Green gi&uacute;p bảo vệ da khỏi t&aacute;c hại của &aacute;nh nắng mặt trời, l&agrave;m giảm nếp nhăn v&agrave; tăng độ đ&agrave;n hồi cho da.</li>\n	<li><strong>Hỗ trợ giảm c&acirc;n:</strong>&nbsp;Chất xơ trong dưa l&ecirc; hồng kim Danny Green gi&uacute;p tạo cảm gi&aacute;c no l&acirc;u, hỗ trợ giảm c&acirc;n hiệu quả.</li>\n</ul>\n\n<p><strong>- C&aacute;ch sử dụng:</strong></p>\n\n<p>Dưa l&ecirc; hồng kim Danny Green c&oacute; thể ăn tươi, l&agrave;m sinh tố, salad, hoặc chế biến th&agrave;nh c&aacute;c m&oacute;n ăn kh&aacute;c nhau như b&aacute;nh, mứt, ...</p>\n\n<p>-&nbsp;<strong>C&aacute;ch lựa chọn dưa l&ecirc; ngon:</strong></p>\n\n<ul>\n	<li>N&ecirc;n chọn mua dưa l&ecirc; hồng kim Danny Green c&oacute; vỏ m&agrave;u v&agrave;ng tươi, b&oacute;ng mượt, kh&ocirc;ng bị dập n&aacute;t hoặc hư hỏng.</li>\n	<li>Với Farmers Market ti&ecirc;u ch&iacute; h&agrave;ng đầu l&agrave; sản phảm đạt chất lượng cao, nguồn gốc r&otilde; r&agrave;ng, mang đến trải nghiệm tốt nhất cho kh&aacute;ch h&agrave;ng.&nbsp;</li>\n</ul>\n\n<p><strong>- Mẹo bảo quản dưa l&ecirc; được l&acirc;u:</strong></p>\n\n<p><strong>+ Bảo quản ở nhiệt độ ph&ograve;ng:</strong></p>\n\n<ul>\n	<li><strong>C&aacute;ch thực hiện:</strong>\n\n	<ul>\n		<li>Sau khi mua dưa l&ecirc; về, bạn chỉ cần rửa sơ qua để loại bỏ những bụi bẩn b&aacute;m b&ecirc;n ngo&agrave;i lớp vỏ, rửa xong chờ r&aacute;o nước v&agrave; đặt dưa l&ecirc; v&agrave;o c&aacute;c rổ, r&aacute; thưa để dưa l&ecirc; được r&aacute;o nước ho&agrave;n to&agrave;n.</li>\n		<li>Để dưa l&ecirc; ở nơi kh&ocirc; r&aacute;o tho&aacute;ng kh&iacute; với nhiệt độ ph&ograve;ng b&igrave;nh thường, tr&aacute;nh &aacute;nh nắng trực tiếp từ mặt trời.</li>\n	</ul>\n	</li>\n	<li><strong>Ưu điểm:</strong>\n	<ul>\n		<li>Đơn giản, dễ thực hiện.</li>\n		<li>Giữ được độ tươi ngon của dưa l&ecirc; trong thời gian ngắn (khoảng 2-3 ng&agrave;y).</li>\n	</ul>\n	</li>\n	<li><strong>Nhược điểm:</strong>\n	<ul>\n		<li>Dưa l&ecirc; sẽ nhanh ch&iacute;n v&agrave; mềm hơn so với bảo quản trong tủ lạnh.</li>\n	</ul>\n	</li>\n</ul>\n\n<p><strong>+ Bảo quản trong tủ lạnh:</strong></p>\n\n<ul>\n	<li><strong>C&aacute;ch thực hiện:</strong>\n\n	<ul>\n		<li>Cho những quả dưa l&ecirc; v&agrave;o t&uacute;i Zip hoặc t&uacute;i nilon nhưng kh&ocirc;ng được bịt k&iacute;n miệng t&uacute;i, đảm bảo t&uacute;i c&oacute; c&aacute;c lỗ nhỏ để kh&ocirc;ng kh&iacute; được lưu th&ocirc;ng.</li>\n		<li>Tiếp theo bạn cho dưa l&ecirc; v&agrave;o ngăn m&aacute;t tủ lạnh để bảo quản, tr&aacute;nh đặt gần những loại tr&aacute;i c&acirc;y hoặc thực phẩm như thịt c&aacute; để k&eacute;o d&agrave;i thời gian bảo quản l&acirc;u hơn.</li>\n	</ul>\n	</li>\n	<li><strong>Ưu điểm:</strong>\n	<ul>\n		<li>Giữ được độ tươi ngon của dưa l&ecirc; trong thời gian l&acirc;u hơn (khoảng 5-7 ng&agrave;y).</li>\n		<li>Dưa l&ecirc; sẽ ch&iacute;n chậm hơn so với bảo quản ở nhiệt độ ph&ograve;ng.</li>\n	</ul>\n	</li>\n	<li><strong>Nhược điểm:</strong>\n	<ul>\n		<li>Cần c&oacute; tủ lạnh để bảo quản.</li>\n	</ul>\n	</li>\n</ul>\n\n<p><strong>+ Bảo quản dưa l&ecirc; đ&atilde; cắt:</strong></p>\n\n<ul>\n	<li><strong>C&aacute;ch thực hiện:</strong>\n\n	<ul>\n		<li>Cắt dưa l&ecirc; th&agrave;nh từng miếng vừa ăn, bọc k&iacute;n m&agrave;ng bọc thực phẩm hoặc cho v&agrave;o hộp đựng thực phẩm c&oacute; nắp đậy.</li>\n		<li>Bảo quản dưa l&ecirc; đ&atilde; cắt trong tủ lạnh ở nhiệt độ 0-4 độ C.</li>\n	</ul>\n	</li>\n	<li><strong>Ưu điểm:</strong>\n	<ul>\n		<li>Giữ được độ tươi ngon của dưa l&ecirc; đ&atilde; cắt trong thời gian ngắn (khoảng 1-2 ng&agrave;y).</li>\n	</ul>\n	</li>\n	<li><strong>Nhược điểm:</strong>\n	<ul>\n		<li>Dưa l&ecirc; đ&atilde; cắt sẽ nhanh bị mất nước v&agrave; vitamin hơn so với dưa l&ecirc; nguy&ecirc;n quả.</li>\n	</ul>\n	</li>\n</ul>\n\n<p><strong>Phụ nữ mang thai v&agrave; trẻ em ăn dưa l&ecirc;&nbsp;c&oacute; được kh&ocirc;ng?</strong></p>\n\n<ul>\n	<li>Dưa l&ecirc;&nbsp;l&agrave; nguồn cung cấp dồi d&agrave;o vitamin A, C, E v&agrave; c&aacute;c kho&aacute;ng chất như kali, magie, canxi,... rất tốt cho sức khỏe của phụ nữ mang thai, nhưng kh&ocirc;ng được ăn khi đang đ&oacute;i</li>\n	<li>Hệ ti&ecirc;u h&oacute;a của<strong>&nbsp;trẻ sơ sinh</strong>&nbsp;c&ograve;n non yếu,<strong>&nbsp;chưa đủ khả năng để ti&ecirc;u h&oacute;a dưa l&ecirc;</strong>. Ăn dưa l&ecirc;&nbsp;c&oacute; thể khiến trẻ bị đầy bụng, kh&oacute; ti&ecirc;u, ti&ecirc;u chảy,...</li>\n</ul>\n', 'Trái cây Việt', 280000, 1, '2024-07-05', 7, '4876e8c06e631f804f45097cc4a72a79', 'i3n5cb5yloywtse5d1m2', 3, 3, 1),
(39, 'Cherry đỏ Mỹ size 9', '<h1><strong>Cherry đỏ Mỹ size 9</strong></h1>\n\n<p><em><strong>Chất lượng : c&oacute; tỉ lệ dẻo tay, nhưng vị ngọt đậm, độ cứng tr&aacute;i tương đối</strong></em></p>\n\n<p><strong>- Xuất xứ:&nbsp;</strong>Mỹ</p>\n\n<p><strong>- Chất lượng:&nbsp;</strong>Nhập khẩu</p>\n\n<p><strong>- Đặc điểm nổi bật:</strong></p>\n\n<ul>\n	<li>Tr&aacute;i mọng nước, chắc thịt c&ugrave;ng vị chua dịu đặc trưng của d&ograve;ng cherry Mỹ rất ngon, đậm vị</li>\n	<li>M&agrave;u đỏ tươi, đỏ thẫm xen kẽ, cứng tr&aacute;i, cuống tươi, &iacute;t rụng</li>\n</ul>\n\n<p><strong>- Th&ocirc;ng tin dinh dưỡng:</strong></p>\n\n<ul>\n	<li>Quả cherry rất tốt cho những người bị cao huyết &aacute;p, gi&uacute;p bạn loại bỏ được mức độ cholesterol xấu trong cơ thể.</li>\n	<li>Ăn cherry thường xuy&ecirc;n gi&uacute;p cải thiện t&igrave;nh trạng mất ngủ của bạn.</li>\n	<li>Cherry l&agrave; thực phẩm tốt cho n&atilde;o bộ, tăng tr&iacute; nhớ bởi ch&uacute;ng chứa c&aacute;c chất chống &ocirc;xy h&oacute;a anthocyanin.</li>\n	<li>Trong loại tr&aacute;i c&acirc;y n&agrave;y c&oacute; chứa rất nhiều vitamin, đặc biệt l&agrave; vitamin A. C&aacute;c vitamin n&agrave;y kh&ocirc;ng chỉ gi&uacute;p tăng sức đề kh&aacute;ng m&agrave; c&ograve;n rất tốt cho mắt.</li>\n	<li>Vị ngọt của cherry ho&agrave;n to&agrave;n tự nhi&ecirc;n, chỉ số đường tương đối thấp, đem đến c&ocirc;ng dụng tuyệt vời cho việc điều trị tiểu đường.</li>\n</ul>\n\n<p><strong>- Hướng dẫn sử dụng:</strong></p>\n\n<ul>\n	<li>\n	<p>Rửa nhẹ nh&agrave;ng tr&aacute;i&nbsp;</p>\n	</li>\n	<li>\n	<p>Chỉ n&ecirc;n rửa một lượng vừa đủ ăn.</p>\n	</li>\n	<li>\n	<p>Cherry&nbsp;c&oacute; thể ăn trực tiếp, l&agrave;m nước &eacute;p, sinh tố, l&agrave;m b&aacute;nh</p>\n	</li>\n</ul>\n\n<p><strong>- C&aacute;ch lựa chọn:</strong></p>\n\n<ul>\n	<li>\n	<p>Kh&aacute;ch h&agrave;ng&nbsp;n&ecirc;n&nbsp;chọn những quả căng b&oacute;ng, c&oacute; m&agrave;u đỏ đều, kh&ocirc;ng bị dập n&aacute;t, thối rữa.</p>\n	</li>\n	<li>\n	<p>Với Farmers Market ti&ecirc;u ch&iacute; h&agrave;ng đầu l&agrave; sản phảm đạt chất lượng cao, nguồn gốc r&otilde; r&agrave;ng, mang đến trải nghiệm tốt nhất cho kh&aacute;ch h&agrave;ng.&nbsp;</p>\n	</li>\n</ul>\n\n<p>-&nbsp;<strong>C&aacute;ch bảo quản</strong><strong>:&nbsp;</strong></p>\n\n<p><strong>+ Bảo quản trong tủ lạnh:</strong></p>\n\n<ul>\n	<li>Sau khi mua về, nhẹ nh&agrave;ng nhặt bỏ những quả bị dập n&aacute;t, thối rữa.</li>\n	<li>Cho cherry v&agrave;o hộp k&iacute;n hoặc t&uacute;i nilon c&oacute; đục lỗ.</li>\n	<li>Bảo quản cherry trong ngăn m&aacute;t tủ lạnh.</li>\n	<li>Kiểm tra thường xuy&ecirc;n v&agrave; loại bỏ những tr&aacute;i hư hỏng</li>\n	<li>Kh&ocirc;ng n&ecirc;n rửa cherry trước khi bảo quản trong tủ lạnh v&igrave; sẽ khiến quả dễ bị thối rữa.</li>\n	<li>Nếu muốn bảo quản cherry l&acirc;u hơn, c&oacute; thể đ&ocirc;ng lạnh cherry.</li>\n</ul>\n\n<p><strong>+ Bảo quản ở nhiệt độ ph&ograve;ng:</strong></p>\n\n<ul>\n	<li>Nếu bạn muốn ăn cherry ngay trong ng&agrave;y, c&oacute; thể bảo quản cherry ở nhiệt độ ph&ograve;ng.</li>\n	<li>Tuy nhi&ecirc;n, cherry sẽ chỉ tươi ngon trong v&ograve;ng 1-2 ng&agrave;y.</li>\n	<li>N&ecirc;n đặt cherry ở nơi tho&aacute;ng m&aacute;t, tr&aacute;nh &aacute;nh nắng trực tiếp.</li>\n</ul>\n\n<p><strong>- Những lưu &yacute; khi thưởng thức cherry:</strong></p>\n\n<ul>\n	<li>N&ecirc;n rửa sạch cherry trước khi ăn để đảm bảo vệ sinh.</li>\n	<li>Nếu bạn c&oacute; dị ứng với cherry hoặc bất kỳ loại tr&aacute;i c&acirc;y n&agrave;o kh&aacute;c, h&atilde;y tr&aacute;nh ăn cherry.</li>\n	<li>Tr&aacute;nh cho trẻ ăn cherry trước khi đi ngủ v&igrave; c&oacute; thể g&acirc;y kh&oacute; ti&ecirc;u.</li>\n</ul>\n\n<p><strong>- Phụ nữ mang thai v&agrave; trẻ em ăn cherry c&oacute; được kh&ocirc;ng?</strong></p>\n\n<ul>\n	<li>\n	<p>Phụ nữ mang thai n&ecirc;n ăn nho với lượng vừa phải, khoảng 100-150 gram mỗi ng&agrave;y</p>\n	</li>\n	<li>\n	<p>Trẻ em tr&ecirc;n 1 tuổi c&oacute; thể ăn cherry, cắt nho th&agrave;nh từng miếng nhỏ để trẻ dễ ăn v&agrave; tr&aacute;nh nguy cơ nghẹn.</p>\n	</li>\n	<li>\n	<p>Cho trẻ ăn nho với lượng vừa phải, khoảng 50-100 gram mỗi ng&agrave;y</p>\n	</li>\n</ul>\n', 'Trái cây Việt', 71800, 1, '2024-07-05', 7, '0d41371d3fa81f393624aa188b806af3', 'nlvt56i9kf86z3ze16sk', 3, 2, 1),
(40, 'Kiwi xanh New Zealand', '<h1><strong>Kiwi xanh New Zealand</strong></h1>\n\n<p><strong>- Xuất xứ:&nbsp;</strong>New Zealand</p>\n\n<p><strong>- Chất lượng:</strong>&nbsp;Nhập khẩu</p>\n\n<p><strong>- Đặc điểm nổi bật:</strong></p>\n\n<ul>\n	<li>Vỏ kiwi xanh c&oacute; l&ocirc;ng, kh&ocirc;ng trơn nhẵn như kiwi v&agrave;ng</li>\n	<li>&nbsp;Vị ngọt dịu k&egrave;m vị chua nhẹ đặc trưng.</li>\n	<li><strong>Xuất xứ:</strong>&nbsp;New Zealand.</li>\n</ul>\n\n<p><strong>- Gi&aacute; trị dinh dưỡng:</strong></p>\n\n<ul>\n	<li><strong>Vitamin C:</strong>&nbsp;Dồi d&agrave;o, cao gấp 2 lần kiwi xanh, gi&uacute;p tăng cường hệ miễn dịch, chống oxy h&oacute;a, bảo vệ da khỏi t&aacute;c hại của &aacute;nh nắng mặt trời.</li>\n	<li><strong>Vitamin E:</strong>&nbsp;Gi&uacute;p bảo vệ tế b&agrave;o khỏi tổn thương, hỗ trợ sức khỏe tim mạch.</li>\n	<li><strong>Kali:</strong>&nbsp;Gi&uacute;p điều h&ograve;a huyết &aacute;p, tốt cho tim mạch.</li>\n	<li><strong>Chất xơ:</strong>&nbsp;Gi&uacute;p hệ ti&ecirc;u h&oacute;a khỏe mạnh, hỗ trợ giảm c&acirc;n.</li>\n	<li><strong>Axit folic:</strong>&nbsp;Quan trọng cho phụ nữ mang thai, gi&uacute;p ph&aacute;t triển tr&iacute; n&atilde;o thai nhi.</li>\n	<li><strong>Ngo&agrave;i ra:</strong>&nbsp;C&ograve;n chứa nhiều dưỡng chất kh&aacute;c như vitamin K, magie, photpho, đồng,...</li>\n</ul>\n\n<p><strong>- Lợi &iacute;ch của việc sử dụng kiwi:</strong></p>\n\n<ul>\n	<li>Tăng cường hệ miễn dịch.</li>\n	<li>Tốt cho sức khỏe tim mạch.</li>\n	<li>Hỗ trợ ti&ecirc;u h&oacute;a.</li>\n	<li>Gi&uacute;p giảm c&acirc;n.</li>\n	<li>Tốt cho da.</li>\n	<li>Tốt cho mắt.</li>\n	<li>Ngăn ngừa ung thư.</li>\n</ul>\n\n<p><strong>- C&aacute;ch sử dụng kiwi:</strong></p>\n\n<ul>\n	<li>C&oacute; thể ăn trực tiếp, gọt vỏ hoặc bỏ vỏ.</li>\n	<li>Th&ecirc;m v&agrave;o salad, sinh tố, sữa chua.</li>\n	<li>L&agrave;m mứt, b&aacute;nh ngọt, kem.</li>\n</ul>\n\n<p>-&nbsp;<strong>C&aacute;ch lựa chọn kiwi ngon:</strong></p>\n\n<ul>\n	<li>N&ecirc;n chọn những quả kiwi c&oacute; vỏ căng mịn, m&agrave;u n&acirc;u v&agrave;ng đồng đều, kh&ocirc;ng bị sần s&ugrave;i, trầy xước hay c&oacute; đốm n&acirc;u.</li>\n	<li>D&ugrave;ng ng&oacute;n tay ấn nhẹ v&agrave;o quả kiwi. Nếu quả kiwi c&oacute; độ mềm vừa phải, hơi đ&agrave;n hồi th&igrave; đ&oacute; l&agrave; quả kiwi ngon.</li>\n	<li>Với Farmers Market ti&ecirc;u ch&iacute; h&agrave;ng đầu l&agrave; sản phảm đạt chất lượng cao, nguồn gốc r&otilde; r&agrave;ng, mang đến trải nghiệm tốt nhất cho kh&aacute;ch h&agrave;ng.&nbsp;</li>\n</ul>\n\n<p><strong>- Để bảo quản kiwi được l&acirc;u v&agrave; giữ được độ tươi ngon, bạn cần lưu &yacute;:</strong></p>\n\n<p><strong>+ Nhiệt độ:</strong></p>\n\n<ul>\n	<li><strong>Nhiệt độ ph&ograve;ng:</strong>&nbsp;Nếu kiwi chưa ch&iacute;n, h&atilde;y để ch&uacute;ng ở nhiệt độ ph&ograve;ng cho đến khi ch&iacute;n. Tr&aacute;nh &aacute;nh nắng trực tiếp.</li>\n	<li><strong>Tủ lạnh:</strong>&nbsp;Khi kiwi đ&atilde; ch&iacute;n, h&atilde;y đặt ch&uacute;ng v&agrave;o tủ lạnh ở nhiệt độ từ 0&deg;C đến 4&deg;C. Ở nhiệt độ n&agrave;y, kiwi c&oacute; thể được bảo quản từ 5 đến 7 ng&agrave;y.</li>\n</ul>\n\n<p><strong>+ Độ ẩm:</strong></p>\n\n<ul>\n	<li>Kiwi cần được bảo quản ở độ ẩm cao để giữ được độ tươi ngon.</li>\n	<li>Bạn c&oacute; thể đặt kiwi v&agrave;o t&uacute;i nhựa hoặc hộp đựng c&oacute; nắp đậy để giữ ẩm.</li>\n	<li>Tr&aacute;nh để kiwi tiếp x&uacute;c trực tiếp với nước v&igrave; sẽ khiến kiwi bị nẫu.</li>\n</ul>\n\n<p><strong>+ Ph&acirc;n chia:</strong></p>\n\n<ul>\n	<li>N&ecirc;n ph&acirc;n chia kiwi ra từng quả ri&ecirc;ng biệt khi bảo quản trong tủ lạnh để tr&aacute;nh kiwi bị dập n&aacute;t.</li>\n	<li>Tr&aacute;nh để kiwi tiếp x&uacute;c với c&aacute;c loại tr&aacute;i c&acirc;y kh&aacute;c như t&aacute;o, chuối, l&ecirc;,... v&igrave; những loại tr&aacute;i c&acirc;y n&agrave;y c&oacute; thể sản sinh ra kh&iacute; ethylene, khiến kiwi ch&iacute;n nhanh hơn v&agrave; dễ bị nẫu.</li>\n</ul>\n\n<p><strong>+ Mẹo bảo quản:</strong></p>\n\n<ul>\n	<li><strong>Để kiwi ch&iacute;n nhanh hơn:</strong>&nbsp;Bạn c&oacute; thể cho kiwi v&agrave;o t&uacute;i giấy c&ugrave;ng với t&aacute;o hoặc chuối.</li>\n	<li><strong>Để kiwi ch&iacute;n chậm hơn:</strong>&nbsp;Bạn c&oacute; thể bọc kiwi bằng giấy nến hoặc giấy b&aacute;o.</li>\n	<li><strong>Để kiwi đ&ocirc;ng lạnh:</strong>&nbsp;Bạn c&oacute; thể cắt kiwi th&agrave;nh từng miếng v&agrave; cho v&agrave;o t&uacute;i đ&ocirc;ng lạnh. Kiwi đ&ocirc;ng lạnh c&oacute; thể được bảo quản trong v&agrave;i th&aacute;ng.</li>\n</ul>\n\n<p><strong>- Lưu &yacute;:</strong></p>\n\n<ul>\n	<li>Kh&ocirc;ng n&ecirc;n bảo quản kiwi ở nhiệt độ qu&aacute; cao v&igrave; sẽ khiến kiwi nhanh bị ch&iacute;n nẫu.</li>\n	<li>Kh&ocirc;ng n&ecirc;n bảo quản kiwi qu&aacute; l&acirc;u v&igrave; sẽ khiến kiwi mất đi hương vị v&agrave; dưỡng chất.</li>\n</ul>\n\n<p><strong>- C&aacute;ch d&ugrave;ng kiwi</strong></p>\n\n<p><strong>+ Ăn trực tiếp:</strong></p>\n\n<ul>\n	<li>Đ&acirc;y l&agrave; c&aacute;ch đơn giản v&agrave; phổ biến nhất để thưởng thức kiwi.</li>\n	<li>Bạn c&oacute; thể rửa sạch kiwi, gọt vỏ hoặc bỏ vỏ (t&ugrave;y sở th&iacute;ch) v&agrave; cắt th&agrave;nh từng miếng vừa ăn.</li>\n	<li>Kiwi c&oacute; thể ăn k&egrave;m với sữa chua, mật ong hoặc th&ecirc;m v&agrave;o salad, sinh tố.</li>\n</ul>\n\n<p><strong>+ L&agrave;m sinh tố:</strong></p>\n\n<ul>\n	<li>Kiwi l&agrave; nguy&ecirc;n liệu tuyệt vời để l&agrave;m sinh tố.</li>\n	<li>Bạn c&oacute; thể kết hợp kiwi với c&aacute;c loại tr&aacute;i c&acirc;y kh&aacute;c như chuối, d&acirc;u t&acirc;y, t&aacute;o,... để tạo ra những ly sinh tố thơm ngon, bổ dưỡng.</li>\n</ul>\n\n<p><strong>+ L&agrave;m mứt:</strong></p>\n\n<ul>\n	<li>Mứt kiwi l&agrave; m&oacute;n ăn k&egrave;m ngon miệng cho b&aacute;nh m&igrave; hoặc b&aacute;nh quy.</li>\n	<li>Để l&agrave;m mứt kiwi, bạn cần sơ chế kiwi, sau đ&oacute; nấu với đường v&agrave; nước cho đến khi sệt lại.</li>\n</ul>\n\n<p><strong>- Phụ nữ mang thai v&agrave; trẻ em ăn kiwi c&oacute; được kh&ocirc;ng?</strong></p>\n\n<p><strong>+ Phụ nữ mang thai</strong></p>\n\n<ul>\n	<li>Kiwi l&agrave; loại tr&aacute;i c&acirc;y&nbsp;<strong>chứa h&agrave;m lượng vitamin C cao gấp hai lần chanh</strong>. B&ecirc;n cạnh đ&oacute;, b&agrave; bầu ăn kiwi c&ograve;n được bổ sung nhiều chất xơ, vitamin E, carbohydrate, năng lượng v&agrave; c&aacute;c kho&aacute;ng chất kh&aacute;c</li>\n	<li>B&agrave; bầu ăn kiwi c&oacute; thể đ&aacute;p ứng đến 140% liều lượng vitamin C khuyến c&aacute;o h&agrave;ng ng&agrave;y. C&oacute; thể n&oacute;i, v&igrave; c&oacute; h&agrave;m lượng vitamin C cao m&agrave; kiwi trở th&agrave;nh loại tr&aacute;i c&acirc;y&nbsp;<strong>rất tốt cho b&agrave; bầu v&agrave; thai nhi.</strong></li>\n	<li><strong>Đối với mẹ bầu trước v&agrave; sau khi sinh</strong>, vitamin C gi&uacute;p cơ thể mẹ hấp thụ sắt tốt hơn để&nbsp;<strong>ngăn ngừa thiếu m&aacute;u</strong></li>\n	<li>Vitamin C trong quả kiwi gi&uacute;p h&igrave;nh th&agrave;nh c&aacute;c chất dẫn truyền thần kinh, một chất rất quan trọng đối với chức năng của n&atilde;o.</li>\n	<li>Vitamin C c&oacute; t&iacute;nh chống oxy h&oacute;a mạnh, gi&uacute;p bảo vệ RNA v&agrave; DNA khỏi t&aacute;c hại của c&aacute;c gốc tự do.</li>\n</ul>\n\n<p><strong>+ Cho b&eacute; ăn kiwi</strong>&nbsp;khi trẻ từ 8 đến 10 th&aacute;ng v&igrave; t&iacute;nh chất chua của n&oacute;. Nếu em b&eacute; của bạn đ&atilde; c&oacute; biểu hiện dị ứng thức ăn hoặc ph&aacute;t ban t&atilde; trước đ&oacute;, h&atilde;y c&acirc;n nhắc cho b&eacute; ăn tr&aacute;i c&acirc;y từ 10 đến 12 th&aacute;ng.</p>\n\n<ul>\n	<li><strong>Vitamin C</strong>: Kiwi rất gi&agrave;u vitamin C, cung cấp 230% lượng vitamin C được khuyến nghị mỗi ng&agrave;y. Vitamin C gi&uacute;p tăng cường hệ miễn dịch, hấp thụ sắt từ thực phẩm kh&aacute;c v&agrave; chữa l&agrave;nh vết thương.</li>\n	<li><strong>Chất xơ</strong>: Kiwi cung cấp 16% lượng chất xơ cần thiết cho trẻ. Chất xơ gi&uacute;p giảm t&aacute;o b&oacute;n v&agrave; duy tr&igrave; hệ ti&ecirc;u h&oacute;a khỏe mạnh.</li>\n</ul>\n\n<p><strong>- Những lưu &yacute; khi thưởng thức:&nbsp;</strong></p>\n\n<ul>\n	<li>Mẹ bầu chỉ n&ecirc;n ăn khoảng 2&ndash;3 tr&aacute;i kiwi mỗi ng&agrave;y l&agrave; ph&ugrave; hợp.</li>\n	<li>Kiwi c&oacute; t&iacute;nh axit cao, v&igrave; vậy đừng ăn qu&aacute; nhiều v&igrave; bạn c&oacute; thể bị lở miệng, lo&eacute;t lưỡi v&agrave; ph&aacute;t ban.</li>\n	<li>Nếu bạn bị dị ứng do di truyền, vi&ecirc;m dạ d&agrave;y hoặc c&aacute;c vấn đề kh&aacute;c th&igrave; cần tr&aacute;nh ăn kiwi khi mang thai.</li>\n	<li>Những người bị vi&ecirc;m mũi dị ứng thường c&oacute; nguy cơ dễ phản ứng dị ứng với kiwi.</li>\n</ul>\n', 'Trái cây Việt', 149000, 1, '2024-07-05', 7, '4dd5ff7842c042d0195411da214fc923', 'afgnjhrgukagfnkzbwvy', 3, 3, 1),
(41, 'Việt quất nguyên cành nội địa Trung', '<h1><strong>iệt quất nội địa Trung - 250G/Hộp</strong></h1>\n\n<p><strong>- Xuất xứ: Nội địa Trung</strong></p>\n\n<p><strong>- Ti&ecirc;u chuẩn:&nbsp;</strong>Nhập khẩu</p>\n\n<p><strong>- Trọng lượng</strong>: 250g/ hộp</p>\n\n<p><strong>- Đặc điểm:</strong></p>\n\n<ul>\n	<li>Vị ngọt, chua nhẹ v&ocirc; c&ugrave;ng h&ograve;a quyện b&ecirc;n cạnh đ&oacute; độ tươi mới của việt quất</li>\n	<li>Hương thơm m&ecirc; mệt của việt quất ch&iacute;nh t&ocirc;ng.</li>\n</ul>\n\n<p><strong>- Th&ocirc;ng tin dinh dưỡng:</strong></p>\n\n<ul>\n	<li><strong>Tăng cường hệ miễn dịch:</strong>&nbsp;Vitamin C trong việt quất gi&uacute;p tăng cường hệ miễn dịch, gi&uacute;p cơ thể chống lại c&aacute;c t&aacute;c nh&acirc;n g&acirc;y bệnh hiệu quả.</li>\n	<li><strong>Hỗ trợ hệ ti&ecirc;u h&oacute;a:</strong>&nbsp;Chất xơ trong việt quất gi&uacute;p hỗ trợ hệ ti&ecirc;u h&oacute;a hoạt động trơn tru, ngăn ngừa t&aacute;o b&oacute;n v&agrave; c&aacute;c vấn đề ti&ecirc;u h&oacute;a kh&aacute;c.</li>\n	<li><strong>Tốt cho tim mạch:</strong>&nbsp;Kali trong việt quất gi&uacute;p điều h&ograve;a huyết &aacute;p, giảm cholesterol xấu v&agrave; bảo vệ tim mạch khỏe mạnh.</li>\n	<li><strong>Gi&uacute;p đẹp da:</strong>&nbsp;Vitamin C v&agrave; c&aacute;c chất chống oxy h&oacute;a trong việt quất gi&uacute;p dưỡng da s&aacute;ng mịn, chống l&atilde;o h&oacute;a v&agrave; giảm nếp nhăn.</li>\n	<li><strong>Hỗ trợ giảm c&acirc;n:</strong>&nbsp;Lượng calo thấp v&agrave; chất xơ cao trong việt quất tạo cảm gi&aacute;c no l&acirc;u, hỗ trợ giảm c&acirc;n hiệu quả v&agrave; l&agrave;nh mạnh.</li>\n	<li><strong>Ngăn ngừa ung thư:</strong>&nbsp;Chất chống oxy h&oacute;a dồi d&agrave;o trong việt quất gi&uacute;p chống lại c&aacute;c gốc tự do, ngăn ngừa sự h&igrave;nh th&agrave;nh tế b&agrave;o ung thư.</li>\n	<li><strong>Tốt cho mắt:</strong>&nbsp;Vitamin A trong việt quất gi&uacute;p cải thiện thị lực, bảo vệ mắt khỏi c&aacute;c bệnh về mắt như đục thủy tinh thể v&agrave; tho&aacute;i h&oacute;a điểm v&agrave;ng.</li>\n	<li><strong>Hỗ trợ sức khỏe răng miệng:</strong>&nbsp;Chất xơ trong việt quất gi&uacute;p loại bỏ mảng b&aacute;m v&agrave; vi khuẩn trong khoang miệng, ngăn ngừa s&acirc;u răng v&agrave; c&aacute;c bệnh về nướu.</li>\n	<li><strong>Gi&uacute;p xương chắc khỏe:</strong>&nbsp;Kali trong việt quất gi&uacute;p tăng cường mật độ xương, ngăn ngừa lo&atilde;ng xương</li>\n</ul>\n\n<p><strong>- Hướng dẫn sử dụng:</strong></p>\n\n<ul>\n	<li>Rửa nhẹ nh&agrave;ng&nbsp;việt quất với nước sạch để loại bỏ phần phấn trắng tr&ecirc;n vỏ.</li>\n	<li>Giữ lạnh việt quất gi&uacute;p bảo quản l&acirc;u hơn.</li>\n	<li>Bạn n&ecirc;n giữ việt quất&nbsp;trong hộp đựng ban đầu v&agrave; l&agrave;m lạnh c&agrave;ng sớm c&agrave;ng tốt.</li>\n	<li>Kh&ocirc;ng rửa&nbsp;trước khi cho v&agrave;o tủ lạnh.</li>\n</ul>\n\n<p><strong>- C&aacute;ch chọn việt quất:</strong></p>\n\n<ul>\n	<li>N&ecirc;n chọn những quả việt quất c&oacute; h&igrave;nh d&aacute;ng đều đặn, tr&ograve;n trịa, kh&ocirc;ng bị dập n&aacute;t hay c&oacute; dấu hiệu hư hỏng.</li>\n	<li>Việt quất&nbsp;khi ch&iacute;n tới sẽ c&oacute; độ mềm vừa phải, khi ấn nhẹ v&agrave;o sẽ cảm nhận được độ gi&ograve;n tan.</li>\n	<li>N&ecirc;n chọn những quả việt quất c&oacute; m&ugrave;i thơm r&otilde; r&agrave;ng, kh&ocirc;ng bị m&ugrave;i h&ocirc;i hoặc m&ugrave;i chua.</li>\n	<li>Với Farmers Market ti&ecirc;u ch&iacute; h&agrave;ng đầu l&agrave; sản phảm đạt chất lượng cao, nguồn gốc r&otilde; r&agrave;ng, mang đến trải nghiệm tốt nhất cho kh&aacute;ch h&agrave;ng.&nbsp;</li>\n</ul>\n\n<p><strong>- Những lưu &yacute; khi xử dụng việt quất:</strong></p>\n\n<ul>\n	<li>Rửa sạch việt quất&nbsp; kỹ trước khi ăn.</li>\n	<li>N&ecirc;n ăn&nbsp;với lượng vừa phải, khoảng 100-150g mỗi ng&agrave;y.</li>\n	<li>Trẻ em dưới 1 tuổi kh&ocirc;ng n&ecirc;n ăn việt quất nguy&ecirc;n quả. N&ecirc;n xay nhuyễn việt quất hoặc trộn v&agrave;o sữa chua, sinh tố để trẻ dễ ăn.</li>\n</ul>\n\n<p><strong>- Phụ nữ mang thai v&agrave; trẻ em ăn việt quất c&oacute; được kh&ocirc;ng?</strong></p>\n\n<ul>\n	<li>Việt quất gi&uacute;p cung cấp vitamin C, folate, vitamin K, kali, chất xơ v&agrave; c&aacute;c chất chống oxy h&oacute;a<strong>&nbsp;cần thiết cho sự ph&aacute;t triển của thai nhi.</strong></li>\n</ul>\n\n<ul>\n	<li>Vitamin C gi&uacute;p tăng cường hệ miễn dịch,&nbsp;<strong>hỗ trợ</strong>&nbsp;<strong>hấp thu sắt v&agrave; collagen.</strong></li>\n	<li>Folate gi&uacute;p&nbsp;<strong>ngăn ngừa dị tật ống thần kinh ở thai nhi.</strong></li>\n	<li>Vitamin K gi&uacute;p<strong>&nbsp;ph&aacute;t triển xương thai nhi.</strong></li>\n	<li>Kali gi&uacute;p điều h&ograve;a huyết &aacute;p,&nbsp;<strong>giảm nguy cơ chuột r&uacute;t</strong>.</li>\n	<li>Trẻ em dưới 1 tuổi kh&ocirc;ng n&ecirc;n ăn việt quất nguy&ecirc;n quả. N&ecirc;n xay nhuyễn việt quất hoặc trộn v&agrave;o sữa chua, sinh tố để trẻ dễ ăn.</li>\n</ul>\n', 'Trái cây Việt', 179900, 1, '2024-07-05', 7, 'fe8c712a0ebef30b04af6a0ead8c18a8', 'gueltlxew8cowkemhzkz', 3, 3, 1);
INSERT INTO `products` (`id`, `nameOfProduct`, `description`, `category`, `price`, `weightDefault`, `dateOfImporting`, `expriredDay`, `imgAssetId`, `imgPublicId`, `adminCreate`, `provider`, `status`) VALUES
(42, 'Chà là vàng tươi Thái Lan', '<h1><strong>Ch&agrave; l&agrave; v&agrave;ng tươi Th&aacute;i Lan&nbsp;</strong></h1>\n\n<p><strong>- Quy c&aacute;ch:&nbsp;</strong>250G&nbsp;</p>\n\n<p><strong>- Xuất xứ:</strong>&nbsp;Th&aacute;i Lan</p>\n\n<p><strong>- Chất lượng:&nbsp;</strong>Nhập khẩu</p>\n\n<p><strong>- Đặc điểm:</strong></p>\n\n<p><strong>Ch&agrave; l&agrave;&nbsp;</strong>tr&aacute;i m&agrave;u v&agrave;ng, to, gi&ograve;n, ngọt đậm xen lẫn ch&uacute;t vị ch&aacute;t nhẹ. Khi ch&iacute;n, tr&aacute;i sẽ c&oacute; đốm n&acirc;u nh&igrave;n giống như bị dập (thực tế ch&agrave; l&agrave; rất kh&oacute; bị dập v&agrave; trầy xước vỏ), tr&aacute;i ch&iacute;n sẽ c&oacute; vị ngọt đậm hơn.</p>\n\n<p><strong>- Th&ocirc;ng tin dinh dưỡng:</strong></p>\n\n<ul>\n	<li>Tăng cường sức khoẻ cho người ốm, kiệt sức</li>\n	<li>Cải thiện ti&ecirc;u ho&aacute;, cũng như l&agrave; chứng t&aacute;o b&oacute;n</li>\n	<li>Ngăn ngừa c&aacute;c t&aacute;c nh&acirc;n g&acirc;y ung thư</li>\n	<li>Kiểm so&aacute;t lượng đường trong m&aacute;u</li>\n	<li>C&aacute;c vitamin v&agrave; kho&aacute;ng chất c&oacute; trong ch&agrave; l&agrave; đặc biệt rất tốt cho người suy nhược thần kinh</li>\n</ul>\n\n<p><strong>- Ch&agrave; l&agrave; v&agrave;ng tươi c&oacute; thể được sử dụng theo nhiều c&aacute;ch kh&aacute;c nhau:</strong></p>\n\n<ul>\n	<li><strong>Ăn tươi:</strong>&nbsp;Ch&agrave; l&agrave; v&agrave;ng tươi c&oacute; thể được ăn tươi như một m&oacute;n ăn nhẹ hoặc tr&aacute;ng miệng.</li>\n	<li><strong>Sấy kh&ocirc;:</strong>&nbsp;Ch&agrave; l&agrave; v&agrave;ng tươi c&oacute; thể được sấy kh&ocirc; để bảo quản l&acirc;u hơn. Ch&agrave; l&agrave; sấy kh&ocirc; c&oacute; thể được ăn như một m&oacute;n ăn nhẹ hoặc th&ecirc;m v&agrave;o c&aacute;c m&oacute;n ăn kh&aacute;c.</li>\n	<li><strong>Th&ecirc;m v&agrave;o c&aacute;c m&oacute;n ăn:</strong>&nbsp;Ch&agrave; l&agrave; v&agrave;ng tươi c&oacute; thể được th&ecirc;m v&agrave;o c&aacute;c m&oacute;n salad, ngũ cốc, sinh tố, b&aacute;nh nướng, b&aacute;nh ngọt v&agrave; c&aacute;c m&oacute;n ăn kh&aacute;c.</li>\n	<li><strong>L&agrave;m mứt, mật ong v&agrave; b&aacute;nh kẹo:</strong>&nbsp;Ch&agrave; l&agrave; v&agrave;ng tươi c&oacute; thể được sử dụng để l&agrave;m mứt, mật ong v&agrave; b&aacute;nh kẹo.</li>\n</ul>\n\n<p><strong>- C&aacute;ch lựa chọn ch&agrave; l&agrave; v&agrave;ng ngon.</strong></p>\n\n<ul>\n	<li>N&ecirc;n chọn những quả ch&agrave; l&agrave; v&agrave;ng c&oacute; h&igrave;nh d&aacute;ng đầy đặn, đều nhau, kh&ocirc;ng bị m&eacute;o m&oacute; hay dập n&aacute;t.</li>\n</ul>\n\n<ul>\n	<li>Với Farmers Market ti&ecirc;u ch&iacute; h&agrave;ng đầu l&agrave; sản phảm đạt chất lượng cao, nguồn gốc r&otilde; r&agrave;ng, mang đến trải nghiệm tốt nhất cho kh&aacute;ch h&agrave;ng.&nbsp;</li>\n</ul>\n\n<p>-&nbsp;<strong>C&aacute;ch bảo quản ch&agrave; l&agrave; được l&acirc;u.</strong></p>\n\n<p><strong>+ Bảo quản ch&agrave; l&agrave; trong hộp k&iacute;n:</strong></p>\n\n<ul>\n	<li>Cho ch&agrave; l&agrave; v&agrave;o hộp k&iacute;n c&oacute; nắp đậy.</li>\n	<li>N&ecirc;n sử dụng hộp nhựa hoặc hộp thủy tinh để bảo quản ch&agrave; l&agrave;.</li>\n	<li>Tr&aacute;nh sử dụng hộp kim loại v&igrave; c&oacute; thể l&agrave;m ch&agrave; l&agrave; bị oxy h&oacute;a.</li>\n</ul>\n\n<p><strong>+ Bảo quản ch&agrave; l&agrave; trong tủ lạnh:</strong></p>\n\n<ul>\n	<li>Để hộp ch&agrave; l&agrave; v&agrave;o ngăn m&aacute;t tủ lạnh.</li>\n	<li>Nhiệt độ l&yacute; tưởng để bảo quản ch&agrave; l&agrave; l&agrave; từ 4&deg;C đến 10&deg;C.</li>\n	<li>Nếu bạn muốn bảo quản ch&agrave; l&agrave; l&acirc;u hơn, bạn c&oacute; thể cho ch&agrave; l&agrave; v&agrave;o ngăn đ&aacute; tủ lạnh.</li>\n	<li>Tuy nhi&ecirc;n, khi r&atilde; đ&ocirc;ng, ch&agrave; l&agrave; sẽ mềm hơn v&agrave; c&oacute; thể mất đi một số hương vị.</li>\n</ul>\n\n<p>-&nbsp;<strong>&nbsp;Một số lưu &yacute; khi sử dụng ch&agrave; l&agrave;:</strong></p>\n\n<ul>\n	<li>Kh&ocirc;ng n&ecirc;n bảo quản ch&agrave; l&agrave; ở nơi c&oacute; &aacute;nh nắng trực tiếp hoặc nơi c&oacute; nhiệt độ cao.</li>\n	<li>Kh&ocirc;ng n&ecirc;n bảo quản ch&agrave; l&agrave; c&ugrave;ng với c&aacute;c loại tr&aacute;i c&acirc;y kh&aacute;c c&oacute; m&ugrave;i mạnh như sầu ri&ecirc;ng, m&iacute;t,... v&igrave; c&oacute; thể l&agrave;m ch&agrave; l&agrave; bị &aacute;m m&ugrave;i.</li>\n	<li>N&ecirc;n kiểm tra ch&agrave; l&agrave; định kỳ để loại bỏ những quả bị hỏng.</li>\n	<li>Ch&agrave; l&agrave; v&agrave;ng c&oacute; h&agrave;m lượng đường cao, v&igrave; vậy h&atilde;y ăn ch&uacute;ng một c&aacute;ch điều độ.</li>\n	<li>Ch&agrave; l&agrave; sấy kh&ocirc; c&oacute; thể bảo quản ở nhiệt độ ph&ograve;ng trong v&agrave;i th&aacute;ng.</li>\n</ul>\n\n<p>-&nbsp;<strong>&nbsp;Phụ nữ mang thai v&agrave; trẻ em c&oacute; ăn được ch&agrave; l&agrave; kh&ocirc;ng ?</strong></p>\n\n<p><strong>+ Phụ nữ mang thai:</strong></p>\n\n<p><strong>C&oacute; thể ăn:</strong>&nbsp;Ch&agrave; l&agrave; l&agrave; nguồn cung cấp vitamin, kho&aacute;ng chất v&agrave; chất xơ dồi d&agrave;o, tốt cho sức khỏe của phụ nữ mang thai. Vitamin C gi&uacute;p tăng cường hệ miễn dịch, vitamin A hỗ trợ ph&aacute;t triển thị gi&aacute;c cho thai nhi, kali gi&uacute;p điều h&ograve;a huyết &aacute;p, v&agrave; chất xơ gi&uacute;p ngăn ngừa t&aacute;o b&oacute;n.</p>\n\n<p>+&nbsp;<strong>Trẻ em:</strong></p>\n\n<p><strong>C&oacute; thể ăn:&nbsp;</strong>&nbsp;Bắt đầu cho b&eacute; ăn khoảng 6 th&aacute;ng tuổi trở l&ecirc;n, cắt nhỏ để tr&aacute;nh bị hốc v&agrave; ăn với h&agrave;m lượng vừa phải cho b&eacute;</p>\n', 'Trái cây Việt', 99000, 1, '2024-07-05', 7, 'f504ec16f56b1ad3744d68d9af31aa8e', 'pnb8pot8fw1w0q19njao', 3, 2, 1),
(43, 'Táo Roxy New Zealand', '<p><strong>&aacute;o Roxy New Zealand&nbsp;</strong></p>\n\n<p>l&agrave; một loại t&aacute;o đỏ, c&oacute; vị ngọt v&agrave; gi&ograve;n. T&aacute;o Roxy được biết đến với k&iacute;ch thước nhỏ gọn v&agrave; vỏ mỏng.</p>\n\n<p>-&nbsp;<strong>Xuất xứ:&nbsp;</strong>New Zealand</p>\n\n<p>-&nbsp;<strong>Ti&ecirc;u chuẩn chất lượng:&nbsp;</strong>Nhập khẩu</p>\n\n<p>-&nbsp;<strong>Quy c&aacute;ch:&nbsp;</strong>0,9 - 1 kg (6 tr&aacute;i / hộp)</p>\n\n<p>- T&aacute;o Roxy l&agrave; một nguồn cung cấp vitamin C, chất xơ v&agrave; kali dồi d&agrave;o. Ch&uacute;ng cũng l&agrave; một nguồn chất chống oxy h&oacute;a tốt. T&aacute;o Roxy c&oacute; thể được ăn tươi, nướng hoặc sử dụng trong c&aacute;c m&oacute;n salad, b&aacute;nh ngọt v&agrave; sinh tố.</p>\n\n<p>-&nbsp;<strong>Đặc điểm:&nbsp;</strong></p>\n\n<ul>\n	<li><strong>Hương vị:</strong>&nbsp;T&aacute;o Roxy c&oacute; vị ngọt v&agrave; gi&ograve;n.</li>\n	<li><strong>Kết cấu:</strong>&nbsp;T&aacute;o Roxy c&oacute; vỏ mỏng v&agrave; thịt gi&ograve;n.</li>\n	<li><strong>K&iacute;ch thước:</strong>&nbsp;T&aacute;o Roxy c&oacute; k&iacute;ch thước nhỏ gọn, thường nặng từ 60 đến 80 gam.</li>\n	<li><strong>M&agrave;u sắc:</strong>&nbsp;T&aacute;o Roxy c&oacute; m&agrave;u đỏ với vệt v&agrave;ng.</li>\n	<li><strong>Sử dụng:</strong>&nbsp;T&aacute;o Roxy c&oacute; thể được ăn tươi, nướng hoặc sử dụng trong c&aacute;c m&oacute;n salad, b&aacute;nh ngọt v&agrave; sinh tố.</li>\n</ul>\n\n<p><strong>- Hướng dẫn sử dụng:</strong></p>\n\n<ul>\n	<li>Rửa nhẹ nh&agrave;ng tr&aacute;i t&aacute;o. Sau đ&oacute; cắt đ&ocirc;i tr&aacute;i&nbsp;t&aacute;o&nbsp;v&agrave; loại bỏ phần hạt b&ecirc;n trong.</li>\n	<li>Bảo quản trong tủ lạnh từ 4 đến 8 độ C. T&aacute;o giữ được độ tươi, độ gi&ograve;n trong v&ograve;ng 1-4 tuần. Sau thời gian n&agrave;y, t&aacute;o sẽ ngọt hơn, độ gi&ograve;n thấp hơn v&agrave; t&aacute;o trở n&ecirc;n xốp.</li>\n	<li>Cần tr&aacute;nh để t&aacute;o với c&aacute;c thực phẩm c&oacute; m&ugrave;i kh&aacute;c như h&agrave;nh, tỏi, t&aacute;o sẽ dễ nhiễm m&ugrave;i.</li>\n	<li>Kh&ocirc;ng rửa&nbsp;trước khi cho v&agrave;o tủ lạnh.</li>\n</ul>\n\n<p><strong>- T&aacute;o c&oacute; thể được sử dụng để l&agrave;m g&igrave;?</strong></p>\n\n<ul>\n	<li>Sinh tố: t&aacute;o kết hợp với c&aacute;c loại tr&aacute;i c&acirc;y kh&aacute;c như chuối, d&acirc;u t&acirc;y, sữa chua,... để tạo th&agrave;nh m&oacute;n sinh tố thơm ngon v&agrave; đầy dinh dưỡng.</li>\n	<li>Salad: T&aacute;o&nbsp;cắt nhỏ, trộn với c&aacute;c loại rau củ quả kh&aacute;c c&ugrave;ng với nước sốt y&ecirc;u th&iacute;ch để tạo th&agrave;nh m&oacute;n salad thanh m&aacute;t v&agrave; đầy đủ dưỡng chất.</li>\n	<li>Nước &eacute;p: T&aacute;o&nbsp;&eacute;p lấy nước c&oacute; thể uống trực tiếp hoặc pha th&ecirc;m mật ong để tăng hương vị.</li>\n	<li>B&aacute;nh t&aacute;o: T&aacute;o&nbsp;được sử dụng l&agrave;m nh&acirc;n cho b&aacute;nh t&aacute;o hoặc trang tr&iacute; mặt b&aacute;nh.</li>\n	<li>Mứt t&aacute;o: T&aacute;o c&oacute; thể được nấu th&agrave;nh mứt để ăn k&egrave;m với b&aacute;nh m&igrave; hoặc sử dụng trong c&aacute;c m&oacute;n tr&aacute;ng miệng.</li>\n</ul>\n\n<p><strong>- C&aacute;ch lựa chọn t&aacute;o:</strong></p>\n\n<ul>\n	<li>\n	<p>Kh&aacute;ch h&agrave;ng&nbsp;n&ecirc;n&nbsp;chọn những quả t&aacute;o c&oacute; h&igrave;nh dạng c&acirc;n đối, kh&ocirc;ng bị m&eacute;o m&oacute;, dập n&aacute;t.</p>\n	</li>\n	<li>\n	<p>Với Farmers Market ti&ecirc;u ch&iacute; h&agrave;ng đầu l&agrave; sản phảm đạt chất lượng cao, nguồn gốc r&otilde; r&agrave;ng, mang đến trải nghiệm tốt nhất cho kh&aacute;ch h&agrave;ng.&nbsp;</p>\n	</li>\n</ul>\n\n<p><strong>- Hướng dẫn bảo quản t&aacute;o</strong></p>\n\n<ul>\n	<li>N&ecirc;n đặt nho v&agrave;o&nbsp;<strong>hộp k&iacute;n kh&iacute;</strong>&nbsp;hoặc&nbsp;<strong>t&uacute;i ziplock&nbsp;</strong>để giữ độ ẩm v&agrave;&nbsp;bảo quan t&aacute;o&nbsp;trong ngăn m&aacute;t tủ lạnh&nbsp;</li>\n	<li>Kh&ocirc;ng n&ecirc;n bảo quản t&aacute;o c&ugrave;ng với c&aacute;c loại tr&aacute;i c&acirc;y kh&aacute;c như chuối, l&ecirc;,... v&igrave; ethylene từ những loại tr&aacute;i c&acirc;y n&agrave;y c&oacute; thể khiến t&aacute;o nhanh ch&iacute;n v&agrave; mềm.</li>\n	<li>T&aacute;o c&oacute; thể bảo quản trong tủ lạnh từ 1-2 tuần.</li>\n</ul>\n\n<p><strong>- Những lưu &yacute; khi sử dụng T&aacute;o:&nbsp;</strong></p>\n\n<ul>\n	<li>Kh&ocirc;ng n&ecirc;n ăn t&aacute;o&nbsp;đ&atilde; bị dập n&aacute;t hoặc hư hỏng.</li>\n	<li>N&ecirc;n ăn t&aacute;o&nbsp;với lượng vừa phải, khoảng 1-2 quả mỗi ng&agrave;y.</li>\n	<li>Người bị bệnh tiểu đường n&ecirc;n ăn t&aacute;o với lượng vừa phải v&igrave; t&aacute;o chứa đường tự nhi&ecirc;n.</li>\n	<li>Trẻ em bị ti&ecirc;u chảy hoặc t&aacute;o b&oacute;n kh&ocirc;ng n&ecirc;n ăn t&aacute;o.</li>\n</ul>\n\n<p><strong>- Phụ nữ mang thai v&agrave; trẻ em ăn t&aacute;o&nbsp;c&oacute; được kh&ocirc;ng?</strong></p>\n\n<ul>\n	<li>T&aacute;o&nbsp;cung cấp vitamin C, folate v&agrave; chất xơ&nbsp;<strong>cần thiết cho phụ nữ mang thai v&agrave; cho con b&uacute;.</strong></li>\n	<li>Chất xơ trong t&aacute;o gi&uacute;p giảm t&aacute;o b&oacute;n, vitamin C tăng cường hệ miễn dịch, folate<strong>&nbsp;tốt cho sự ph&aacute;t triển của thai nhi</strong>.</li>\n	<li>\n	<p>Trẻ em tr&ecirc;n 1 tuổi c&oacute; thể ăn t&aacute;o, cắt nho th&agrave;nh từng miếng nhỏ để trẻ dễ ăn v&agrave; tr&aacute;nh nguy cơ nghẹn.</p>\n	</li>\n</ul>\n\n<ul>\n	<li><strong>Cho trẻ ăn t&aacute;o với lượng vừa phải:</strong>&nbsp;Trẻ em n&ecirc;n ăn t&aacute;o với lượng vừa phải, khoảng 50-100 gram mỗi ng&agrave;y. Ăn qu&aacute; nhiều t&aacute;o c&oacute; thể g&acirc;y đầy bụng, kh&oacute; ti&ecirc;u.</li>\n</ul>\n', 'Trái cây Việt', 79000, 1, '2024-07-05', 7, '4897bd0d3f1b9881ebffe12cdcff5d21', 'cyzvixezspfbkjgifttt', 3, 3, 1),
(44, 'Mây VIP Indonesia', '<h1><strong>M&acirc;y Indonesia</strong></h1>\n\n<p>c&ograve;n được gọi l&agrave; m&acirc;y da rắn, l&agrave; một loại tr&aacute;i c&acirc;y nhiệt đới c&oacute; nguồn gốc từ Indonesia.</p>\n\n<p><strong>- Xuất xứ:</strong>&nbsp;Indonesia</p>\n\n<p><strong>- Chất lượng:</strong>&nbsp;Nhập khẩu</p>\n\n<p><strong>- Khối lượng:</strong>&nbsp;0.5Kg</p>\n\n<p><strong>- Đặc điểm nổi bật:</strong></p>\n\n<ul>\n	<li>M&acirc;y Indo - loại tr&aacute;i c&acirc;y c&oacute; nhiều hương vị của quả kh&aacute;c</li>\n	<li>Thịt m&agrave;u trắng hơi ngả v&agrave;ng nhạt, ă ngọt k&egrave;m vị gi&ograve;n b&ugrave;i b&ugrave;i, vị thơm đặc trưng v&agrave; ngon nhất khi d&ugrave;ng lạnh .</li>\n	<li>C&oacute; vị ngọt ngọt chua chua như vị dứa, phảng phất hương thơm&nbsp;</li>\n	<li>H&agrave;ng về mới c&ograve;n nguy&ecirc;n gai</li>\n	<li>M&acirc;y Indo nhanh ch&iacute;n, n&ecirc;n bảo quản ở nơi tho&aacute;ng m&aacute;t hoặc cho v&agrave;o tủ lạnh.</li>\n</ul>\n\n<p>- M&acirc;y Indonesia l&agrave; nguồn&nbsp;<strong>cung cấp vitamin C, kali, pectin, canxi, tannin, saponin, flavonoid v&agrave; beta-carotene dồi d&agrave;o</strong>. Vitamin C gi&uacute;p tăng cường hệ miễn dịch, kali gi&uacute;p điều h&ograve;a huyết &aacute;p, pectin hỗ trợ ti&ecirc;u h&oacute;a, canxi gi&uacute;p xương chắc khỏe, tannin c&oacute; t&aacute;c dụng chống ti&ecirc;u chảy, saponin gi&uacute;p giảm cholesterol, flavonoid c&oacute; đặc t&iacute;nh chống oxy h&oacute;a v&agrave; beta-carotene tốt cho mắt.</p>\n\n<p>-&nbsp; M&acirc;y Indonesia c&oacute; thể thưởng thức trực tiếp, l&agrave;m sinh tố, mứt hoặc salad. Loại tr&aacute;i c&acirc;y n&agrave;y thường được bảo quản ở nơi tho&aacute;ng m&aacute;t hoặc trong tủ lạnh để giữ độ tươi ngon.</p>\n', 'Trái cây Việt', 199500, 1, '2024-07-05', 7, '29902469dc55a2417a26a1eb626f133e', 'jyntc4q6rmoxbwe7ta5h', 3, 1, 1),
(45, 'Nho đỏ kẹo hữu cơ Candy Snaps Mỹ ', '<h1><strong>Nho đỏ kẹo hữu cơ Candy Snaps Mỹ</strong></h1>\n\n<p><strong>- Xuất xứ:</strong>&nbsp;Mỹ</p>\n\n<p><strong>- Ti&ecirc;u chuẩn chất lượng</strong>: Nhập khẩu</p>\n\n<p><strong>- Đặc điểm nổi bật:</strong></p>\n\n<ul>\n	<li>Nho kẹo l&agrave; sự kết hợp độc đ&aacute;o giữa vị nho v&agrave; kẹo lạ miệng&nbsp;</li>\n	<li>H&agrave;ng đầu m&ugrave;a, nho ngọt dịu, chua nhẹ&nbsp;</li>\n	<li>Đ&oacute;ng hộp 454g tiện lợi, ch&ugrave;m đầy, tr&aacute;i đẹp thơm ngon hấp dẫn&nbsp;</li>\n	<li>Cam kết chất lượng nhập khẩu Mỹ, giấy tờ đầy đủ</li>\n</ul>\n\n<p><strong>- Th&ocirc;ng tin dinh dưỡng:</strong></p>\n\n<ul>\n	<li>Nho đỏ c&oacute; thể giảm nguy cơ tai biến tim mạch, giảm t&aacute;c hại của thuốc l&aacute; đối với phổi, ngăn ngừa ung thư v&agrave; gi&uacute;p cơ thể chống đỡ tốt hơn những t&aacute;c dụng phụ m&agrave; việc điều trị ung thư g&acirc;y ra, rất ph&ugrave; hợp l&agrave;m qu&agrave; biếu.</li>\n	<li>Hợp chất resveratrol trong lớp vỏ của nho đỏ (nho xanh kh&ocirc;ng c&oacute; chất n&agrave;y) c&oacute; t&aacute;c dụng ngăn hồng cầu kết d&iacute;nh v&agrave;o nhau, l&agrave;m giảm nguy cơ h&igrave;nh th&agrave;nh c&aacute;c cục m&aacute;u đ&ocirc;ng, tăng cholesterol tốt (HDL) v&agrave; giảm cholesterol xấu (LDL). Resveratrol c&oacute; khả năng chống oxy h&oacute;a mạnh gấp 7 lần vitamin E.</li>\n	<li>Nước &eacute;p từ quả nho đỏ c&oacute; khả năng ngăn ngừa ung thư v&agrave; tăng cường sức chống đỡ của cơ thể trước những t&aacute;c hại của h&oacute;a trị v&agrave; xạ trị. Một nghi&ecirc;n cứu kh&aacute;c cho thấy, nước &eacute;p nho đỏ c&oacute; hoạt t&iacute;nh chống oxy h&oacute;a mạnh gấp 3 lần so với nước &eacute;p c&agrave; chua, cam, bưởi.</li>\n</ul>\n\n<p><strong>- C&aacute;ch bảo quản:</strong></p>\n\n<ul>\n	<li>Nhiệt độ để bảo quản nho từ khoảng 0 &ndash; 4 độ C,&nbsp; lu&ocirc;n lu&ocirc;n để nho chưa rửa trong hộp k&iacute;n v&agrave;o tủ lạnh để kh&ocirc;ng l&agrave;m đi lớp phấn phủ tự nhi&ecirc;n của quả Nho, chỉ n&ecirc;n rửa trước khi ăn hoặc chế biến.&nbsp;</li>\n	<li>Nho thường d&ugrave;ng để ăn tươi hoặc &eacute;p nước rất ngon.</li>\n</ul>\n\n<p><strong>- C&aacute;ch lựa chọn:</strong></p>\n\n<ul>\n	<li>\n	<p>Kh&aacute;ch h&agrave;ng&nbsp;n&ecirc;n&nbsp;chọn những quả nho c&oacute; quả&nbsp;to, đều, vỏ căng b&oacute;ng, kh&ocirc;ng bị dập n&aacute;t.</p>\n	</li>\n	<li>\n	<p>Với Farmers Market ti&ecirc;u ch&iacute; h&agrave;ng đầu l&agrave; sản phảm đạt chất lượng cao, nguồn gốc r&otilde; r&agrave;ng, mang đến trải nghiệm tốt nhất cho kh&aacute;ch h&agrave;ng.&nbsp;</p>\n	</li>\n</ul>\n\n<p>-&nbsp;<strong>C&aacute;ch bảo quản</strong><strong>:&nbsp;</strong></p>\n\n<ul>\n	<li>\n	<p>N&ecirc;n đặt nho v&agrave;o&nbsp;<strong>hộp k&iacute;n kh&iacute;</strong>&nbsp;hoặc&nbsp;<strong>t&uacute;i ziplock&nbsp;</strong>để giữ độ ẩm v&agrave;&nbsp;bảo quan nho trong ngăn m&aacute;t tủ lạnh&nbsp;</p>\n	</li>\n	<li>\n	<p>Kh&ocirc;ng n&ecirc;n rửa nho trước khi bảo quản trong ngăn m&aacute;t tủ lạnh v&igrave; nếu rửa trước nho sẽ dễ bị hư, thối.</p>\n	</li>\n	<li>\n	<p>Kiểm tra thường xuy&ecirc;n v&agrave; loại bỏ những tr&aacute;i hư hỏng</p>\n	</li>\n</ul>\n\n<ul>\n	<li>\n	<p>Kh&ocirc;ng n&ecirc;n bảo quản nho ở nhiệt độ ph&ograve;ng v&igrave; nho sẽ ch&iacute;n nhanh v&agrave; dễ bị hư hỏng.</p>\n	</li>\n	<li>\n	<p>Kh&ocirc;ng n&ecirc;n đ&ocirc;ng lạnh nho v&igrave; nho sẽ bị mềm nhũn v&agrave; mất đi hương vị.</p>\n	</li>\n</ul>\n\n<p><strong>- Những lưu &yacute; khi thưởng thức nho :&nbsp;</strong></p>\n\n<ul>\n	<li>\n	<p><strong>Kh&ocirc;ng n&ecirc;n ăn nho qu&aacute; nhiều</strong>&nbsp;v&igrave; c&oacute; thể g&acirc;y đầy bụng, kh&oacute; ti&ecirc;u.</p>\n	</li>\n	<li>\n	<p><strong>Người bị bệnh tiểu đường&nbsp;</strong>n&ecirc;n tham khảo &yacute; kiến b&aacute;c sĩ trước khi ăn nho.</p>\n	</li>\n	<li>\n	<p><strong>Ăn nho sau bữa ăn</strong>&nbsp;sẽ gi&uacute;p hạn chế lượng đường nạp v&agrave;o cơ thể, đồng thời gi&uacute;p ti&ecirc;u h&oacute;a tốt hơn</p>\n	</li>\n</ul>\n\n<p><strong>- Phụ nữ mang thai v&agrave; trẻ em ăn nho c&oacute; được kh&ocirc;ng?</strong></p>\n\n<ul>\n	<li>\n	<p>Phụ nữ mang thai n&ecirc;n ăn nho với lượng vừa phải, khoảng 100-150 gram mỗi ng&agrave;y</p>\n	</li>\n	<li>\n	<p><strong>V&agrave;o 3 th&aacute;ng cuối thai kỳ</strong>, phụ nữ mang thai n&ecirc;n hạn chế ăn nho v&igrave; c&oacute; thể g&acirc;y ra t&igrave;nh trạng n&oacute;ng trong, ảnh hưởng đến sức khỏe của mẹ v&agrave; b&eacute;</p>\n	</li>\n	<li>\n	<p>Trẻ em tr&ecirc;n 1 tuổi c&oacute; thể ăn nho, cắt nho th&agrave;nh từng miếng nhỏ để trẻ dễ ăn v&agrave; tr&aacute;nh nguy cơ nghẹn.</p>\n	</li>\n	<li>\n	<p>Cho trẻ ăn nho với lượng vừa phải, khoảng 50-100 gram mỗi ng&agrave;y</p>\n	</li>\n</ul>\n', 'Trái cây Việt', 229000, 1, '2024-07-05', 7, '9a8bfd2c017140220d3331d2694feb0f', 'ik90ojn6vfkau8tf4b9j', 3, 2, 1),
(46, 'Thơm mini Thái Lan', '<h1><strong>hơm mini Th&aacute;i Lan&nbsp;</strong></h1>\n\n<p><strong>- Xuất xứ:</strong>&nbsp;Th&aacute;i Lan</p>\n\n<p><strong>- Chất lượng:&nbsp;</strong>Nhập khẩu</p>\n\n<p><strong>- Khối lượng:</strong>&nbsp;500G</p>\n\n<p><strong>- Đặc điểm nổi bật:</strong>&nbsp;Thơm mini, gọt sẵn tiện lợi, tr&aacute;i nhỏ nhưng ngọt v&ocirc; c&ugrave;ng, kh&ocirc;ng r&aacute;t lưỡi</p>\n\n<p><strong>- Đặc điểm:</strong></p>\n\n<ul>\n	<li>Thơm mini Th&aacute;i Lan, c&ograve;n c&oacute; t&ecirc;n gọi kh&aacute;c l&agrave; dứa mini Th&aacute;i Lan, l&agrave; một loại dứa (thơm) c&oacute; k&iacute;ch thước nhỏ hơn nhiều so với dứa Việt Nam.</li>\n	<li>Quả thơm mini Th&aacute;i Lan c&oacute; h&igrave;nh bầu dục, vỏ m&agrave;u v&agrave;ng tươi khi ch&iacute;n, mắt dứa nhỏ v&agrave; xếp kh&iacute;t nhau.</li>\n	<li>Thơm mini Th&aacute;i Lan c&oacute; trọng lượng trung b&igrave;nh từ 500 gram đến 1 kg, chiều d&agrave;i khoảng 20-25 cm.</li>\n	<li>Thịt thơm mini Th&aacute;i Lan m&agrave;u v&agrave;ng ươm, mọng nước, ngọt thanh, c&oacute; vị chua nhẹ v&agrave; thơm m&ugrave;i dứa đặc trưng.</li>\n	<li>Thơm mini Th&aacute;i Lan chứa nhiều vitamin C, vitamin B1, B6, kali, magie, phốt pho, chất xơ v&agrave; chất chống oxy h&oacute;a.</li>\n</ul>\n\n<p><strong>- C&ocirc;ng dụng:</strong></p>\n\n<ul>\n	<li>Ăn trực tiếp</li>\n	<li>L&agrave;m salad</li>\n	<li>L&agrave;m sinh tố</li>\n	<li>L&agrave;m mứt</li>\n	<li>D&ugrave;ng trong c&aacute;c m&oacute;n tr&aacute;ng miệng</li>\n</ul>\n\n<p><strong>- C&aacute;ch bảo quản thơm:</strong></p>\n\n<p><strong>+ Bảo quản ở nhiệt độ ph&ograve;ng:</strong></p>\n\n<ul>\n	<li>Khi mua về, nếu bạn chưa muốn ăn ngay, c&oacute; thể để Thơm mini Th&aacute;i Lan ở nhiệt độ ph&ograve;ng trong khoảng 2-3 ng&agrave;y.</li>\n	<li>N&ecirc;n để Thơm mini ở nơi kh&ocirc; r&aacute;o, tho&aacute;ng m&aacute;t, tr&aacute;nh &aacute;nh nắng trực tiếp v&agrave; nơi ẩm ướt.</li>\n	<li>Tr&aacute;nh xếp chồng Thơm mini l&ecirc;n nhau v&igrave; c&oacute; thể l&agrave;m dập n&aacute;t quả.</li>\n</ul>\n\n<p><strong>+ Bảo quản trong tủ lạnh:</strong></p>\n\n<ul>\n	<li>Nếu bạn muốn bảo quản Thơm mini Th&aacute;i Lan l&acirc;u hơn, c&oacute; thể cho v&agrave;o tủ lạnh.</li>\n	<li>N&ecirc;n rửa sạch Thơm mini v&agrave; để r&aacute;o nước trước khi cho v&agrave;o tủ lạnh.</li>\n	<li>C&oacute; thể bọc Thơm mini bằng giấy hoặc cho v&agrave;o hộp k&iacute;n để giữ độ tươi ngon.</li>\n	<li>Thơm mini Th&aacute;i Lan c&oacute; thể bảo quản trong tủ lạnh từ 5-7 ng&agrave;y.</li>\n</ul>\n\n<p><strong>+ Bảo quản Thơm mini đ&atilde; cắt:</strong></p>\n\n<ul>\n	<li>Thơm mini đ&atilde; cắt sẽ dễ bị mất đi độ tươi ngon v&agrave; vitamin hơn so với Thơm mini nguy&ecirc;n quả.</li>\n	<li>N&ecirc;n bọc Thơm mini đ&atilde; cắt bằng m&agrave;ng bọc thực phẩm hoặc cho v&agrave;o hộp k&iacute;n để bảo quản.</li>\n	<li>Thơm mini đ&atilde; cắt chỉ n&ecirc;n bảo quản trong tủ lạnh tối đa 2 ng&agrave;y.</li>\n</ul>\n\n<p>-&nbsp;<strong>C&aacute;ch lựa chọn thơm ngon:</strong></p>\n\n<ul>\n	<li>N&ecirc;n chọn những quả Thơm mini Th&aacute;i c&oacute; h&igrave;nh dạng đều đặn, c&acirc;n đối, kh&ocirc;ng bị m&eacute;o m&oacute; hay dập n&aacute;t.</li>\n	<li>Với Farmers Market ti&ecirc;u ch&iacute; h&agrave;ng đầu l&agrave; sản phảm đạt chất lượng cao, nguồn gốc r&otilde; r&agrave;ng, mang đến trải nghiệm tốt nhất cho kh&aacute;ch h&agrave;ng.&nbsp;</li>\n</ul>\n\n<p>-&nbsp;<strong>Phụ nữ mang thai v&agrave; trẻ em c&oacute; ăn thơm được kh&ocirc;ng?</strong></p>\n\n<p><strong>+&nbsp;</strong>Phụ nữ mang thai c&oacute; thể ăn thơm m&agrave; kh&ocirc;ng gặp vấn đề g&igrave; đ&aacute;ng lo ngại.</p>\n\n<ul>\n	<li><strong>Cung cấp vitamin v&agrave; kho&aacute;ng chất</strong>: Dứa chứa nhiều vitamin C, folate, sắt, magie, mangan, đồng v&agrave; vitamin B6. Những chất n&agrave;y đ&oacute;ng vai tr&ograve; quan trọng cho việc ph&aacute;t triển thai nhi</li>\n	<li>Tăng cường hệ miễn dịch v&agrave; hệ ti&ecirc;u h&oacute;a</li>\n	<li><strong>Phục hồi sau phẩu thuật</strong>: ăn thơm gi&uacute;p giảm th&acirc;m bầm t&iacute;m, sưng đau v&agrave; vi&ecirc;m sau phẩu thuật</li>\n	<li><strong>Điều h&ograve;a huyết &aacute;p:</strong>&nbsp;Thơm c&oacute; thể hỗ trợ điều h&ograve;a huyết &aacute;p, một kh&iacute;a cạnh quan trọng trong thai k&igrave;</li>\n	<li>Ăn với h&agrave;m lượng vừa phải khoảng&nbsp;<strong>1/2 dứa mỗi tuần&nbsp;</strong></li>\n</ul>\n\n<p>+ Trẻ em c&oacute; thể ăn dứa khi trẻ được 1&nbsp;tuổi &ndash; l&uacute;c n&agrave;y hệ ti&ecirc;u h&oacute;a của b&eacute; đ&atilde; dần th&iacute;ch nghi tốt với chế độ ăn dặm.&nbsp;</p>\n\n<ul>\n	<li>Hỗ trợ hoạt động&nbsp;n&atilde;o bộ</li>\n	<li>Củng cố sức đề kh&aacute;ng</li>\n	<li>Cải thiện hệ vận động</li>\n	<li>Ăn với h&agrave;m lượng vừa phải, tr&aacute;nh k&iacute;ch th&iacute;ch dạ d&agrave;y của trẻ</li>\n</ul>\n', 'Trái cây Việt', 69000, 1, '2024-07-05', 7, 'f862d852e2ec056e0efc3e0785532a0f', 'p88rmbf1k5pzibxwx3nb', 3, 3, 1),
(47, 'áo xanh Granny Smith New Zealand', '<h1><strong>T&aacute;o xanh Granny Smith New Zealand size 90 - 100&nbsp;</strong></h1>\n\n<p><strong>- Xuất xứ:&nbsp;</strong>New Zealand</p>\n\n<p><strong>- Chất lượng:</strong>&nbsp;Nhập khẩu</p>\n\n<p>-&nbsp;<strong>&nbsp;Khối lượng:&nbsp;</strong>1kg</p>\n\n<p><strong>- Đặc Điểm:</strong></p>\n\n<ul>\n	<li>T&aacute;o Granny Smith - loại t&aacute;o độc đ&aacute;o với vỏ m&agrave;u xanh b&oacute;ng&nbsp;</li>\n	<li>Nổi bật với độ gi&ograve;n cao, rất thơm &amp; đường &iacute;t, c&oacute; vị chua đặc trưng&nbsp;</li>\n	<li>T&aacute;o xanh&nbsp;thường d&ugrave;ng để l&agrave;m nước &eacute;p, detox, salad...</li>\n</ul>\n\n<p><strong>T&aacute;o Xanh Granny Smith&nbsp;</strong>c&oacute; hương vị chua thanh, ngọt dịu nhẹ, hương thơm dịu sẽ l&agrave; m&oacute;n tr&aacute;ng miệng kh&ocirc;ng thể thiếu sau mỗi bữa cơm. Ngo&agrave;i ra t&aacute;o xanh tươi c&ograve;n được biết đến với nhiều gi&aacute; trị dinh dưỡng tốt cho cơ thể.</p>\n\n<p><strong>- Lợi &iacute;ch:</strong></p>\n\n<ul>\n	<li><strong>Gi&agrave;u vitamin v&agrave; kho&aacute;ng chất:</strong>&nbsp;T&aacute;o chứa nhiều vitamin C, vitamin K, kali v&agrave; chất xơ, tốt cho sức khỏe tim mạch, hệ miễn dịch v&agrave; hệ ti&ecirc;u h&oacute;a.</li>\n	<li><strong>&Iacute;t calo:</strong>&nbsp;T&aacute;o chỉ chứa khoảng 60 calo mỗi quả, ph&ugrave; hợp cho những người ăn ki&ecirc;ng hoặc đang muốn giảm c&acirc;n.</li>\n	<li><strong>Tiện lợi:</strong>&nbsp;K&iacute;ch thước nhỏ gọn v&agrave; được đ&oacute;ng g&oacute;i ri&ecirc;ng lẻ gi&uacute;p t&aacute;o&nbsp;dễ d&agrave;ng mang theo v&agrave; thưởng thức mọi l&uacute;c mọi nơi.</li>\n	<li><strong>Gi&ograve;n tan v&agrave; mọng nước:</strong>&nbsp;T&aacute;p&nbsp;c&oacute; vị ngọt thanh, gi&ograve;n tan v&agrave; mọng nước, mang lại cảm gi&aacute;c ngon miệng khi thưởng thức.</li>\n</ul>\n\n<p><strong>- Hướng dẫn sử dụng:</strong></p>\n\n<ul>\n	<li>Rửa nhẹ nh&agrave;ng tr&aacute;i t&aacute;o. Sau đ&oacute; cắt đ&ocirc;i tr&aacute;i&nbsp;t&aacute;o&nbsp;v&agrave; loại bỏ phần hạt b&ecirc;n trong.</li>\n	<li>Bảo quản trong ngăn m&aacute;t&nbsp;tủ lạnh. T&aacute;o giữ được độ tươi, độ gi&ograve;n trong v&ograve;ng 1-4 tuần. Sau thời gian n&agrave;y, t&aacute;o sẽ ngọt hơn, độ gi&ograve;n thấp hơn v&agrave; t&aacute;o trở n&ecirc;n xốp.</li>\n	<li>Cần tr&aacute;nh để t&aacute;o với c&aacute;c thực phẩm c&oacute; m&ugrave;i kh&aacute;c như h&agrave;nh, tỏi, t&aacute;o sẽ dễ nhiễm m&ugrave;i.</li>\n	<li>Kh&ocirc;ng rửa&nbsp;trước khi cho v&agrave;o tủ lạnh.</li>\n</ul>\n\n<p><strong>- T&aacute;o c&oacute; thể được sử dụng để l&agrave;m g&igrave;?</strong></p>\n\n<ul>\n	<li>Sinh tố: t&aacute;o kết hợp với c&aacute;c loại tr&aacute;i c&acirc;y kh&aacute;c như chuối, d&acirc;u t&acirc;y, sữa chua,... để tạo th&agrave;nh m&oacute;n sinh tố thơm ngon v&agrave; đầy dinh dưỡng.</li>\n	<li>Salad: T&aacute;o&nbsp;cắt nhỏ, trộn với c&aacute;c loại rau củ quả kh&aacute;c c&ugrave;ng với nước sốt y&ecirc;u th&iacute;ch để tạo th&agrave;nh m&oacute;n salad thanh m&aacute;t v&agrave; đầy đủ dưỡng chất.</li>\n	<li>Nước &eacute;p: T&aacute;o&nbsp;&eacute;p lấy nước c&oacute; thể uống trực tiếp hoặc pha th&ecirc;m mật ong để tăng hương vị.</li>\n	<li>B&aacute;nh t&aacute;o: T&aacute;o&nbsp;được sử dụng l&agrave;m nh&acirc;n cho b&aacute;nh t&aacute;o hoặc trang tr&iacute; mặt b&aacute;nh.</li>\n	<li>Mứt t&aacute;o: T&aacute;o c&oacute; thể được nấu th&agrave;nh mứt để ăn k&egrave;m với b&aacute;nh m&igrave; hoặc sử dụng trong c&aacute;c m&oacute;n tr&aacute;ng miệng.</li>\n</ul>\n\n<p><strong>- C&aacute;ch lựa chọn t&aacute;o:</strong></p>\n\n<ul>\n	<li>\n	<p>Kh&aacute;ch h&agrave;ng&nbsp;n&ecirc;n&nbsp;chọn những quả t&aacute;o c&oacute; h&igrave;nh dạng c&acirc;n đối, kh&ocirc;ng bị m&eacute;o m&oacute;, dập n&aacute;t.</p>\n	</li>\n	<li>\n	<p>Với Farmers Market ti&ecirc;u ch&iacute; h&agrave;ng đầu l&agrave; sản phảm đạt chất lượng cao, nguồn gốc r&otilde; r&agrave;ng, mang đến trải nghiệm tốt nhất cho kh&aacute;ch h&agrave;ng.&nbsp;</p>\n	</li>\n</ul>\n\n<p><strong>- Hướng dẫn bảo quản t&aacute;o</strong></p>\n\n<ul>\n	<li>N&ecirc;n đặt nho v&agrave;o&nbsp;<strong>hộp k&iacute;n kh&iacute;</strong>&nbsp;hoặc&nbsp;<strong>t&uacute;i ziplock&nbsp;</strong>để giữ độ ẩm v&agrave;&nbsp;bảo quan t&aacute;o&nbsp;trong ngăn m&aacute;t tủ lạnh&nbsp;</li>\n	<li>Kh&ocirc;ng n&ecirc;n bảo quản t&aacute;o c&ugrave;ng với c&aacute;c loại tr&aacute;i c&acirc;y kh&aacute;c như chuối, l&ecirc;,... v&igrave; ethylene từ những loại tr&aacute;i c&acirc;y n&agrave;y c&oacute; thể khiến t&aacute;o nhanh ch&iacute;n v&agrave; mềm.</li>\n	<li>T&aacute;o c&oacute; thể bảo quản trong tủ lạnh từ 1-2 tuần.</li>\n</ul>\n\n<p><strong>- Những lưu &yacute; khi sử dụng T&aacute;o:&nbsp;</strong></p>\n\n<ul>\n	<li>Kh&ocirc;ng n&ecirc;n ăn t&aacute;o&nbsp;đ&atilde; bị dập n&aacute;t hoặc hư hỏng.</li>\n	<li>N&ecirc;n ăn t&aacute;o&nbsp;với lượng vừa phải, khoảng 1-2 quả mỗi ng&agrave;y.</li>\n	<li>Người bị bệnh tiểu đường n&ecirc;n ăn t&aacute;o với lượng vừa phải v&igrave; t&aacute;o chứa đường tự nhi&ecirc;n.</li>\n	<li>Trẻ em bị ti&ecirc;u chảy hoặc t&aacute;o b&oacute;n kh&ocirc;ng n&ecirc;n ăn t&aacute;o.</li>\n</ul>\n\n<p><strong>- Phụ nữ mang thai v&agrave; trẻ em ăn t&aacute;o&nbsp;c&oacute; được kh&ocirc;ng?</strong></p>\n\n<ul>\n	<li>T&aacute;o&nbsp;cung cấp vitamin C, folate v&agrave; chất xơ&nbsp;<strong>cần thiết cho phụ nữ mang thai v&agrave; cho con b&uacute;.</strong></li>\n	<li>Chất xơ trong t&aacute;o gi&uacute;p giảm t&aacute;o b&oacute;n, vitamin C tăng cường hệ miễn dịch, folate<strong>&nbsp;tốt cho sự ph&aacute;t triển của thai nhi</strong>.</li>\n	<li>\n	<p>Trẻ em tr&ecirc;n 1 tuổi c&oacute; thể ăn t&aacute;o, cắt nho th&agrave;nh từng miếng nhỏ để trẻ dễ ăn v&agrave; tr&aacute;nh nguy cơ nghẹn.</p>\n	</li>\n</ul>\n\n<ul>\n	<li><strong>Cho trẻ ăn t&aacute;o với lượng vừa phải:</strong>&nbsp;Trẻ em n&ecirc;n ăn t&aacute;o với lượng vừa phải, khoảng 50-100 gram mỗi ng&agrave;y. Ăn qu&aacute; nhiều t&aacute;o c&oacute; thể g&acirc;y đầy bụng, kh&oacute; ti&ecirc;u.</li>\n</ul>\n', 'Trái cây Việt', 99000, 1, '2024-07-05', 7, '150103e8a2905e78c441d313d9a1abba', 'nbxajk0pmglojx6jqrtk', 3, 1, 1),
(48, 'Táo Rockit New Zealand', '<h1><strong>T&aacute;o Rockit New Zealand&nbsp;</strong></h1>\n\n<p><strong>- Xuất xứ</strong>:&nbsp;New Zealand&nbsp;</p>\n\n<p><strong>- Trọng lượng:</strong>&nbsp;4 tr&aacute;i/ ống&nbsp;- 372g</p>\n\n<p><strong>- Ti&ecirc;u chuẩn chất lượng</strong>: Nhập khẩu</p>\n\n<p>-&nbsp;<strong>Đặc điểm sản phẩm:&nbsp;</strong></p>\n\n<ul>\n	<li>&nbsp;Tr&aacute;i vừa ăn, gi&ograve;n, ngọt v&agrave; c&oacute; ch&uacute;t chua nhẹ.</li>\n	<li>&nbsp;Loại t&aacute;o ngon được nhiều người y&ecirc;u th&iacute;ch. Tr&aacute;i to vừa với hộp ống tiện lợi mang đi.</li>\n</ul>\n\n<p><strong>- Lợi &iacute;ch:</strong></p>\n\n<ul>\n	<li><strong>Gi&agrave;u vitamin v&agrave; kho&aacute;ng chất:</strong>&nbsp;T&aacute;o Rockit chứa nhiều vitamin C, vitamin K, kali v&agrave; chất xơ, tốt cho sức khỏe tim mạch, hệ miễn dịch v&agrave; hệ ti&ecirc;u h&oacute;a.</li>\n	<li><strong>&Iacute;t calo:</strong>&nbsp;T&aacute;o Rockit chỉ chứa khoảng 60 calo mỗi quả, ph&ugrave; hợp cho những người ăn ki&ecirc;ng hoặc đang muốn giảm c&acirc;n.</li>\n	<li><strong>Tiện lợi:</strong>&nbsp;K&iacute;ch thước nhỏ gọn v&agrave; được đ&oacute;ng g&oacute;i ri&ecirc;ng lẻ gi&uacute;p t&aacute;o Rockit dễ d&agrave;ng mang theo v&agrave; thưởng thức mọi l&uacute;c mọi nơi.</li>\n	<li><strong>Gi&ograve;n tan v&agrave; mọng nước:</strong>&nbsp;T&aacute;o Rockit c&oacute; vị ngọt thanh, gi&ograve;n tan v&agrave; mọng nước, mang lại cảm gi&aacute;c ngon miệng khi thưởng thức.</li>\n</ul>\n\n<p><strong>- Hướng dẫn sử dụng:</strong></p>\n\n<ul>\n	<li>Rửa nhẹ nh&agrave;ng tr&aacute;i t&aacute;o. Sau đ&oacute; cắt đ&ocirc;i tr&aacute;i&nbsp;t&aacute;o&nbsp;v&agrave; loại bỏ phần hạt b&ecirc;n trong.</li>\n	<li>Bảo quản trong ngăn m&aacute;t&nbsp;tủ lạnh. T&aacute;o giữ được độ tươi, độ gi&ograve;n trong v&ograve;ng 1-4 tuần. Sau thời gian n&agrave;y, t&aacute;o sẽ ngọt hơn, độ gi&ograve;n thấp hơn v&agrave; t&aacute;o trở n&ecirc;n xốp.</li>\n	<li>Cần tr&aacute;nh để t&aacute;o với c&aacute;c thực phẩm c&oacute; m&ugrave;i kh&aacute;c như h&agrave;nh, tỏi, t&aacute;o sẽ dễ nhiễm m&ugrave;i.</li>\n	<li>Kh&ocirc;ng rửa&nbsp;trước khi cho v&agrave;o tủ lạnh.</li>\n</ul>\n\n<p><strong>- T&aacute;o c&oacute; thể được sử dụng để l&agrave;m g&igrave;?</strong></p>\n\n<ul>\n	<li>Sinh tố: t&aacute;o kết hợp với c&aacute;c loại tr&aacute;i c&acirc;y kh&aacute;c như chuối, d&acirc;u t&acirc;y, sữa chua,... để tạo th&agrave;nh m&oacute;n sinh tố thơm ngon v&agrave; đầy dinh dưỡng.</li>\n	<li>Salad: T&aacute;o&nbsp;cắt nhỏ, trộn với c&aacute;c loại rau củ quả kh&aacute;c c&ugrave;ng với nước sốt y&ecirc;u th&iacute;ch để tạo th&agrave;nh m&oacute;n salad thanh m&aacute;t v&agrave; đầy đủ dưỡng chất.</li>\n	<li>Nước &eacute;p: T&aacute;o&nbsp;&eacute;p lấy nước c&oacute; thể uống trực tiếp hoặc pha th&ecirc;m mật ong để tăng hương vị.</li>\n	<li>B&aacute;nh t&aacute;o: T&aacute;o&nbsp;được sử dụng l&agrave;m nh&acirc;n cho b&aacute;nh t&aacute;o hoặc trang tr&iacute; mặt b&aacute;nh.</li>\n	<li>Mứt t&aacute;o: T&aacute;o c&oacute; thể được nấu th&agrave;nh mứt để ăn k&egrave;m với b&aacute;nh m&igrave; hoặc sử dụng trong c&aacute;c m&oacute;n tr&aacute;ng miệng.</li>\n</ul>\n\n<p><strong>- C&aacute;ch lựa chọn t&aacute;o:</strong></p>\n\n<ul>\n	<li>\n	<p>Kh&aacute;ch h&agrave;ng&nbsp;n&ecirc;n&nbsp;chọn những quả t&aacute;o c&oacute; h&igrave;nh dạng c&acirc;n đối, kh&ocirc;ng bị m&eacute;o m&oacute;, dập n&aacute;t.</p>\n	</li>\n	<li>\n	<p>Với Farmers Market ti&ecirc;u ch&iacute; h&agrave;ng đầu l&agrave; sản phảm đạt chất lượng cao, nguồn gốc r&otilde; r&agrave;ng, mang đến trải nghiệm tốt nhất cho kh&aacute;ch h&agrave;ng.&nbsp;</p>\n	</li>\n</ul>\n\n<p><strong>- Hướng dẫn bảo quản t&aacute;o</strong></p>\n\n<ul>\n	<li>N&ecirc;n đặt nho v&agrave;o&nbsp;<strong>hộp k&iacute;n kh&iacute;</strong>&nbsp;hoặc&nbsp;<strong>t&uacute;i ziplock&nbsp;</strong>để giữ độ ẩm v&agrave;&nbsp;bảo quan t&aacute;o&nbsp;trong ngăn m&aacute;t tủ lạnh&nbsp;</li>\n	<li>Kh&ocirc;ng n&ecirc;n bảo quản t&aacute;o c&ugrave;ng với c&aacute;c loại tr&aacute;i c&acirc;y kh&aacute;c như chuối, l&ecirc;,... v&igrave; ethylene từ những loại tr&aacute;i c&acirc;y n&agrave;y c&oacute; thể khiến t&aacute;o nhanh ch&iacute;n v&agrave; mềm.</li>\n	<li>T&aacute;o c&oacute; thể bảo quản trong tủ lạnh từ 1-2 tuần.</li>\n</ul>\n\n<p><strong>- Những lưu &yacute; khi sử dụng T&aacute;o:&nbsp;</strong></p>\n\n<ul>\n	<li>Kh&ocirc;ng n&ecirc;n ăn t&aacute;o&nbsp;đ&atilde; bị dập n&aacute;t hoặc hư hỏng.</li>\n	<li>N&ecirc;n ăn t&aacute;o&nbsp;với lượng vừa phải, khoảng 1-2 quả mỗi ng&agrave;y.</li>\n	<li>Người bị bệnh tiểu đường n&ecirc;n ăn t&aacute;o với lượng vừa phải v&igrave; t&aacute;o chứa đường tự nhi&ecirc;n.</li>\n	<li>Trẻ em bị ti&ecirc;u chảy hoặc t&aacute;o b&oacute;n kh&ocirc;ng n&ecirc;n ăn t&aacute;o.</li>\n</ul>\n\n<p><strong>- Phụ nữ mang thai v&agrave; trẻ em ăn t&aacute;o&nbsp;c&oacute; được kh&ocirc;ng?</strong></p>\n\n<ul>\n	<li>T&aacute;o&nbsp;cung cấp vitamin C, folate v&agrave; chất xơ&nbsp;<strong>cần thiết cho phụ nữ mang thai v&agrave; cho con b&uacute;.</strong></li>\n	<li>Chất xơ trong t&aacute;o gi&uacute;p giảm t&aacute;o b&oacute;n, vitamin C tăng cường hệ miễn dịch, folate<strong>&nbsp;tốt cho sự ph&aacute;t triển của thai nhi</strong>.</li>\n	<li>\n	<p>Trẻ em tr&ecirc;n 1 tuổi c&oacute; thể ăn t&aacute;o, cắt nho th&agrave;nh từng miếng nhỏ để trẻ dễ ăn v&agrave; tr&aacute;nh nguy cơ nghẹn.</p>\n	</li>\n</ul>\n\n<ul>\n	<li><strong>Cho trẻ ăn t&aacute;o với lượng vừa phải:</strong>&nbsp;Trẻ em n&ecirc;n ăn t&aacute;o với lượng vừa phải, khoảng 50-100 gram mỗi ng&agrave;y. Ăn qu&aacute; nhiều t&aacute;o c&oacute; thể g&acirc;y đầy bụng, kh&oacute; ti&ecirc;u.</li>\n</ul>\n', 'Trái cây Việt', 99000, 1, '2024-07-05', 7, 'cc3b3eb37f78a1e2e0bdb84e1d2b828b', 'fbmnzbpht3rggmuhlhwb', 3, 2, 1),
(49, 'Nho đỏ kẹo Candy Snaps', '<h1><strong>Nho đỏ kẹo Candy Snaps Mỹ&nbsp;</strong></h1>\n\n<p><strong>- Xuất xứ:</strong>&nbsp;Mỹ</p>\n\n<p><strong>- Ti&ecirc;u chuẩn chất lượng</strong>: Nhập khẩu</p>\n\n<p><strong>- Đặc điểm nổi bật:</strong></p>\n\n<ul>\n	<li>Nho kẹo l&agrave; sự kết hợp độc đ&aacute;o giữa vị nho v&agrave; kẹo lạ miệng&nbsp;</li>\n	<li>H&agrave;ng đầu m&ugrave;a, nho ngọt chua xen kẽ</li>\n	<li>Đ&oacute;ng hộp 454g tiện lợi, ch&ugrave;m đầy, tr&aacute;i đẹp thơm ngon hấp dẫn&nbsp;</li>\n	<li>Cam kết chất lượng nhập khẩu Mỹ, giấy tờ đầy đủ</li>\n</ul>\n\n<p>&nbsp;</p>\n\n<p><strong>- Th&ocirc;ng tin dinh dưỡng:</strong></p>\n\n<ul>\n	<li>Nho đỏ c&oacute; thể giảm nguy cơ tai biến tim mạch, giảm t&aacute;c hại của thuốc l&aacute; đối với phổi, ngăn ngừa ung thư v&agrave; gi&uacute;p cơ thể chống đỡ tốt hơn những t&aacute;c dụng phụ m&agrave; việc điều trị ung thư g&acirc;y ra, rất ph&ugrave; hợp l&agrave;m qu&agrave; biếu.</li>\n	<li>Hợp chất resveratrol trong lớp vỏ của nho đỏ (nho xanh kh&ocirc;ng c&oacute; chất n&agrave;y) c&oacute; t&aacute;c dụng ngăn hồng cầu kết d&iacute;nh v&agrave;o nhau, l&agrave;m giảm nguy cơ h&igrave;nh th&agrave;nh c&aacute;c cục m&aacute;u đ&ocirc;ng, tăng cholesterol tốt (HDL) v&agrave; giảm cholesterol xấu (LDL). Resveratrol c&oacute; khả năng chống oxy h&oacute;a mạnh gấp 7 lần vitamin E.</li>\n	<li>Nước &eacute;p từ quả nho đỏ c&oacute; khả năng ngăn ngừa ung thư v&agrave; tăng cường sức chống đỡ của cơ thể trước những t&aacute;c hại của h&oacute;a trị v&agrave; xạ trị. Một nghi&ecirc;n cứu kh&aacute;c cho thấy, nước &eacute;p nho đỏ c&oacute; hoạt t&iacute;nh chống oxy h&oacute;a mạnh gấp 3 lần so với nước &eacute;p c&agrave; chua, cam, bưởi.</li>\n</ul>\n\n<p><strong>- C&aacute;ch bảo quản:</strong></p>\n\n<ul>\n	<li>Nhiệt độ để bảo quản nho từ khoảng 0 &ndash; 4 độ C,&nbsp; lu&ocirc;n lu&ocirc;n để nho chưa rửa trong hộp k&iacute;n v&agrave;o tủ lạnh để kh&ocirc;ng l&agrave;m đi lớp phấn phủ tự nhi&ecirc;n của quả Nho, chỉ n&ecirc;n rửa trước khi ăn hoặc chế biến.&nbsp;</li>\n	<li>Nho thường d&ugrave;ng để ăn tươi hoặc &eacute;p nước rất ngon.</li>\n</ul>\n\n<p><strong>- C&aacute;ch lựa chọn:</strong></p>\n\n<ul>\n	<li>\n	<p>Kh&aacute;ch h&agrave;ng&nbsp;n&ecirc;n&nbsp;chọn những quả nho c&oacute; quả&nbsp;to, đều, vỏ căng b&oacute;ng, kh&ocirc;ng bị dập n&aacute;t.</p>\n	</li>\n	<li>\n	<p>Với Farmers Market ti&ecirc;u ch&iacute; h&agrave;ng đầu l&agrave; sản phảm đạt chất lượng cao, nguồn gốc r&otilde; r&agrave;ng, mang đến trải nghiệm tốt nhất cho kh&aacute;ch h&agrave;ng.&nbsp;</p>\n	</li>\n</ul>\n\n<p>-&nbsp;<strong>C&aacute;ch bảo quản</strong><strong>:&nbsp;</strong></p>\n\n<ul>\n	<li>\n	<p>N&ecirc;n đặt nho v&agrave;o&nbsp;<strong>hộp k&iacute;n kh&iacute;</strong>&nbsp;hoặc&nbsp;<strong>t&uacute;i ziplock&nbsp;</strong>để giữ độ ẩm v&agrave;&nbsp;bảo quan nho trong ngăn m&aacute;t tủ lạnh&nbsp;</p>\n	</li>\n	<li>\n	<p>Kh&ocirc;ng n&ecirc;n rửa nho trước khi bảo quản trong ngăn m&aacute;t tủ lạnh v&igrave; nếu rửa trước nho sẽ dễ bị hư, thối.</p>\n	</li>\n	<li>\n	<p>Kiểm tra thường xuy&ecirc;n v&agrave; loại bỏ những tr&aacute;i hư hỏng</p>\n	</li>\n</ul>\n\n<ul>\n	<li>\n	<p>Kh&ocirc;ng n&ecirc;n bảo quản nho ở nhiệt độ ph&ograve;ng v&igrave; nho sẽ ch&iacute;n nhanh v&agrave; dễ bị hư hỏng.</p>\n	</li>\n	<li>\n	<p>Kh&ocirc;ng n&ecirc;n đ&ocirc;ng lạnh nho v&igrave; nho sẽ bị mềm nhũn v&agrave; mất đi hương vị.</p>\n	</li>\n</ul>\n\n<p><strong>- Những lưu &yacute; khi thưởng thức nho :&nbsp;</strong></p>\n\n<ul>\n	<li>\n	<p><strong>Kh&ocirc;ng n&ecirc;n ăn nho qu&aacute; nhiều</strong>&nbsp;v&igrave; c&oacute; thể g&acirc;y đầy bụng, kh&oacute; ti&ecirc;u.</p>\n	</li>\n	<li>\n	<p><strong>Người bị bệnh tiểu đường&nbsp;</strong>n&ecirc;n tham khảo &yacute; kiến b&aacute;c sĩ trước khi ăn nho.</p>\n	</li>\n	<li>\n	<p><strong>Ăn nho sau bữa ăn</strong>&nbsp;sẽ gi&uacute;p hạn chế lượng đường nạp v&agrave;o cơ thể, đồng thời gi&uacute;p ti&ecirc;u h&oacute;a tốt hơn</p>\n	</li>\n</ul>\n\n<p><strong>- Phụ nữ mang thai v&agrave; trẻ em ăn nho c&oacute; được kh&ocirc;ng?</strong></p>\n\n<ul>\n	<li>\n	<p>Phụ nữ mang thai n&ecirc;n ăn nho với lượng vừa phải, khoảng 100-150 gram mỗi ng&agrave;y</p>\n	</li>\n	<li>\n	<p><strong>V&agrave;o 3 th&aacute;ng cuối thai kỳ</strong>, phụ nữ mang thai n&ecirc;n hạn chế ăn nho v&igrave; c&oacute; thể g&acirc;y ra t&igrave;nh trạng n&oacute;ng trong, ảnh hưởng đến sức khỏe của mẹ v&agrave; b&eacute;</p>\n	</li>\n	<li>\n	<p>Trẻ em tr&ecirc;n 1 tuổi c&oacute; thể ăn nho, cắt nho th&agrave;nh từng miếng nhỏ để trẻ dễ ăn v&agrave; tr&aacute;nh nguy cơ nghẹn.</p>\n	</li>\n	<li>\n	<p>Cho trẻ ăn nho với lượng vừa phải, khoảng 50-100 gram mỗi ng&agrave;y</p>\n	</li>\n</ul>\n', 'Trái cây Việt', 209000, 1, '2024-07-05', 7, '87cbfc55c2e4b7af45837c2475cf7ead', 'w5diy4q5j7eho48oaw1f', 3, 3, 1),
(50, 'Lê sữa nội địa Trung', '<h1><strong>L&ecirc; sữa nội địa Trung</strong></h1>\n\n<p><strong>- Xuất xứ:</strong>&nbsp;Nội địa Trung</p>\n\n<p><strong>- Ti&ecirc;u chuẩn chất lượng:</strong>&nbsp;Nhập khẩu</p>\n\n<p><strong>- Quy c&aacute;ch:&nbsp;</strong>1kg<br />\n<br />\n<strong>- Đặc điểm:</strong></p>\n\n<ul>\n	<li>L&ecirc; c&oacute; vỏ mỏng, căng, mượt,&nbsp;nhiều nước, thịt l&ecirc; gi&ograve;n v&agrave; ngọt, quả to, tươi ngon kh&oacute; cưỡng.&nbsp;</li>\n	<li>C&oacute; thể sử dụng l&ecirc; tươi để l&agrave;m nước &eacute;p, l&agrave;m b&aacute;nh, nấu ch&egrave; giải nhiệt.</li>\n	<li>Thịt l&ecirc; gi&ograve;n, ngọt thanh, nhiều nước, c&oacute; vị mật ong đặc trưng.</li>\n	<li>Quả to, trọng lượng trung b&igrave;nh từ 500g - 1kg.</li>\n	<li>L&otilde;i nhỏ, chỉ chiếm khoảng 10% trọng lượng quả.</li>\n	<li>Chứa nhiều vitamin C, kali, chất xơ v&agrave; c&aacute;c kho&aacute;ng chất thiết yếu cho cơ thể.</li>\n</ul>\n\n<p><strong>L&ecirc; sữa nội địa Trung</strong>&nbsp;được trồng chủ yếu ở c&aacute;c tỉnh ph&iacute;a T&acirc;y Bắc của Trung Quốc như Thiểm T&acirc;y, Cam T&uacute;c, T&acirc;n Cương. Loại l&ecirc; n&agrave;y được trồng tại độ cao 1500 - 2000m so với mực nước biển, kh&iacute; hậu &ocirc;n h&ograve;a, ban ng&agrave;y nắng nhiều, ban đ&ecirc;m m&aacute;t mẻ, tạo điều kiện cho l&ecirc; sữa ph&aacute;t triển tốt nhất</p>\n\n<p><strong>- Lợi &iacute;ch:</strong></p>\n\n<ul>\n	<li>Tăng cường hệ miễn dịch, ph&ograve;ng chống c&aacute;c bệnh cảm c&uacute;m, ho.</li>\n	<li>Hỗ trợ hệ ti&ecirc;u h&oacute;a khỏe mạnh, ngăn ngừa t&aacute;o b&oacute;n.</li>\n	<li>Gi&uacute;p thanh nhiệt, giải độc cơ thể.</li>\n	<li>L&agrave;m đẹp da, chống l&atilde;o h&oacute;a.</li>\n	<li>Tốt cho tim mạch, gi&uacute;p hạ huyết &aacute;p.</li>\n</ul>\n\n<p><strong>- Hướng dẫn sử dụng:</strong></p>\n\n<ul>\n	<li>Rửa nhẹ nh&agrave;ng tr&aacute;i l&ecirc;. Sau đ&oacute; cắt đ&ocirc;i tr&aacute;i l&ecirc; v&agrave; loại bỏ phần hạt b&ecirc;n trong.</li>\n	<li>Vỏ l&ecirc; chứa rất nhiều chất dinh dưỡng, bạn kh&ocirc;ng n&ecirc;n gọt bỏ.</li>\n	<li>Giữ lạnh&nbsp;l&ecirc;&nbsp;gi&uacute;p bảo quản l&acirc;u hơn.</li>\n	<li>Kh&ocirc;ng rửa&nbsp;trước khi cho v&agrave;o tủ lạnh</li>\n</ul>\n\n<p><strong>- C&aacute;ch sử dụng l&ecirc;:</strong></p>\n\n<ul>\n	<li>L&ecirc; sữa c&oacute; thể ăn trực tiếp, &eacute;p nước, l&agrave;m sinh tố, salad, hoặc chế biến th&agrave;nh c&aacute;c m&oacute;n ăn kh&aacute;c như ch&egrave;, b&aacute;nh</li>\n</ul>\n\n<p><strong>- C&aacute;ch lựa chọn l&ecirc; ngon:</strong></p>\n\n<ul>\n	<li>N&ecirc;n chọn mua l&ecirc; c&oacute; vỏ mịn, &iacute;t đốm v&agrave; nhạt m&agrave;u. Tr&aacute;nh chọn những quả c&oacute; lớp vỏ hơi th&ocirc;, xuất hiện nhiều đường kẻ m&agrave;u n&acirc;u, đốm đen v&agrave; m&agrave;u hơi sẫm. Những quả như vậy thường &iacute;t nước v&agrave; sắp bị hỏng</li>\n	<li>Với Farmers Market ti&ecirc;u ch&iacute; h&agrave;ng đầu l&agrave; sản phảm đạt chất lượng cao, nguồn gốc r&otilde; r&agrave;ng, mang đến trải nghiệm tốt nhất cho kh&aacute;ch h&agrave;ng.&nbsp;</li>\n</ul>\n\n<p><strong>- C&aacute;ch bảo quản l&ecirc; được l&acirc;u:</strong></p>\n\n<p><strong>+ Bảo quản l&ecirc; ở nhiệt độ ph&ograve;ng:</strong></p>\n\n<ul>\n	<li>Nếu bạn muốn l&ecirc; ch&iacute;n nhanh hơn, bạn c&oacute; thể bảo quản l&ecirc; ở nhiệt độ ph&ograve;ng.</li>\n	<li>Đặt l&ecirc; trong t&uacute;i giấy hoặc t&uacute;i nhựa v&agrave; để ở nơi tho&aacute;ng m&aacute;t, tr&aacute;nh &aacute;nh nắng trực tiếp.</li>\n	<li>L&ecirc; sẽ ch&iacute;n trong v&ograve;ng v&agrave;i ng&agrave;y.</li>\n</ul>\n\n<p><strong>+ Bảo quản l&ecirc; trong tủ lạnh:</strong></p>\n\n<ul>\n	<li>Nếu bạn muốn bảo quản l&ecirc; l&acirc;u hơn, bạn n&ecirc;n bảo quản l&ecirc; trong tủ lạnh.</li>\n	<li>Đặt l&ecirc; v&agrave;o khay mềm v&agrave; để trong ngăn m&aacute;t tủ lạnh.</li>\n	<li>L&ecirc; sẽ giữ được tươi ngon trong v&agrave;i tuần.</li>\n	<li>Kh&ocirc;ng n&ecirc;n rửa l&ecirc; trước khi bảo quản v&igrave; điều n&agrave;y c&oacute; thể l&agrave;m tăng nguy cơ thối rữa.</li>\n	<li>N&ecirc;n bảo quản l&ecirc; ri&ecirc;ng với c&aacute;c loại tr&aacute;i c&acirc;y v&agrave; rau củ kh&aacute;c v&igrave; l&ecirc; c&oacute; thể hấp thụ m&ugrave;i của c&aacute;c loại thực phẩm kh&aacute;c.</li>\n</ul>\n\n<p><strong>- Phụ nữ mang thai v&agrave; trẻ em ăn l&ecirc; sữa c&oacute; được kh&ocirc;ng?</strong></p>\n\n<ul>\n	<li><strong>Quả l&ecirc;</strong>&nbsp;gi&uacute;p ngăn ngừa c&aacute;c bệnh truyền nhiễm&nbsp;th&ocirc;ng thường như cảm lạnh, ho, c&uacute;m.L&ecirc; c&ograve;n l&agrave; nguồn gi&agrave;u vitamin C,&nbsp;<strong>rất tốt cho thai kỳ</strong>. Một quả l&ecirc; đơn chứa 10mg vitamin, chiếm tới 11% lượng bổ sung thực phẩm được đề nghị h&agrave;ng ng&agrave;y (RDA) cho phụ nữ.</li>\n	<li><strong>Khi b&eacute; được 4 đến 6 th&aacute;ng tuổi c&oacute; thể ăn l&ecirc; ở dạng nhuyễn</strong>. L&ecirc; gi&uacute;p ngăn ngừa t&aacute;o b&oacute;n ở trẻ sơ sinh. Ch&uacute;ng cũng chứa nhiều vitamin C v&agrave; Kali, c&oacute; lợi cho sự ph&aacute;t triển to&agrave;n diện ở trẻ. Mẹ cần quan s&aacute;t cho trẻ ăn loại quả n&agrave;y, để đảm bảo trẻ kh&ocirc;ng bị kh&oacute; chịu dạ d&agrave;y v&agrave; tr&agrave;o ngược.</li>\n</ul>\n\n<p><strong>- Những lưu &yacute; khi d&ugrave;ng l&ecirc;:&nbsp;</strong></p>\n\n<ul>\n	<li>Nếu ăn l&ecirc; kết hợp với củ cải sẽ l&agrave;m sưng tuyến gi&aacute;p.</li>\n	<li>Những người c&oacute; đường ti&ecirc;u h&oacute;a yếu hoặc rối loạn đường ti&ecirc;u h&oacute;a h&ocirc;ng n&ecirc;n ăn rau dền c&ugrave;ng với l&ecirc;. Sẽ dễ bị n&ocirc;n v&agrave; xuất hiện c&aacute;c vấn đề ti&ecirc;u h&oacute;a kh&aacute;c.</li>\n	<li>Sau sinh ăn l&ecirc;&nbsp;giai đoạn ở cữ sẽ kh&ocirc;ng c&oacute; lợi cho mẹ</li>\n</ul>\n', 'Trái cây Việt', 99000, 1, '2024-07-05', 7, '26aaba73889ecb2770399309525d8aca', 'ei0qgij9tgfskamjauto', 3, 3, 1);
INSERT INTO `products` (`id`, `nameOfProduct`, `description`, `category`, `price`, `weightDefault`, `dateOfImporting`, `expriredDay`, `imgAssetId`, `imgPublicId`, `adminCreate`, `provider`, `status`) VALUES
(51, 'Nho mẫu đơn Premium Hàn Quốc', '<h1><strong>Nho mẫu đơn Premium H&agrave;n Quốc (1kg)</strong></h1>\n\n<p><strong>- Xuất xứ:</strong>&nbsp;H&agrave;n Quốc</p>\n\n<p><strong>- Trọng lượng sản phẩm1K</strong>G/hộp</p>\n\n<p><strong>- Đặc điểm:</strong></p>\n\n<ul>\n	<li>Đ&oacute;ng g&oacute;i: 60-7000G/Hộp</li>\n	<li>Tr&aacute;i to, dạng nho sữa tr&aacute;i&nbsp;mềm, ko gi&ograve;n, độ ngọt vừa phải, thơm sữa thoang thoảng.&nbsp;</li>\n	<li>&nbsp;Vỏ mỏng, thịt quả d&agrave;y, độ ngọt ho&agrave;n hảo, thơm m&ugrave;i sữa, đặc biệt kh&ocirc;ng bị ch&aacute;t vỏ như những d&ograve;ng nho xanh kh&aacute;c.</li>\n</ul>\n\n<p>-&nbsp;<strong>Gi&aacute; trị dinh dưỡng:</strong>&nbsp;Nho Mẫu Đơn chứa nhiều vitamin, kho&aacute;ng chất v&agrave; chất chống oxy h&oacute;a, gi&uacute;p tăng cường sức khỏe, hệ miễn dịch v&agrave; l&agrave;m đẹp da.</p>\n\n<p><strong>Nho Mẫu Đơn&nbsp;</strong>c&oacute; h&igrave;nh d&aacute;ng to tr&ograve;n, vỏ mỏng m&agrave;u hồng hoặc đỏ, quả mọng nước, vị ngọt thanh v&agrave; hương thơm dịu nhẹ. Nho Mẫu Đơn được đ&aacute;nh gi&aacute; cao về chất lượng v&agrave; gi&aacute; trị dinh dưỡng, được nhiều người y&ecirc;u th&iacute;ch v&agrave; ưa chuộng.</p>\n\n<p>-&nbsp;<strong>Lợi &iacute;ch sức khỏe:</strong></p>\n\n<ul>\n	<li>Nho Mẫu Đơn chứa nhiều vitamin C, gi&uacute;p tăng cường hệ miễn dịch v&agrave; bảo vệ cơ thể khỏi c&aacute;c bệnh nhiễm tr&ugrave;ng.</li>\n	<li>Nho Mẫu Đơn chứa nhiều chất chống oxy h&oacute;a, gi&uacute;p giảm nguy cơ mắc c&aacute;c bệnh tim mạch, đột quỵ v&agrave; ung thư.</li>\n	<li>Nho Mẫu Đơn chứa anthocyanin, một loại hợp chất flavonoid gi&uacute;p cải thiện tr&iacute; nhớ v&agrave; tăng cường chức năng n&atilde;o bộ.</li>\n	<li>Nho Mẫu Đơn chứa nhiều vitamin A v&agrave; E, gi&uacute;p l&agrave;m đẹp da, chống l&atilde;o h&oacute;a v&agrave; giảm nếp nhăn.</li>\n</ul>\n\n<p><strong>- Hướng dẫn sử dụng:</strong></p>\n\n<ul>\n	<li>\n	<p>Rửa nhẹ nh&agrave;ng tr&aacute;i nho trước khi sử dụng.</p>\n	</li>\n	<li>\n	<p>Chỉ n&ecirc;n rửa một lượng vừa đủ ăn.</p>\n	</li>\n	<li>\n	<p>C&oacute; thể ăn trực tiếp, l&agrave;m nước &eacute;p, sinh tố, l&agrave;m b&aacute;nh.</p>\n	</li>\n</ul>\n\n<p><strong>- C&aacute;ch lựa chọn nho ngon:</strong></p>\n\n<ul>\n	<li>\n	<p>Kh&aacute;ch h&agrave;ng&nbsp;n&ecirc;n&nbsp;chọn những quả nho c&oacute; vỏ căng b&oacute;ng, mọng nước, kh&ocirc;ng bị dập n&aacute;t hay hư hỏng.</p>\n	</li>\n	<li>\n	<p>Với Farmers Market ti&ecirc;u ch&iacute; h&agrave;ng đầu l&agrave; sản phảm đạt chất lượng cao, nguồn gốc r&otilde; r&agrave;ng, mang đến trải nghiệm tốt nhất cho kh&aacute;ch h&agrave;ng.&nbsp;</p>\n	</li>\n</ul>\n\n<p>-&nbsp;<strong>C&aacute;ch bảo quản nho xanh được l&acirc;u</strong><strong>:&nbsp;</strong></p>\n\n<ul>\n	<li>\n	<p>N&ecirc;n đặt nho v&agrave;o&nbsp;<strong>hộp k&iacute;n kh&iacute;</strong>&nbsp;hoặc&nbsp;<strong>t&uacute;i ziplock&nbsp;</strong>để giữ độ ẩm v&agrave;&nbsp;bảo quan nho trong ngăn m&aacute;t tủ lạnh&nbsp;</p>\n	</li>\n	<li>\n	<p>Kh&ocirc;ng n&ecirc;n rửa nho trước khi bảo quản trong ngăn m&aacute;t tủ lạnh v&igrave; nếu rửa trước nho sẽ dễ bị hư, thối.</p>\n	</li>\n	<li>\n	<p>Kiểm tra thường xuy&ecirc;n v&agrave; loại bỏ những tr&aacute;i hư hỏng</p>\n	</li>\n</ul>\n\n<ul>\n	<li>\n	<p>Kh&ocirc;ng n&ecirc;n bảo quản nho ở nhiệt độ ph&ograve;ng v&igrave; nho sẽ ch&iacute;n nhanh v&agrave; dễ bị hư hỏng.</p>\n	</li>\n	<li>\n	<p>Kh&ocirc;ng n&ecirc;n đ&ocirc;ng lạnh nho v&igrave; nho sẽ bị mềm nhũn v&agrave; mất đi hương vị.</p>\n	</li>\n</ul>\n\n<p><strong>- Những lưu &yacute; khi thưởng thức nho xanh:&nbsp;</strong></p>\n\n<ul>\n	<li>\n	<p><strong>Kh&ocirc;ng n&ecirc;n ăn nho qu&aacute; nhiều</strong>&nbsp;v&igrave; c&oacute; thể g&acirc;y đầy bụng, kh&oacute; ti&ecirc;u.</p>\n	</li>\n	<li>\n	<p><strong>Người bị bệnh tiểu đường&nbsp;</strong>n&ecirc;n tham khảo &yacute; kiến b&aacute;c sĩ trước khi ăn nho.</p>\n	</li>\n</ul>\n\n<p><strong>- Phụ nữ mang thai v&agrave; trẻ em ăn nho c&oacute; được kh&ocirc;ng?</strong></p>\n\n<ul>\n	<li>\n	<p>Phụ nữ mang thai n&ecirc;n ăn nho với lượng vừa phải, khoảng 100-150 gram mỗi ng&agrave;y</p>\n	</li>\n	<li>\n	<p>Trẻ em tr&ecirc;n 1 tuổi c&oacute; thể ăn nho xanh, cắt nho th&agrave;nh từng miếng nhỏ để trẻ dễ ăn v&agrave; tr&aacute;nh nguy cơ nghẹn.</p>\n	</li>\n	<li>\n	<p>Cho trẻ ăn nho với lượng vừa phải, khoảng 50-100 gram mỗi ng&agrave;y</p>\n	</li>\n</ul>\n', 'Trái cây Việt', 699000, 1, '2024-07-05', 7, '67a9553e0203df9830116d46359dbf30', 'daitsnbvigiqqhqtyuez', 3, 2, 1),
(52, 'Mận róc hạt', '<h1><strong>Mận r&oacute;c hạt nội địa Trung - 500G/Hộp&nbsp;</strong></h1>\n\n<p><strong>- Xuất xứ</strong>: Trung Quốc</p>\n\n<p><strong>- Ti&ecirc;u chuẩn chất lượng:</strong>&nbsp;tr&aacute;i c&acirc;y nhập khẩu</p>\n\n<p><strong>- Trọng lượng</strong>: 500g/Hộp</p>\n\n<p><strong>- Đặc điểm:&nbsp;</strong></p>\n\n<ul>\n	<li>Đạt ti&ecirc;u chuẩn chất lượng nhập khẩu.&nbsp;</li>\n	<li>Mận gi&ograve;n rụm, ngọt thanh, chua ch&aacute;t nhẹ đặc trưng,&nbsp;</li>\n	<li>Tr&aacute;i cứng l&acirc;u, hạt rất nhỏ dễ tr&oacute;c.</li>\n</ul>\n\n<p><strong>-Th&ocirc;ng tin dinh dưỡng:</strong></p>\n\n<ul>\n	<li>\n	<p>H&agrave;m lượng dinh dưỡng trong mận rất dồi d&agrave;o. Trong mận chứa c&aacute;c th&agrave;nh phần chống oxy h&oacute;a, chứa crom, kali, selen, vitamin C, beta-carotene, đường, carb, chất xơ, Vitamin A, K, mangan&hellip;</p>\n	</li>\n	<li>\n	<p>Mận rất gi&agrave;u chất xơ n&ecirc;n ăn mật sẽ rất tốt cho hệ ti&ecirc;u h&oacute;a. N&oacute; gi&uacute;p ngăn ngừa c&aacute;c t&igrave;nh trạng li&ecirc;n quan đến t&aacute;o b&oacute;n, đầy hơi, k&iacute;ch th&iacute;ch qu&aacute; tr&igrave;nh ti&ecirc;u h&oacute;a.</p>\n	</li>\n	<li>\n	<p>Mận tốt cho b&agrave; bầu: Gi&uacute;p b&agrave; bầu bổ sung đầy đủ dinh dưỡng, giảm t&aacute;o b&oacute;n, ngăn chặn c&aacute;c t&igrave;nh trạng mụn nhọt, ph&aacute;t ban, tăng cường sức đề kh&aacute;ng v&agrave; hỗ trợ thai kỳ khỏe mạnh.</p>\n	</li>\n	<li>\n	<p>Tốt cho mắt: Ăn mận sẽ gi&uacute;p giảm được t&igrave;nh trạng tho&aacute;i h&oacute;a điểm v&agrave;ng ở mắt, bổ sung vitamin A cho mắt, nhờ vậy đ&ocirc;i mắt sẽ s&aacute;ng v&agrave; khỏe.</p>\n	</li>\n	<li>\n	<p>Ăn mận cũng gi&uacute;p giảm mỡ m&aacute;u, huyết &aacute;p v&igrave; n&oacute; chứa chất chống oxy h&oacute;a. Nhờ vậy bạn sẽ giữ cho tr&aacute;i tim m&igrave;nh khỏe mạnh.</p>\n	</li>\n	<li>\n	<p>Gi&uacute;p chắc xương v&igrave; trong mận cũng chứa nhiều canxi, n&oacute; g&oacute;p phần v&agrave;o việc bảo vệ xương v&agrave; gi&uacute;p xương chắc khỏe.</p>\n	</li>\n</ul>\n\n<p><strong>- Hướng dẫn sử dụng:</strong></p>\n\n<ul>\n	<li>N&ecirc;n chọn những quả mận c&oacute; m&agrave;u sắc tươi s&aacute;ng, đều đặn, kh&ocirc;ng bị dập n&aacute;t hay sần s&ugrave;i.</li>\n	<li>Vỏ mận căng b&oacute;ng, mịn m&agrave;ng, kh&ocirc;ng bị nhăn nheo.</li>\n	<li>Cuống mận c&ograve;n tươi xanh, kh&ocirc;ng h&eacute;o &uacute;a.</li>\n	<li>Mận ch&iacute;n tới sẽ c&oacute; m&ugrave;i thơm nhẹ.</li>\n	<li>N&ecirc;n chọn mận c&oacute; độ mềm vừa phải, kh&ocirc;ng qu&aacute; cứng hay qu&aacute; mềm.</li>\n</ul>\n\n<p><strong>- C&aacute;ch bảo quản mận</strong></p>\n\n<ul>\n	<li>Bảo quản mận ở nhiệt độ ph&ograve;ng khi chưa ch&iacute;n.</li>\n	<li>Khi mận đ&atilde; ch&iacute;n, n&ecirc;n bảo quản trong tủ lạnh.</li>\n	<li>N&ecirc;n bọc mận trong t&uacute;i nilon hoặc hộp k&iacute;n để giữ độ tươi ngon.</li>\n	<li>N&ecirc;n sử dụng mận trong v&ograve;ng 2-3 ng&agrave;y sau khi mua.</li>\n</ul>\n\n<p><strong>- C&aacute;ch sơ chế mận:</strong></p>\n\n<ul>\n	<li>Rửa sạch mận dưới v&ograve;i nước chảy.</li>\n	<li>D&ugrave;ng dao gọt vỏ mận (t&ugrave;y chọn).</li>\n	<li>Cắt mận th&agrave;nh miếng vừa ăn.</li>\n	<li>C&oacute; thể bỏ hạt hoặc giữ nguy&ecirc;n t&ugrave;y sở th&iacute;ch</li>\n</ul>\n\n<p><strong>- Những lưu &yacute; khi sử dụng:&nbsp;</strong></p>\n\n<ul>\n	<li>N&ecirc;n ăn mận khi đ&atilde; ch&iacute;n để c&oacute; hương vị thơm ngon nhất.</li>\n	<li>Kh&ocirc;ng n&ecirc;n ăn mận qu&aacute; nhiều v&igrave; c&oacute; thể g&acirc;y n&oacute;ng trong người.</li>\n	<li>Đối với những người c&oacute; hệ ti&ecirc;u h&oacute;a yếu, n&ecirc;n ăn mận sau bữa ăn.</li>\n</ul>\n\n<p><strong>- Phụ nữ mang thai v&agrave; trẻ em ăn d&acirc;u&nbsp;c&oacute; được kh&ocirc;ng?</strong></p>\n\n<ul>\n	<li>\n	<p><strong>Đối với phụ nữ mang thai: C&oacute; thể ăn mận:</strong>&nbsp;Mận l&agrave; nguồn cung cấp vitamin, kho&aacute;ng chất v&agrave; chất xơ dồi d&agrave;o, tốt cho sức khỏe của cả mẹ v&agrave; b&eacute;.</p>\n	</li>\n	<li><strong>C&oacute; thể cho trẻ ăn mận khi b&eacute; đủ 6 th&aacute;ng tuổi:</strong>&nbsp;Mận l&agrave; nguồn cung cấp vitamin, kho&aacute;ng chất v&agrave; chất xơ dồi d&agrave;o, tốt cho sự ph&aacute;t triển của trẻ.</li>\n</ul>\n\n<p><strong>- Ngo&agrave;i ra, bạn cũng cần lưu &yacute;:</strong></p>\n\n<ul>\n	<li>N&ecirc;n rửa sạch mận trước khi cho trẻ ăn để loại bỏ bụi bẩn v&agrave; vi khuẩn.</li>\n	<li>N&ecirc;n gọt vỏ mận trước khi cho trẻ ăn để tr&aacute;nh trẻ ăn phải thuốc trừ s&acirc;u.</li>\n	<li>N&ecirc;n quan s&aacute;t trẻ khi ăn mận để tr&aacute;nh nguy cơ nghẹn.</li>\n</ul>\n', 'Trái cây Việt', 59000, 1, '2024-07-05', 7, '11592258b850e79ff432095ae19424d3', 'w6lspnkuyqgedyuluryi', 3, 3, 1),
(53, 'Đào vàng trơn', '<h1><strong>Đ&agrave;o v&agrave;ng trơn nội địa Trung&nbsp;</strong></h1>\n\n<p><strong>- Xuất xứ</strong>: Trung Quốc</p>\n\n<p><strong>- Ti&ecirc;u chuẩn chất lượng:</strong>&nbsp;tr&aacute;i c&acirc;y nhập khẩu</p>\n\n<p><strong>- Trọng lượng</strong>: 1kg/hộp</p>\n\n<p><strong>- Đặc điểm:&nbsp;</strong></p>\n\n<ul>\n	<li>Đạt ti&ecirc;u chuẩn chất lượng nhập khẩu.&nbsp;</li>\n	<li>M&agrave;u v&agrave;ng s&aacute;ng xen ch&uacute;t hồng bắt mắt,&nbsp;</li>\n	<li>vị gi&ograve;n ngọt, ăn rất cuốn.</li>\n</ul>\n\n<p><strong>-Th&ocirc;ng tin dinh dưỡng:</strong></p>\n\n<ul>\n	<li>Quả đ&agrave;o&nbsp;c&oacute; nhiều vitamin A, beta &ndash; carotene v&agrave; vitamin C, vitamin E, vitamin B1, B2, B3, B6, folate v&agrave; axit pantothenic. Đ&agrave;o cũng chứa một lượng lớn c&aacute;c loại chất kho&aacute;ng như canxi, kali, magie, sắt, mangan, photpho, kẽm v&agrave; đồng.</li>\n	<li>&nbsp;Đ&agrave;o v&agrave;ng gi&uacute;p ngăn ngừa l&atilde;o h&oacute;a</li>\n	<li>Quả đ&agrave;o v&agrave;ng gi&uacute;p nu&ocirc;i dưỡng chất lỏng cơ thể v&agrave; l&agrave;m dịu cơn kh&aacute;t</li>\n	<li>Đ&agrave;o v&agrave;ng rất hữu &iacute;ch để giải độc v&agrave; chăm s&oacute;c sức khỏe</li>\n	<li>Ăn đ&agrave;o gi&uacute;p tăng cường chức năng cho hệ ti&ecirc;u h&oacute;a</li>\n	<li>Vitamin A, C v&agrave; acid ascorbic v&agrave; kẽm c&oacute; thể tăng cường chức năng cơ thể, đồng thời n&acirc;ng cao sức khỏe hệ miễn dịch.&nbsp;</li>\n</ul>\n\n<p><strong>- Hướng dẫn sử dụng:</strong></p>\n\n<ul>\n	<li>Cuống đ&agrave;o c&ograve;n tươi xanh, kh&ocirc;ng h&eacute;o &uacute;a.</li>\n	<li>Đ&agrave;o ch&iacute;n tới sẽ c&oacute; m&ugrave;i thơm nhẹ.</li>\n	<li>N&ecirc;n chọn đ&agrave;o c&oacute; độ mềm vừa phải, kh&ocirc;ng qu&aacute; cứng hay qu&aacute; mềm.</li>\n</ul>\n\n<p><strong>- C&aacute;ch bảo quản</strong></p>\n\n<ul>\n	<li>Bảo quản đ&agrave;o ở nhiệt độ ph&ograve;ng khi chưa ch&iacute;n.</li>\n	<li>Khi đ&agrave;o đ&atilde; ch&iacute;n, n&ecirc;n bảo quản trong tủ lạnh.</li>\n	<li>N&ecirc;n bọc đ&agrave;o trong t&uacute;i nilon hoặc hộp k&iacute;n để giữ độ tươi ngon.</li>\n	<li>N&ecirc;n sử dụng đ&agrave;o trong v&ograve;ng 2-3 ng&agrave;y sau khi mua.</li>\n</ul>\n\n<p><strong>- C&aacute;ch sử dụng:</strong></p>\n\n<ul>\n	<li><strong>Đ&agrave;o c&oacute; thể ăn trực tiếp hoặc chế biến th&agrave;nh nhiều m&oacute;n ăn ngon như:</strong>\n\n	<ul>\n		<li><strong>Sinh tố đ&agrave;o:</strong>&nbsp;Xay nhuyễn đ&agrave;o với sữa chua, đ&aacute; b&agrave;o v&agrave; mật ong.</li>\n		<li><strong>Salad đ&agrave;o:</strong>&nbsp;Trộn đ&agrave;o với c&aacute;c loại rau củ quả kh&aacute;c như x&agrave; l&aacute;ch, c&agrave; chua, dưa chuột,...</li>\n		<li><strong>B&aacute;nh đ&agrave;o:</strong>&nbsp;L&agrave;m b&aacute;nh quy, b&aacute;nh su kem, b&aacute;nh mousse với nh&acirc;n đ&agrave;o.</li>\n		<li><strong>Mứt đ&agrave;o:</strong>&nbsp;Nấu đ&agrave;o với đường v&agrave; nước cho đến khi sệt lại.</li>\n	</ul>\n	</li>\n</ul>\n\n<p><strong>- Những lưu &yacute; khi sử dụng:&nbsp;</strong></p>\n\n<ul>\n	<li>N&ecirc;n ăn đ&agrave;o khi đ&atilde; ch&iacute;n để c&oacute; hương vị thơm ngon nhất.</li>\n	<li>Kh&ocirc;ng n&ecirc;n ăn đ&agrave;o qu&aacute; nhiều v&igrave; c&oacute; thể g&acirc;y n&oacute;ng trong người.</li>\n	<li>Đối với những người c&oacute; hệ ti&ecirc;u h&oacute;a yếu, n&ecirc;n ăn đ&agrave;o sau bữa ăn<strong>.</strong></li>\n</ul>\n\n<p><strong>- Phụ nữ mang thai v&agrave; trẻ em ăn đ&agrave;o&nbsp;c&oacute; được kh&ocirc;ng?</strong></p>\n\n<ul>\n	<li>\n	<p><strong>Đối với phụ nữ mang thai:&nbsp;C&oacute; thể ăn đ&agrave;o:</strong>&nbsp;Đ&agrave;o l&agrave; nguồn cung cấp vitamin, kho&aacute;ng chất v&agrave; chất xơ dồi d&agrave;o, tốt cho sức khỏe của cả mẹ v&agrave; b&eacute;.</p>\n	</li>\n	<li>\n	<p><strong>C&oacute; thể cho trẻ ăn đ&agrave;o khi b&eacute; đủ 6 th&aacute;ng tuổi:</strong>&nbsp;Đ&agrave;o l&agrave; nguồn cung cấp vitamin, kho&aacute;ng chất v&agrave; chất xơ dồi d&agrave;o, tốt cho sự ph&aacute;t triển của trẻ.&nbsp;</p>\n	</li>\n</ul>\n\n<p><strong>- Cần lưu &yacute;:</strong></p>\n\n<ul>\n	<li><em><strong>N&ecirc;n cắt đ&agrave;o th&agrave;nh những miếng nhỏ để trẻ dễ ăn v&agrave; tr&aacute;nh nguy cơ nghẹn.</strong></em></li>\n	<li><em><strong>N&ecirc;n cho trẻ ăn đ&agrave;o ở mức độ vừa phải, kh&ocirc;ng n&ecirc;n ăn qu&aacute; nhiều.</strong></em></li>\n	<li><em><strong>Nếu trẻ c&oacute; bất kỳ vấn đề sức khỏe n&agrave;o, h&atilde;y tham khảo &yacute; kiến b&aacute;c sĩ trước khi cho trẻ ăn đ&agrave;o.</strong></em></li>\n</ul>\n', 'Trái cây Việt', 69000, 1, '2024-07-05', 7, '60cf7ab2bbd91501d7e9b67defb02c46', 'scdohkygpwn2vmzkrzsm', 3, 2, 1),
(54, 'Đào mỏ quạ Sơn La', '<h1><strong>Đ&agrave;o mỏ quạ Sơn La</strong></h1>\n\n<p><strong>- Xuất xứ:&nbsp;</strong>Việt Nam&nbsp;</p>\n\n<p>-&nbsp;<strong>Ti&ecirc;u chuẩn chất lượng:&nbsp;</strong>Tr&aacute;i c&acirc;y sạch</p>\n\n<p><strong>- Đặc điểm :</strong></p>\n\n<ul>\n	<li>K&iacute;ch thước của quả đ&agrave;o mỏ quạ thường nhỏ hơn c&aacute;c loại đ&agrave;o kh&aacute;c</li>\n	<li><strong>Vỏ&nbsp;</strong>quả đ&agrave;o mỏ quạ c&oacute; nhiều l&ocirc;ng v&agrave; phần đ&iacute;t quả hơi cong l&ecirc;n giống h&igrave;nh mỏ của con quạ.</li>\n	<li>Tr&aacute;i xanh gi&ograve;n, ngọt thanh.</li>\n	<li>Tr&aacute;i ch&iacute;n mọng nước, thịt mềm, m&ugrave;i thơm ngọt ng&agrave;o.</li>\n</ul>\n\n<p><strong>Đ&agrave;o mỏ quạ</strong>&nbsp;l&agrave; một nguồn cung cấp vitamin v&agrave; kho&aacute;ng chất dồi d&agrave;o, bao gồm vitamin C, A, kali v&agrave; magi&ecirc;. Ch&uacute;ng cũng chứa chất chống oxy h&oacute;a, c&oacute; thể gi&uacute;p bảo vệ cơ thể khỏi t&aacute;c hại của c&aacute;c gốc tự do.</p>\n\n<p><strong>- Lợi &iacute;ch:</strong></p>\n\n<ul>\n	<li>Đ&agrave;o được đ&aacute;nh gi&aacute; l&agrave; tr&aacute;i c&acirc;y bổ sung cho cơ thể đa dạng c&aacute;c nh&oacute;m chất chống oxy h&oacute;a. Theo đ&oacute;, carotenoid v&agrave; axit caffeic được t&igrave;m thấy nhiều ở cả phần vỏ lẫn thịt quả đ&agrave;o</li>\n	<li>Một trong những t&aacute;c dụng của quả đ&agrave;o l&agrave;&nbsp;c&oacute; khả năng chống vi&ecirc;m, c&oacute; thể kiểm so&aacute;t lượng đường huyết trong m&aacute;u, gi&agrave;u chất xơ v&agrave; nước n&ecirc;n rất th&iacute;ch hợp d&agrave;nh cho người đang muốn giảm c&acirc;n.&nbsp;</li>\n</ul>\n\n<p><strong>- C&aacute;ch sử dụng:&nbsp;</strong></p>\n\n<p>Đ&agrave;o mỏ quạ c&oacute; thể được thưởng thức tươi, nấu ch&iacute;n hoặc sấy kh&ocirc;. Ch&uacute;ng thường được sử dụng để l&agrave;m m&oacute;n tr&aacute;ng miệng, mứt v&agrave; nước tr&aacute;i c&acirc;y. Ch&uacute;ng cũng c&oacute; thể được th&ecirc;m v&agrave;o c&aacute;c m&oacute;n salad, m&oacute;n x&agrave;o v&agrave; m&oacute;n hầm.</p>\n\n<p><strong>- C&aacute;ch lựa chọn đ&agrave;o&nbsp;ngon</strong></p>\n\n<ul>\n	<li>N&ecirc;n chọn những quả đ&agrave;o c&oacute; vỏ m&agrave;u đỏ hồng hoặc đỏ đậm, xen kẽ m&agrave;u v&agrave;ng xanh. Vỏ s&aacute;ng b&oacute;ng, mịn m&agrave;ng, kh&ocirc;ng bị sần s&ugrave;i, bầm dập hay nứt nẻ.</li>\n	<li>Với Farmers Market ti&ecirc;u ch&iacute; h&agrave;ng đầu l&agrave; sản phảm đạt chất lượng cao, nguồn gốc r&otilde; r&agrave;ng, mang đến trải nghiệm tốt nhất cho kh&aacute;ch h&agrave;ng.&nbsp;</li>\n</ul>\n', 'Trái cây Việt', 129000, 1, '2024-07-05', 7, 'bdd41ac69ee26fecbf8d986671cf652d', 'b8toszx7cjfqj65iiiac', 3, 1, 1),
(55, 'Dâu tây', '<h1><strong>D&acirc;u t&acirc;y Mỹ&nbsp;</strong></h1>\n\n<p><strong>- Xuất xứ</strong>: Mỹ</p>\n\n<p><strong>- Ti&ecirc;u chuẩn chất lượng:</strong>&nbsp;tr&aacute;i c&acirc;y nhập khẩu</p>\n\n<p><strong>- Trọng lượng</strong>: 250g/Hộp</p>\n\n<p><strong>- Đặc điểm:&nbsp;</strong></p>\n\n<ul>\n	<li>Đạt ti&ecirc;u chuẩn chất lượng nhập khẩu.&nbsp;</li>\n	<li>Với m&ugrave;i thơm đặc trưng của quả mọng c&ugrave;ng hương vị chua ngọt dễ chịu v&agrave; cực gi&agrave;u dinh dưỡng</li>\n	<li>D&acirc;u t&acirc;y Mỹ nhập khẩu l&agrave; một trong những loại d&acirc;u ngon bậc nhất tr&ecirc;n thế giới, được nhiều người ưa th&iacute;ch, săn đ&oacute;n.</li>\n</ul>\n\n<p><strong>-Th&ocirc;ng tin dinh dưỡng:</strong></p>\n\n<ul>\n	<li>D&acirc;u t&acirc;y chứa c&aacute;c vitamin v&agrave; chất dinh dưỡng thiết yếu như, vitamin C, chất xơ, kali, vitamin B v&agrave; magi&ecirc; l&agrave;m cho ch&uacute;ng ho&agrave;n hảo cho một chế độ ăn uống l&agrave;nh mạnh.</li>\n	<li>Ph&ograve;ng chống c&aacute;c bệnh ung thư: Vitamin C kết hợp axit ellagic, lutein v&agrave; zeathaccins c&oacute; trong d&acirc;u t&acirc;y gi&uacute;p ngăn ngừa ung thư, chế ngự sự ph&aacute;t triển của c&aacute;c khối u, ti&ecirc;u diệt c&aacute;c gốc tự do v&agrave; trung h&ograve;a ảnh hưởng ti&ecirc;u cực c&oacute; khả năng xảy ra ở tế b&agrave;o trong cơ thể.</li>\n	<li>Tăng cường hệ miễn dịch: D&acirc;u t&acirc;y rất gi&agrave;u vitamin C gi&uacute;p cơ thể tăng cường hệ miễn dịch, đồng thời c&ograve;n l&agrave; chất chống oxy h&oacute;a mạnh mẽ.</li>\n	<li>Đẹp da: Vitamin C trong d&acirc;u t&acirc;y c&oacute; t&aacute;c dụng sản sinh ra collagen, cải thiện độ đ&agrave;n hồi cho da.</li>\n	<li>Ngo&agrave;i ra, d&acirc;u t&acirc;y c&ograve;n gi&uacute;p ổn định c&acirc;n nặng. Ph&ugrave; hợp với những chế độ ăn ki&ecirc;ng d&agrave;nh cho người b&eacute;o.</li>\n	<li>D&acirc;u t&acirc;y rất ph&ugrave; hợp ăn k&egrave;m c&ugrave;ng yogurt, xay sinh tố hoặc ăn trực tiếp.</li>\n</ul>\n\n<p><strong>- Hướng dẫn sử dụng:</strong></p>\n\n<ul>\n	<li>Rửa nhẹ nh&agrave;ng d&acirc;u t&acirc;y v&agrave; loại bỏ mũ l&aacute; khi bạn rửa xong.&nbsp;</li>\n	<li>Bạn c&oacute; thể thưởng thức d&acirc;u tươi trực tiếp hoặc chấm th&ecirc;m sữa chua, mật ong để tăng th&ecirc;m hương vị.</li>\n	<li>C&oacute; thể kết hợp d&acirc;u với c&aacute;c loại tr&aacute;i c&acirc;y kh&aacute;c như chuối, kiwi, t&aacute;o,... để tạo n&ecirc;n những ly sinh tố thơm ngon, bổ dưỡng.</li>\n	<li>D&acirc;u cũng c&oacute; thể được th&ecirc;m v&agrave;o c&aacute;c m&oacute;n salad g&agrave; nướng, salad c&aacute; hồi,... để tăng th&ecirc;m hương vị v&agrave; gi&aacute; trị dinh dưỡng.</li>\n	<li>Bạn cũng c&oacute; thể l&agrave;m mứt d&acirc;u, kem d&acirc;u để ăn k&egrave;m với b&aacute;nh m&igrave;, yogurt hoặc sử dụng trong c&aacute;c m&oacute;n tr&aacute;ng miệng kh&aacute;c.</li>\n	<li>D&acirc;u cũng c&oacute; thể được kết hợp với ph&ocirc; mai, giấm balsamic để l&agrave;m m&oacute;n khai vị thanh tao v&agrave; đầy tinh tế.</li>\n	<li>Vị ngọt thanh của d&acirc;u h&ograve;a quyện với c&aacute;c loại rượu, nước tr&aacute;i c&acirc;y kh&aacute;c tạo n&ecirc;n thức uống độc đ&aacute;o v&agrave; sảng kho&aacute;i.</li>\n</ul>\n\n<p><strong>- C&aacute;ch bảo quản d&acirc;u được l&acirc;u:&nbsp;</strong></p>\n\n<ul>\n	<li>Khi được bảo quản đ&uacute;ng c&aacute;ch tại nh&agrave;, d&acirc;u t&acirc;y c&oacute; thể tươi từ 2 - 4 ng&agrave;y sau khi mua.&nbsp;</li>\n	<li>Giữ lạnh d&acirc;u t&acirc;y gi&uacute;p bảo quản l&acirc;u hơn.&nbsp;</li>\n	<li>Ch&uacute;ng t&ocirc;i giữ c&aacute;c loại quả mọng trong tủ lạnh v&agrave; đặc biệt khuy&ecirc;n bạn n&ecirc;n giữ ch&uacute;ng trong hộp đựng ban đầu v&agrave; l&agrave;m lạnh ch&uacute;ng c&agrave;ng sớm c&agrave;ng tốt.&nbsp;</li>\n	<li>Kh&ocirc;ng rửa trước khi cho v&agrave;o tủ lạnh.</li>\n</ul>\n\n<p><strong>- C&aacute;ch lựa chọn d&acirc;u ngon:&nbsp;</strong></p>\n\n<ul>\n	<li><strong>Quan s&aacute;t phần cuống của d&acirc;u:</strong>&nbsp;Nếu cuống d&acirc;u c&ograve;n xanh v&agrave; d&iacute;nh chặt v&agrave;o quả th&igrave; đ&acirc;y l&agrave; dấu hiệu của quả d&acirc;u tươi ngon.</li>\n	<li><strong>Nhẹ nh&agrave;ng ấn v&agrave;o quả d&acirc;u:</strong>&nbsp;Nếu quả d&acirc;u c&oacute; độ đ&agrave;n hồi tốt v&agrave; kh&ocirc;ng bị dập n&aacute;t th&igrave; đ&acirc;y l&agrave; dấu hiệu của quả d&acirc;u tươi ngon.</li>\n</ul>\n\n<p><strong>- Những lưu &yacute; khi sử dụng:&nbsp;</strong></p>\n\n<ul>\n	<li>N&ecirc;n ăn d&acirc;u t&acirc;y trong v&ograve;ng 1-2 ng&agrave;y sau khi mua</li>\n	<li>N&ecirc;n ăn d&acirc;u t&acirc;y với lượng vừa phải, khoảng 1-2 quả mỗi ng&agrave;y.&nbsp;Ăn qu&aacute; nhiều d&acirc;u c&oacute; thể g&acirc;y đầy bụng, kh&oacute; ti&ecirc;u.</li>\n	<li><strong>Cho trẻ ăn d&acirc;u với lượng vừa phải:</strong>&nbsp;Trẻ em n&ecirc;n ăn d&acirc;u với lượng vừa phải, khoảng 50-100 gram mỗi ng&agrave;y.</li>\n	<li><strong>Trẻ em bị ti&ecirc;u chảy hoặc t&aacute;o b&oacute;n kh&ocirc;ng n&ecirc;n ăn d&acirc;u.</strong></li>\n</ul>\n\n<p><strong>- Phụ nữ mang thai v&agrave; trẻ em ăn d&acirc;u&nbsp;c&oacute; được kh&ocirc;ng?</strong></p>\n\n<ul>\n	<li>D&acirc;u t&acirc;y&nbsp;gi&agrave;u vitamin C, kali, folate, chất xơ,...&nbsp;<strong>cần thiết cho sự ph&aacute;t triển của thai nhi v&agrave; sức khỏe của mẹ bầu</strong>.</li>\n	<li>Chất xơ trong d&acirc;u gi&uacute;p&nbsp;<strong>ngăn ngừa t&aacute;o b&oacute;n</strong>, một vấn đề phổ biến ở phụ nữ mang thai.</li>\n	<li>Vitamin C trong d&acirc;u gi&uacute;p tăng cường hệ miễn dịch,<strong>&nbsp;gi&uacute;p mẹ bầu chống lại c&aacute;c bệnh nhiễm tr&ugrave;ng</strong></li>\n	<li>Folate trong d&acirc;u gi&uacute;p<strong>&nbsp;giảm nguy cơ dị tật ống thần kinh ở thai nhi</strong>.</li>\n	<li><strong>Trẻ em từ 6 th&aacute;ng tuổi</strong>&nbsp;trở l&ecirc;n c&oacute; thể ăn D&acirc;u H&agrave;n Quốc Joy Farm 200g</li>\n</ul>\n', 'Trái cây Việt', 299000, 1, '2024-07-05', 7, '2d069bae791702d54520f0d67473bd91', 'dykpudhgyvgvzkyaqnkd', 3, 3, 1),
(56, 'Nho đen Autumn Royal', '<h2><strong>Nho đen Autumn Royal &Uacute;c - 500g</strong></h2>\n\n<p><strong>- Khối lượng:</strong>&nbsp;1kg</p>\n\n<p><strong>- Xuất xứ:</strong>&nbsp;&Uacute;c</p>\n\n<p><strong>- Ti&ecirc;u chuẩn chất lượng:&nbsp;</strong>Nhập khẩu</p>\n\n<p><strong>- Đặc điểm:</strong></p>\n\n<ul>\n	<li>Nho c&oacute; vỏ đen sẫm,&nbsp;căng b&oacute;ng,&nbsp;mọng nước.</li>\n	<li>Thịt quả gi&ograve;n,&nbsp;vị ngọt thanh,&nbsp;c&oacute; ch&uacute;t chua nhẹ.</li>\n	<li>Hạt nhỏ,&nbsp;dễ t&aacute;ch, vỏ d&agrave;y n&ecirc;n c&oacute; tỉ lệ ch&aacute;t vỏ&nbsp;</li>\n	<li>Chứa nhiều vitamin v&agrave; kho&aacute;ng chất tốt cho sức khỏe</li>\n	<li>Cuống hơi se nhẹ</li>\n</ul>\n\n<p><strong>- Lợi &iacute;ch:</strong></p>\n\n<ul>\n	<li>Nho đen Autumn Royal &Uacute;c l&agrave; nguồn cung cấp vitamin C, vitamin K, kali v&agrave; chất xơ dồi d&agrave;o.</li>\n	<li>Vitamin C gi&uacute;p tăng cường hệ miễn dịch, chống oxy h&oacute;a v&agrave; bảo vệ cơ thể khỏi c&aacute;c bệnh tim mạch.</li>\n	<li>Vitamin K gi&uacute;p hỗ trợ qu&aacute; tr&igrave;nh đ&ocirc;ng m&aacute;u v&agrave; ngăn ngừa lo&atilde;ng xương.</li>\n	<li>Kali gi&uacute;p điều h&ograve;a huyết &aacute;p v&agrave; giảm nguy cơ đột quỵ.</li>\n	<li>Chất xơ gi&uacute;p cải thiện hệ ti&ecirc;u h&oacute;a, ngăn ngừa t&aacute;o b&oacute;n v&agrave; giảm cholesterol.</li>\n</ul>\n\n<p><strong>- C&aacute;ch sử dụng:</strong></p>\n\n<ul>\n	<li>Nho đen Autumn Royal &Uacute;c&nbsp;c&oacute; thể ăn tươi, l&agrave;m nước &eacute;p, sinh tố hoặc sử dụng để chế biến c&aacute;c m&oacute;n tr&aacute;ng miệng như b&aacute;nh nho, kem nho.</li>\n	<li>N&ecirc;n rửa sạch nho trước khi ăn.</li>\n	<li>Nho c&oacute; thể bảo quản trong tủ lạnh từ 5 đến 7 ng&agrave;y.</li>\n</ul>\n\n<p><strong>- C&aacute;ch lựa chọn nho ngon:</strong></p>\n\n<ul>\n	<li>\n	<p>Kh&aacute;ch h&agrave;ng&nbsp;n&ecirc;n&nbsp;chọn những quả nho c&oacute; vỏ căng b&oacute;ng, mọng nước, kh&ocirc;ng bị dập n&aacute;t hay hư hỏng.</p>\n	</li>\n	<li>\n	<p>Với Farmers Market ti&ecirc;u ch&iacute; h&agrave;ng đầu l&agrave; sản phẩm đạt chất lượng cao, nguồn gốc r&otilde; r&agrave;ng, mang đến trải nghiệm tốt nhất cho kh&aacute;ch h&agrave;ng.&nbsp;</p>\n	</li>\n</ul>\n\n<p>-&nbsp;<strong>C&aacute;ch bảo quản nho được l&acirc;u:&nbsp;</strong></p>\n\n<ul>\n	<li>\n	<p>N&ecirc;n đặt nho v&agrave;o&nbsp;<strong>hộp k&iacute;n kh&iacute;</strong>&nbsp;hoặc&nbsp;<strong>t&uacute;i ziplock&nbsp;</strong>để giữ độ ẩm v&agrave;&nbsp;bảo quan nho trong ngăn m&aacute;t tủ lạnh&nbsp;</p>\n	</li>\n	<li>\n	<p>Kh&ocirc;ng n&ecirc;n rửa nho trước khi bảo quản trong ngăn m&aacute;t tủ lạnh v&igrave; nếu rửa trước nho sẽ dễ bị hư, thối.</p>\n	</li>\n	<li>\n	<p>Kiểm tra thường xuy&ecirc;n v&agrave; loại bỏ những tr&aacute;i hư hỏng</p>\n	</li>\n</ul>\n\n<ul>\n	<li>\n	<p>Kh&ocirc;ng n&ecirc;n bảo quản nho ở nhiệt độ ph&ograve;ng v&igrave; nho sẽ ch&iacute;n nhanh v&agrave; dễ bị hư hỏng.</p>\n	</li>\n	<li>\n	<p>Kh&ocirc;ng n&ecirc;n đ&ocirc;ng lạnh nho v&igrave; nho sẽ bị mềm nhũn v&agrave; mất đi hương vị.</p>\n	</li>\n</ul>\n\n<p><strong>- Những lưu &yacute; khi thưởng thức nho xanh:&nbsp;</strong></p>\n\n<ul>\n	<li>\n	<p><strong>Kh&ocirc;ng n&ecirc;n ăn nho qu&aacute; nhiều</strong>&nbsp;v&igrave; c&oacute; thể g&acirc;y đầy bụng, kh&oacute; ti&ecirc;u.</p>\n	</li>\n	<li>\n	<p><strong>Người bị bệnh tiểu đường&nbsp;</strong>n&ecirc;n tham khảo &yacute; kiến b&aacute;c sĩ trước khi ăn nho.</p>\n	</li>\n</ul>\n\n<p><strong>- Phụ nữ mang thai v&agrave; trẻ em ăn nho c&oacute; được kh&ocirc;ng?</strong></p>\n\n<ul>\n	<li>\n	<p>Phụ nữ mang thai n&ecirc;n ăn nho với lượng vừa phải, khoảng 100-150 gram mỗi ng&agrave;y</p>\n	</li>\n	<li>\n	<p>Trẻ em tr&ecirc;n 1 tuổi c&oacute; thể ăn nho, cắt nho th&agrave;nh từng miếng nhỏ để trẻ dễ ăn v&agrave; tr&aacute;nh nguy cơ nghẹn.</p>\n	</li>\n	<li>\n	<p>Cho trẻ ăn nho với lượng vừa phải, khoảng 50-100 gram mỗi ng&agrave;y</p>\n	</li>\n</ul>\n', 'Trái cây Việt', 169000, 1, '2024-07-05', 7, 'df119397c1259dc85cc56213299244d1', 'ookswvdkggnisvuvxhgb', 3, 3, 1),
(57, 'Vải trứng siêu to Phù Cừ Hưng Yên', '<h1><strong>Vải trứng si&ecirc;u to Ph&ugrave; Cừ Hưng Y&ecirc;n</strong></h1>\n\n<p><strong>- Xuất xứ:</strong>&nbsp;Việt Nam</p>\n\n<p><strong>- Ti&ecirc;u chuẩn chất lượng:</strong>&nbsp;Tr&aacute;i c&acirc;y sạch</p>\n\n<p><strong>- Quy c&aacute;ch:&nbsp;</strong>500g</p>\n\n<p>-&nbsp;Vải trứng si&ecirc;u to Ph&ugrave; Cừ Hưng Y&ecirc;n l&agrave; loại quả đặc sản nổi tiếng với k&iacute;ch thước to lớn, vỏ mỏng v&agrave; vị ngọt thanh m&aacute;t.</p>\n\n<p><strong>- Đặc điểm:</strong></p>\n\n<ul>\n	<li>K&iacute;ch thước: To như quả trứng g&agrave; hoặc quả trứng vịt, nặng từ 50-70 gram mỗi quả.</li>\n	<li>Vỏ: Mỏng, m&agrave;u đỏ tươi khi ch&iacute;n.</li>\n	<li>C&ugrave;i: D&agrave;y, m&agrave;u trắng ng&agrave;, nhiều nước.</li>\n	<li>Vị: Ngọt thanh, thơm m&aacute;t, kh&ocirc;ng c&oacute; vị chua hay ch&aacute;t.</li>\n</ul>\n\n<p><strong>- Th&ocirc;ng tinh dinh dưỡng:</strong></p>\n\n<ul>\n	<li>Vải chứa nhiều Potassium (kali) gi&uacute;p kiểm so&aacute;t huyết &aacute;p tốt.</li>\n	<li>Tr&aacute;i vải c&ograve;n chứa một hợp chất v&ocirc; c&ugrave;ng qu&yacute; gi&aacute; l&agrave; oligonol (R) gi&uacute;p cho tim khỏe mạnh hơn. Ngo&agrave;i ra, do chứa h&agrave;m lượng vitamin C cao n&ecirc;n vải c&ograve;n c&oacute; chức năng bảo vệ tim.</li>\n	<li>Vải rất gi&agrave;u vitamin C, E, B6, K gi&uacute;p tăng cường khả năng miễn dịch, cải thiện khả năng ti&ecirc;u h&oacute;a.</li>\n</ul>\n\n<p><strong>- Hướng dẫn sử dụng:</strong></p>\n\n<ul>\n	<li>Rửa sơ bằng nước sạch, sau đ&oacute; d&ugrave;ng tay b&oacute;c nhẹ để loại bỏ lớp vỏ ngo&agrave;i.</li>\n	<li>Vải&nbsp;c&oacute; thể ăn trực tiếp hoặc&nbsp;nấu ch&egrave;, l&agrave;m sinh tố, b&oacute;p gỏi.</li>\n</ul>\n', 'Trái cây Việt', 149500, 1, '2024-07-05', 7, '9fd779a8852deeacbdca89616a3b6e67', 'oe8rsvdtpou36qr75e7p', 3, 1, 1),
(58, 'Cam Sành Vắt Nước', '<h1><strong>Cam S&agrave;nh Vắt Nước</strong></h1>\n\n<p><strong>Xuất xứ:&nbsp;</strong>Việt Nam</p>\n\n<p><strong>Ti&ecirc;u chuẩn chất lượng:&nbsp;</strong>Tr&aacute;i c&acirc;y sạch</p>\n\n<p>Khối lượng: 5&nbsp;- 6Tr&aacute;i/Kg</p>\n\n<p><strong>Đặc điểm nổi bật của cam s&agrave;nh</strong></p>\n\n<ul>\n	<li>Quy tr&igrave;nh chăm s&oacute;c ti&ecirc;n tiến</li>\n	<li>Dễ nhận dạng nhờ lớp vỏ d&agrave;y, sần s&ugrave;i, thường c&oacute; m&agrave;u xanh lục nhạt, khi ch&iacute;n c&oacute; sắc cam.</li>\n	<li>M&uacute;i thịt m&agrave;u cam, c&oacute; vị thơm đặc trưng, ngọt dịu.</li>\n</ul>\n\n<p><strong>Cam S&agrave;nh</strong>&nbsp;c&oacute; k&iacute;ch thước vừa phải, mọng nước, vỏ xanh xen ch&uacute;t v&agrave;ng với m&uacute;i m&agrave;u cam đẹp mắt. D&ugrave;ng l&agrave;m nước &eacute;p cũng rất ngon</p>\n\n<p><strong>Lợi &iacute;ch khi sử dụng cam s&agrave;nh</strong></p>\n\n<ul>\n	<li>Nước cam chứa h&agrave;m lượng vitamin C cao, l&agrave; chất chống oxy h&oacute;a mạnh mẽ gi&uacute;p bảo vệ cơ thể khỏi c&aacute;c gốc tự do, giảm nguy cơ mắc c&aacute;c bệnh m&atilde;n t&iacute;nh như ung thư, tim mạch v&agrave; l&atilde;o h&oacute;a.</li>\n	<li>Ngo&agrave;i vitamin C, nước cam c&ograve;n chứa c&aacute;c hợp chất chống oxy h&oacute;a kh&aacute;c như flavonoid, carotenoid, anthocyanin,... gi&uacute;p tăng cường hệ miễn dịch v&agrave; bảo vệ cơ thể khỏi t&aacute;c hại của m&ocirc;i trường.</li>\n	<li>Nước cam l&agrave; nguồn cung cấp kali dồi d&agrave;o, gi&uacute;p b&agrave;i tiết citrat qua nước tiểu, từ đ&oacute; l&agrave;m giảm nguy cơ h&igrave;nh th&agrave;nh sỏi thận.</li>\n	<li>Vitamin C trong nước cam gi&uacute;p tăng cường sức khỏe của mạch m&aacute;u, giảm nguy cơ đột quỵ.</li>\n	<li>Chất xơ trong nước cam cũng gi&uacute;p tăng cường hệ vi khuẩn đường ruột, hỗ trợ ti&ecirc;u h&oacute;a tốt hơn.</li>\n	<li>Nước cam c&ograve;n c&oacute; t&aacute;c dụng thanh lọc cơ thể, gi&uacute;p loại bỏ độc tố ra khỏi cơ thể.</li>\n	<li>Nước cam c&ograve;n chứa c&aacute;c vitamin v&agrave; kho&aacute;ng chất kh&aacute;c như vitamin A, vitamin E, kẽm,... g&oacute;p phần tăng cường sức đề kh&aacute;ng cho cơ thể.</li>\n	<li>Vitamin C trong nước cam gi&uacute;p sản sinh collagen, tăng độ đ&agrave;n hồi cho da, giảm nếp nhăn v&agrave; chống l&atilde;o h&oacute;a.</li>\n</ul>\n\n<p><strong>C&aacute;ch lựa chọn những tr&aacute;i cam s&agrave;nh ngon, ngọt v&agrave; kh&ocirc;ng bị chua</strong></p>\n\n<ul>\n	<li>Chọn những quả cam s&agrave;nh c&oacute; h&igrave;nh d&aacute;ng tr&ograve;n đều, kh&ocirc;ng bị m&eacute;o m&oacute; hay b&ecirc;n to b&ecirc;n nhỏ. Tr&aacute;nh chọn những quả cam bị dập n&aacute;t hoặc c&oacute; dấu hiệu bị s&acirc;u bệnh.</li>\n	<li>Chọn những quả c&oacute; cam s&agrave;nh ngon vỏ thường c&oacute; m&agrave;u xanh b&oacute;ng, kh&ocirc;ng qu&aacute; to hay qu&aacute; sần s&ugrave;i b&ecirc;n ngo&agrave;i.</li>\n	<li>Cam s&agrave;nh ngon thường c&oacute; k&iacute;ch thước vừa phải, cầm lại kh&aacute; nặng tay. Điều n&agrave;y cho thấy quả cam c&oacute; nhiều nước v&agrave; mọng thịt.</li>\n	<li>Cuống cam s&agrave;nh ngon phải c&ograve;n tươi v&agrave; d&iacute;nh chặt v&agrave;o quả. Tr&aacute;nh chọn những quả cam c&oacute; cuống bị h&eacute;o &uacute;a hoặc rụng rời.</li>\n	<li>Với Farmers Market ti&ecirc;u ch&iacute; h&agrave;ng đầu l&agrave; sản phảm đạt chất lượng cao, nguồn gốc r&otilde; r&agrave;ng, mang đến trải nghiệm tốt nhất cho kh&aacute;ch h&agrave;ng.&nbsp;</li>\n</ul>\n\n<p><strong>C&aacute;ch bảo quản cam s&agrave;nh tươi v&agrave; ngon</strong></p>\n\n<ul>\n	<li>Cam n&ecirc;n được bảo quản ở nhiệt độ từ 10&deg;C đến 15&deg;C. Đ&acirc;y l&agrave; nhiệt độ l&yacute; tưởng để cam giữ được độ tươi ngon trong thời gian d&agrave;i nhất.</li>\n	<li>Cam cần được bảo quản ở độ ẩm khoảng 85%. Độ ẩm cao sẽ khiến cam bị thối rữa, trong khi độ ẩm thấp sẽ khiến cam bị kh&ocirc; h&eacute;o.</li>\n	<li>Cam cần được bảo quản ở nơi c&oacute; lưu th&ocirc;ng kh&ocirc;ng kh&iacute; tốt để tr&aacute;nh bị thối rữa</li>\n	<li>Kh&ocirc;ng n&ecirc;n bọc k&iacute;n t&uacute;i đựng cam v&igrave; điều n&agrave;y sẽ khiến cam dễ bị hỏng</li>\n	<li>N&ecirc;n để cam ở ngăn m&aacute;t tủ lạnh, tr&aacute;nh xa c&aacute;c loại tr&aacute;i c&acirc;y v&agrave; thực phẩm kh&aacute;c như thịt, c&aacute; v&igrave; những thực phẩm n&agrave;y c&oacute; thể khiến cam bị &aacute;m m&ugrave;i.</li>\n	<li>Bạn c&oacute; thể bọc từng quả cam bằng giấy b&aacute;o trước khi cho v&agrave;o t&uacute;i đựng để gi&uacute;p cam giữ được độ tươi ngon l&acirc;u hơn.</li>\n	<li>N&ecirc;n sử dụng t&uacute;i zip, t&uacute;i nhựa hoặc t&uacute;i lưới để bảo quản cam v&igrave; những loại t&uacute;i n&agrave;y c&oacute; thể gi&uacute;p cam được th&ocirc;ng tho&aacute;ng kh&iacute;.</li>\n</ul>\n\n<p><strong>Những lưu &yacute; cần biết khi sử dụng cam s&agrave;nh</strong></p>\n\n<ul>\n	<li>Những người c&oacute; bệnh về dạ d&agrave;y, t&aacute; tr&agrave;ng hoặc vi&ecirc;m tuyến tụy n&ecirc;n hạn chế hoặc tr&aacute;nh sử dụng cam, đặc biệt l&agrave; cam chua</li>\n	<li>Buổi s&aacute;ng l&agrave; thời điểm tốt nhất để sử dụng nước cam, nhất l&agrave; sau bữa s&aacute;ng khoảng 1 đến 2 tiếng</li>\n</ul>\n', 'Trái cây Việt', 29000, 1, '2024-07-05', 7, 'f5b4c1fe5d80872b48b88e21b7d0222e', 'lyvmoiw8jlidhemkzpd3', 3, 2, 1),
(59, 'Mận xanh Tam Hoa', '<h1><strong>Mận xanh Tam Hoa&nbsp;</strong></h1>\n\n<p><strong>- Xuất xứ</strong>: Việt Nam</p>\n\n<p><strong>- Ti&ecirc;u chuẩn chất lượng:</strong>&nbsp;Tr&aacute;i c&acirc;y sạch</p>\n\n<p><strong>- Trọng lượng:</strong>&nbsp;1kg</p>\n\n<p><strong>- Đặc điểm của Mận xanh Tam Hoa:</strong></p>\n\n<ul>\n	<li><strong>Quả:</strong>&nbsp;Mận xanh Tam Hoa c&oacute; h&igrave;nh chu&ocirc;ng, hơi bầu dục, vỏ m&agrave;u xanh sẫm khi c&ograve;n non v&agrave; chuyển sang m&agrave;u xanh v&agrave;ng khi ch&iacute;n. Quả c&oacute; trọng lượng trung b&igrave;nh từ 30 gram đến 50 gram.</li>\n	<li><strong>Thịt quả:</strong>&nbsp;Thịt quả mận xanh Tam Hoa c&oacute; m&agrave;u v&agrave;ng ươm, mềm mịn, mọng nước v&agrave; c&oacute; vị chua ngọt thanh m&aacute;t.</li>\n	<li><strong>Hạt:</strong>&nbsp;Hạt mận xanh Tam Hoa nhỏ, m&agrave;u n&acirc;u v&agrave; &iacute;t.</li>\n</ul>\n\n<p><strong>- Lợi &iacute;ch của Mận xanh Tam Hoa:</strong></p>\n\n<ul>\n	<li><strong>Gi&agrave;u dinh dưỡng:</strong>&nbsp;Mận xanh Tam Hoa l&agrave; nguồn cung cấp dồi d&agrave;o vitamin C, vitamin A, kali v&agrave; chất xơ.</li>\n	<li><strong>Tốt cho sức khỏe:</strong>&nbsp;Mận xanh Tam Hoa gi&uacute;p tăng cường hệ miễn dịch, cải thiện sức khỏe ti&ecirc;u h&oacute;a, tốt cho tim mạch v&agrave; gi&uacute;p đẹp da.</li>\n	<li><strong>Giải nhiệt:</strong>&nbsp;Mận xanh Tam Hoa c&oacute; t&iacute;nh giải nhiệt cao, rất th&iacute;ch hợp để sử dụng trong những ng&agrave;y n&oacute;ng bức.</li>\n</ul>\n\n<p><strong>- C&aacute;ch sử dụng Mận xanh Tam Hoa:</strong></p>\n\n<ul>\n	<li>Mận xanh Tam Hoa c&oacute; thể ăn tươi trực tiếp như một m&oacute;n ăn nhẹ hoặc tr&aacute;ng miệng.</li>\n	<li>Mận xanh Tam Hoa cũng c&oacute; thể được sử dụng để l&agrave;m sinh tố, salad, mứt, nước &eacute;p tr&aacute;i c&acirc;y,...</li>\n</ul>\n\n<p><strong>- C&aacute;ch bảo quản Mận xanh Tam Hoa:</strong></p>\n\n<ul>\n	<li>Mận xanh Tam Hoa n&ecirc;n được bảo quản ở nhiệt độ ph&ograve;ng khi c&ograve;n xanh v&agrave; chuyển v&agrave;o tủ lạnh khi đ&atilde; ch&iacute;n.</li>\n	<li>Mận xanh Tam Hoa c&oacute; thể bảo quản trong tủ lạnh từ 3 đến 5 ng&agrave;y.</li>\n	<li>N&ecirc;n để mận xanh Tam Hoa trong hộp k&iacute;n hoặc rổ c&oacute; l&oacute;t khăn giấy để tr&aacute;nh bị b&iacute; v&agrave; hư hỏng.</li>\n</ul>\n\n<p><strong>- C&aacute;ch chọn mận xanh Tam Hoa:</strong></p>\n\n<ul>\n	<li>N&ecirc;n chọn những quả c&oacute; h&igrave;nh dạng c&acirc;n đối, vỏ m&agrave;u xanh sẫm khi c&ograve;n non v&agrave; xanh v&agrave;ng khi ch&iacute;n, sờ v&agrave;o c&oacute; cảm gi&aacute;c mềm nhẹ.</li>\n	<li>Với Farmers Market ti&ecirc;u ch&iacute; h&agrave;ng đầu l&agrave; sản phảm đạt chất lượng cao, nguồn gốc r&otilde; r&agrave;ng, mang đến trải nghiệm tốt nhất cho kh&aacute;ch h&agrave;ng.&nbsp;</li>\n</ul>\n', 'Trái cây Việt', 109000, 1, '2024-07-05', 7, '1a275095d3bf4c2b835e7087a9cc22fc', 'cim76ctiv9qtrlaktekk', 3, 3, 1),
(60, 'Lê Nam Phi ', '<h1><strong>L&ecirc; Nam Phi&nbsp;</strong></h1>\n\n<p><strong>- Xuất xứ:</strong>&nbsp;Nam Phi</p>\n\n<p><strong>- Ti&ecirc;u chuẩn chất lượng:&nbsp;</strong>Nhập khẩu</p>\n\n<p><strong>- Khối lượng:&nbsp;</strong>1kg</p>\n\n<p><strong>- Đặc điểm:</strong></p>\n\n<ul>\n	<li>L&ecirc; Nam Phi c&oacute; vị ngọt thanh, rất gi&ograve;n v&agrave; thơm, ngon hơn khi ăn lạnh v&agrave;o thời tiết n&oacute;ng bức, được người ti&ecirc;u d&ugrave;ng tr&ecirc;n to&agrave;n thế giới ưa chuộng, xuất nhiều nhất sang Ch&acirc;u &Acirc;u thị trường cực kỳ kh&oacute; t&iacute;nh về chất lượng v&agrave; an to&agrave;n thực phẩm.</li>\n	<li>Vỏ m&agrave;u xanh xen lẫn m&agrave;u v&agrave;ng c&oacute; vệt đỏ&nbsp;rực rỡ&nbsp;khi quả ch&iacute;n.&nbsp;Quả&nbsp;h&igrave;nh chu&ocirc;ng&nbsp;nhỏ, tr&ograve;n v&agrave;&nbsp;thon đều, b&ecirc;n trong thịt trắng với hương vị thơm&nbsp;m&aacute;t, ngọt thanh.</li>\n</ul>\n\n<p><strong>- Gi&aacute; trị dinh dưỡng:</strong></p>\n\n<p><strong>L&ecirc; Nam Phi</strong>&nbsp;l&agrave; nguồn cung cấp dồi d&agrave;o vitamin C, chất xơ, kali, magie v&agrave; c&aacute;c kho&aacute;ng chất thiết yếu kh&aacute;c cho cơ thể.</p>\n\n<ul>\n	<li><strong>Vitamin C:</strong>&nbsp;Gi&uacute;p tăng cường hệ miễn dịch, chống oxy h&oacute;a v&agrave; bảo vệ cơ thể khỏi c&aacute;c bệnh tật.</li>\n	<li><strong>Chất xơ:</strong>&nbsp;Gi&uacute;p hỗ trợ ti&ecirc;u h&oacute;a, ngăn ngừa t&aacute;o b&oacute;n v&agrave; giảm cholesterol.</li>\n	<li><strong>Kali:</strong>&nbsp;Gi&uacute;p điều h&ograve;a huyết &aacute;p v&agrave; duy tr&igrave; chức năng tim mạch.</li>\n	<li><strong>Magie:</strong>&nbsp;Gi&uacute;p hỗ trợ hệ thần kinh v&agrave; cơ bắp.</li>\n</ul>\n\n<p><strong>- Lợi &iacute;ch sức khỏe:</strong></p>\n\n<ul>\n	<li><strong>Tăng cường hệ miễn dịch:</strong>&nbsp;Vitamin C trong l&ecirc; Nam Phi gi&uacute;p tăng cường hệ miễn dịch, chống lại c&aacute;c bệnh cảm c&uacute;m, ho.</li>\n	<li><strong>Hỗ trợ ti&ecirc;u h&oacute;a:</strong>&nbsp;Chất xơ trong l&ecirc; Nam Phi gi&uacute;p nhuận tr&agrave;ng, ngăn ngừa t&aacute;o b&oacute;n.</li>\n	<li><strong>Giảm cholesterol:</strong>&nbsp;Chất xơ trong l&ecirc; Nam Phi cũng gi&uacute;p giảm cholesterol xấu (LDL) trong m&aacute;u, bảo vệ sức khỏe tim mạch.</li>\n	<li><strong>Kiểm so&aacute;t c&acirc;n nặng:</strong>&nbsp;L&ecirc; Nam Phi c&oacute; lượng calo thấp v&agrave; gi&agrave;u chất xơ, gi&uacute;p bạn cảm thấy no l&acirc;u v&agrave; hạn chế cảm gi&aacute;c th&egrave;m ăn, hỗ trợ giảm c&acirc;n hiệu quả.</li>\n	<li><strong>L&agrave;m đẹp da:</strong>&nbsp;Vitamin C v&agrave; chất chống oxy h&oacute;a trong l&ecirc; Nam Phi gi&uacute;p bảo vệ da khỏi t&aacute;c hại của &aacute;nh nắng mặt trời, l&agrave;m giảm nếp nhăn v&agrave; tăng độ đ&agrave;n hồi cho da.</li>\n</ul>\n\n<p><strong>- C&aacute;ch sử dụng:</strong></p>\n\n<p>L&ecirc; Nam Phi c&oacute; thể ăn trực tiếp, &eacute;p nước, l&agrave;m sinh tố, salad hoặc chế biến th&agrave;nh c&aacute;c m&oacute;n ăn kh&aacute;c nhau như b&aacute;nh, mứt, ...</p>\n\n<p><strong>- C&aacute;ch lựa chọn l&ecirc; ngon:</strong></p>\n\n<ul>\n	<li>N&ecirc;n chọn mua l&ecirc; c&oacute; vỏ mịn, &iacute;t đốm v&agrave; nhạt m&agrave;u. Tr&aacute;nh chọn những quả c&oacute; lớp vỏ hơi th&ocirc;, xuất hiện nhiều đường kẻ m&agrave;u n&acirc;u, đốm đen v&agrave; m&agrave;u hơi sẫm. Những quả như vậy thường &iacute;t nước v&agrave; sắp bị hỏng</li>\n	<li>Với Farmers Market ti&ecirc;u ch&iacute; h&agrave;ng đầu l&agrave; sản phảm đạt chất lượng cao, nguồn gốc r&otilde; r&agrave;ng, mang đến trải nghiệm tốt nhất cho kh&aacute;ch h&agrave;ng.&nbsp;</li>\n</ul>\n\n<p><strong>- C&aacute;ch bảo quản l&ecirc; được l&acirc;u:</strong></p>\n\n<p><strong>+ Bảo quản l&ecirc; ở nhiệt độ ph&ograve;ng:</strong></p>\n\n<ul>\n	<li>Nếu bạn muốn l&ecirc; ch&iacute;n nhanh hơn, bạn c&oacute; thể bảo quản l&ecirc; ở nhiệt độ ph&ograve;ng.</li>\n	<li>Đặt l&ecirc; trong t&uacute;i giấy hoặc t&uacute;i nhựa v&agrave; để ở nơi tho&aacute;ng m&aacute;t, tr&aacute;nh &aacute;nh nắng trực tiếp.</li>\n	<li>L&ecirc; sẽ ch&iacute;n trong v&ograve;ng v&agrave;i ng&agrave;y.</li>\n</ul>\n\n<p><strong>+ Bảo quản l&ecirc; trong tủ lạnh:</strong></p>\n\n<ul>\n	<li>Nếu bạn muốn bảo quản l&ecirc; l&acirc;u hơn, bạn n&ecirc;n bảo quản l&ecirc; trong tủ lạnh.</li>\n	<li>Đặt l&ecirc; v&agrave;o khay mềm v&agrave; để trong ngăn m&aacute;t tủ lạnh.</li>\n	<li>L&ecirc; sẽ giữ được tươi ngon trong v&agrave;i tuần.</li>\n	<li>Kh&ocirc;ng n&ecirc;n rửa l&ecirc; trước khi bảo quản v&igrave; điều n&agrave;y c&oacute; thể l&agrave;m tăng nguy cơ thối rữa.</li>\n	<li>N&ecirc;n bảo quản l&ecirc; ri&ecirc;ng với c&aacute;c loại tr&aacute;i c&acirc;y v&agrave; rau củ kh&aacute;c v&igrave; l&ecirc; c&oacute; thể hấp thụ m&ugrave;i của c&aacute;c loại thực phẩm kh&aacute;c.</li>\n</ul>\n\n<p><strong>- Phụ nữ mang thai v&agrave; trẻ em ăn l&ecirc; c&oacute; được kh&ocirc;ng?</strong></p>\n\n<ul>\n	<li><strong>Quả l&ecirc;</strong>&nbsp;gi&uacute;p ngăn ngừa c&aacute;c bệnh truyền nhiễm&nbsp;th&ocirc;ng thường như cảm lạnh, ho, c&uacute;m.L&ecirc; c&ograve;n l&agrave; nguồn gi&agrave;u vitamin C,&nbsp;<strong>rất tốt cho thai kỳ</strong>. Một quả l&ecirc; đơn chứa 10mg vitamin, chiếm tới 11% lượng bổ sung thực phẩm được đề nghị h&agrave;ng ng&agrave;y (RDA) cho phụ nữ.</li>\n	<li><strong>Khi b&eacute; được 4 đến 6 th&aacute;ng tuổi c&oacute; thể ăn l&ecirc; ở dạng nhuyễn</strong>. L&ecirc; gi&uacute;p ngăn ngừa t&aacute;o b&oacute;n ở trẻ sơ sinh. Ch&uacute;ng cũng chứa nhiều vitamin C v&agrave; Kali, c&oacute; lợi cho sự ph&aacute;t triển to&agrave;n diện ở trẻ. Mẹ cần quan s&aacute;t cho trẻ ăn loại quả n&agrave;y, để đảm bảo trẻ kh&ocirc;ng bị kh&oacute; chịu dạ d&agrave;y v&agrave; tr&agrave;o ngược.</li>\n</ul>\n\n<p><strong>- Những lưu &yacute; khi d&ugrave;ng l&ecirc;:&nbsp;</strong></p>\n\n<ul>\n	<li>Nếu ăn l&ecirc; kết hợp với củ cải sẽ l&agrave;m sưng tuyến gi&aacute;p.</li>\n	<li>Những người c&oacute; đường ti&ecirc;u h&oacute;a yếu hoặc rối loạn đường ti&ecirc;u h&oacute;a h&ocirc;ng n&ecirc;n ăn rau dền c&ugrave;ng với l&ecirc;. Sẽ dễ bị n&ocirc;n v&agrave; xuất hiện c&aacute;c vấn đề ti&ecirc;u h&oacute;a kh&aacute;c.</li>\n	<li>Sau sinh ăn l&ecirc;&nbsp;giai đoạn ở cữ sẽ kh&ocirc;ng c&oacute; lợi cho mẹ</li>\n</ul>\n', 'Trái cây Việt', 78900, 1, '2024-07-05', 7, '3ef1a0cf3e51635ec4797cd76fa731b4', 'ltedhrwzabs21rsjzijj', 3, 2, 1),
(61, 'Mận đỏ An Phước', '<p><strong>Mận đỏ An Phước</strong></p>\n\n<p><strong>- Xuất xứ:&nbsp;</strong>Việt Nam</p>\n\n<p>-&nbsp;<strong>Ti&ecirc;u chuẩn chất lượng:&nbsp;</strong>Tr&aacute;i c&acirc;y sạch</p>\n\n<p>-&nbsp;<strong>Trọng lượng:&nbsp;</strong>1kg</p>\n\n<p><strong>- Đặc Điểm:</strong></p>\n\n<ul>\n	<li>C&oacute; vị ngọt dịu, gi&ograve;n xốp, kh&aacute; mọng nước.</li>\n	<li>Nhiều nước v&agrave; vitamin</li>\n</ul>\n\n<p><strong>Mận Đỏ An Phước (KG)</strong>&nbsp;l&agrave; một tr&aacute;i c&acirc;y quen thuộc được nhiều người y&ecirc;u th&iacute;ch bởi t&aacute;c dụng thanh nhiệt. Mận được trồng tại An Phước với m&agrave;u đỏ v&agrave; b&oacute;ng, tr&aacute;i to vừa v&agrave; ngọt thanh, tỉ lệ gặp tr&aacute;i c&oacute; vị chua rất &iacute;t (trừ tr&aacute;i c&ograve;n xanh, chưa ch&iacute;n).</p>\n\n<p><strong>- Lợi &iacute;ch của Mận đỏ An Phước:</strong></p>\n\n<ul>\n	<li>Mận đỏ An Phước l&agrave; nguồn cung cấp dồi d&agrave;o vitamin C, vitamin A, kali v&agrave; chất xơ.</li>\n	<li>Gi&uacute;p tăng cường hệ miễn dịch, cải thiện sức khỏe ti&ecirc;u h&oacute;a, tốt cho tim mạch v&agrave; gi&uacute;p đẹp da.</li>\n	<li>C&oacute; t&iacute;nh giải nhiệt cao, rất th&iacute;ch hợp để sử dụng trong những ng&agrave;y n&oacute;ng bức.</li>\n</ul>\n\n<p><strong>- C&aacute;ch sử dụng Mận đỏ An Phước:</strong></p>\n\n<ul>\n	<li>Mận đỏ An Phước c&oacute; thể ăn tươi trực tiếp như một m&oacute;n ăn nhẹ hoặc tr&aacute;ng miệng.</li>\n	<li>Mận đỏ An Phước cũng c&oacute; thể được sử dụng để l&agrave;m sinh tố, salad, mứt, nước &eacute;p tr&aacute;i c&acirc;y,...</li>\n</ul>\n\n<p><strong>- C&aacute;ch bảo quản Mận đỏ An Phước:</strong></p>\n\n<ul>\n	<li>Mận đỏ An Phước n&ecirc;n được bảo quản ở nhiệt độ ph&ograve;ng khi c&ograve;n xanh v&agrave; chuyển v&agrave;o tủ lạnh khi đ&atilde; ch&iacute;n.</li>\n	<li>Mận đỏ An Phước c&oacute; thể bảo quản trong tủ lạnh từ 3 đến 5 ng&agrave;y.</li>\n	<li>N&ecirc;n để mận đỏ An Phước trong hộp k&iacute;n hoặc rổ c&oacute; l&oacute;t khăn giấy để tr&aacute;nh bị b&iacute; v&agrave; hư hỏng.</li>\n</ul>\n', 'Trái cây Việt', 119000, 1, '2024-07-05', 7, '5e69feda7da8116c90d00ac987ac5a79', 'swkspwg2vrnjnsulc0wq', 3, 3, 1);
INSERT INTO `products` (`id`, `nameOfProduct`, `description`, `category`, `price`, `weightDefault`, `dateOfImporting`, `expriredDay`, `imgAssetId`, `imgPublicId`, `adminCreate`, `provider`, `status`) VALUES
(62, 'Lựu đỏ Peru size Jumbo', '<h1><strong>Lựu đỏ Peru size Jumbo&nbsp;</strong></h1>\n\n<p><strong>- Xuất xứ: Peru</strong></p>\n\n<p><strong>- Ti&ecirc;u chuẩn chất lượng:&nbsp;</strong>Nhập khẩu</p>\n\n<p><strong>- Đặc điểm:</strong></p>\n\n<ul>\n	<li><strong>K&iacute;ch thước:</strong>&nbsp;Jumbo, quả to, thường nặng 800g - 850g/tr&aacute;i</li>\n	<li><strong>M&agrave;u sắc:</strong>&nbsp;Vỏ đỏ tươi, b&ecirc;n trong hạt m&agrave;u đỏ ruby đẹp mắt</li>\n	<li><strong>Hương vị:</strong>&nbsp;Ngọt đậm, mọng nước, vị chua dịu</li>\n</ul>\n\n<p><strong>- Gi&aacute; trị dinh dưỡng:</strong></p>\n\n<ul>\n	<li><strong>Gi&agrave;u chất chống oxy h&oacute;a:</strong>&nbsp;Polyphenol, anthocyanin gi&uacute;p tăng cường hệ miễn dịch, chống l&atilde;o h&oacute;a, giảm nguy cơ mắc bệnh tim mạch, ung thư.</li>\n	<li><strong>Chứa nhiều vitamin:</strong>&nbsp;Vitamin C, A, E, K tốt cho sức khỏe mắt, da, hệ miễn dịch v&agrave; xương khớp.</li>\n	<li><strong>Gi&agrave;u kho&aacute;ng chất:</strong>&nbsp;Kali, magie, sắt tốt cho tim mạch, hệ thần kinh v&agrave; m&aacute;u.</li>\n	<li><strong>Chất xơ:</strong>&nbsp;Hỗ trợ ti&ecirc;u h&oacute;a, giảm cholesterol.</li>\n	<li><strong>Kali:</strong>&nbsp;Gi&uacute;p điều h&ograve;a huyết &aacute;p.</li>\n</ul>\n\n<p><strong>- Hướng dẫn sử dụng lựu</strong></p>\n\n<ul>\n	<li>Rửa nhẹ nh&agrave;ngvỏ ngo&agrave;i của lựu.</li>\n	<li>C&aacute;ch t&aacute;ch hạt lựu ra khỏi lớp vỏ quả nhanh nhất l&agrave; t&aacute;ch quả lựu th&agrave;nh từng kh&iacute;a mỏng, sau đ&oacute; d&ugrave;ng muỗng đập nhẹ l&ecirc;n lớp v&otilde;, những hạt lựu sẽ từ từ rơi ra hết.</li>\n	<li>Giữ lạnh lựu gi&uacute;p bảo quản l&acirc;u hơn.</li>\n</ul>\n\n<p><strong>- Hướng dẫn sử dụng:</strong></p>\n\n<ul>\n	<li><strong>Phương ph&aacute;p truyền thống:</strong>\n\n	<ol>\n		<li>Cắt ngang quả lựu th&agrave;nh hai phần theo chiều ngang.</li>\n		<li>D&ugrave;ng muỗng g&otilde; nhẹ v&agrave;o vỏ lựu để t&aacute;ch hạt rơi ra.</li>\n		<li>Nhặt bỏ phần c&ugrave;i trắng v&agrave; vỏ c&ograve;n s&oacute;t lại.</li>\n	</ol>\n	</li>\n	<li><strong>Phương ph&aacute;p sử dụng nước:</strong>\n	<ol>\n		<li>Cắt ngang quả lựu th&agrave;nh hai phần theo chiều ngang.</li>\n		<li>Cho lựu v&agrave;o t&ocirc; nước v&agrave; ng&acirc;m trong v&agrave;i ph&uacute;t.</li>\n		<li>D&ugrave;ng tay b&oacute;p nhẹ vỏ lựu để t&aacute;ch hạt ra.</li>\n		<li>Vớt hạt lựu ra khỏi nước v&agrave; để r&aacute;o.</li>\n	</ol>\n	</li>\n	<li>Lựu&nbsp;c&oacute; thể ăn trực tiếp, l&agrave;m nước &eacute;p, sinh tố, salad</li>\n</ul>\n\n<p><strong>- C&aacute;ch lựa chọn lựu ngon:</strong></p>\n\n<ul>\n	<li>\n	<p>Kh&aacute;ch h&agrave;ng&nbsp;n&ecirc;n&nbsp;chọn những quả nho c&oacute; vỏ&nbsp;căng b&oacute;ng, m&agrave;u sắc tươi s&aacute;ng, kh&ocirc;ng bị dập n&aacute;t hay c&oacute; dấu hiệu thối rữa.</p>\n	</li>\n	<li>\n	<p>Với Farmers Market ti&ecirc;u ch&iacute; h&agrave;ng đầu l&agrave; sản phảm đạt chất lượng cao, nguồn gốc r&otilde; r&agrave;ng, mang đến trải nghiệm tốt nhất cho kh&aacute;ch h&agrave;ng.&nbsp;</p>\n	</li>\n</ul>\n\n<p>-&nbsp;<strong>C&aacute;ch bảo quản lựu</strong><strong>:&nbsp;</strong></p>\n\n<p><strong>+ Bảo quản lựu ở nhiệt độ thường:</strong></p>\n\n<ul>\n	<li>Để lựu ở nơi kh&ocirc; r&aacute;o, tho&aacute;ng m&aacute;t, tr&aacute;nh &aacute;nh nắng trực tiếp.</li>\n	<li>Lựu c&oacute; thể bảo quản ở nhiệt độ ph&ograve;ng trong khoảng 1-2 tuần.</li>\n	<li>Kh&ocirc;ng n&ecirc;n rửa lựu trước khi bảo quản v&igrave; sẽ khiến lựu nhanh bị thối rữa.</li>\n</ul>\n\n<p><strong>+ Bảo quản lựu trong tủ lạnh:</strong></p>\n\n<ul>\n	<li>Cho lựu v&agrave;o t&uacute;i nilon hoặc hộp k&iacute;n v&agrave; bảo quản trong ngăn m&aacute;t tủ lạnh.</li>\n	<li>Lựu c&oacute; thể bảo quản trong tủ lạnh trong khoảng 2-3 th&aacute;ng.</li>\n	<li>Kh&ocirc;ng n&ecirc;n rửa lựu trước khi bảo quản v&igrave; sẽ khiến lựu nhanh bị thối rữa.</li>\n	<li>Hạt lựu đ&ocirc;ng lạnh c&oacute; thể bảo quản trong khoảng 6 th&aacute;ng.</li>\n</ul>\n\n<p><strong>- Những lưu &yacute; khi thưởng thức lựu:&nbsp;</strong></p>\n\n<ul>\n	<li>Tr&aacute;nh d&ugrave;ng chiết xuất từ vỏ lựu v&igrave; n&oacute; c&oacute; thể g&acirc;y co thắt dẫn đến chuyển dạ sớm</li>\n	<li>Nước &eacute;p lựu c&oacute; h&agrave;m lượng calo cao, do đ&oacute; bạn n&ecirc;n uống với lượng vừa phải</li>\n	<li>Lựu khi v&agrave;o cơ thể sẽ tương t&aacute;c với một số loại thuốc kh&aacute;c như thuốc giảm lo&atilde;ng m&aacute;u, thuốc ức chế ACE, statin v&agrave; thuốc trị huyết &aacute;p.</li>\n</ul>\n\n<p><strong>- Phụ nữ mang thai v&agrave; trẻ em ăn lựu&nbsp;c&oacute; được kh&ocirc;ng?</strong></p>\n\n<ul>\n	<li>\n	<p>Phụ nữ mang thai n&ecirc;n ăn lựu&nbsp;với lượng vừa phải, khoảng 400-600 mgram mỗi ng&agrave;y</p>\n	</li>\n	<li>\n	<p>Trẻ em tr&ecirc;n 6 th&aacute;ng&nbsp;tuổi c&oacute; thể ăn lựu, tuy nhi&ecirc;n hạt lựu c&oacute; thể&nbsp;g&acirc;y nguy cơ nghẹt thở cho trẻ nhỏ, ba mẹ n&ecirc;n &eacute;p nước cho b&eacute; d&ugrave;ng</p>\n	</li>\n	<li>\n	<p>Cho trẻ ăn nho với lượng vừa phải, khoảng 50-100 gram mỗi ng&agrave;y</p>\n	</li>\n</ul>\n', 'Trái cây Việt', 211650, 1, '2024-07-05', 7, 'c1cc2ffdc36300df349f45cd802cfe72', 'jcyblntjocb8m9xfd3qs', 3, 2, 1),
(63, 'Me Thái Lan', '<h1><strong>Me Th&aacute;i Lan</strong></h1>\n\n<p><strong>- Xuất xứ:&nbsp;</strong>Th&aacute;i Lan</p>\n\n<p><strong>- Khối lượng:</strong>&nbsp;1Kg/T&uacute;i</p>\n\n<p><strong>- Ti&ecirc;u chuẩn chất lượng:&nbsp;</strong>Nhập Khẩu</p>\n\n<p><strong>- Đặc điểm:&nbsp;</strong></p>\n\n<ul>\n	<li>Me c&oacute; vị ngọt, thơm đặc trưng, thịt d&agrave;y v&agrave; dẻo.&nbsp;</li>\n	<li>Chứa nhiều vitamin v&agrave; kho&aacute;ng chất tốt cho cơ thể.&nbsp;</li>\n	<li>Ăn me c&oacute; t&aacute;c dụng tr&aacute;nh tăng huyết &aacute;p, ngăn ngừa b&eacute;o ph&igrave;, ph&ograve;ng ngừa ung thư, lọc m&aacute;u, bổ mắt v&agrave; tim mạch.&nbsp;</li>\n	<li>C&oacute; thể d&ugrave;ng để ăn trực tiếp hoặc l&agrave;m gia vị cho m&oacute;n&nbsp;</li>\n</ul>\n\n<p><strong>- C&ocirc;ng dụng:</strong></p>\n\n<ul>\n	<li>Ăn trực tiếp</li>\n	<li>Nấu canh, l&agrave;m gỏi, l&agrave;m mứt</li>\n	<li>D&ugrave;ng l&agrave;m gia vị cho c&aacute;c m&oacute;n ăn</li>\n	<li>L&agrave;m tr&agrave; me</li>\n</ul>\n\n<p><strong>- Bảo quản:</strong>&nbsp;Nơi kh&ocirc; r&aacute;o, tho&aacute;ng m&aacute;t. Me c&oacute; thể bảo quản trong ngăn m&aacute;t tủ lạnh để giữ độ tươi ngon l&acirc;u hơn.</p>\n\n<p><strong>- Lợi &iacute;ch sức khỏe:</strong></p>\n\n<ul>\n	<li>Gi&agrave;u vitamin C, tốt cho hệ miễn dịch.</li>\n	<li>Chứa nhiều chất chống oxy h&oacute;a, gi&uacute;p bảo vệ cơ thể khỏi c&aacute;c gốc tự do.</li>\n	<li>Tốt cho ti&ecirc;u h&oacute;a</li>\n	<li>Gi&uacute;p giảm cholesterol</li>\n	<li>Hỗ trợ giảm c&acirc;n</li>\n</ul>\n\n<p><strong>- C&aacute;ch lựa chọn me ngon</strong></p>\n\n<ul>\n	<li>N&ecirc;n&nbsp;chọn những tr&aacute;i me to, d&aacute;ng thẳng đứng v&agrave; tr&ograve;n đều. Vỏ me n&ecirc;n cứng, b&ecirc;n ngo&agrave;i mịn m&agrave;ng.</li>\n	<li>Với Farmers Market ti&ecirc;u ch&iacute; h&agrave;ng đầu l&agrave; sản phảm đạt chất lượng cao, nguồn gốc r&otilde; r&agrave;ng, mang đến trải nghiệm tốt nhất cho kh&aacute;ch h&agrave;ng.&nbsp;</li>\n</ul>\n', 'Trái cây Việt', 159000, 1, '2024-07-05', 7, 'bf8181afee2fbac359d73303d78150ae', 'yylpunfjjxqhqypjgaio', 3, 1, 1),
(64, 'Bưởi đường lá cam Tân Triều', '<h1><strong>Bưởi đường l&aacute; cam T&acirc;n Triều&nbsp;</strong></h1>\n\n<p><strong>- Xuất xứ:&nbsp;</strong>Việt Nam</p>\n\n<p><strong>- Ti&ecirc;u chuẩn chất lượng:&nbsp;</strong>Tr&aacute;i c&acirc;y sạch</p>\n\n<p><strong>- Khối lượng:</strong>&nbsp;(0,9 - 1.2Kg/Tr&aacute;i)</p>\n\n<p><strong>- Đặc điểm nổi bật của bưởi đường l&aacute; cam T&acirc;n Triều</strong></p>\n\n<p>Vỏ mỏng, mọng nước, vị đậm đ&agrave;, m&uacute;i bưởi dẻo kh&aacute;c hẵn c&aacute;c loại bưởi kh&aacute;c, c&oacute; thể để được rất l&acirc;u từ 20 - 30 ng&agrave;y m&agrave; kh&ocirc;ng hư, khi bưởi ch&iacute;n vỏ tr&aacute;i dần chuyển sang m&agrave;u v&agrave;ng, l&uacute;c n&agrave;y bưởi rất thơm, kể cả kh&ocirc;ng cần lột vỏ cũng c&oacute; thể ngửi được m&ugrave;i thơm</p>\n\n<p><strong>- Lợi &iacute;ch khi sử dụng bưởi đường l&aacute; cam T&acirc;n Triều</strong></p>\n\n<ul>\n	<li>Loại bưởi n&agrave;y nổi tiếng l&agrave; nguồn cung cấp dồi d&agrave;o c&aacute;c vitamin v&agrave; kho&aacute;ng chất thiết yếu cho cơ thể, mang lại nhiều lợi &iacute;ch cho sức khỏe.</li>\n	<li><strong>Vitamin C:</strong>&nbsp;Bưởi đường l&aacute; cam chứa h&agrave;m lượng vitamin C cao, gi&uacute;p tăng cường hệ miễn dịch, bảo vệ cơ thể khỏi c&aacute;c t&aacute;c nh&acirc;n g&acirc;y bệnh v&agrave; hỗ trợ qu&aacute; tr&igrave;nh sản xuất collagen.</li>\n	<li><strong>Vitamin A:</strong>&nbsp;Vitamin A trong bưởi tốt cho mắt, gi&uacute;p cải thiện thị lực v&agrave; ngăn ngừa c&aacute;c bệnh về mắt.</li>\n	<li><strong>Kali:</strong>&nbsp;Kali gi&uacute;p điều h&ograve;a huyết &aacute;p, giảm nguy cơ mắc c&aacute;c bệnh tim mạch v&agrave; đột quỵ.</li>\n	<li><strong>Chất chống oxy h&oacute;a:</strong>&nbsp;Bưởi đường l&aacute; cam chứa nhiều chất chống oxy h&oacute;a như flavonoid, gi&uacute;p chống lại c&aacute;c gốc tự do, giảm nguy cơ ung thư v&agrave; l&atilde;o h&oacute;a.</li>\n</ul>\n\n<p><strong>- C&aacute;ch lựa chọn bưởi đường l&aacute; cam T&acirc;n Triều ngon</strong></p>\n\n<ul>\n	<li>Bưởi đường l&aacute; cam T&acirc;n Triều c&oacute; h&igrave;nh d&aacute;ng quả tr&ograve;n, đều đặn, kh&ocirc;ng bị m&eacute;o m&oacute; hay xước x&aacute;t. Tr&aacute;nh chọn những quả bưởi c&oacute; h&igrave;nh dạng kỳ dị hoặc bị dập n&aacute;t.</li>\n	<li>N&ecirc;n chọn những quả bưởi c&oacute; k&iacute;ch thước vừa phải, cầm nặng tay v&agrave; cảm gi&aacute;c chắc chắn.</li>\n	<li>Bưởi c&oacute; trọng lượng c&agrave;ng lớn th&igrave; thường chứa nhiều nước v&agrave; mọng thịt hơn.</li>\n	<li>Cuống bưởi đường l&aacute; cam T&acirc;n Triều c&ograve;n tươi, chắc, c&oacute; m&agrave;u xanh v&agrave; d&iacute;nh chặt v&agrave;o quả. Tr&aacute;nh chọn những quả bưởi c&oacute; cuống bị h&eacute;o &uacute;a hoặc rụng rời.</li>\n	<li>Bưởi đường l&aacute; cam T&acirc;n Triều c&oacute; m&ugrave;i thơm nhẹ, thoang thoảng như m&ugrave;i ổi ch&iacute;n. M&ugrave;i thơm n&agrave;y l&agrave; dấu hiệu cho thấy bưởi đ&atilde; ch&iacute;n v&agrave; c&oacute; vị ngọt thanh.</li>\n	<li>Với Farmers Market ti&ecirc;u ch&iacute; h&agrave;ng đầu l&agrave; sản phảm đạt chất lượng cao, nguồn gốc r&otilde; r&agrave;ng, mang đến trải nghiệm tốt nhất cho kh&aacute;ch h&agrave;ng.&nbsp;</li>\n</ul>\n\n<p><strong>- Để bảo quản bưởi đường l&aacute; cam T&acirc;n Triều l&acirc;u, c&oacute; thể sử dụng những c&aacute;ch sau:</strong></p>\n\n<ul>\n	<li><strong>Bảo quản nơi kh&ocirc; r&aacute;o, tho&aacute;ng m&aacute;t:</strong>&nbsp;Sau khi mua về, bạn n&ecirc;n rửa sạch bưởi bằng nước v&agrave; để r&aacute;o nước. Sau đ&oacute;, bạn cho bưởi v&agrave;o t&uacute;i lưới hoặc bọc giấy b&aacute;o v&agrave; để ở nơi kh&ocirc; r&aacute;o, tho&aacute;ng m&aacute;t. C&aacute;ch bảo quản n&agrave;y gi&uacute;p bưởi giữ được độ tươi ngon trong khoảng 2 - 3 tuần</li>\n	<li><strong>Bảo quản trong tủ lạnh:</strong>&nbsp;Nếu muốn bảo quản bưởi trong thời gian d&agrave;i, bạn c&oacute; thể cho bưởi v&agrave;o ngăn m&aacute;t tủ lạnh.&nbsp;Bạn rửa sạch bưởi bằng nước v&agrave; để r&aacute;o nước. Sau đ&oacute;, bạn cho bưởi v&agrave;o t&uacute;i nhựa hoặc hộp đựng thực phẩm v&agrave; để v&agrave;o ngăn m&aacute;t tủ lạnh.</li>\n</ul>\n\n<p><strong>- Những lưu &yacute; cần tr&aacute;nh khi sử dụng bưởi đường l&aacute; cam T&acirc;n Triều</strong></p>\n\n<ul>\n	<li>Người đang đ&oacute;i kh&ocirc;ng n&ecirc;n ăn bưởi.</li>\n	<li>Kh&ocirc;ng n&ecirc;n ăn bưởi khi vừa uống rượu, h&uacute;t thuốc.</li>\n	<li>Người bị ti&ecirc;u h&oacute;a k&eacute;m kh&ocirc;ng n&ecirc;n ăn bưởi</li>\n	<li>Người đang d&ugrave;ng thuốc hạ huyết &aacute;p kh&ocirc;ng n&ecirc;n ăn bưởi</li>\n	<li>Người c&oacute; lượng mỡ trong m&aacute;u cao kh&ocirc;ng n&ecirc;n ăn bưởi</li>\n</ul>\n', 'Trái cây Việt', 49900, 1, '2024-07-05', 7, '3457793c7f9a0007089bb8e5cfd63582', 'gqf91uxqkmtio265w6qa', 3, 1, 1),
(65, 'Đu đủ ruột đỏ giống Đài Loan ', '<h1><strong>Đu đủ ruột đỏ giống Đ&agrave;i Loan</strong></h1>\n\n<p><strong>- Xuất xứ:&nbsp;</strong>Việt Nam</p>\n\n<p><strong>- Trọng lượng sản phẩm</strong>: 1KG</p>\n\n<p><strong>-&nbsp;Đặc điểm nổi bật:</strong>&nbsp;</p>\n\n<ul>\n	<li>Đu đủ l&agrave; một loại tr&aacute;i c&acirc;y m&agrave; gia đ&igrave;nh thường lựa chọn để ăn.</li>\n	<li>Quả đu đủ ruột đỏ giống Đ&agrave;i Loan c&oacute; h&igrave;nh bầu dục, vỏ m&agrave;u v&agrave;ng cam, ruột m&agrave;u đỏ tươi. Quả c&oacute; trọng lượng trung b&igrave;nh từ 1,5 - 2 kg.</li>\n</ul>\n\n<p><strong>- Lợi &iacute;ch tuyệt vời của đu đủ ruột đỏ giống Đ&agrave;i Loan</strong></p>\n\n<ul>\n	<li>Đu đủ ruột đỏ giống Đ&agrave;i Loan l&agrave; nguồn cung cấp dồi d&agrave;o vitamin A, C, E v&agrave; c&aacute;c kho&aacute;ng chất như kali, magie, canxi,...</li>\n	<li>Đu đủ l&agrave; nguồn cung cấp dồi d&agrave;o vitamin, kho&aacute;ng chất v&agrave; chất chống oxy h&oacute;a c&oacute; lợi cho sức khỏe.</li>\n	<li>Ăn đu đủ ch&iacute;n rất tốt cho hệ ti&ecirc;u h&oacute;a v&igrave; n&oacute; chứa h&agrave;m lượng chất xơ&nbsp;dồi d&agrave;o t&aacute;c dụng chữa t&aacute;o b&oacute;n, nhuận tr&agrave;ng, giảm đầy hơi, chướng bụng. Đu đủ ch&iacute;n c&ograve;n chứa nhiều chất chống oxy h&oacute;a c&oacute; thể giảm vi&ecirc;m, cải thiện sức khỏe tim mạch v&agrave; gi&uacute;p trẻ h&oacute;a, săn chắc l&agrave;n da.</li>\n	<li>Đu đủ chứa lycopene, một chất chống oxy h&oacute;a mạnh gi&uacute;p bảo vệ cơ thể khỏi c&aacute;c gốc tự do, giảm nguy cơ mắc c&aacute;c bệnh ung thư.</li>\n	<li>Vitamin C trong đu đủ gi&uacute;p tăng cường hệ miễn dịch ở trẻ em.</li>\n</ul>\n\n<p><strong>- C&aacute;ch lựa chọn đu đủ giống Đ&agrave;i Loan ngon</strong></p>\n\n<ul>\n	<li>N&ecirc;n chọn quả d&agrave;i, cầm nặng tay, cuống c&ograve;n nhựa d&iacute;nh. Những quả n&agrave;y khi ăn th&igrave; vừa ngọt lại thơm ngon, &iacute;t hạt, thịt dầy</li>\n	<li>Với Farmers Market ti&ecirc;u ch&iacute; h&agrave;ng đầu l&agrave; sản phẩm đạt chất lượng cao, nguồn gốc r&otilde; r&agrave;ng, mang đến trải nghiệm tốt nhất cho kh&aacute;ch h&agrave;ng.&nbsp;</li>\n</ul>\n\n<p><strong>- C&aacute;ch bảo quản đu đủ tốt nhất</strong></p>\n\n<ul>\n	<li>Cần tr&aacute;nh để đu đủ dưới &aacute;nh nắng trực tiếp v&igrave; c&oacute; thể khiến quả bị nẫu hoặc hư hỏng.</li>\n	<li>Bạn c&oacute; thể bọc đu đủ xanh bằng giấy b&aacute;o hoặc t&uacute;i giấy để tạo m&ocirc;i trường k&iacute;n. Ethylene do quả tiết ra sẽ bị giữ lại b&ecirc;n trong, th&uacute;c đẩy qu&aacute; tr&igrave;nh ch&iacute;n nhanh hơn.</li>\n	<li>Đảm bảo nơi bảo quản đu đủ c&oacute; th&ocirc;ng gi&oacute; tốt để quả kh&ocirc;ng bị b&iacute; v&agrave; thối rữa.</li>\n	<li>C&oacute; thể bảo quản đu đủ ruột đỏ giống Đ&agrave;i Loan trong tủ lạnh để giữ quả được l&acirc;u hơn.</li>\n	<li>\n	<p>Cần tr&aacute;nh để đu đủ dưới &aacute;nh nắng trực tiếp v&igrave; c&oacute; thể khiến quả bị nẫu hoặc hư hỏng.</p>\n	</li>\n	<li>\n	<p>Đu đủ ruột đỏ giống Đ&agrave;i Loan c&oacute; thể bảo quản ở nơi kh&ocirc; r&aacute;o, tho&aacute;ng m&aacute;t trong v&ograve;ng 3 - 5 ng&agrave;y.</p>\n	</li>\n</ul>\n\n<p><strong>- Những lưu &yacute; khi thưởng thức đu đủ</strong></p>\n\n<ul>\n	<li>Kh&ocirc;ng n&ecirc;n ăn đu đủ ruột đỏ giống Đ&agrave;i Loan khi quả chưa ch&iacute;n hoặc đ&atilde; ch&iacute;n nẫu.</li>\n	<li>Người c&oacute; bệnh về dạ d&agrave;y, t&aacute; tr&agrave;ng hoặc vi&ecirc;m tuyến tụy n&ecirc;n hạn chế ăn đu đủ ruột đỏ giống Đ&agrave;i Loan.</li>\n	<li>Phụ nữ mang thai n&ecirc;n tham khảo &yacute; kiến b&aacute;c sĩ trước khi ăn đu đủ ruột đỏ giống Đ&agrave;i Loan.</li>\n</ul>\n\n<p><strong>- Những đối tượng n&agrave;o th&igrave; kh&ocirc;ng n&ecirc;n sử dụng đu đủ ?</strong></p>\n\n<ul>\n	<li>Đu đủ ch&iacute;n l&agrave; thực phẩm an to&agrave;n v&agrave; bổ dưỡng đối với phụ nữ mang thai. Tuy nhi&ecirc;n, đu đủ xanh được xem l&agrave; thực phẩm cấm kị.</li>\n	<li>Một số người kh&ocirc;ng n&ecirc;n ăn đu đủ như người đường huyết cao hoặc người bị v&agrave;ng da, người bị dạ d&agrave;y, người c&oacute; cơ địa dị ứng, người ti&ecirc;u h&oacute;a k&eacute;m, người bị lo&atilde;ng m&aacute;u&hellip; v&igrave; c&oacute; thể l&agrave;m cho t&igrave;nh trạng bệnh t&igrave;nh th&ecirc;m trầm trọng hơn.</li>\n	<li>Kh&ocirc;ng an to&agrave;n cho trẻ sơ sinh dưới 1 tuổi</li>\n</ul>\n', 'Trái cây Việt', 35500, 1, '2024-07-05', 7, 'ddd5e8a6837151834aa8947e34d9376c', 'whywb9gtgzomsa3tr3ga', 3, 3, 1),
(66, 'Chuối xiêm', '<h1><strong>Chuối xi&ecirc;m</strong></h1>\n\n<p><strong>Xuất xứ:</strong>&nbsp;Việt Nam</p>\n\n<p><strong>Ti&ecirc;u chuẩn chất lượng:&nbsp;</strong>Tr&aacute;i c&acirc;y sạch<br />\n<strong>Trọng lượng sản phẩm:</strong>&nbsp;1.5KG- 2KG</p>\n\n<p><strong>Đặc điểm nổi bật của chuối xi&ecirc;m</strong></p>\n\n<ul>\n	<li>Đ&oacute;ng g&oacute;i: 1.5 KG- 2KG/Nh&aacute;nh</li>\n	<li>Với h&igrave;nh d&aacute;ng hai đầu thon v&agrave; nhỏ, phần giữa to hơn, tr&ecirc;n vỏ c&oacute; ba gờ v&agrave; cuống d&agrave;i. Khi ch&iacute;n c&oacute; m&agrave;u v&agrave;ng, phần thịt m&agrave;u trắng n&otilde;n v&agrave; c&oacute; vị ngọt.</li>\n</ul>\n\n<p>Chuối xi&ecirc;m c&oacute; vị ngọt thanh, thơm ngon, kh&ocirc;ng bị ch&aacute;t như chuối ti&ecirc;u, khi ch&iacute;n chuối xi&ecirc;m c&oacute; m&ugrave;i thơm nồng n&agrave;n đặc trưng.</p>\n\n<p><strong>Lợi &iacute;ch của chuối xi&ecirc;m</strong></p>\n\n<ul>\n	<li>Chuối xi&ecirc;m c&oacute; nhiều lợi &iacute;ch cho sức khoẻ như tăng cường hệ miễn dịch, hỗ trợ ti&ecirc;u ho&aacute;, l&agrave;m đẹp da, tốt cho tim mạch</li>\n</ul>\n\n<ul>\n	<li>Chuối chứa rất nhiều chất dinh dưỡng quan trọng cho cơ thể, những chất dinh dưỡng trong chuối gi&uacute;p c&acirc;n bằng lượng đường trong m&aacute;u v&agrave; gi&uacute;p cải thiện sức khoẻ ti&ecirc;u ho&aacute;.</li>\n	<li>Chuối&nbsp;gi&uacute;p bạn duy tr&igrave; sức sống v&agrave; tinh thần tr&agrave;n đầy mỗi ng&agrave;y.&nbsp;</li>\n	<li>Chuối&nbsp;gi&agrave;u vitamin và khoáng ch&acirc;́t t&ocirc;́t cho não b&ocirc;̣, h&ocirc;̃ trợ trí nhớ, b&ocirc;̉ sung dưỡng ch&acirc;́t, &nbsp;v&agrave; c&oacute; t&aacute;c dụng trong việc l&agrave;m đẹp hiệu quả.</li>\n	<li>Với sự kết hợp độc đ&aacute;o của hương vị tuyệt vời v&agrave; gi&aacute; trị dinh dưỡng cao, chuối xi&ecirc;m&nbsp;kh&ocirc;ng chỉ l&agrave; một lựa chọn ngon miệng m&agrave; c&ograve;n l&agrave; nguồn năng lượng v&agrave; sức khỏe to&agrave;n diện cho mỗi ng&agrave;y của bạn.</li>\n</ul>\n\n<p><strong>C&aacute;ch lựa chọn</strong></p>\n\n<ul>\n	<li>Kh&aacute;ch h&agrave;ng n&ecirc;n lựa chọn những nh&aacute;nh chuối xi&ecirc;m ch&iacute;n c&acirc;y c&oacute; lớp vỏ m&agrave;u v&agrave;ng, hơi kh&ocirc;, mỏng, da hơi th&acirc;m kim l&agrave; chuối ngon, dẻo. Tr&ecirc;n vỏ thi thoảng c&oacute; những đốm th&acirc;m đen.</li>\n	<li>Với Farmers Market ti&ecirc;u ch&iacute; h&agrave;ng đầu l&agrave; sản phảm đạt chất lượng cao, nguồn gốc r&otilde; r&agrave;ng, mang đến trải nghiệm tốt nhất cho kh&aacute;ch h&agrave;ng.&nbsp;</li>\n</ul>\n\n<p><strong>C&aacute;ch bảo quản chuối xi&ecirc;m</strong></p>\n\n<ul>\n	<li>Bạn c&oacute; thể d&ugrave;ng giấy b&aacute;o hoặc m&agrave;ng bọc thực phẩm để g&oacute;i từng quả chuối lại. Sau đ&oacute; bạn chỉ việc cho chuối v&agrave;o tủ lạnh l&agrave; đ&atilde; c&oacute; thể giữ cho chuối tươi ngon l&acirc;u hơn.</li>\n	<li>Bọc cuốn chuối lại để k&eacute;o d&agrave;i thờ gian sử dụng</li>\n</ul>\n\n<p><strong>Những lưu &yacute; khi thưởng thức chuối xi&ecirc;m</strong></p>\n\n<ul>\n	<li>Kh&ocirc;ng n&ecirc;n ăn chuối khi đang đ&oacute;i</li>\n	<li>N&ecirc;n ăn chuối sau bữa ăn khoảng 1 đến 2 tiếng</li>\n	<li>Kh&ocirc;ng n&ecirc;n sử dụng chuối kết hợp với một số loại thực phẩm kh&aacute;c như khoai t&acirc;y, khoai lang, dưa hấu, ...</li>\n	<li>Kh&ocirc;ng n&ecirc;n ăn qu&aacute; nhiều chuối trong một ng&agrave;y</li>\n</ul>\n', 'Trái cây Việt', 29000, 1, '2024-07-05', 7, '2f7b1c340876857c478a312986b25da2', 'gjdea7vgqbhwxvvvqmmw', 3, 1, 1),
(67, 'Ổi Ruby ruột đỏ', '<h1><strong>Ổi Ruby ruột đỏ</strong></h1>\n\n<p>Ổi Ruby ruột đỏ l&agrave; một giống ổi được lai tạo từ Đ&agrave;i Loan, được trồng nhiều ở Việt Nam trong những năm gần đ&acirc;y. Loại ổi n&agrave;y được ưa chuộng bởi hương vị thơm ngon, gi&ograve;n ngọt v&agrave; &iacute;t hạt.<br />\n<strong>- Gi&aacute; trị dinh dưỡng của ổi tr&acirc;n ch&acirc;u ruột đỏ</strong></p>\n\n<ul>\n	<li>\n	<p>Trong ổi tr&acirc;n ch&acirc;u ruột đỏ chứa lượng vitamin C rất lớn, cần thiết cho sức khỏe. Ngo&agrave;i ra c&ograve;n chứa vitamin A, vitamin B, c&aacute;c kho&aacute;ng chất như kali, natri, magie,...</p>\n	</li>\n	<li>\n	<p>Trong 100g ổi tr&acirc;n ch&acirc;u ruột đỏ c&oacute; khoảng<strong>&nbsp;68 Kcal.</strong></p>\n\n	<p>Ổi n&ecirc;n được bảo quản trong ngăn m&aacute;t tủ lạnh để gi&uacute;p ổi được tươi ngon v&agrave; gi&ograve;n l&acirc;u hơn.</p>\n	</li>\n</ul>\n\n<p><strong>- Lợi &iacute;ch sức khỏe của ổi Ruby ruột đỏ:</strong></p>\n\n<ul>\n	<li>Tăng cường hệ miễn dịch: Vitamin C trong ổi Ruby ruột đỏ gi&uacute;p tăng cường hệ miễn dịch, chống lại c&aacute;c bệnh cảm c&uacute;m, ho.</li>\n	<li>Hỗ trợ sức khỏe tim mạch: Kali trong ổi Ruby ruột đỏ gi&uacute;p điều h&ograve;a huyết &aacute;p, giảm nguy cơ mắc c&aacute;c bệnh tim mạch.</li>\n	<li>Tốt cho mắt: Vitamin A trong ổi Ruby ruột đỏ gi&uacute;p bảo vệ mắt, ngăn ngừa c&aacute;c bệnh về mắt như đục thủy tinh thể, tho&aacute;i h&oacute;a điểm v&agrave;ng.</li>\n	<li>Hỗ trợ hệ ti&ecirc;u h&oacute;a: Chất xơ trong ổi Ruby ruột đỏ gi&uacute;p hỗ trợ hệ ti&ecirc;u h&oacute;a, ngăn ngừa t&aacute;o b&oacute;n.</li>\n</ul>\n\n<p><strong>- C&aacute;ch sử dụng ổi Ruby ruột đỏ:</strong></p>\n\n<p>Ổi Ruby ruột đỏ c&oacute; thể ăn tươi, l&agrave;m sinh tố,&nbsp;hoặc chế biến th&agrave;nh c&aacute;c m&oacute;n ăn kh&aacute;c nhau như mứt, ...</p>\n\n<p>-&nbsp;<strong>C&aacute;ch lựa chọn ổi ngon:</strong></p>\n\n<ul>\n	<li>N&ecirc;n chọn mua ổi Ruby ruột đỏ c&oacute; vỏ m&agrave;u xanh s&aacute;ng, b&oacute;ng mượt, kh&ocirc;ng bị dập n&aacute;t hoặc hư hỏng.</li>\n	<li>Với Farmers Market ti&ecirc;u ch&iacute; h&agrave;ng đầu l&agrave; sản phảm đạt chất lượng cao, nguồn gốc r&otilde; r&agrave;ng, mang đến trải nghiệm tốt nhất cho kh&aacute;ch h&agrave;ng.&nbsp;</li>\n</ul>\n\n<p><strong>- Mẹo bảo quản ổi được l&acirc;u:</strong></p>\n\n<p><strong>+ Bảo quản ở nhiệt độ ph&ograve;ng:</strong></p>\n\n<ul>\n	<li><strong>C&aacute;ch thực hiện:</strong>\n\n	<ul>\n		<li>Sau khi mua ổi về, bạn chỉ cần rửa sơ qua để loại bỏ những bụi bẩn b&aacute;m b&ecirc;n ngo&agrave;i lớp vỏ, rửa xong chờ r&aacute;o nước v&agrave; đặt ổi v&agrave;o c&aacute;c rổ, r&aacute; thưa để ổi được r&aacute;o nước ho&agrave;n to&agrave;n.</li>\n		<li>Để ổi v&agrave;o nơi kh&ocirc; r&aacute;o tho&aacute;ng kh&iacute; với nhiệt độ ph&ograve;ng b&igrave;nh thường, tr&aacute;nh &aacute;nh nắng trực tiếp từ mặt trời.</li>\n	</ul>\n	</li>\n	<li><strong>Ưu điểm:</strong>\n	<ul>\n		<li>Đơn giản, dễ thực hiện.</li>\n		<li>Giữ được độ tươi ngon của ổi trong thời gian ngắn (khoảng 2-3 ng&agrave;y).</li>\n	</ul>\n	</li>\n	<li><strong>Nhược điểm:</strong>\n	<ul>\n		<li>Ổi sẽ nhanh ch&iacute;n v&agrave; mềm hơn so với bảo quản trong tủ lạnh.</li>\n	</ul>\n	</li>\n</ul>\n\n<p><strong>+ Bảo quản trong tủ lạnh:</strong></p>\n\n<ul>\n	<li><strong>C&aacute;ch thực hiện:</strong>\n\n	<ul>\n		<li>Cho những quả ổi v&agrave;o t&uacute;i Zip hoặc t&uacute;i nilon nhưng kh&ocirc;ng được bịt k&iacute;n miệng t&uacute;i, đảm bảo t&uacute;i c&oacute; c&aacute;c lỗ nhỏ để kh&ocirc;ng kh&iacute; được lưu th&ocirc;ng.</li>\n		<li>Tiếp theo bạn cho ổi v&agrave;o ngăn m&aacute;t tủ lạnh để bảo quản, tr&aacute;nh đặt gần những loại tr&aacute;i c&acirc;y hoặc thực phẩm như thịt c&aacute; để k&eacute;o d&agrave;i thời gian bảo quản l&acirc;u hơn.</li>\n	</ul>\n	</li>\n	<li><strong>Ưu điểm:</strong>\n	<ul>\n		<li>Giữ được độ tươi ngon của ổi trong thời gian l&acirc;u hơn (khoảng 5-7 ng&agrave;y).</li>\n		<li>Ổi sẽ ch&iacute;n chậm hơn so với bảo quản ở nhiệt độ ph&ograve;ng.</li>\n	</ul>\n	</li>\n	<li><strong>Nhược điểm:</strong>\n	<ul>\n		<li>Cần c&oacute; tủ lạnh để bảo quản.</li>\n	</ul>\n	</li>\n</ul>\n', 'Trái cây Việt', 49900, 1, '2024-07-05', 7, 'd2ad1cc51699ef49d8a53f32b4c84d72', 'hyj2k1jyhpfkqvm5pawi', 3, 1, 1),
(68, 'Na dai Thái', '<h1><strong>Na dai Th&aacute;i</strong></h1>\n\n<p><strong>- Xuất xứ</strong>: Việt Nam</p>\n\n<p><strong>- Ti&ecirc;u chuẩn chất lượng</strong>: Tr&aacute;i c&acirc;y sạch</p>\n\n<p>-&nbsp;<strong>Trọng lượng:&nbsp;</strong>1kg</p>\n\n<p>- Na hay c&ograve;n gọi l&agrave;&nbsp;<strong>m&atilde;ng cầu&nbsp;</strong>l&agrave; loại quả tr&ograve;n, to, nhiều hạt, khi ch&iacute;n c&ugrave;i trắng, mềm v&agrave; ngọt. Đ&acirc;y l&agrave; một loại tr&aacute;i c&acirc;y được nhiều người Việt Nam y&ecirc;u th&iacute;ch mỗi khi h&egrave; về.</p>\n\n<p><strong>- Đặc điểm sản phẩm:&nbsp;</strong>Tr&aacute;i rất to, &iacute;t hạt. Cơm trắng, ngọt đậm, thịt dai ngọt.</p>\n\n<p><strong>- Na Dai Th&aacute;i&nbsp;</strong>c&oacute; k&iacute;ch thước lớn, vị ngọt đậm, m&ugrave;i thơm, thịt rất chắc, &iacute;t hạt, vỏ mỏng, phần mắt vỏ to v&agrave; trơn hơn na Việt Nam.</p>\n\n<ul>\n	<li>Na c&oacute; thể ăn trực tiếp.</li>\n	<li>Na ngon hơn khi ướp lạnh trước khi ăn.</li>\n</ul>\n\n<p><strong>- Lợi &iacute;ch sức khỏe của na dai Th&aacute;i Lan:</strong></p>\n\n<ul>\n	<li>Tăng cường hệ miễn dịch: Vitamin C trong na dai Th&aacute;i Lan gi&uacute;p tăng cường hệ miễn dịch, chống lại c&aacute;c bệnh cảm c&uacute;m, ho.</li>\n	<li>Hỗ trợ hệ thần kinh: Vitamin B6 trong na dai Th&aacute;i Lan gi&uacute;p hỗ trợ hệ thần kinh, giảm căng thẳng v&agrave; mệt mỏi.</li>\n	<li>Điều h&ograve;a huyết &aacute;p: Kali trong na dai Th&aacute;i Lan gi&uacute;p điều h&ograve;a huyết &aacute;p, giảm nguy cơ mắc c&aacute;c bệnh tim mạch.</li>\n	<li>Hỗ trợ hệ ti&ecirc;u h&oacute;a: Chất xơ trong na dai Th&aacute;i Lan gi&uacute;p hỗ trợ hệ ti&ecirc;u h&oacute;a, ngăn ngừa t&aacute;o b&oacute;n.</li>\n</ul>\n\n<p><strong>- C&aacute;ch sử dụng na dai Th&aacute;i Lan:</strong></p>\n\n<ul>\n	<li><strong>Ăn trực tiếp:</strong>&nbsp;Bạn c&oacute; thể thưởng thức quả na ch&iacute;nh ngọt trực tiếp.</li>\n	<li><strong>Xay sinh tố:</strong>&nbsp;Bạn cũng c&oacute; thể loại bỏ hết phần hột của na v&agrave; xay sinh tố để uống.</li>\n	<li><strong>Na dầm:</strong>&nbsp;Nếu bạn kh&ocirc;ng th&iacute;ch sinh tố, th&igrave; c&oacute; thể cho phần thịt na v&agrave; dầm với đ&aacute;, đường để thưởng thức.</li>\n	<li>N&ecirc;n rửa sạch na dai Th&aacute;i Lan trước khi ăn để đảm bảo vệ sinh.</li>\n</ul>\n\n<p><strong>- C&aacute;ch lựa chọn na dai Th&aacute;i:</strong></p>\n\n<ul>\n	<li>N&ecirc;n chọn mua na dai Th&aacute;i Lan c&oacute; vỏ m&agrave;u xanh v&agrave;ng, b&oacute;ng mượt, kh&ocirc;ng bị dập n&aacute;t hoặc hư hỏng.</li>\n	<li>Với Farmers Market ti&ecirc;u ch&iacute; h&agrave;ng đầu l&agrave; sản phảm đạt chất lượng cao, nguồn gốc r&otilde; r&agrave;ng, mang đến trải nghiệm tốt nhất cho kh&aacute;ch h&agrave;ng.&nbsp;</li>\n</ul>\n\n<p><strong>-&nbsp; Phụ nữ mang thai v&agrave; trẻ em ăn na ( m&atilde;ng cầu)&nbsp;c&oacute; được kh&ocirc;ng?</strong></p>\n\n<ul>\n	<li>Khi mang thai, bạn c&oacute; thể ăn na trong suốt thai kỳ. Đ&acirc;y l&agrave; một loại tr&aacute;i c&acirc;y tốt cho thai kỳ v&agrave; nhiều chất dinh dưỡng. Tuy nhi&ecirc;n, bạn kh&ocirc;ng n&ecirc;n ăn qu&aacute; nhiều na trong một thời gian d&agrave;i. Điều n&agrave;y c&oacute; thể g&acirc;y phản t&aacute;c dụng hoặc mất c&acirc;n bằng chất dinh dưỡng.</li>\n	<li>Trẻ từ 6 th&aacute;ng tuổi đ&atilde; c&oacute; thể ăn được na t&aacute;ch hạt, nghiền nhuyễn.</li>\n</ul>\n\n<ul>\n	<li>Quả na chỉ tốt khi mẹ cho b&eacute; ăn quả đ&atilde; ch&iacute;n. Quả c&ograve;n sống sẽ cứng v&agrave; c&oacute; vị ch&aacute;t. Đồng thời, c&ograve;n l&agrave;m ảnh hưởng đến hệ ti&ecirc;u h&oacute;a của con</li>\n</ul>\n', 'Trái cây Việt', 139000, 1, '2024-07-05', 7, 'ce34e44f9cc51303bb39d011ba80407e', 'hmetr6lc96qv3efnlbmc', 3, 2, 1),
(69, 'Phúc bồn tử đỏ Huỳnh Trung Quân', '<h1><strong>Ph&uacute;c bồn tử đỏ Huỳnh Trung Qu&acirc;n</strong></h1>\n\n<p><strong>L</strong>&agrave; sản phẩm được l&agrave;m từ 100% tr&aacute;i ph&uacute;c bồn tử tươi, được trồng v&agrave; thu hoạch tại Đ&agrave; Lạt, L&acirc;m Đồng. Sản phẩm được sấy theo quy tr&igrave;nh hiện đại, đảm bảo giữ nguy&ecirc;n hương vị v&agrave; dưỡng chất tự nhi&ecirc;n của tr&aacute;i ph&uacute;c bồn tử.</p>\n\n<p><strong>- Xuất xứ</strong>: Việt Nam</p>\n\n<p><strong>- Trọng lượng</strong>: 170g/Hộp</p>\n\n<p><strong>- Ti&ecirc;u chuẩn:</strong>&nbsp;Tr&aacute;i c&acirc;y sạch</p>\n\n<p><strong>- Th&agrave;nh phần:</strong></p>\n\n<ul>\n	<li>Tr&aacute;i ph&uacute;c bồn tử tươi (100%)</li>\n</ul>\n\n<p><strong>- C&ocirc;ng dụng:</strong></p>\n\n<ul>\n	<li>Ph&uacute;c bồn tử đỏ Huỳnh Trung Qu&acirc;n c&oacute; nhiều c&ocirc;ng dụng tốt cho sức khỏe như:\n	<ul>\n		<li>Tăng cường hệ miễn dịch</li>\n		<li>Chống oxy h&oacute;a</li>\n		<li>Tốt cho tim mạch</li>\n		<li>Hỗ trợ giảm c&acirc;n</li>\n		<li>L&agrave;m đẹp da</li>\n		<li>Tốt cho mắt</li>\n	</ul>\n	</li>\n</ul>\n\n<p><strong>- C&aacute;ch sử dụng:</strong></p>\n\n<p>+&nbsp;<strong>Ph&uacute;c bồn tử (m&acirc;m x&ocirc;i)</strong>&nbsp;l&agrave; một loại tr&aacute;i c&acirc;y thơm ngon v&agrave; bổ dưỡng, c&oacute; thể sử dụng theo nhiều c&aacute;ch kh&aacute;c nhau. Dưới đ&acirc;y l&agrave; một số c&aacute;ch sử dụng ph&uacute;c bồn tử phổ biến:</p>\n\n<p><strong>+ Ăn tươi:</strong></p>\n\n<ul>\n	<li>Ph&uacute;c bồn tử c&oacute; thể ăn tươi trực tiếp như một m&oacute;n ăn nhẹ hoặc tr&aacute;ng miệng.</li>\n	<li>Bạn c&oacute; thể rửa sạch ph&uacute;c bồn tử v&agrave; chấm với sữa chua, kem tươi hoặc mật ong.</li>\n	<li>Ph&uacute;c bồn tử cũng c&oacute; thể được th&ecirc;m v&agrave;o salad tr&aacute;i c&acirc;y, ngũ cốc hoặc granola.</li>\n</ul>\n\n<p><strong>&nbsp;+ L&agrave;m b&aacute;nh ngọt:</strong></p>\n\n<ul>\n	<li>Ph&uacute;c bồn tử c&oacute; thể được sử dụng để l&agrave;m b&aacute;nh tart, pie, cupcake, muffin,...</li>\n	<li>Bạn c&oacute; thể th&ecirc;m ph&uacute;c bồn tử v&agrave;o lớp vỏ b&aacute;nh hoặc nh&acirc;n b&aacute;nh.</li>\n	<li>Ph&uacute;c bồn tử cũng c&oacute; thể được sử dụng để trang tr&iacute; b&aacute;nh.</li>\n	<li>\n	<p><strong>+ L&agrave;m mứt:</strong></p>\n	</li>\n	<li>Mứt ph&uacute;c bồn tử l&agrave; một m&oacute;n ăn ngon v&agrave; c&oacute; thể bảo quản được l&acirc;u.</li>\n	<li>Để l&agrave;m mứt ph&uacute;c bồn tử, bạn cần đun s&ocirc;i ph&uacute;c bồn tử với đường v&agrave; nước cho đến khi hỗn hợp sệt lại.</li>\n	<li>C&oacute; thể th&ecirc;m c&aacute;c loại gia vị kh&aacute;c như vani, quế hoặc chanh để tăng th&ecirc;m hương vị cho mứt.</li>\n	<li>\n	<p><strong>+ L&agrave;m sinh tố:</strong></p>\n	</li>\n	<li>Ph&uacute;c bồn tử l&agrave; nguy&ecirc;n liệu tuyệt vời để l&agrave;m sinh tố.</li>\n	<li>Bạn c&oacute; thể kết hợp ph&uacute;c bồn tử với c&aacute;c loại tr&aacute;i c&acirc;y kh&aacute;c như chuối, d&acirc;u t&acirc;y, việt quất,... để tạo ra những ly sinh tố thơm ngon v&agrave; bổ dưỡng.</li>\n	<li>Th&ecirc;m sữa chua, sữa tươi hoặc nước dừa v&agrave;o sinh tố để tăng th&ecirc;m hương vị v&agrave; dưỡng chất.</li>\n</ul>\n\n<p><strong>- C&aacute;ch lựa chọn:</strong></p>\n\n<ul>\n	<li>\n	<p>N&ecirc;n chọn những quả ph&uacute;c bồn tử c&oacute; m&agrave;u đỏ sẫm, đều m&agrave;u, kh&ocirc;ng bị nhợt nhạt hoặc sẫm qu&aacute;.</p>\n	</li>\n	<li>\n	<p>Với Farmers Market ti&ecirc;u ch&iacute; h&agrave;ng đầu l&agrave; sản phảm đạt chất lượng cao, nguồn gốc r&otilde; r&agrave;ng, mang đến trải nghiệm tốt nhất cho kh&aacute;ch h&agrave;ng.&nbsp;</p>\n	</li>\n</ul>\n\n<p>-&nbsp;<strong>C&aacute;ch bảo quản:</strong></p>\n\n<p><strong>+ Bảo quản ở nhiệt độ ph&ograve;ng:</strong></p>\n\n<ul>\n	<li>Ph&uacute;c bồn tử chỉ n&ecirc;n bảo quản ở nhiệt độ ph&ograve;ng trong v&ograve;ng 1-2 ng&agrave;y sau khi mua.</li>\n	<li>N&ecirc;n để ph&uacute;c bồn tử trong hộp tho&aacute;ng kh&iacute; để tr&aacute;nh bị b&iacute; v&agrave; hư hỏng.</li>\n	<li>Tr&aacute;nh để ph&uacute;c bồn tử tiếp x&uacute;c trực tiếp với &aacute;nh nắng mặt trời hoặc nguồn nhiệt.</li>\n</ul>\n\n<p><strong>+ Bảo quản trong tủ lạnh:</strong></p>\n\n<ul>\n	<li>Để bảo quản ph&uacute;c bồn tử l&acirc;u hơn, bạn n&ecirc;n cho v&agrave;o tủ lạnh.</li>\n	<li>N&ecirc;n đặt ph&uacute;c bồn tử trong ngăn m&aacute;t tủ lạnh, nơi c&oacute; nhiệt độ khoảng 4&deg;C.</li>\n	<li>C&oacute; thể để ph&uacute;c bồn tử trong hộp k&iacute;n hoặc rổ c&oacute; l&oacute;t khăn giấy.</li>\n	<li>Ph&uacute;c bồn tử c&oacute; thể bảo quản trong tủ lạnh từ 3-5 ng&agrave;y.</li>\n</ul>\n\n<p><strong>- Lưu &yacute;:</strong></p>\n\n<ul>\n	<li>Kh&ocirc;ng n&ecirc;n rửa ph&uacute;c bồn tử trước khi cho v&agrave;o tủ lạnh hoặc ngăn đ&aacute; v&igrave; sẽ khiến ph&uacute;c bồn tử dễ bị hư hỏng.</li>\n	<li>N&ecirc;n r&atilde; đ&ocirc;ng ph&uacute;c bồn tử tự nhi&ecirc;n trước khi sử dụng.</li>\n	<li>Ph&uacute;c bồn tử đ&atilde; r&atilde; đ&ocirc;ng kh&ocirc;ng n&ecirc;n cho v&agrave;o tủ lạnh lại.</li>\n</ul>\n\n<p><strong>- Phụ nữ mang thai v&agrave; trẻ em ăn ph&uacute;c bồn tử&nbsp;c&oacute; được kh&ocirc;ng?</strong></p>\n\n<p>+ Khi mang thai hệ ti&ecirc;u h&oacute;a của mẹ bầu hầu hết sẽ bị ảnh hưởng v&agrave; l&agrave;m cho hệ thống ti&ecirc;u h&oacute;a trở n&ecirc;n &ldquo;kh&oacute; t&iacute;nh&rdquo; v&agrave; chậm chạp hơn. Quả m&acirc;m x&ocirc;i vốn c&oacute; h&agrave;m lượng chất xơ cao n&ecirc;n&nbsp;<strong>gi&uacute;p chăm s&oacute;c c&aacute;c vấn đề ti&ecirc;u h&oacute;a khi mang thai rất hiệu quả.</strong></p>\n\n<p>+&nbsp;Quả m&acirc;m x&ocirc;i rất gi&agrave;u folate, v&igrave; vậy m&agrave; loại quả n&agrave;y rất hữu &iacute;ch trong việc ngăn ngừa c&aacute;c loại dị tật bẩm sinh kh&aacute;c nhau,<strong>&nbsp;l&agrave;m giảm khả năng sảy thai.</strong></p>\n\n<p>+ N&ecirc;n cho&nbsp;<strong>trẻ em tr&ecirc;n 1 tuổi&nbsp;</strong>&nbsp;ăn m&acirc;m x&ocirc;i với&nbsp;số lượng quả mọng mỗi ng&agrave;y sẽ tăng dần l&ecirc;n 50 - 100 g.</p>\n\n<p>+&nbsp;Nếu trẻ dễ bị dị ứng, n&ecirc;n điều trị cho trẻ bằng quả m&acirc;m x&ocirc;i kh&ocirc;ng sớm hơn 3 tuổi.</p>\n', 'Trái cây Việt', 129000, 1, '2024-07-05', 7, 'b5f847a91003da12352ca775ec45f23b', 'vpep3sdodfrz043xa0l6', 3, 2, 1),
(70, 'Nhãn xuồng cơm vàng Vũng Tàu', '<p><strong>Nh&atilde;n xuồng cơm v&agrave;ng Vũng T&agrave;u</strong></p>\n\n<p><strong>- Xuất xứ:</strong>&nbsp;Việt Nam</p>\n\n<p><strong>- Ti&ecirc;u chuẩn chất lượng:</strong>&nbsp;Tr&aacute;i c&acirc;y sạch</p>\n\n<p><strong>- Quy c&aacute;ch:</strong>&nbsp;1kg</p>\n\n<p><strong>- Đặc điểm:</strong></p>\n\n<ul>\n	<li>\n	<p>Nh&atilde;n xuồng&nbsp;nổi tiếng với tr&aacute;i to, cơm d&agrave;y, thịt dai, hạt nhỏ, ngọt thanh v&agrave; mang hương thơm đặc trưng.</p>\n	</li>\n	<li>\n	<p>Nhiều dưỡng chất thiết yếu như gi&agrave;u vitamin C,B nhiều chất sắt, chất xơ, protein,&hellip; cần thiết cho cơ thể&nbsp;</p>\n	</li>\n	<li>\n	<p>Đặc biệt phần cơm gi&ograve;n, sần sật l&agrave; điểm cộng khiến nh&atilde;n xuồng&nbsp;kh&aacute;c biệt so với c&aacute;c loại nh&atilde;n kh&aacute;c&nbsp;</p>\n	</li>\n	<li>\n	<p>Với dưỡng chất dồi d&agrave;o nh&atilde;n Ido c&ograve;n gi&uacute;p ngăn ngừa cảm c&uacute;m, cảm lạnh, bổ sung sắt ngăn ngừa thiếu m&aacute;u, cải thiện tuần ho&agrave;n m&aacute;u, ti&ecirc;u diệt vi tr&ugrave;ng lao, tằng cường tr&iacute; nhớ, tăng tuổi thọ,&hellip;&nbsp;</p>\n	</li>\n</ul>\n\n<p><strong>- Hướng dẫn sử dụng:</strong></p>\n\n<ul>\n	<li>\n	<p>Thường được d&ugrave;ng để ăn trực tiếp, nấu ch&egrave;,</p>\n	</li>\n	<li>\n	<p>Lưu &yacute;, những người c&oacute; bệnh tiểu đường v&agrave; thai phụ kh&ocirc;ng n&ecirc;n ăn&nbsp;qu&aacute; nhiều nh&atilde;n.</p>\n	</li>\n</ul>\n\n<p><strong>- Lợi &iacute;ch sức khoẻ</strong></p>\n\n<ul>\n	<li>\n	<p><strong>Tăng cường hệ miễn dịch:</strong>&nbsp;Nh&atilde;n chứa h&agrave;m lượng vitamin C dồi d&agrave;o, gi&uacute;p tăng cường hệ miễn dịch, chống lại c&aacute;c bệnh cảm c&uacute;m, cảm lạnh v&agrave; c&aacute;c bệnh nhiễm tr&ugrave;ng kh&aacute;c.</p>\n	</li>\n</ul>\n\n<ul>\n	<li>\n	<p><strong>Hỗ trợ hệ thần kinh:</strong>&nbsp;Nh&atilde;n chứa thiamin, một loại vitamin B1, gi&uacute;p cải thiện chức năng thần kinh, giảm căng thẳng v&agrave; lo &acirc;u.</p>\n	</li>\n	<li>\n	<p><strong>Hỗ trợ ti&ecirc;u h&oacute;a:</strong>&nbsp;Nh&atilde;n chứa chất xơ, gi&uacute;p hỗ trợ ti&ecirc;u h&oacute;a, ngăn ngừa t&aacute;o b&oacute;n v&agrave; th&uacute;c đẩy sức khỏe đường ruột.</p>\n	</li>\n	<li>\n	<p><strong>Tăng cường sức khỏe xương khớp:</strong>&nbsp;Nh&atilde;n chứa canxi v&agrave; mangan, gi&uacute;p tăng cường sức khỏe xương khớp v&agrave; ngăn ngừa lo&atilde;ng xương.</p>\n	</li>\n	<li>\n	<p><strong>L&agrave;m đẹp da:</strong>&nbsp;Nh&atilde;n chứa vitamin C v&agrave; E, gi&uacute;p chống l&atilde;o h&oacute;a da, l&agrave;m đẹp da v&agrave; t&oacute;c.</p>\n	</li>\n</ul>\n\n<p><strong>- C&aacute;ch chọn quả ngon</strong></p>\n\n<ul>\n	<li>\n	<p>N&ecirc;n chọn những quả nh&atilde;n c&oacute; k&iacute;ch thước to đều, vỏ nhẵn b&oacute;ng, m&agrave;u v&agrave;ng n&acirc;u sậm.</p>\n	</li>\n	<li>\n	<p>Tr&aacute;nh chọn những quả nh&atilde;n c&oacute; vỏ sần s&ugrave;i, m&agrave;u xanh hoặc c&oacute; đốm đen, v&igrave; đ&acirc;y c&oacute; thể l&agrave; dấu hiệu của nh&atilde;n chưa ch&iacute;n hoặc bị hư hỏng.</p>\n	</li>\n	<li>\n	<p>Vỏ nh&atilde;n d&agrave;y v&agrave; c&oacute; gai nhỏ l&agrave; dấu hiệu của nh&atilde;n c&oacute; c&ugrave;i d&agrave;y v&agrave; mọng nước.</p>\n	</li>\n	<li>\n	<p>Với Farmers Market ti&ecirc;u ch&iacute; h&agrave;ng đầu l&agrave; sản phảm đạt chất lượng cao, nguồn gốc r&otilde; r&agrave;ng, mang đến trải nghiệm tốt nhất cho kh&aacute;ch h&agrave;ng.&nbsp;</p>\n	</li>\n</ul>\n\n<p><strong>&nbsp;+ Bảo quản nh&atilde;n ở nhiệt độ ph&ograve;ng:</strong></p>\n\n<ul>\n	<li>\n	<p>Nh&atilde;n c&oacute; thể bảo quản ở nhiệt độ ph&ograve;ng trong khoảng 2-3 ng&agrave;y.</p>\n	</li>\n	<li>\n	<p>N&ecirc;n để nh&atilde;n ở nơi kh&ocirc; r&aacute;o, tho&aacute;ng m&aacute;t, tr&aacute;nh &aacute;nh nắng trực tiếp.</p>\n	</li>\n</ul>\n\n<p><strong>+ Bảo quản nh&atilde;n trong tủ lạnh:</strong></p>\n\n<ul>\n	<li>\n	<p>Nh&atilde;n c&oacute; thể bảo quản trong tủ lạnh trong khoảng 1-2 tuần.</p>\n	</li>\n	<li>\n	<p>N&ecirc;n rửa sạch v&agrave; lau kh&ocirc; nh&atilde;n trước khi cho v&agrave;o tủ lạnh.</p>\n	</li>\n	<li>\n	<p>C&oacute; thể cho nh&atilde;n v&agrave;o t&uacute;i zip hoặc hộp đựng thực phẩm k&iacute;n để bảo quản.</p>\n	</li>\n</ul>\n', 'Trái cây Việt', 99000, 1, '2024-07-05', 7, 'b4dc8bb06cdf3d8dbb10df6da9da7d2d', 'kiqxpvq0q0nmj5h2apu0', 3, 3, 1),
(71, 'Quả Mây Thái Lan', '<h1><strong>Quả M&acirc;y Th&aacute;i Lan</strong></h1>\n\n<p><strong>Xuất xứ:&nbsp;</strong>Th&aacute;i Lan</p>\n\n<p><strong>Đặc điểm:</strong></p>\n\n<ul>\n	<li>Tr&aacute;i m&acirc;y Th&aacute;i (Th&aacute;i Lan) mềm, thơm, ngọt v&agrave; mọng nước, với vị gợi nhớ đến dứa&nbsp;v&agrave; l&ecirc;.&nbsp;</li>\n	<li>Tr&aacute;i n&agrave;y d&aacute;ng thon hoặc hơi tr&ograve;n, dạng d&ugrave;m, c&oacute; l&ocirc;ng, gai nhỏ, vỏ mỏng.&nbsp;</li>\n	<li>Phần thịt tr&aacute;i thường c&oacute; 3 nh&aacute;nh, m&agrave;u v&agrave;ng kem nhạt.&nbsp;</li>\n	<li>M&ugrave;i tr&aacute;i m&acirc;y th&aacute;i kh&aacute; đặc trưng kh&oacute; diễn tả, nhiều&nbsp; người cho rằng n&oacute; c&oacute; m&ugrave;i giống m&iacute;t, sầu ri&ecirc;ng.&nbsp;</li>\n	<li>Th&ocirc;ng thường tr&aacute;i c&oacute; k&iacute;ch thước từ 3-5cm v&agrave; nặng khoảng 20 đến 50g/ tr&aacute;i.&nbsp;</li>\n	<li>Tr&aacute;i m&acirc;y th&aacute;i c&oacute; nhiều hạt nhỏ ăn được nhưng kh&ocirc;ng ngon.&nbsp;</li>\n	<li>Loại tr&aacute;i n&agrave;y gi&agrave;u vitamin C, chất xơ, nhiều dinh dưỡng.&nbsp;</li>\n</ul>\n\n<p><strong>Nguồn gốc:</strong></p>\n\n<ul>\n	<li>Tuy được trồng nhiều tại Th&aacute;i Lan, nhưng thực tế, nhiều người cho rằng n&oacute; c&oacute; nguồn gốc v&agrave; bu&ocirc;n b&aacute;n rộng r&atilde;i ở Ấn Độ.&nbsp;</li>\n	<li>Ở &Aacute;n Độ, m&acirc;y th&aacute;i l&agrave; loại tr&aacute;i c&acirc;y phổ biến, đ&oacute;ng g&oacute;p nhiều cho nền kinh tế n&ocirc;ng nghiệp ở nước n&agrave;y.&nbsp;</li>\n	<li>M&acirc;y Th&aacute;i hiện được trồng rộng r&atilde;i v&agrave; thương mại nhiều ở Đ&ocirc;ng Nam &Aacute;.&nbsp;</li>\n</ul>\n\n<p><strong>Hướng dẫn sử dụng:</strong></p>\n\n<ul>\n	<li>Bạn c&oacute; thể thưởng thức Quả M&acirc;y Th&aacute;i Lan trực tiếp bằng c&aacute;ch t&aacute;ch từng m&uacute;i, b&oacute;c vỏ v&agrave; ăn phần ruột b&ecirc;n trong.</li>\n	<li>Chấm muối l&agrave; c&aacute;ch đơn giản để tăng th&ecirc;m hương vị cho Quả M&acirc;y Th&aacute;i Lan.</li>\n	<li>Bạn c&oacute; thể cho Quả M&acirc;y đ&atilde; t&aacute;ch m&uacute;i v&agrave;o hộp, th&ecirc;m muối ớt v&agrave; lắc đều.</li>\n	<li>Sinh tố Quả M&acirc;y cung cấp nhiều vitamin, kho&aacute;ng chất v&agrave; chất chống oxy h&oacute;a, tốt cho sức khỏe v&agrave; gi&uacute;p đẹp da.</li>\n	<li>Mứt Quả M&acirc;y Th&aacute;i Lan c&oacute; vị chua ngọt đặc trưng, ​​th&iacute;ch hợp để ăn k&egrave;m với b&aacute;nh m&igrave;, yogurt hoặc pha tr&agrave;</li>\n</ul>\n\n<p><strong>- C&aacute;ch lựa chọn quả m&acirc;y:</strong></p>\n\n<ul>\n	<li>N&ecirc;n chọn những quả M&acirc;y Th&aacute;i Lan c&oacute; h&igrave;nh d&aacute;ng đều đặn, thon d&agrave;i, kh&ocirc;ng bị dập n&aacute;t hay c&oacute; dấu hiệu hư hỏng.</li>\n	<li>Với Farmers Market ti&ecirc;u ch&iacute; h&agrave;ng đầu l&agrave; sản phảm đạt chất lượng cao, nguồn gốc r&otilde; r&agrave;ng, mang đến trải nghiệm tốt nhất cho kh&aacute;ch h&agrave;ng.&nbsp;</li>\n</ul>\n\n<p>-&nbsp;<strong>C&aacute;ch bảo quản quả m&acirc;y</strong><strong>:&nbsp;</strong></p>\n\n<p><strong>1. Bảo quản ở nhiệt độ ph&ograve;ng:</strong></p>\n\n<ul>\n	<li>M&acirc;y Th&aacute;i Lan khi mua về nếu chưa ch&iacute;n tới c&oacute; thể bảo quản ở nhiệt độ ph&ograve;ng tho&aacute;ng m&aacute;t, tr&aacute;nh &aacute;nh nắng trực tiếp.</li>\n	<li>N&ecirc;n l&oacute;t giấy b&aacute;o hoặc khăn mềm dưới quả M&acirc;y để tr&aacute;nh dập n&aacute;t.</li>\n	<li>M&acirc;y Th&aacute;i Lan sẽ ch&iacute;n sau khoảng 2-3 ng&agrave;y ở nhiệt độ ph&ograve;ng.</li>\n</ul>\n\n<p><strong>2. Bảo quản trong tủ lạnh:</strong></p>\n\n<ul>\n	<li>M&acirc;y Th&aacute;i Lan đ&atilde; ch&iacute;n n&ecirc;n bảo quản trong tủ lạnh để giữ độ tươi ngon.</li>\n	<li>N&ecirc;n bọc M&acirc;y bằng giấy hoặc cho v&agrave;o hộp k&iacute;n để tr&aacute;nh M&acirc;y bị mất nước v&agrave; teo t&oacute;p.</li>\n	<li>M&acirc;y Th&aacute;i Lan bảo quản trong tủ lạnh c&oacute; thể giữ được 3-5 ng&agrave;y.</li>\n</ul>\n\n<ul>\n	<li>Kh&ocirc;ng n&ecirc;n rửa M&acirc;y Th&aacute;i Lan trước khi bảo quản v&igrave; sẽ khiến M&acirc;y nhanh bị hỏng.</li>\n	<li>Kh&ocirc;ng n&ecirc;n bảo quản M&acirc;y Th&aacute;i Lan chung với c&aacute;c loại tr&aacute;i c&acirc;y kh&aacute;c như t&aacute;o, chuối v&igrave; c&oacute; thể khiến M&acirc;y nhanh ch&iacute;n v&agrave; bị mềm.</li>\n	<li>Rửa sạch Quả M&acirc;y kỹ trước khi sử dụng.</li>\n	<li>N&ecirc;n ăn Quả M&acirc;y Th&aacute;i Lan trong v&ograve;ng 1-2 ng&agrave;y sau khi mua.</li>\n</ul>\n\n<p>- Những lưu &yacute; khi sử dụng quả m&acirc;y:</p>\n\n<ul>\n	<li>Trẻ em dưới 1 tuổi kh&ocirc;ng n&ecirc;n ăn M&acirc;y Th&aacute;i nguy&ecirc;n quả. N&ecirc;n xay nhuyễn M&acirc;y Th&aacute;i hoặc trộn v&agrave;o sữa chua, sinh tố để trẻ dễ ăn.</li>\n	<li>N&ecirc;n ăn M&acirc;y Th&aacute;i với lượng vừa phải, khoảng 100-150g mỗi ng&agrave;y.</li>\n	<li>Nếu bạn c&oacute; bất kỳ vấn đề sức khỏe n&agrave;o, h&atilde;y tham khảo &yacute; kiến b&aacute;c sĩ trước khi ăn M&acirc;y Th&aacute;i</li>\n</ul>\n\n<p><strong>- Phụ nữ mang thai v&agrave; trẻ em ăn m&acirc;y th&aacute;i c&oacute; được kh&ocirc;ng?</strong></p>\n\n<p><strong>Phụ nữ mang thai v&agrave; trẻ em đều c&oacute; thể ăn M&acirc;y Th&aacute;i (M&acirc;y Rừng).</strong>&nbsp;M&acirc;y Th&aacute;i l&agrave; loại tr&aacute;i c&acirc;y gi&agrave;u vitamin, kho&aacute;ng chất v&agrave; chất chống oxy h&oacute;a, mang lại nhiều lợi &iacute;ch cho sức khỏe.</p>\n\n<ul>\n	<li>M&acirc;y Th&aacute;i gi&uacute;p cung cấp vitamin C, folate, vitamin K, kali, chất xơ v&agrave; c&aacute;c chất chống oxy h&oacute;a&nbsp;<strong>cần thiết cho sự ph&aacute;t triển của thai nhi.</strong></li>\n	<li>Vitamin C gi&uacute;p tăng cường hệ miễn dịch,&nbsp;<strong>hỗ trợ hấp thu sắt v&agrave; collagen</strong>.</li>\n	<li>Folate gi&uacute;p<strong>&nbsp;ngăn ngừa dị tật ống thần kinh ở thai nhi</strong>.</li>\n	<li>Vitamin K<strong>&nbsp;gi&uacute;p ph&aacute;t triển xương thai nhi</strong>.</li>\n	<li>Kali gi&uacute;p điều h&ograve;a huyết &aacute;p,&nbsp;<strong>giảm nguy cơ chuột r&uacute;t.</strong></li>\n	<li>M&acirc;y Th&aacute;i gi&uacute;p tăng cường hệ miễn dịch,&nbsp;<strong>hỗ trợ ph&aacute;t triển tr&iacute; n&atilde;o, thị lực v&agrave; hệ xương của trẻ.</strong></li>\n	<li>M&acirc;y Th&aacute;i cũng gi&uacute;p&nbsp;<strong>cải thiện khả năng ghi nhớ v&agrave; tập trung ở trẻ.</strong></li>\n	<li>Chất xơ trong M&acirc;y Th&aacute;i gi&uacute;p&nbsp;<strong>ngăn ngừa t&aacute;o b&oacute;n ở trẻ.</strong></li>\n</ul>\n', 'Trái cây Việt', 119000, 1, '2024-07-05', 7, '3f50dec2006e7c6b2d83bbf4ee1bf69e', 'wh7jcxnb0tjrxciacv52', 3, 1, 1),
(72, 'Bòn bon Thái Lan', '<h1><strong>B&ograve;n bon Th&aacute;i Lan</strong></h1>\n\n<p><strong>- Xuất xứ:&nbsp;</strong>Th&aacute;i lan<br />\n<strong>- Trọng lương:</strong>&nbsp;500G</p>\n\n<p><strong>- Ti&ecirc;u chuẩn chất lượng:</strong>&nbsp;Tr&aacute;i c&acirc;y sạch</p>\n\n<p><strong>- Đặc điểm nổi bật:&nbsp;</strong></p>\n\n<ul>\n	<li>Ch&ugrave;m quả to, vỏ mỏng căng mọng, ruột m&agrave;u trắng đục v&agrave; ngọt dịu, tuỳ v&agrave;o vị gi&aacute;c mỗi người, sẽ c&oacute; th&ecirc;m vị chua nh&egrave; nhẹ.&nbsp;</li>\n	<li>Kh&ocirc;ng n&ecirc;n d&ugrave;ng miệng để t&aacute;ch vỏ v&agrave; nhai lu&ocirc;n hạt. V&igrave; vỏ c&oacute; mủ sữa v&agrave; hạt&nbsp;</li>\n</ul>\n\n<p>-<strong>&nbsp;B&ograve;n bon Th&aacute;i Lan</strong>&nbsp;l&agrave; nguồn cung cấp dồi d&agrave;o vitamin C, vitamin A, kali, chất xơ v&agrave; c&aacute;c kho&aacute;ng chất kh&aacute;c. Loại tr&aacute;i c&acirc;y n&agrave;y c&oacute; nhiều lợi &iacute;ch cho sức khỏe như:</p>\n\n<ul>\n	<li>Tăng cường hệ miễn dịch</li>\n	<li>Hỗ trợ hệ ti&ecirc;u h&oacute;a</li>\n	<li>Gi&uacute;p đẹp da</li>\n	<li>Thanh lọc cơ thể</li>\n	<li>Hỗ trợ giảm c&acirc;n</li>\n</ul>\n\n<p><strong>- C&aacute;ch sử dụng</strong>: B&ograve;n bon Th&aacute;i Lan c&oacute; thể ăn trực tiếp, l&agrave;m nước &eacute;p, sinh tố, hoặc sấy kh&ocirc;. Loại tr&aacute;i c&acirc;y n&agrave;y cũng được sử dụng để chế biến c&aacute;c m&oacute;n ăn như salad, gỏi, hoặc l&agrave;m mứt</p>\n\n<p>-&nbsp;<strong>C&aacute;ch chọn b&ograve;n bon ngon</strong></p>\n\n<ul>\n	<li>N&ecirc;n chọn mua b&ograve;n bon Th&aacute;i Lan c&oacute; vỏ s&aacute;ng b&oacute;ng, kh&ocirc;ng bị dập n&aacute;t.</li>\n	<li>Với Farmers Market ti&ecirc;u ch&iacute; h&agrave;ng đầu l&agrave; sản phảm đạt chất lượng cao, nguồn gốc r&otilde; r&agrave;ng, mang đến trải nghiệm tốt nhất cho kh&aacute;ch h&agrave;ng.&nbsp;</li>\n</ul>\n\n<p>-&nbsp;<strong>C&aacute;ch bảo quản b&ograve;n bon</strong></p>\n\n<p><strong>+ Bảo quản ở nhiệt độ ph&ograve;ng:</strong></p>\n\n<ul>\n	<li>B&ograve;n bon Th&aacute;i Lan c&oacute; thể bảo quản ở nhiệt độ ph&ograve;ng trong khoảng 2-3 ng&agrave;y.</li>\n	<li>N&ecirc;n chọn những quả B&ograve;n bon Th&aacute;i Lan ch&iacute;n tới, vỏ c&oacute; m&agrave;u v&agrave;ng nhạt hơi sần s&ugrave;i.</li>\n	<li>Để B&ograve;n bon Th&aacute;i Lan ở nơi tho&aacute;ng m&aacute;t, tr&aacute;nh &aacute;nh nắng trực tiếp.</li>\n	<li>Tr&aacute;nh xếp B&ograve;n bon Th&aacute;i Lan chồng l&ecirc;n nhau qu&aacute; nhiều để tr&aacute;nh dập n&aacute;t.</li>\n</ul>\n\n<p><strong>+&nbsp; Bảo quản trong tủ lạnh:</strong></p>\n\n<ul>\n	<li>Nếu bạn muốn bảo quản B&ograve;n bon Th&aacute;i Lan l&acirc;u hơn, bạn c&oacute; thể cho v&agrave;o tủ lạnh.</li>\n	<li>B&ograve;n bon Th&aacute;i Lan c&oacute; thể bảo quản trong tủ lạnh từ 5-7 ng&agrave;y.</li>\n	<li>N&ecirc;n cho B&ograve;n bon Th&aacute;i Lan v&agrave;o t&uacute;i zip hoặc hộp k&iacute;n c&oacute; nắp đậy để tr&aacute;nh bị mất nước.</li>\n	<li>Bảo quản B&ograve;n bon Th&aacute;i Lan ở ngăn m&aacute;t tủ lạnh.</li>\n</ul>\n', 'Trái cây Việt', 89500, 1, '2024-07-05', 7, 'b2a377a012c248da3518949333a87ca6', 'oyvqhq9wq25mau28dq7y', 3, 2, 1);
INSERT INTO `products` (`id`, `nameOfProduct`, `description`, `category`, `price`, `weightDefault`, `dateOfImporting`, `expriredDay`, `imgAssetId`, `imgPublicId`, `adminCreate`, `provider`, `status`) VALUES
(73, 'Dưa hấu có hạt giống Hàn Quốc', '<h1><strong>Dưa hấu c&oacute; hạt giống H&agrave;n Quốc</strong></h1>\n\n<p><strong>- Giống:</strong>&nbsp;H&agrave;n Quốc</p>\n\n<p><strong>- Khối lượng:</strong>&nbsp;1,5 - 2kg/tr&aacute;i</p>\n\n<p><strong>- Ti&ecirc;u chuẩn chất lượng:&nbsp;</strong>Tr&aacute;i c&acirc;y sạch</p>\n\n<p><strong>- Đặc điểm nổi bật:</strong></p>\n\n<ul>\n	<li>Dưa hấu đỏ&nbsp;mọng nước v&agrave; ngọt đậm. Dưa được trồng v&agrave; thu hoạch đạt ti&ecirc;u chuẩn an to&agrave;n thực phẩm, đảm bảo cho người ti&ecirc;u d&ugrave;ng.</li>\n	<li>Vỏ dưa hấu đỏ H&agrave;n Quốc c&oacute; m&agrave;u xanh đậm, xen kẽ những sọc xanh nhạt. Vỏ dưa mỏng, mịn v&agrave; b&oacute;ng lo&aacute;ng.</li>\n	<li>Thịt dưa hấu đỏ H&agrave;n Quốc c&oacute; m&agrave;u đỏ tươi, xen kẽ những hạt đen nhỏ.</li>\n	<li>Dưa hấu đỏ H&agrave;n Quốc c&oacute; vị ngọt thanh, m&aacute;t lạnh v&agrave; rất gi&ograve;n.</li>\n</ul>\n\n<p><strong>- Lợi &iacute;ch tuyệt vời của dưa hấu đỏ giống H&agrave;n Quốc</strong></p>\n\n<ul>\n	<li>Cung cấp nước cho cơ thể, v&igrave; nước trong quả dưa hấu chiếm đến 92% n&ecirc;n đ&acirc;y l&agrave; loại tr&aacute;i c&acirc;y th&iacute;ch hợp để cung cấp lượng nước cần thiết cơ thể, đặc biệt l&agrave; trong c&aacute;c ng&agrave;y n&oacute;ng nực hoặc khi bạn cần th&ecirc;m nước để duy tr&igrave; sự c&acirc;n bằng nước trong cơ thể.&nbsp;&nbsp;</li>\n	<li>Dưa hấu c&oacute; chứa rất nhiều chất chống oxy h&oacute;a, c&oacute; khả năng ngăn ngừa mức độ ảnh hưởng của gốc tự do, l&agrave;m chậm qu&aacute; tr&igrave;nh tho&aacute;i h&oacute;a v&agrave; bảo vệ c&aacute;c cơ quan trong cơ thể.</li>\n	<li>Lycopene trong dưa hấu c&oacute; thể ph&ograve;ng ngừa bệnh ung thư &ndash; đặc biệt l&agrave; ung thư đường ti&ecirc;u ho&aacute;</li>\n	<li>Ph&ograve;ng ngừa bệnh l&yacute; tim mạch</li>\n	<li>Dưa hấu chứa một lượng chất xơ nhỏ v&agrave; nước c&oacute; vai tr&ograve; quan trọng trong việc duy tr&igrave; sự hoạt động của hệ ti&ecirc;u h&oacute;a.&nbsp;</li>\n</ul>\n\n<p><strong>- C&aacute;ch lựa chọn được quả dưa hấu đỏ giống H&agrave;n Quốc ngon v&agrave; ngọt</strong></p>\n\n<ul>\n	<li>Chọn phần đ&aacute;y dưa c&oacute; vết r&aacute;m, m&agrave;u đốm v&agrave;ng</li>\n	<li>Nếu phần rốn của quả dưa to th&igrave; chứng tỏ phần vỏ dưa sẽ d&agrave;y, ruột nhạt. Nếu phần rốn nhỏ th&igrave; chứng tỏ quả dưa n&agrave;y c&oacute; vỏ mỏng, phần ruột cũng đủ độ ngọt. V&igrave; vậy, h&atilde;y chọn dưa c&oacute; phần rốn nhỏ v&agrave; hơi l&otilde;m một ch&uacute;t sẽ tốt nhất.</li>\n	<li>Bạn d&ugrave;ng mắt nh&igrave;n thấy cuống dưa nhỏ v&agrave; h&eacute;o kh&ocirc;, c&agrave;ng xoăn lại c&agrave;ng tốt. Điều n&agrave;y thể hiện tr&aacute;i dưa đ&atilde; ch&iacute;n rồi, quả dưa gi&agrave; n&ecirc;n sẽ rất ngon v&agrave; ngọt.</li>\n</ul>\n\n<p><strong>- C&aacute;ch bảo quản dưa hấu:</strong></p>\n\n<p><strong>+ Bảo quản ở nhiệt độ ph&ograve;ng:</strong></p>\n\n<ul>\n	<li><strong>Chọn dưa hấu ch&iacute;n tới:</strong>&nbsp;N&ecirc;n chọn những quả dưa hấu c&oacute; m&agrave;u đỏ tươi, gai nhọn đều đặn, vỏ hơi mềm khi ấn nhẹ. Tr&aacute;nh chọn những quả dưa hấu chưa ch&iacute;n hoặc qu&aacute; ch&iacute;n v&igrave; dễ bị dập n&aacute;t v&agrave; nhanh hỏng.</li>\n	<li><strong>Rửa sạch v&agrave; để r&aacute;o:</strong>&nbsp;Sau khi mua về, bạn n&ecirc;n rửa sạch dưa hấu dưới v&ograve;i nước chảy để loại bỏ bụi bẩn v&agrave; vi khuẩn. Sau đ&oacute;, để r&aacute;o nước ho&agrave;n to&agrave;n.</li>\n	<li><strong>Bảo quản ở nơi tho&aacute;ng m&aacute;t:</strong>&nbsp;N&ecirc;n bảo quản dưa hấu ở nơi tho&aacute;ng m&aacute;t, tr&aacute;nh &aacute;nh nắng trực tiếp. Bạn c&oacute; thể để dưa hấu trong rổ hoặc hộp c&oacute; lỗ th&ocirc;ng hơi để đảm bảo lưu th&ocirc;ng kh&iacute; tốt.</li>\n	<li><strong>Hạn chế chồng chất:</strong>&nbsp;Tr&aacute;nh chồng chất dưa hấu l&ecirc;n nhau v&igrave; c&oacute; thể l&agrave;m dập n&aacute;t quả. N&ecirc;n xếp dưa hấu th&agrave;nh từng lớp ri&ecirc;ng biệt.</li>\n	<li><strong>Thời gian bảo quản:</strong>&nbsp;Với c&aacute;ch bảo quản n&agrave;y, dưa hấu c&oacute; thể giữ được độ tươi ngon trong khoảng 1-2 tuần.</li>\n</ul>\n\n<p><strong>+ Bảo quản trong tủ lạnh:</strong></p>\n\n<ul>\n	<li><strong>C&aacute;ch bảo quản:</strong>&nbsp;Dưa hấu c&oacute; thể được bảo quản trong tủ lạnh để giữ được độ tươi ngon l&acirc;u hơn.</li>\n	<li><strong>Chuẩn bị:</strong>&nbsp;Rửa sạch dưa hấu, để r&aacute;o nước. Sau đ&oacute;, cho dưa hấu v&agrave;o hộp k&iacute;n hoặc t&uacute;i nilon c&oacute; đục lỗ để đảm bảo lưu th&ocirc;ng kh&iacute;.</li>\n	<li><strong>Nhiệt độ:</strong>&nbsp;N&ecirc;n bảo quản dưa hấu ở ngăn m&aacute;t tủ lạnh với nhiệt độ từ 8-10 độ C.</li>\n	<li><strong>Thời gian bảo quản:</strong>&nbsp;Dưa hấu bảo quản trong tủ lạnh c&oacute; thể giữ được độ tươi ngon trong khoảng 2-3 tuần.</li>\n</ul>\n', 'Trái cây Việt', 59000, 1, '2024-07-05', 7, '3a7b34eba2c71ae6a05b00d88dcc3c06', 'lpllx1zegq26urlawbha', 3, 2, 1),
(74, 'Sầu riêng Musang King Việt Nam', '<h1><strong>Sầu ri&ecirc;ng Musang King Việt Nam</strong></h1>\n\n<p><strong>- Thương Hiệu:&nbsp;</strong>Musang King</p>\n\n<p><strong>- Trọng lượng sản phẩm:</strong>&nbsp;1,5kg/Tr&aacute;i</p>\n\n<p><strong>- M&ocirc; tả sản phẩm:</strong></p>\n\n<ul>\n	<li>&nbsp;Đ&oacute;ng g&oacute;i<strong>:&nbsp;</strong>250G /Khay</li>\n	<li>Sầu Musang King c&oacute; m&agrave;u v&agrave;ng đậm rất mịn kh&ocirc;ng c&oacute; sợi, thịt cơm li&ecirc;n kết với nhau rất chắc. Sầu ri&ecirc;ng mang một hương vị đậm đ&agrave;, hương thơm đặc trưng.&nbsp;</li>\n	<li>&nbsp;M&uacute;i m&agrave;u v&agrave;ng nghệ v&agrave; mịn m&agrave;ng</li>\n</ul>\n\n<p><strong>- Lưu trữ, bảo quản:</strong>&nbsp;Bảo quản ở ngăn m&aacute;t tủ lạnh</p>\n\n<p><strong>- Th&ocirc;ng tin dinh dưỡng:</strong></p>\n\n<ul>\n	<li>Sầu ri&ecirc;ng c&oacute; chứa một lượng vitamin B kh&aacute; cao, c&aacute;c vitamin B sẽ c&oacute; một loạt c&aacute;c lợi &iacute;ch cho sức khỏe như ngăn ngừa l&atilde;o h&oacute;a v&agrave; bệnh tim, gi&uacute;p tăng HDL (cholesterol tốt) v&agrave; thậm ch&iacute; c&oacute; thể gi&uacute;p cải thiện t&acirc;m trạng, giảm bớt trầm cảm.</li>\n	<li>Trong sầu ri&ecirc;ng chứa một lượng&nbsp;kali phong ph&uacute;,&nbsp;l&agrave; chất điện ph&acirc;n quan trọng gi&uacute;p kiểm so&aacute;t huyết &aacute;p, giữ cho nồng độ natri ở mức độ cho ph&eacute;p v&agrave; gi&uacute;p điều h&ograve;a nhịp tim.</li>\n	<li>Một trong những lợi &iacute;ch kh&aacute;c của sầu ri&ecirc;ng l&agrave; c&oacute; chứa h&agrave;m lượng chất xơ cao. Mỗi khẩu phần ăn n&ecirc;n cần chứa 9g chất xơ tương đương 37% nhu cầu h&agrave;ng ng&agrave;y của bạn, điều n&agrave;y thật kh&oacute; thực hiện khi hầu như loại vi chất n&agrave;y c&oacute; nhiều trong tr&aacute;i c&acirc;y hơn l&agrave; c&aacute;c bữa ăn. Ch&iacute;nh v&igrave; thế, nếu bạn cần hấp thụ một lượng chất xơ ổn định, th&igrave; sầu ri&ecirc;ng l&agrave; loại tr&aacute;i c&acirc;y tốt để bạn sử dụng.</li>\n	<li>S&acirc;u ri&ecirc;ng c&ograve;n l&agrave; loại tr&aacute;i c&acirc;y chứa nhiều vitamin C, gi&uacute;p tăng cường sức đề kh&aacute;ng, giảm cholesterol, hỗ trợ chữa l&agrave;nh vết thương.</li>\n</ul>\n', 'Trái cây Việt', 598500, 1, '2024-07-05', 7, '60b571fb56e3f00a1560e56d400b60b7', 'gh0zjpi5q40gvbyf2rrr', 3, 3, 1),
(75, 'Sung tươi Camy', '<h1><strong>Sung tươi Camy giống Mỹ&nbsp;</strong></h1>\n\n<p><strong>- Xuất xứ:</strong>&nbsp;Việt Nam</p>\n\n<p><strong>- Ti&ecirc;u chuẩn chất lượng</strong>: Tr&aacute;i c&acirc;y sạch</p>\n\n<p><strong>- Trọng lượng:&nbsp;</strong>500g/Hộp&nbsp;</p>\n\n<p><strong>Đặc điểm của sung tươi Camy giống Mỹ:</strong></p>\n\n<ul>\n	<li><strong>H&igrave;nh dạng:</strong>&nbsp;Sung tươi Camy giống Mỹ c&oacute; h&igrave;nh bầu dục, hơi thu&ocirc;n d&agrave;i, với phần cuống nhỏ. Khi ch&iacute;n, quả sung c&oacute; m&agrave;u n&acirc;u đỏ, vỏ mỏng v&agrave; căng mọng.</li>\n	<li><strong>Thịt quả:</strong>&nbsp;Thịt quả d&agrave;y, m&agrave;u trắng hồng, &iacute;t xơ v&agrave; c&oacute; vị ngọt thanh, gi&ograve;n tan. Khi ch&iacute;n, thịt quả mềm dẻo, mọng nước v&agrave; c&oacute; hương thơm đặc trưng.</li>\n	<li><strong>Hạt:</strong>&nbsp;Hạt sung tươi Camy giống Mỹ nhỏ, m&agrave;u n&acirc;u đen v&agrave; b&aacute;m chắc v&agrave;o c&ugrave;i.</li>\n	<li><strong>K&iacute;ch thước:</strong>&nbsp;Sung tươi Camy giống Mỹ c&oacute; k&iacute;ch thước trung b&igrave;nh, d&agrave;i khoảng 4-5cm v&agrave; đường k&iacute;nh 3-4cm.</li>\n	<li><strong>Trọng lượng:</strong>&nbsp;Sung tươi Camy giống Mỹ c&oacute; trọng lượng trung b&igrave;nh khoảng 20-30g/quả.</li>\n</ul>\n\n<p><strong>- Lợi &iacute;ch của sung tươi Camy giống Mỹ:</strong></p>\n\n<ul>\n	<li><strong>Gi&agrave;u chất xơ:</strong>&nbsp;Sung tươi Camy giống Mỹ l&agrave; nguồn cung cấp dồi d&agrave;o chất xơ, gi&uacute;p hỗ trợ hệ ti&ecirc;u h&oacute;a, ngăn ngừa t&aacute;o b&oacute;n.</li>\n	<li><strong>Gi&agrave;u kali:</strong>&nbsp;Sung tươi Camy giống Mỹ chứa nhiều kali, gi&uacute;p điều h&ograve;a huyết &aacute;p, giảm nguy cơ mắc c&aacute;c bệnh tim mạch.</li>\n	<li><strong>Gi&agrave;u vitamin C:</strong>&nbsp;Sung tươi Camy giống Mỹ chứa nhiều vitamin C, gi&uacute;p tăng cường hệ miễn dịch, chống lại c&aacute;c bệnh cảm c&uacute;m, ho.</li>\n	<li><strong>Gi&agrave;u vitamin A:</strong>&nbsp;Sung tươi Camy giống Mỹ chứa nhiều vitamin A, gi&uacute;p bảo vệ mắt, ngăn ngừa c&aacute;c bệnh về mắt như đục thủy tinh thể, tho&aacute;i h&oacute;a điểm v&agrave;ng.</li>\n	<li><strong>Gi&uacute;p giảm c&acirc;n:</strong>&nbsp;Chất xơ trong sung tươi Camy giống Mỹ gi&uacute;p tạo cảm gi&aacute;c no l&acirc;u, hỗ trợ giảm c&acirc;n hiệu quả.</li>\n	<li><strong>L&agrave;m đẹp da:</strong>&nbsp;Vitamin C v&agrave; chất chống oxy h&oacute;a trong sung tươi Camy giống Mỹ gi&uacute;p bảo vệ da khỏi t&aacute;c hại của &aacute;nh nắng mặt trời, l&agrave;m giảm nếp nhăn v&agrave; tăng độ đ&agrave;n hồi cho da.</li>\n</ul>\n\n<p><strong>- C&aacute;ch d&ugrave;ng sung tươi</strong></p>\n\n<p>Sung tươi l&agrave; một loại tr&aacute;i c&acirc;y thơm ngon, bổ dưỡng v&agrave; c&oacute; nhiều c&aacute;ch sử dụng kh&aacute;c nhau. Dưới đ&acirc;y l&agrave; một số c&aacute;ch d&ugrave;ng sung tươi phổ biến:</p>\n\n<p><strong>+&nbsp; Ăn tươi:</strong></p>\n\n<ul>\n	<li>Sung tươi c&oacute; thể ăn trực tiếp sau khi rửa sạch. Vỏ sung mỏng v&agrave; mềm n&ecirc;n bạn c&oacute; thể ăn cả vỏ.</li>\n	<li>Sung tươi c&oacute; vị ngọt thanh, gi&ograve;n tan v&agrave; nhiều nước, rất th&iacute;ch hợp để ăn tr&aacute;ng miệng hoặc ăn vặt.</li>\n</ul>\n\n<p><strong>+ L&agrave;m sinh tố:</strong></p>\n\n<ul>\n	<li>Sung tươi c&oacute; thể xay sinh tố c&ugrave;ng với c&aacute;c loại tr&aacute;i c&acirc;y kh&aacute;c như chuối, d&acirc;u t&acirc;y, xo&agrave;i,... để tạo n&ecirc;n một thức uống thơm ngon v&agrave; bổ dưỡng.</li>\n	<li>Sinh tố sung tươi c&oacute; thể gi&uacute;p tăng cường hệ miễn dịch, hỗ trợ ti&ecirc;u h&oacute;a v&agrave; l&agrave;m đẹp da.</li>\n</ul>\n\n<p><strong>+ L&agrave;m salad:</strong></p>\n\n<ul>\n	<li>Sung tươi c&oacute; thể cắt l&aacute;t mỏng v&agrave; th&ecirc;m v&agrave;o salad c&ugrave;ng với c&aacute;c loại rau củ quả kh&aacute;c như x&agrave; l&aacute;ch, c&agrave; chua, dưa chuột,...</li>\n	<li>Salad sung tươi c&oacute; vị ngọt thanh, gi&ograve;n tan v&agrave; nhiều dinh dưỡng, rất th&iacute;ch hợp để ăn v&agrave;o bữa trưa hoặc bữa tối.</li>\n</ul>\n\n<p><strong>+ L&agrave;m mứt:</strong></p>\n\n<ul>\n	<li>Sung tươi c&oacute; thể nấu th&agrave;nh mứt để bảo quản v&agrave; sử dụng l&acirc;u d&agrave;i.</li>\n	<li>Mứt sung c&oacute; vị ngọt thanh, dẻo dai v&agrave; c&oacute; thể d&ugrave;ng để ăn k&egrave;m với b&aacute;nh m&igrave;, ph&ocirc; mai hoặc pha tr&agrave;.</li>\n</ul>\n\n<p><strong>- C&aacute;ch lựa chọn sung ngon:</strong></p>\n\n<ul>\n	<li>N&ecirc;n chọn mua sung tươi c&oacute; vỏ m&agrave;u n&acirc;u đỏ, b&oacute;ng mượt, kh&ocirc;ng bị dập n&aacute;t hoặc hư hỏng.</li>\n	<li>Với Farmers Market ti&ecirc;u ch&iacute; h&agrave;ng đầu l&agrave; sản phảm đạt chất lượng cao, nguồn gốc r&otilde; r&agrave;ng, mang đến trải nghiệm tốt nhất cho kh&aacute;ch h&agrave;ng.&nbsp;</li>\n</ul>\n', 'Trái cây Việt', 194500, 1, '2024-07-05', 7, '40f86674becb06616c06c5e82206a249', 'vuzd2w1ewsmcnltihdnc', 3, 3, 1),
(76, 'Quýt Úc size XL', '<h1><strong>Qu&yacute;t &Uacute;c size XL</strong></h1>\n\n<p><strong>- Xuất xứ</strong>: &Uacute;c</p>\n\n<p><strong>- Ti&ecirc;u chuẩn chất lượng</strong>: Nhập Khẩu</p>\n\n<p><strong>- Đặc điểm sản phẩm:</strong></p>\n\n<ul>\n	<li>Qu&yacute;t &Uacute;c c&oacute; vị ngọt r&otilde; rệt, xen lẫn vị ngọt thanh&nbsp;m&aacute;t v&agrave; mọng nước.&nbsp;</li>\n	<li>Loại qu&yacute;t n&agrave;y đặc trưng kh&ocirc;ng ngọt tuyệt đối m&agrave; c&oacute; ch&uacute;t chua xen kẽ.&nbsp;</li>\n	<li>Mỗi m&uacute;i qu&yacute;t &Uacute;c đều rất mọng nước v&agrave; dễ b&oacute;c.&nbsp;</li>\n	<li>Tuỳ thời điểm, qu&yacute;t &Uacute;c tại Farmers Market sẽ l&agrave; giống APH v&agrave; giống 2PH.&nbsp;\n	<ul>\n		<li>Giống qu&yacute;t APH v&agrave;ng tươi ngon, mọng nước v&agrave; thương thơm đặc biệt.&nbsp;</li>\n		<li>Giống qu&yacute;t 2PH l&agrave; giống đặc biệt được trồng rộng r&atilde;i tại &Uacute;c. Nổi tiếng với hương vị độc đ&aacute;o, c&oacute; vị ngọt tự nhi&ecirc;n pha ch&uacute;t chua nhẹ, tạo hương vị c&acirc;n bằng dễ chịu.&nbsp;</li>\n	</ul>\n	</li>\n</ul>\n\n<p><strong>- Dinh dưỡng của tr&aacute;i qu&yacute;t &Uacute;c:</strong></p>\n\n<ul>\n	<li>Loại tr&aacute;i n&agrave;y giảu vitamin C, kali, chất chống oxy ho&aacute;.&nbsp;</li>\n	<li>Qu&yacute;t chứa &iacute;t calo v&agrave; chất b&eacute;o, nhưng lại gi&agrave;u hydrat cacbon, nhiều nước. Gi&uacute;p tăng cường năng lượng cho cỡ thể.&nbsp;</li>\n	<li>Vitamin C gi&uacute;p cung cấp năng lượng cho c&oacute; thể, tăng cường sức đề kh&aacute;ng, hệ miễn dịch.&nbsp;</li>\n</ul>\n\n<p><strong>- C&aacute;ch sử dụng:</strong></p>\n\n<ul>\n	<li>Qu&yacute;t&nbsp;c&oacute; thể ăn trực tiếp hoặc chế biến th&agrave;nh nhiều m&oacute;n ăn ngon như salad, sinh tố, mứt,...</li>\n	<li>Qu&yacute;t cũng c&oacute; thể được sử dụng để trang tr&iacute; c&aacute;c m&oacute;n ăn hoặc l&agrave;m qu&agrave; tặng.</li>\n</ul>\n\n<p><strong>- C&aacute;ch lựa chọn qu&yacute;t ngon:</strong></p>\n\n<ul>\n	<li>N&ecirc;n chọn những quả c&oacute; m&agrave;u v&agrave;ng cam s&aacute;ng b&oacute;ng, đều m&agrave;u, kh&ocirc;ng bị sần s&ugrave;i hay nhợt nhạt.</li>\n	<li>Với Farmers Market ti&ecirc;u ch&iacute; h&agrave;ng đầu l&agrave; sản phảm đạt chất lượng cao, nguồn gốc r&otilde; r&agrave;ng, mang đến trải nghiệm tốt nhất cho kh&aacute;ch h&agrave;ng.&nbsp;</li>\n</ul>\n\n<p>-&nbsp;<strong>C&aacute;ch bảo quản qu&yacute;t được l&acirc;u</strong><strong>:&nbsp;</strong></p>\n\n<p>Qu&yacute;t&nbsp;l&agrave; loại tr&aacute;i c&acirc;y thơm ngon, bổ dưỡng nhưng lại dễ bị hư hỏng nếu kh&ocirc;ng bảo quản đ&uacute;ng c&aacute;ch</p>\n\n<p><strong>1. Bảo quản ở nhiệt độ ph&ograve;ng:</strong></p>\n\n<ul>\n	<li>Khi mua về, nếu qu&yacute;t chưa ch&iacute;n tới, bạn c&oacute; thể bảo quản ở nhiệt độ ph&ograve;ng tho&aacute;ng m&aacute;t, tr&aacute;nh &aacute;nh nắng trực tiếp.</li>\n	<li>N&ecirc;n l&oacute;t giấy b&aacute;o hoặc khăn mềm dưới quả qu&yacute;t để tr&aacute;nh dập n&aacute;t.</li>\n	<li>Qu&yacute;t sẽ ch&iacute;n sau khoảng 2-3 ng&agrave;y ở nhiệt độ ph&ograve;ng.</li>\n</ul>\n\n<p><strong>2. Bảo quản trong tủ lạnh:</strong></p>\n\n<ul>\n	<li>Qu&yacute;t đ&atilde; ch&iacute;n n&ecirc;n bảo quản trong ngăn m&aacute;t tủ lạnh để giữ độ tươi ngon.</li>\n	<li>N&ecirc;n bọc qu&yacute;t bằng giấy hoặc cho v&agrave;o hộp k&iacute;n để tr&aacute;nh mất nước v&agrave; teo t&oacute;p.</li>\n	<li>Qu&yacute;t bảo quản trong tủ lạnh c&oacute; thể giữ được 3-5 ng&agrave;y.</li>\n	<li>Kh&ocirc;ng n&ecirc;n bảo quản qu&yacute;t chung với c&aacute;c loại tr&aacute;i c&acirc;y kh&aacute;c như t&aacute;o, chuối v&igrave; c&oacute; thể khiến qu&yacute;t nhanh ch&iacute;n v&agrave; bị mềm.</li>\n	<li>N&ecirc;n ăn qu&yacute;t trong v&ograve;ng 1-2 tuần sau khi mua</li>\n</ul>\n\n<p><strong>- Phụ nữ mang thai v&agrave; trẻ em ăn qu&yacute;t c&oacute; được kh&ocirc;ng?</strong></p>\n\n<p><strong>+ Phụ nữ mang thai&nbsp;</strong>c&oacute; thể ăn qu&yacute;t,&nbsp;oại tr&aacute;i c&acirc;y chứa nhiều vitamin, kho&aacute;ng chất v&agrave; chất chống oxy h&oacute;a tốt cho sức khỏe, đặc biệt l&agrave; vitamin C, folate v&agrave; kali.</p>\n\n<ul>\n	<li><strong>Vitamin C:</strong>&nbsp;Gi&uacute;p tăng cường hệ miễn dịch, hỗ trợ hấp thu sắt v&agrave; collagen,&nbsp;<strong>giảm nguy cơ tiền sản giật.</strong></li>\n	<li><strong>Folate:</strong>&nbsp;Gi&uacute;p ngăn&nbsp;<strong>ngừa dị tật ống thần kinh ở thai nhi</strong>.</li>\n	<li><strong>Kali:</strong>&nbsp;Gi&uacute;p điều h&ograve;a huyết &aacute;p, giảm nguy cơ chuột r&uacute;t</li>\n</ul>\n\n<p><strong>+ C&oacute; thể cho trẻ ăn qu&yacute;t khi b&eacute; đủ 6 th&aacute;ng tuổi.</strong>&nbsp;Qu&yacute;t l&agrave; nguồn cung cấp vitamin C, chất xơ v&agrave; kali dồi d&agrave;o, tốt cho sức khỏe của trẻ.</p>\n\n<ul>\n	<li><strong>Vitamin C:</strong>&nbsp;Gi&uacute;p tăng cường hệ miễn dịch, chống lại c&aacute;c bệnh nhiễm tr&ugrave;ng.</li>\n	<li><strong>Chất xơ:</strong>&nbsp;Hỗ trợ ti&ecirc;u h&oacute;a, ngăn ngừa t&aacute;o b&oacute;n.</li>\n	<li><strong>Kali:</strong>&nbsp;Gi&uacute;p điều h&ograve;a huyết &aacute;p, ph&aacute;t triển cơ bắp v&agrave; hệ thần kinh.</li>\n</ul>\n\n<p><strong>- Những lưu &yacute; khi thưởng thức:&nbsp;</strong></p>\n\n<ul>\n	<li>Ăn qu&yacute;t với lượng vừa phải, khoảng 1-2 quả mỗi ng&agrave;y.</li>\n	<li>Tr&aacute;nh ăn qu&yacute;t khi bụng đ&oacute;i.</li>\n	<li>Bắt đầu cho trẻ ăn qu&yacute;t với lượng nhỏ, khoảng 1/4 quả mỗi ng&agrave;y.</li>\n	<li>Cho trẻ ăn qu&yacute;t khi b&eacute; đang ngồi v&agrave; c&oacute; người lớn gi&aacute;m s&aacute;t.</li>\n	<li>Cắt qu&yacute;t th&agrave;nh những miếng nhỏ để tr&aacute;nh trẻ bị nghẹn.</li>\n</ul>\n\n<div class=\"eJOY__extension_root_class\" id=\"eJOY__extension_root\" style=\"all: unset;\">&nbsp;</div>\n', 'Trái cây Nhập', 150000, 1, '2024-07-14', 7, 'e0bb0afebdb87c52caa0dcac0e0b1a5c', 'qvcjnw7cqqmiiibweygk', 4, 1, 1),
(77, 'Kiwi vàng New Zealand (I0008776)', '<h1><strong>Kiwi v&agrave;ng New Zealand&nbsp;</strong></h1>\n\n<p><strong>- L&agrave; một loại tr&aacute;i c&acirc;y nhiệt đới, chứa nhiều h&agrave;m lượng vitamin, axit folic hỗ trợ ti&ecirc;u h&oacute;a v&agrave; tốt cho sức khỏe. Kiwi v&agrave;ng cắt đ&ocirc;i sẽ cảm nhận được độ ngọt xen kẻ một &iacute;t hậu vị chua nằm ở phần thịt s&aacute;t vỏ, tr&aacute;i vừa mềm tay l&agrave; l&uacute;c thưởng thức kiwi ngon nhất, độ mật vừa đủ, thịt kh&ocirc;ng bỡ, mọng nước.</strong></p>\n\n<p><strong>- Xuất xứ:&nbsp;</strong>New Zealand</p>\n\n<p>-&nbsp;<strong>Ti&ecirc;u chuẩn chất lượng:&nbsp;</strong>Nhập khẩu</p>\n\n<p><strong>- Đặc điểm:</strong></p>\n\n<ul>\n	<li>Khoảng 7 tr&aacute;i/ 1kg, đều, đẹp, vỏ mỏng, thịt v&agrave;ng &oacute;ng &aacute;nh.</li>\n	<li>Thịt mọng nước, ngọt thơm, chua dịu ở phần hạt</li>\n	<li>Kiwi V&agrave;ng Zespri New Zealand c&oacute; ruột b&ecirc;n trong m&agrave;u v&agrave;ng, c&oacute; vỏ mượt, m&agrave;u đồng, ở đầu quả c&oacute; đ&agrave;i giống như vương miện.&nbsp;</li>\n	<li>Hương vị ngọt ng&agrave;o v&ugrave;ng nhiệt đới, thơm m&aacute;t đ&atilde; khiến n&oacute; trở th&agrave;nh m&oacute;n tr&aacute;ng miệng m&ugrave;a h&egrave; tuyệt vời.&nbsp;</li>\n	<li>Ngo&agrave;i ra kiwi tươi c&ograve;n được biết đến với nhiều gi&aacute; trị dinh dưỡng tốt cho cơ thể.</li>\n</ul>\n\n<p><strong>- Gi&aacute; trị dinh dưỡng:</strong></p>\n\n<ul>\n	<li><strong>Vitamin C:</strong>&nbsp;Dồi d&agrave;o, cao gấp 2 lần kiwi xanh, gi&uacute;p tăng cường hệ miễn dịch, chống oxy h&oacute;a, bảo vệ da khỏi t&aacute;c hại của &aacute;nh nắng mặt trời.</li>\n	<li><strong>Vitamin E:</strong>&nbsp;Gi&uacute;p bảo vệ tế b&agrave;o khỏi tổn thương, hỗ trợ sức khỏe tim mạch.</li>\n	<li><strong>Kali:</strong>&nbsp;Gi&uacute;p điều h&ograve;a huyết &aacute;p, tốt cho tim mạch.</li>\n	<li><strong>Chất xơ:</strong>&nbsp;Gi&uacute;p hệ ti&ecirc;u h&oacute;a khỏe mạnh, hỗ trợ giảm c&acirc;n.</li>\n	<li><strong>Axit folic:</strong>&nbsp;Quan trọng cho phụ nữ mang thai, gi&uacute;p ph&aacute;t triển tr&iacute; n&atilde;o thai nhi.</li>\n	<li><strong>Ngo&agrave;i ra:</strong>&nbsp;C&ograve;n chứa nhiều dưỡng chất kh&aacute;c như vitamin K, magie, photpho, đồng,...</li>\n</ul>\n\n<p><strong>- Lợi &iacute;ch của việc sử dụng kiwi:</strong></p>\n\n<ul>\n	<li>Tăng cường hệ miễn dịch.</li>\n	<li>Tốt cho sức khỏe tim mạch.</li>\n	<li>Hỗ trợ ti&ecirc;u h&oacute;a.</li>\n	<li>Gi&uacute;p giảm c&acirc;n.</li>\n	<li>Tốt cho da.</li>\n	<li>Tốt cho mắt.</li>\n	<li>Ngăn ngừa ung thư.</li>\n</ul>\n\n<p><strong>- C&aacute;ch sử dụng kiwi:</strong></p>\n\n<ul>\n	<li>C&oacute; thể ăn trực tiếp, gọt vỏ hoặc bỏ vỏ.</li>\n	<li>Th&ecirc;m v&agrave;o salad, sinh tố, sữa chua.</li>\n	<li>L&agrave;m mứt, b&aacute;nh ngọt, kem.</li>\n</ul>\n\n<p>-&nbsp;<strong>C&aacute;ch lựa chọn kiwi ngon:</strong></p>\n\n<ul>\n	<li>N&ecirc;n chọn những quả kiwi c&oacute; vỏ căng mịn, m&agrave;u n&acirc;u v&agrave;ng đồng đều, kh&ocirc;ng bị sần s&ugrave;i, trầy xước hay c&oacute; đốm n&acirc;u.</li>\n	<li>D&ugrave;ng ng&oacute;n tay ấn nhẹ v&agrave;o quả kiwi. Nếu quả kiwi c&oacute; độ mềm vừa phải, hơi đ&agrave;n hồi th&igrave; đ&oacute; l&agrave; quả kiwi ngon.</li>\n	<li>Với Farmers Market ti&ecirc;u ch&iacute; h&agrave;ng đầu l&agrave; sản phảm đạt chất lượng cao, nguồn gốc r&otilde; r&agrave;ng, mang đến trải nghiệm tốt nhất cho kh&aacute;ch h&agrave;ng.&nbsp;</li>\n</ul>\n\n<p><strong>- Để bảo quản kiwi được l&acirc;u v&agrave; giữ được độ tươi ngon, bạn cần lưu &yacute;:</strong></p>\n\n<p><strong>+ Nhiệt độ:</strong></p>\n\n<ul>\n	<li><strong>Nhiệt độ ph&ograve;ng:</strong>&nbsp;Nếu kiwi chưa ch&iacute;n, h&atilde;y để ch&uacute;ng ở nhiệt độ ph&ograve;ng cho đến khi ch&iacute;n. Tr&aacute;nh &aacute;nh nắng trực tiếp.</li>\n	<li><strong>Tủ lạnh:</strong>&nbsp;Khi kiwi đ&atilde; ch&iacute;n, h&atilde;y đặt ch&uacute;ng v&agrave;o tủ lạnh ở nhiệt độ từ 0&deg;C đến 4&deg;C. Ở nhiệt độ n&agrave;y, kiwi c&oacute; thể được bảo quản từ 5 đến 7 ng&agrave;y.</li>\n</ul>\n\n<p><strong>+ Độ ẩm:</strong></p>\n\n<ul>\n	<li>Kiwi cần được bảo quản ở độ ẩm cao để giữ được độ tươi ngon.</li>\n	<li>Bạn c&oacute; thể đặt kiwi v&agrave;o t&uacute;i nhựa hoặc hộp đựng c&oacute; nắp đậy để giữ ẩm.</li>\n	<li>Tr&aacute;nh để kiwi tiếp x&uacute;c trực tiếp với nước v&igrave; sẽ khiến kiwi bị nẫu.</li>\n</ul>\n\n<p><strong>+ Ph&acirc;n chia:</strong></p>\n\n<ul>\n	<li>N&ecirc;n ph&acirc;n chia kiwi ra từng quả ri&ecirc;ng biệt khi bảo quản trong tủ lạnh để tr&aacute;nh kiwi bị dập n&aacute;t.</li>\n	<li>Tr&aacute;nh để kiwi tiếp x&uacute;c với c&aacute;c loại tr&aacute;i c&acirc;y kh&aacute;c như t&aacute;o, chuối, l&ecirc;,... v&igrave; những loại tr&aacute;i c&acirc;y n&agrave;y c&oacute; thể sản sinh ra kh&iacute; ethylene, khiến kiwi ch&iacute;n nhanh hơn v&agrave; dễ bị nẫu.</li>\n</ul>\n\n<p><strong>+ Mẹo bảo quản:</strong></p>\n\n<ul>\n	<li><strong>Để kiwi ch&iacute;n nhanh hơn:</strong>&nbsp;Bạn c&oacute; thể cho kiwi v&agrave;o t&uacute;i giấy c&ugrave;ng với t&aacute;o hoặc chuối.</li>\n	<li><strong>Để kiwi ch&iacute;n chậm hơn:</strong>&nbsp;Bạn c&oacute; thể bọc kiwi bằng giấy nến hoặc giấy b&aacute;o.</li>\n	<li><strong>Để kiwi đ&ocirc;ng lạnh:</strong>&nbsp;Bạn c&oacute; thể cắt kiwi th&agrave;nh từng miếng v&agrave; cho v&agrave;o t&uacute;i đ&ocirc;ng lạnh. Kiwi đ&ocirc;ng lạnh c&oacute; thể được bảo quản trong v&agrave;i th&aacute;ng.</li>\n</ul>\n\n<p><strong>- Lưu &yacute;:</strong></p>\n\n<ul>\n	<li>Kh&ocirc;ng n&ecirc;n bảo quản kiwi ở nhiệt độ qu&aacute; cao v&igrave; sẽ khiến kiwi nhanh bị ch&iacute;n nẫu.</li>\n	<li>Kh&ocirc;ng n&ecirc;n bảo quản kiwi qu&aacute; l&acirc;u v&igrave; sẽ khiến kiwi mất đi hương vị v&agrave; dưỡng chất.</li>\n</ul>\n\n<p><strong>- C&aacute;ch d&ugrave;ng kiwi</strong></p>\n\n<p><strong>+ Ăn trực tiếp:</strong></p>\n\n<ul>\n	<li>Đ&acirc;y l&agrave; c&aacute;ch đơn giản v&agrave; phổ biến nhất để thưởng thức kiwi.</li>\n	<li>Bạn c&oacute; thể rửa sạch kiwi, gọt vỏ hoặc bỏ vỏ (t&ugrave;y sở th&iacute;ch) v&agrave; cắt th&agrave;nh từng miếng vừa ăn.</li>\n	<li>Kiwi c&oacute; thể ăn k&egrave;m với sữa chua, mật ong hoặc th&ecirc;m v&agrave;o salad, sinh tố.</li>\n</ul>\n\n<p><strong>+ L&agrave;m sinh tố:</strong></p>\n\n<ul>\n	<li>Kiwi l&agrave; nguy&ecirc;n liệu tuyệt vời để l&agrave;m sinh tố.</li>\n	<li>Bạn c&oacute; thể kết hợp kiwi với c&aacute;c loại tr&aacute;i c&acirc;y kh&aacute;c như chuối, d&acirc;u t&acirc;y, t&aacute;o,... để tạo ra những ly sinh tố thơm ngon, bổ dưỡng.</li>\n</ul>\n\n<p><strong>+ L&agrave;m mứt:</strong></p>\n\n<ul>\n	<li>Mứt kiwi l&agrave; m&oacute;n ăn k&egrave;m ngon miệng cho b&aacute;nh m&igrave; hoặc b&aacute;nh quy.</li>\n	<li>Để l&agrave;m mứt kiwi, bạn cần sơ chế kiwi, sau đ&oacute; nấu với đường v&agrave; nước cho đến khi sệt lại.</li>\n</ul>\n\n<p><strong>- Phụ nữ mang thai v&agrave; trẻ em ăn kiwi c&oacute; được kh&ocirc;ng?</strong></p>\n\n<p><strong>+ Phụ nữ mang thai</strong></p>\n\n<ul>\n	<li>Kiwi l&agrave; loại tr&aacute;i c&acirc;y&nbsp;<strong>chứa h&agrave;m lượng vitamin C cao gấp hai lần chanh</strong>. B&ecirc;n cạnh đ&oacute;, b&agrave; bầu ăn kiwi c&ograve;n được bổ sung nhiều chất xơ, vitamin E, carbohydrate, năng lượng v&agrave; c&aacute;c kho&aacute;ng chất kh&aacute;c</li>\n	<li>B&agrave; bầu ăn kiwi c&oacute; thể đ&aacute;p ứng đến 140% liều lượng vitamin C khuyến c&aacute;o h&agrave;ng ng&agrave;y. C&oacute; thể n&oacute;i, v&igrave; c&oacute; h&agrave;m lượng vitamin C cao m&agrave; kiwi trở th&agrave;nh loại tr&aacute;i c&acirc;y&nbsp;<strong>rất tốt cho b&agrave; bầu v&agrave; thai nhi.</strong></li>\n	<li><strong>Đối với mẹ bầu trước v&agrave; sau khi sinh</strong>, vitamin C gi&uacute;p cơ thể mẹ hấp thụ sắt tốt hơn để&nbsp;<strong>ngăn ngừa thiếu m&aacute;u</strong></li>\n	<li>Vitamin C trong quả kiwi gi&uacute;p h&igrave;nh th&agrave;nh c&aacute;c chất dẫn truyền thần kinh, một chất rất quan trọng đối với chức năng của n&atilde;o.</li>\n	<li>Vitamin C c&oacute; t&iacute;nh chống oxy h&oacute;a mạnh, gi&uacute;p bảo vệ RNA v&agrave; DNA khỏi t&aacute;c hại của c&aacute;c gốc tự do.</li>\n</ul>\n\n<p><strong>+ Cho b&eacute; ăn kiwi</strong>&nbsp;khi trẻ từ 8 đến 10 th&aacute;ng v&igrave; t&iacute;nh chất chua của n&oacute;. Nếu em b&eacute; của bạn đ&atilde; c&oacute; biểu hiện dị ứng thức ăn hoặc ph&aacute;t ban t&atilde; trước đ&oacute;, h&atilde;y c&acirc;n nhắc cho b&eacute; ăn tr&aacute;i c&acirc;y từ 10 đến 12 th&aacute;ng.</p>\n\n<ul>\n	<li><strong>Vitamin C</strong>: Kiwi rất gi&agrave;u vitamin C, cung cấp 230% lượng vitamin C được khuyến nghị mỗi ng&agrave;y. Vitamin C gi&uacute;p tăng cường hệ miễn dịch, hấp thụ sắt từ thực phẩm kh&aacute;c v&agrave; chữa l&agrave;nh vết thương.</li>\n	<li><strong>Chất xơ</strong>: Kiwi cung cấp 16% lượng chất xơ cần thiết cho trẻ. Chất xơ gi&uacute;p giảm t&aacute;o b&oacute;n v&agrave; duy tr&igrave; hệ ti&ecirc;u h&oacute;a khỏe mạnh.</li>\n</ul>\n\n<p><strong>- Những lưu &yacute; khi thưởng thức:&nbsp;</strong></p>\n\n<ul>\n	<li>Mẹ bầu chỉ n&ecirc;n ăn khoảng 2&ndash;3 tr&aacute;i kiwi mỗi ng&agrave;y l&agrave; ph&ugrave; hợp.</li>\n	<li>Kiwi c&oacute; t&iacute;nh axit cao, v&igrave; vậy đừng ăn qu&aacute; nhiều v&igrave; bạn c&oacute; thể bị lở miệng, lo&eacute;t lưỡi v&agrave; ph&aacute;t ban.</li>\n	<li>Nếu bạn bị dị ứng do di truyền, vi&ecirc;m dạ d&agrave;y hoặc c&aacute;c vấn đề kh&aacute;c th&igrave; cần tr&aacute;nh ăn kiwi khi mang thai.</li>\n	<li>Những người bị vi&ecirc;m mũi dị ứng thường c&oacute; nguy cơ dễ phản ứng dị ứng với kiwi.</li>\n</ul>\n', 'Trái cây Nhập', 159000, 1, '2024-07-14', 7, 'b23dcb467ccd3433266533968b315989', 'epnd4yvfchkfkiayqr8s', 4, 2, 1),
(78, 'Cam vàng Navel Úc', '<h2><strong>Cam v&agrave;ng Navel &Uacute;c</strong></h2>\n\n<p>-&nbsp;<strong>Xuất xứ:&nbsp;</strong>&Uacute;c</p>\n\n<p>-&nbsp;<strong>Ti&ecirc;u chuẩn chất lượng:&nbsp;</strong>Nhập khẩu</p>\n\n<p>-&nbsp;<strong>Đặc điểm:</strong></p>\n\n<ul>\n	<li><strong>Vỏ:</strong>&nbsp;Lớp vỏ cam Navel Mỹ c&oacute; độ d&agrave;y vừa phải, dễ b&oacute;c.</li>\n	<li><strong>M&uacute;i:</strong>&nbsp;T&eacute;p mọng nước m&agrave;u v&agrave;ng tươi, &iacute;t xơ,</li>\n	<li><strong>Hương vị:</strong>&nbsp;Ngọt thanh, thơm m&aacute;t.&nbsp;</li>\n</ul>\n\n<p><strong>- Gi&aacute; trị dinh dưỡng:</strong></p>\n\n<p><strong>Cam v&agrave;ng Navel&nbsp;&nbsp;</strong>l&agrave; nguồn cung cấp dồi d&agrave;o vitamin C, A, B, canxi, magie, kali, chất xơ, v&agrave; chất chống oxy h&oacute;a.</p>\n\n<ul>\n	<li><strong>Vitamin C:</strong>&nbsp;Gi&uacute;p tăng cường hệ miễn dịch, chống l&atilde;o h&oacute;a, giảm cholesterol, v&agrave; hỗ trợ qu&aacute; tr&igrave;nh hấp thu sắt.</li>\n	<li><strong>Vitamin A:</strong>&nbsp;Tốt cho sức khỏe của mắt, da, v&agrave; hệ miễn dịch.</li>\n	<li><strong>Vitamin B:</strong>&nbsp;Gi&uacute;p chuyển h&oacute;a thức ăn th&agrave;nh năng lượng, duy tr&igrave; hệ thần kinh khỏe mạnh, v&agrave; cải thiện t&acirc;m trạng.</li>\n	<li><strong>Canxi:</strong>&nbsp;Gi&uacute;p ph&aacute;t triển v&agrave; bảo vệ hệ xương khớp.</li>\n	<li><strong>Magie:</strong>&nbsp;Gi&uacute;p điều h&ograve;a huyết &aacute;p, giảm nguy cơ mắc bệnh tim mạch, v&agrave; hỗ trợ chức năng thần kinh.</li>\n	<li><strong>Kali:</strong>&nbsp;Gi&uacute;p kiểm so&aacute;t huyết &aacute;p, duy tr&igrave; c&acirc;n bằng điện giải trong cơ thể, v&agrave; hỗ trợ chức năng cơ bắp.</li>\n	<li><strong>Chất xơ:</strong>&nbsp;Gi&uacute;p hỗ trợ hệ ti&ecirc;u h&oacute;a, ngăn ngừa t&aacute;o b&oacute;n, v&agrave; tạo cảm gi&aacute;c no l&acirc;u.</li>\n	<li><strong>Chất chống oxy h&oacute;a:</strong>&nbsp;Gi&uacute;p bảo vệ cơ thể khỏi t&aacute;c hại của c&aacute;c gốc tự do, giảm nguy cơ mắc bệnh ung thư, v&agrave; tim mạch.</li>\n</ul>\n\n<p><strong>- Lợi &iacute;ch:</strong></p>\n\n<ul>\n	<li>Tăng cường hệ miễn dịch</li>\n	<li>Chống l&atilde;o h&oacute;a</li>\n	<li>Giảm cholesterol</li>\n	<li>Hỗ trợ qu&aacute; tr&igrave;nh hấp thu sắt</li>\n	<li>Tốt cho sức khỏe của mắt, da, v&agrave; hệ miễn dịch</li>\n	<li>Gi&uacute;p chuyển h&oacute;a thức ăn th&agrave;nh năng lượng</li>\n	<li>Duy tr&igrave; hệ thần kinh khỏe mạnh</li>\n	<li>Cải thiện t&acirc;m trạng</li>\n	<li>Gi&uacute;p ph&aacute;t triển v&agrave; bảo vệ hệ xương khớp</li>\n	<li>Gi&uacute;p điều h&ograve;a huyết &aacute;p</li>\n	<li>Giảm nguy cơ mắc bệnh tim mạch</li>\n	<li>Hỗ trợ chức năng cơ bắp</li>\n	<li>Hỗ trợ hệ ti&ecirc;u h&oacute;a</li>\n	<li>Ngăn ngừa t&aacute;o b&oacute;n</li>\n	<li>Tạo cảm gi&aacute;c no l&acirc;u</li>\n	<li>Giảm nguy cơ mắc bệnh ung thư</li>\n</ul>\n\n<p><strong>- C&aacute;ch sử dụng:</strong></p>\n\n<p>Cam v&agrave;ng Navel&nbsp;c&oacute; thể được sử dụng trực tiếp, &eacute;p nước, l&agrave;m sinh tố, hoặc chế biến th&agrave;nh c&aacute;c m&oacute;n ăn kh&aacute;c nhau như salad, b&aacute;nh, mứt,...</p>\n\n<p><strong>- C&aacute;ch lựa chọn cam ngon:</strong></p>\n\n<ul>\n	<li>N&ecirc;n chọn những quả cam c&oacute; h&igrave;nh tr&ograve;n đều, kh&ocirc;ng bị m&eacute;o m&oacute;, sần s&ugrave;i. Chọn quả cam c&oacute; k&iacute;ch thước vừa phải, kh&ocirc;ng qu&aacute; to cũng kh&ocirc;ng qu&aacute; nhỏ. Quả cam to thường bị xốp v&agrave; &iacute;t nước, c&ograve;n quả cam nhỏ thường chưa ch&iacute;n tới.</li>\n	<li>Với Farmers Market ti&ecirc;u ch&iacute; h&agrave;ng đầu l&agrave; sản phảm đạt chất lượng cao, nguồn gốc r&otilde; r&agrave;ng, mang đến trải nghiệm tốt nhất cho kh&aacute;ch h&agrave;ng.&nbsp;</li>\n</ul>\n\n<p><strong>- C&aacute;ch bảo quản cam được l&acirc;u:</strong></p>\n\n<p><strong>+ Bảo quản cam ở nhiệt độ ph&ograve;ng:</strong></p>\n\n<ul>\n	<li><strong>Nếu bạn muốn cam ch&iacute;n nhanh hơn, bạn c&oacute; thể bảo quản cam ở nhiệt độ ph&ograve;ng.</strong></li>\n	<li>Đặt cam trong t&uacute;i giấy hoặc t&uacute;i nhựa v&agrave; để ở nơi tho&aacute;ng m&aacute;t, tr&aacute;nh &aacute;nh nắng trực tiếp.</li>\n	<li>Cam sẽ ch&iacute;n trong v&ograve;ng v&agrave;i ng&agrave;y.</li>\n</ul>\n\n<p><strong>+ Bảo quản cam trong tủ lạnh:</strong></p>\n\n<ul>\n	<li><strong>Nếu bạn muốn bảo quản cam l&acirc;u hơn, bạn n&ecirc;n bảo quản cam trong tủ lạnh.</strong></li>\n	<li>Đặt cam v&agrave;o khay mềm v&agrave; để trong ngăn m&aacute;t tủ lạnh.</li>\n	<li>Cam sẽ giữ được tươi ngon trong v&agrave;i tuần.</li>\n	<li>Kh&ocirc;ng n&ecirc;n rửa cam trước khi bảo quản v&igrave; điều n&agrave;y c&oacute; thể l&agrave;m tăng nguy cơ thối rữa.</li>\n</ul>\n\n<p><strong>- Phụ nữ mang thai v&agrave; trẻ em ăn cam c&oacute; được kh&ocirc;ng?</strong></p>\n\n<p><strong>+ B&agrave; bầu cần</strong>&nbsp;phải th&ecirc;m cam v&agrave;o trong thực đơn h&agrave;ng ng&agrave;y của m&igrave;nh v&igrave; loại tr&aacute;i c&acirc;y&nbsp;n&agrave;y c&oacute; nhiều gi&aacute; trị về mặt dinh dưỡng đối với thai kỳ.</p>\n\n<ul>\n	<li>\n	<h3>Tăng cường hệ miễn dịch cho cơ thể</h3>\n	</li>\n</ul>\n\n<ul>\n	<li>\n	<h3>Gi&uacute;p ph&aacute;t triển tr&iacute; n&atilde;o của thai nhi</h3>\n	</li>\n</ul>\n\n<ul>\n	<li>\n	<h3>Gi&uacute;p ngừa chứng t&aacute;o b&oacute;n hiệu quả</h3>\n	</li>\n</ul>\n\n<ul>\n	<li>\n	<h3>Gi&uacute;p điều h&ograve;a huyết &aacute;p cho cơ thể, giải độc, lợi tiểu cho mẹ</h3>\n	</li>\n	<li>\n	<p>Đối với phụ nữ mang thai, lượng vitamin C cần thiết để cung cấp cho cơ thể l&agrave; 80 mg v&agrave; tăng l&ecirc;n 120 mg khi bước v&agrave;o giai đoạn cho con b&uacute;</p>\n	</li>\n</ul>\n\n<p>+<strong>&nbsp;Đối với trẻ em&nbsp;sau 6 th&aacute;ng tuổi</strong>&nbsp;c&oacute; thể ăn cam được, chỉ n&ecirc;n cho trẻ ăn nửa quả cam mỗi ng&agrave;y, kể cả khi b&eacute; rất muốn ăn cũng kh&ocirc;ng n&ecirc;n cho ăn nhiều v&igrave; c&oacute; thể xảy ra t&igrave;nh trạng dị ứng cam.</p>\n', 'Trái cây Nhập', 119000, 1, '2024-07-14', 7, 'f18e2aaf72c8602643e74251ca04a1c0', 'zunhmn32doumxfxc2xrn', 4, 1, 1),
(79, 'Kiwi vàng hữu cơ New Zealand', '<h1><strong>Kiwi v&agrave;ng hữu cơ New Zealand</strong></h1>\n\n<p><strong>- Xuất xứ:&nbsp;</strong>New Zealand</p>\n\n<p>-&nbsp;<strong>Ti&ecirc;u chuẩn chất lượng:&nbsp;</strong>Nhập khẩu</p>\n\n<p><strong>- Đặc điểm nổi bật:</strong></p>\n\n<ul>\n	<li>Đạt chuẩn chất lượng nhập khẩu từ New Zealand.&nbsp;</li>\n	<li>Quả to mọng, vỏ mỏng,vị ngọt dịu xen ch&uacute;t chua đặc trưng.</li>\n	<li><strong>Thịt quả:</strong>&nbsp;M&agrave;u v&agrave;ng trong, bắt mắt với nhiều hạt đen tạo th&agrave;nh v&ograve;ng tr&ograve;n xung quanh trục dọc của quả.</li>\n	<li><strong>Vị:</strong>&nbsp;Ngọt m&aacute;t, đặc trưng.</li>\n	<li><strong>Xuất xứ:</strong>&nbsp;New Zealand.</li>\n	<li><strong>Chứng nhận:</strong>&nbsp;Hữu cơ BioGro New Zealand.</li>\n</ul>\n\n<p><strong>- Gi&aacute; trị dinh dưỡng:</strong></p>\n\n<ul>\n	<li><strong>Vitamin C:</strong>&nbsp;Dồi d&agrave;o, cao gấp 2 lần kiwi xanh, gi&uacute;p tăng cường hệ miễn dịch, chống oxy h&oacute;a, bảo vệ da khỏi t&aacute;c hại của &aacute;nh nắng mặt trời.</li>\n	<li><strong>Vitamin E:</strong>&nbsp;Gi&uacute;p bảo vệ tế b&agrave;o khỏi tổn thương, hỗ trợ sức khỏe tim mạch.</li>\n	<li><strong>Kali:</strong>&nbsp;Gi&uacute;p điều h&ograve;a huyết &aacute;p, tốt cho tim mạch.</li>\n	<li><strong>Chất xơ:</strong>&nbsp;Gi&uacute;p hệ ti&ecirc;u h&oacute;a khỏe mạnh, hỗ trợ giảm c&acirc;n.</li>\n	<li><strong>Axit folic:</strong>&nbsp;Quan trọng cho phụ nữ mang thai, gi&uacute;p ph&aacute;t triển tr&iacute; n&atilde;o thai nhi.</li>\n	<li><strong>Ngo&agrave;i ra:</strong>&nbsp;C&ograve;n chứa nhiều dưỡng chất kh&aacute;c như vitamin K, magie, photpho, đồng,...</li>\n</ul>\n\n<p><strong>- Lợi &iacute;ch của việc sử dụng kiwi:</strong></p>\n\n<ul>\n	<li>Tăng cường hệ miễn dịch.</li>\n	<li>Tốt cho sức khỏe tim mạch.</li>\n	<li>Hỗ trợ ti&ecirc;u h&oacute;a.</li>\n	<li>Gi&uacute;p giảm c&acirc;n.</li>\n	<li>Tốt cho da.</li>\n	<li>Tốt cho mắt.</li>\n	<li>Ngăn ngừa ung thư.</li>\n</ul>\n\n<p><strong>- C&aacute;ch sử dụng kiwi:</strong></p>\n\n<ul>\n	<li>C&oacute; thể ăn trực tiếp, gọt vỏ hoặc bỏ vỏ.</li>\n	<li>Th&ecirc;m v&agrave;o salad, sinh tố, sữa chua.</li>\n	<li>L&agrave;m mứt, b&aacute;nh ngọt, kem.</li>\n</ul>\n\n<p>-&nbsp;<strong>C&aacute;ch lựa chọn kiwi ngon:</strong></p>\n\n<ul>\n	<li>N&ecirc;n chọn những quả kiwi c&oacute; vỏ căng mịn, m&agrave;u n&acirc;u v&agrave;ng đồng đều, kh&ocirc;ng bị sần s&ugrave;i, trầy xước hay c&oacute; đốm n&acirc;u.</li>\n	<li>D&ugrave;ng ng&oacute;n tay ấn nhẹ v&agrave;o quả kiwi. Nếu quả kiwi c&oacute; độ mềm vừa phải, hơi đ&agrave;n hồi th&igrave; đ&oacute; l&agrave; quả kiwi ngon.</li>\n	<li>Với Farmers Market ti&ecirc;u ch&iacute; h&agrave;ng đầu l&agrave; sản phảm đạt chất lượng cao, nguồn gốc r&otilde; r&agrave;ng, mang đến trải nghiệm tốt nhất cho kh&aacute;ch h&agrave;ng.&nbsp;</li>\n</ul>\n\n<p><strong>- Để bảo quản kiwi được l&acirc;u v&agrave; giữ được độ tươi ngon, bạn cần lưu &yacute;:</strong></p>\n\n<p><strong>+ Nhiệt độ:</strong></p>\n\n<ul>\n	<li><strong>Nhiệt độ ph&ograve;ng:</strong>&nbsp;Nếu kiwi chưa ch&iacute;n, h&atilde;y để ch&uacute;ng ở nhiệt độ ph&ograve;ng cho đến khi ch&iacute;n. Tr&aacute;nh &aacute;nh nắng trực tiếp.</li>\n	<li><strong>Tủ lạnh:</strong>&nbsp;Khi kiwi đ&atilde; ch&iacute;n, h&atilde;y đặt ch&uacute;ng v&agrave;o tủ lạnh ở nhiệt độ từ 0&deg;C đến 4&deg;C. Ở nhiệt độ n&agrave;y, kiwi c&oacute; thể được bảo quản từ 5 đến 7 ng&agrave;y.</li>\n</ul>\n\n<p><strong>+ Độ ẩm:</strong></p>\n\n<ul>\n	<li>Kiwi cần được bảo quản ở độ ẩm cao để giữ được độ tươi ngon.</li>\n	<li>Bạn c&oacute; thể đặt kiwi v&agrave;o t&uacute;i nhựa hoặc hộp đựng c&oacute; nắp đậy để giữ ẩm.</li>\n	<li>Tr&aacute;nh để kiwi tiếp x&uacute;c trực tiếp với nước v&igrave; sẽ khiến kiwi bị nẫu.</li>\n</ul>\n\n<p><strong>+ Ph&acirc;n chia:</strong></p>\n\n<ul>\n	<li>N&ecirc;n ph&acirc;n chia kiwi ra từng quả ri&ecirc;ng biệt khi bảo quản trong tủ lạnh để tr&aacute;nh kiwi bị dập n&aacute;t.</li>\n	<li>Tr&aacute;nh để kiwi tiếp x&uacute;c với c&aacute;c loại tr&aacute;i c&acirc;y kh&aacute;c như t&aacute;o, chuối, l&ecirc;,... v&igrave; những loại tr&aacute;i c&acirc;y n&agrave;y c&oacute; thể sản sinh ra kh&iacute; ethylene, khiến kiwi ch&iacute;n nhanh hơn v&agrave; dễ bị nẫu.</li>\n</ul>\n\n<p><strong>+ Mẹo bảo quản:</strong></p>\n\n<ul>\n	<li><strong>Để kiwi ch&iacute;n nhanh hơn:</strong>&nbsp;Bạn c&oacute; thể cho kiwi v&agrave;o t&uacute;i giấy c&ugrave;ng với t&aacute;o hoặc chuối.</li>\n	<li><strong>Để kiwi ch&iacute;n chậm hơn:</strong>&nbsp;Bạn c&oacute; thể bọc kiwi bằng giấy nến hoặc giấy b&aacute;o.</li>\n	<li><strong>Để kiwi đ&ocirc;ng lạnh:</strong>&nbsp;Bạn c&oacute; thể cắt kiwi th&agrave;nh từng miếng v&agrave; cho v&agrave;o t&uacute;i đ&ocirc;ng lạnh. Kiwi đ&ocirc;ng lạnh c&oacute; thể được bảo quản trong v&agrave;i th&aacute;ng.</li>\n</ul>\n\n<p><strong>- Lưu &yacute;:</strong></p>\n\n<ul>\n	<li>Kh&ocirc;ng n&ecirc;n bảo quản kiwi ở nhiệt độ qu&aacute; cao v&igrave; sẽ khiến kiwi nhanh bị ch&iacute;n nẫu.</li>\n	<li>Kh&ocirc;ng n&ecirc;n bảo quản kiwi qu&aacute; l&acirc;u v&igrave; sẽ khiến kiwi mất đi hương vị v&agrave; dưỡng chất.</li>\n</ul>\n\n<p><strong>- C&aacute;ch d&ugrave;ng kiwi</strong></p>\n\n<p><strong>+ Ăn trực tiếp:</strong></p>\n\n<ul>\n	<li>Đ&acirc;y l&agrave; c&aacute;ch đơn giản v&agrave; phổ biến nhất để thưởng thức kiwi.</li>\n	<li>Bạn c&oacute; thể rửa sạch kiwi, gọt vỏ hoặc bỏ vỏ (t&ugrave;y sở th&iacute;ch) v&agrave; cắt th&agrave;nh từng miếng vừa ăn.</li>\n	<li>Kiwi c&oacute; thể ăn k&egrave;m với sữa chua, mật ong hoặc th&ecirc;m v&agrave;o salad, sinh tố.</li>\n</ul>\n\n<p><strong>+ L&agrave;m sinh tố:</strong></p>\n\n<ul>\n	<li>Kiwi l&agrave; nguy&ecirc;n liệu tuyệt vời để l&agrave;m sinh tố.</li>\n	<li>Bạn c&oacute; thể kết hợp kiwi với c&aacute;c loại tr&aacute;i c&acirc;y kh&aacute;c như chuối, d&acirc;u t&acirc;y, t&aacute;o,... để tạo ra những ly sinh tố thơm ngon, bổ dưỡng.</li>\n</ul>\n\n<p><strong>+ L&agrave;m mứt:</strong></p>\n\n<ul>\n	<li>Mứt kiwi l&agrave; m&oacute;n ăn k&egrave;m ngon miệng cho b&aacute;nh m&igrave; hoặc b&aacute;nh quy.</li>\n	<li>Để l&agrave;m mứt kiwi, bạn cần sơ chế kiwi, sau đ&oacute; nấu với đường v&agrave; nước cho đến khi sệt lại.</li>\n</ul>\n\n<p><strong>- Phụ nữ mang thai v&agrave; trẻ em ăn kiwi c&oacute; được kh&ocirc;ng?</strong></p>\n\n<p><strong>+ Phụ nữ mang thai</strong></p>\n\n<ul>\n	<li>Kiwi l&agrave; loại tr&aacute;i c&acirc;y&nbsp;<strong>chứa h&agrave;m lượng vitamin C cao gấp hai lần chanh</strong>. B&ecirc;n cạnh đ&oacute;, b&agrave; bầu ăn kiwi c&ograve;n được bổ sung nhiều chất xơ, vitamin E, carbohydrate, năng lượng v&agrave; c&aacute;c kho&aacute;ng chất kh&aacute;c</li>\n	<li>B&agrave; bầu ăn kiwi c&oacute; thể đ&aacute;p ứng đến 140% liều lượng vitamin C khuyến c&aacute;o h&agrave;ng ng&agrave;y. C&oacute; thể n&oacute;i, v&igrave; c&oacute; h&agrave;m lượng vitamin C cao m&agrave; kiwi trở th&agrave;nh loại tr&aacute;i c&acirc;y&nbsp;<strong>rất tốt cho b&agrave; bầu v&agrave; thai nhi.</strong></li>\n	<li><strong>Đối với mẹ bầu trước v&agrave; sau khi sinh</strong>, vitamin C gi&uacute;p cơ thể mẹ hấp thụ sắt tốt hơn để&nbsp;<strong>ngăn ngừa thiếu m&aacute;u</strong></li>\n	<li>Vitamin C trong quả kiwi gi&uacute;p h&igrave;nh th&agrave;nh c&aacute;c chất dẫn truyền thần kinh, một chất rất quan trọng đối với chức năng của n&atilde;o.</li>\n	<li>Vitamin C c&oacute; t&iacute;nh chống oxy h&oacute;a mạnh, gi&uacute;p bảo vệ RNA v&agrave; DNA khỏi t&aacute;c hại của c&aacute;c gốc tự do.</li>\n</ul>\n\n<p><strong>+ Cho b&eacute; ăn kiwi</strong>&nbsp;khi trẻ từ 8 đến 10 th&aacute;ng v&igrave; t&iacute;nh chất chua của n&oacute;. Nếu em b&eacute; của bạn đ&atilde; c&oacute; biểu hiện dị ứng thức ăn hoặc ph&aacute;t ban t&atilde; trước đ&oacute;, h&atilde;y c&acirc;n nhắc cho b&eacute; ăn tr&aacute;i c&acirc;y từ 10 đến 12 th&aacute;ng.</p>\n\n<ul>\n	<li><strong>Vitamin C</strong>: Kiwi rất gi&agrave;u vitamin C, cung cấp 230% lượng vitamin C được khuyến nghị mỗi ng&agrave;y. Vitamin C gi&uacute;p tăng cường hệ miễn dịch, hấp thụ sắt từ thực phẩm kh&aacute;c v&agrave; chữa l&agrave;nh vết thương.</li>\n	<li><strong>Chất xơ</strong>: Kiwi cung cấp 16% lượng chất xơ cần thiết cho trẻ. Chất xơ gi&uacute;p giảm t&aacute;o b&oacute;n v&agrave; duy tr&igrave; hệ ti&ecirc;u h&oacute;a khỏe mạnh.</li>\n</ul>\n\n<p><strong>- Những lưu &yacute; khi thưởng thức:&nbsp;</strong></p>\n\n<ul>\n	<li>Mẹ bầu chỉ n&ecirc;n ăn khoảng 2&ndash;3 tr&aacute;i kiwi mỗi ng&agrave;y l&agrave; ph&ugrave; hợp.</li>\n	<li>Kiwi c&oacute; t&iacute;nh axit cao, v&igrave; vậy đừng ăn qu&aacute; nhiều v&igrave; bạn c&oacute; thể bị lở miệng, lo&eacute;t lưỡi v&agrave; ph&aacute;t ban.</li>\n	<li>Nếu bạn bị dị ứng do di truyền, vi&ecirc;m dạ d&agrave;y hoặc c&aacute;c vấn đề kh&aacute;c th&igrave; cần tr&aacute;nh ăn kiwi khi mang thai.</li>\n	<li>Những người bị vi&ecirc;m mũi dị ứng thường c&oacute; nguy cơ dễ phản ứng dị ứng với kiwi.</li>\n</ul>\n', 'Trái cây Nhập', 269000, 1, '2024-07-14', 7, '6e9e2af098dd693c24e44de4b47c8a94', 'amrw0mwplv5jjd3xh55i', 4, 1, 1),
(80, 'Táo xanh Granny Smith New Zealand size 90 - 100', '<h1><strong>T&aacute;o xanh Granny Smith New Zealand size 90 - 100&nbsp;</strong></h1>\n\n<p><strong>- Xuất xứ:&nbsp;</strong>New Zealand</p>\n\n<p><strong>- Chất lượng:</strong>&nbsp;Nhập khẩu</p>\n\n<p>-&nbsp;<strong>&nbsp;Khối lượng:&nbsp;</strong>1kg</p>\n\n<p><strong>- Đặc Điểm:</strong></p>\n\n<ul>\n	<li>Đạt ti&ecirc;u chuẩn chất lượng nhập khẩu.&nbsp;</li>\n	<li>T&aacute;o Granny Smith - loại t&aacute;o độc đ&aacute;o với vỏ m&agrave;u xanh b&oacute;ng.&nbsp;</li>\n	<li>Nổi bật với độ gi&ograve;n cao, rất thơm &amp; đường &iacute;t, c&oacute; vị chua đặc trưng.</li>\n</ul>\n\n<p><strong>T&aacute;o Xanh Granny Smith&nbsp;</strong>c&oacute; hương vị chua thanh, ngọt dịu nhẹ, hương thơm dịu sẽ l&agrave; m&oacute;n tr&aacute;ng miệng kh&ocirc;ng thể thiếu sau mỗi bữa cơm. Ngo&agrave;i ra t&aacute;o xanh tươi c&ograve;n được biết đến với nhiều gi&aacute; trị dinh dưỡng tốt cho cơ thể.</p>\n\n<p><strong>- Lợi &iacute;ch:</strong></p>\n\n<ul>\n	<li><strong>Gi&agrave;u vitamin v&agrave; kho&aacute;ng chất:</strong>&nbsp;T&aacute;o chứa nhiều vitamin C, vitamin K, kali v&agrave; chất xơ, tốt cho sức khỏe tim mạch, hệ miễn dịch v&agrave; hệ ti&ecirc;u h&oacute;a.</li>\n	<li><strong>&Iacute;t calo:</strong>&nbsp;T&aacute;o chỉ chứa khoảng 60 calo mỗi quả, ph&ugrave; hợp cho những người ăn ki&ecirc;ng hoặc đang muốn giảm c&acirc;n.</li>\n	<li><strong>Tiện lợi:</strong>&nbsp;K&iacute;ch thước nhỏ gọn v&agrave; được đ&oacute;ng g&oacute;i ri&ecirc;ng lẻ gi&uacute;p t&aacute;o&nbsp;dễ d&agrave;ng mang theo v&agrave; thưởng thức mọi l&uacute;c mọi nơi.</li>\n	<li><strong>Gi&ograve;n tan v&agrave; mọng nước:</strong>&nbsp;T&aacute;p&nbsp;c&oacute; vị ngọt thanh, gi&ograve;n tan v&agrave; mọng nước, mang lại cảm gi&aacute;c ngon miệng khi thưởng thức.</li>\n</ul>\n\n<p><strong>- Hướng dẫn sử dụng:</strong></p>\n\n<ul>\n	<li>Rửa nhẹ nh&agrave;ng tr&aacute;i t&aacute;o. Sau đ&oacute; cắt đ&ocirc;i tr&aacute;i&nbsp;t&aacute;o&nbsp;v&agrave; loại bỏ phần hạt b&ecirc;n trong.</li>\n	<li>Bảo quản trong ngăn m&aacute;t&nbsp;tủ lạnh. T&aacute;o giữ được độ tươi, độ gi&ograve;n trong v&ograve;ng 1-4 tuần. Sau thời gian n&agrave;y, t&aacute;o sẽ ngọt hơn, độ gi&ograve;n thấp hơn v&agrave; t&aacute;o trở n&ecirc;n xốp.</li>\n	<li>Cần tr&aacute;nh để t&aacute;o với c&aacute;c thực phẩm c&oacute; m&ugrave;i kh&aacute;c như h&agrave;nh, tỏi, t&aacute;o sẽ dễ nhiễm m&ugrave;i.</li>\n	<li>Kh&ocirc;ng rửa&nbsp;trước khi cho v&agrave;o tủ lạnh.</li>\n</ul>\n\n<p><strong>- T&aacute;o c&oacute; thể được sử dụng để l&agrave;m g&igrave;?</strong></p>\n\n<ul>\n	<li>Sinh tố: t&aacute;o kết hợp với c&aacute;c loại tr&aacute;i c&acirc;y kh&aacute;c như chuối, d&acirc;u t&acirc;y, sữa chua,... để tạo th&agrave;nh m&oacute;n sinh tố thơm ngon v&agrave; đầy dinh dưỡng.</li>\n	<li>Salad: T&aacute;o&nbsp;cắt nhỏ, trộn với c&aacute;c loại rau củ quả kh&aacute;c c&ugrave;ng với nước sốt y&ecirc;u th&iacute;ch để tạo th&agrave;nh m&oacute;n salad thanh m&aacute;t v&agrave; đầy đủ dưỡng chất.</li>\n	<li>Nước &eacute;p: T&aacute;o&nbsp;&eacute;p lấy nước c&oacute; thể uống trực tiếp hoặc pha th&ecirc;m mật ong để tăng hương vị.</li>\n	<li>B&aacute;nh t&aacute;o: T&aacute;o&nbsp;được sử dụng l&agrave;m nh&acirc;n cho b&aacute;nh t&aacute;o hoặc trang tr&iacute; mặt b&aacute;nh.</li>\n	<li>Mứt t&aacute;o: T&aacute;o c&oacute; thể được nấu th&agrave;nh mứt để ăn k&egrave;m với b&aacute;nh m&igrave; hoặc sử dụng trong c&aacute;c m&oacute;n tr&aacute;ng miệng.</li>\n</ul>\n\n<p><strong>- C&aacute;ch lựa chọn t&aacute;o:</strong></p>\n\n<ul>\n	<li>\n	<p>Kh&aacute;ch h&agrave;ng&nbsp;n&ecirc;n&nbsp;chọn những quả t&aacute;o c&oacute; h&igrave;nh dạng c&acirc;n đối, kh&ocirc;ng bị m&eacute;o m&oacute;, dập n&aacute;t.</p>\n	</li>\n	<li>\n	<p>Với Farmers Market ti&ecirc;u ch&iacute; h&agrave;ng đầu l&agrave; sản phảm đạt chất lượng cao, nguồn gốc r&otilde; r&agrave;ng, mang đến trải nghiệm tốt nhất cho kh&aacute;ch h&agrave;ng.&nbsp;</p>\n	</li>\n</ul>\n\n<p><strong>- Hướng dẫn bảo quản t&aacute;o</strong></p>\n\n<ul>\n	<li>N&ecirc;n đặt nho v&agrave;o&nbsp;<strong>hộp k&iacute;n kh&iacute;</strong>&nbsp;hoặc&nbsp;<strong>t&uacute;i ziplock&nbsp;</strong>để giữ độ ẩm v&agrave;&nbsp;bảo quan t&aacute;o&nbsp;trong ngăn m&aacute;t tủ lạnh&nbsp;</li>\n	<li>Kh&ocirc;ng n&ecirc;n bảo quản t&aacute;o c&ugrave;ng với c&aacute;c loại tr&aacute;i c&acirc;y kh&aacute;c như chuối, l&ecirc;,... v&igrave; ethylene từ những loại tr&aacute;i c&acirc;y n&agrave;y c&oacute; thể khiến t&aacute;o nhanh ch&iacute;n v&agrave; mềm.</li>\n	<li>T&aacute;o c&oacute; thể bảo quản trong tủ lạnh từ 1-2 tuần.</li>\n</ul>\n\n<p><strong>- Những lưu &yacute; khi sử dụng T&aacute;o:&nbsp;</strong></p>\n\n<ul>\n	<li>Kh&ocirc;ng n&ecirc;n ăn t&aacute;o&nbsp;đ&atilde; bị dập n&aacute;t hoặc hư hỏng.</li>\n	<li>N&ecirc;n ăn t&aacute;o&nbsp;với lượng vừa phải, khoảng 1-2 quả mỗi ng&agrave;y.</li>\n	<li>Người bị bệnh tiểu đường n&ecirc;n ăn t&aacute;o với lượng vừa phải v&igrave; t&aacute;o chứa đường tự nhi&ecirc;n.</li>\n	<li>Trẻ em bị ti&ecirc;u chảy hoặc t&aacute;o b&oacute;n kh&ocirc;ng n&ecirc;n ăn t&aacute;o.</li>\n</ul>\n\n<p><strong>- Phụ nữ mang thai v&agrave; trẻ em ăn t&aacute;o&nbsp;c&oacute; được kh&ocirc;ng?</strong></p>\n\n<ul>\n	<li>T&aacute;o&nbsp;cung cấp vitamin C, folate v&agrave; chất xơ&nbsp;<strong>cần thiết cho phụ nữ mang thai v&agrave; cho con b&uacute;.</strong></li>\n	<li>Chất xơ trong t&aacute;o gi&uacute;p giảm t&aacute;o b&oacute;n, vitamin C tăng cường hệ miễn dịch, folate<strong>&nbsp;tốt cho sự ph&aacute;t triển của thai nhi</strong>.</li>\n	<li>\n	<p>Trẻ em tr&ecirc;n 1 tuổi c&oacute; thể ăn t&aacute;o, cắt nho th&agrave;nh từng miếng nhỏ để trẻ dễ ăn v&agrave; tr&aacute;nh nguy cơ nghẹn.</p>\n	</li>\n</ul>\n\n<ul>\n	<li><strong>Cho trẻ ăn t&aacute;o với lượng vừa phải:</strong>&nbsp;Trẻ em n&ecirc;n ăn t&aacute;o với lượng vừa phải, khoảng 50-100 gram mỗi ng&agrave;y. Ăn qu&aacute; nhiều t&aacute;o c&oacute; thể g&acirc;y đầy bụng, kh&oacute; ti&ecirc;u.</li>\n</ul>\n\n<div class=\"eJOY__extension_root_class\" id=\"eJOY__extension_root\" style=\"all: unset;\">&nbsp;</div>\n', 'Trái cây Nhập', 109000, 1, '2024-07-14', 7, 'a66ed84a180ecda9687c4d589b573576', 'jtqrvzv31i3bxneoa7yd', 4, 2, 1);
